routine.definition:
FUNCTION "DATEDIFF" 
 /*
 Return Date Diffirence like SQL Server.
 Created by: Dilmurat
 Final update: 05/17/2005
*/(difftype IN VARCHAR,   startdate IN DATE,   enddate IN DATE) RETURN pls_integer AS sdays NUMBER;
edays NUMBER;
BEGIN
  sdays := to_number(to_char(startdate,   'J'));
  edays := to_number(to_char(enddate,   'J'));
  CASE initcap(difftype)
WHEN 'Day' THEN
  RETURN to_date(to_char(enddate,   'mm/dd/yyyy'),   'mm/dd/yyyy') -to_date(to_char(startdate,   'mm/dd/yyyy'),   'mm/dd/yyyy');
WHEN 'Month' THEN
  CASE
  WHEN months_between(enddate,   startdate) > 0 THEN
    RETURN FLOOR(months_between(enddate,   startdate));
  ELSE
    RETURN ceil(months_between(enddate,   startdate));
  END
  CASE;
WHEN 'Year' THEN
  RETURN to_number(to_char(enddate,   'yyyy')) -to_number(to_char(startdate,   'yyyy'));
WHEN 'Hour' THEN
  RETURN(edays * 24 + to_number(to_char(enddate,   'hh24'))) -(sdays * 24 + to_number(to_char(startdate,   'hh24')));
WHEN 'Min' THEN
  RETURN(edays * 24 * 60 + to_number(to_char(enddate,   'hh24')) * 60 + to_number(to_char(enddate,   'mi'))) -(sdays * 24 * 60 + to_number(to_char(startdate,   'hh24')) * 60 + to_number(to_char(startdate,   'mi')));
WHEN 'Sec' THEN
  RETURN(edays * 24 * 3600 + to_number(to_char(enddate,   'hh24')) * 3600 + to_number(to_char(enddate,   'mi')) * 60 + to_number(to_char(enddate,   'ss'))) -(sdays * 24 * 3600 + to_number(to_char(startdate,   'hh24')) * 3600 + to_number(to_char(startdate,   'mi')) * 60 + to_number(to_char(startdate,   'ss')));
END
CASE;
END;

FUNCTION DECODETEXT 
(
  INPUTTEXT IN VARCHAR2 
) RETURN VARCHAR2 AS returnval varchar2(4000);
textval varchar2(4000);
n  Numeric(2) := 0;
BEGIN
    textval := INPUTTEXT;
    if textval is null then
        return null;
    elsif length(textval) < 50 then
        Begin
            if isDate(textval) = 1 then
                return textval;
            elsif isNumeric(textval) = 1 then
                return textval;
            end if;
        End;
    End if;

    <<DECODEAMP>>
    select instr(textval, '&amp;') into n from dual;
    if n > 0 then
        textval := Replace(textval, '&amp;', '&');
        GOTO DECODEAMP;
    end if;
    for val in (Select htmlcode, htmlcharacter from list_htmlcodes )
    Loop
        Begin
            n := 0;
            select instr(textval, val.htmlcode) into n from dual;
            if n > 0 then
                textval := Replace(textval, val.htmlcode, val.htmlcharacter);
            end if;
        end;
    end loop;
    return textval;
END DECODETEXT;

PROCEDURE             "DUMP_TABLE_TO_CSV" (
    p_tname    IN VARCHAR2,
    p_dir      IN VARCHAR2,
    p_filename IN VARCHAR2,
    startId IN NUMBER,
    endId IN NUMBER,
    s3BucketName  in  varchar2,
    s3Prifix  in  varchar2
)
AS
l_output utl_file.file_type;
l_theCursor   INTEGER DEFAULT dbms_sql.open_cursor;
l_columnValue VARCHAR2(2000);
l_status      INTEGER;
l_query       VARCHAR2(1000) DEFAULT 'SELECT /*+ parallel */  * from ' || p_tname || ' Where SID Between ' || startId || ' and ' || endId;
l_colCnt      NUMBER := 0;
l_separator   VARCHAR2(1);
l_descTbl dbms_sql.desc_tab;
taskId      varchar2(30);
newFileName varchar2(50) := p_filename || '.' || endId || '.csv';
dbtaskLog   number(1);
BEGIN
    l_output := utl_file.fopen( p_dir, newFileName, 'w', 32767);
    EXECUTE immediate 'alter session set nls_date_format=''dd-mon-yyyy hh24:mi:ss'' ';
    dbms_sql.parse( l_theCursor, l_query, dbms_sql.native );
    dbms_sql.describe_columns( l_theCursor, l_colCnt, l_descTbl );
    FOR i IN 1 .. l_colCnt
    LOOP
        dbms_sql.define_column( l_theCursor, i, l_columnValue, 2000 );
    END LOOP;
    l_status := dbms_sql.execute(l_theCursor);
    WHILE ( dbms_sql.fetch_rows(l_theCursor) > 0 )
    LOOP
        l_separator := '';
        FOR i IN 2 .. l_colCnt
        LOOP
            dbms_sql.column_value( l_theCursor, i, l_columnValue);
            utl_file.put( l_output, l_separator || '"' || Replace(l_columnValue, '"', '""') || '"' );
            l_separator := ',';
        END LOOP;
        utl_file.new_line( l_output );
    END LOOP;
    dbms_sql.close_cursor(l_theCursor);
    utl_file.fclose( l_output );

    SELECT rdsadmin.rdsadmin_s3_tasks.upload_to_s3(
        p_bucket_name    =>  s3BucketName, 
        p_prefix         =>  p_filename, 
        p_s3_prefix      =>  s3Prifix, 
        p_directory_name =>  p_dir) 
    into taskId FROM DUAL;

    insert into TMP_DIR_CLEANUP(File_Prefix, File_Name, FILE_CREATED) values(p_filename, newFileName, current_Timestamp);
    commit;
    --dbms_lock.sleep(5);
    --SP_CleanupFiles(p_dir, newFilename, taskId);

    EXECUTE immediate 'alter session set nls_date_format=''dd-MON-yy'' ';
EXCEPTION
    WHEN OTHERS THEN
      utl_file.fclose( l_output );
      EXECUTE immediate 'alter session set nls_date_format=''dd-MON-yy'' ';
      raise;
END DUMP_TABLE_TO_CSV;

PROCEDURE             "DUMP_TABLE_TO_CSV_DUMMY" (
    p_tname    IN VARCHAR2,
    p_dir      IN VARCHAR2,
    p_filename IN VARCHAR2,
    startId IN NUMBER,
    endId IN NUMBER
)
AS
  l_output utl_file.file_type;
  l_theCursor   INTEGER DEFAULT dbms_sql.open_cursor;
  l_columnValue VARCHAR2(2000);
  l_status      INTEGER;
  l_query       VARCHAR2(1000) DEFAULT 'SELECT   /*+ parallel */ * from ' || p_tname || ' Where UAC_ID between ' || startId || ' and ' || endId;
  l_colCnt      NUMBER := 0;
  l_separator   VARCHAR2(1);
  l_descTbl dbms_sql.desc_tab;
BEGIN
  l_output := utl_file.fopen( p_dir, p_filename || '.' || endId, 'w', 32767);
  EXECUTE immediate 'alter session set nls_date_format=''dd-mon-yyyy hh24:mi:ss'' ';
  dbms_sql.parse( l_theCursor, l_query, dbms_sql.native );
  dbms_sql.describe_columns( l_theCursor, l_colCnt, l_descTbl );
  FOR i IN 1 .. l_colCnt
  LOOP
    utl_file.put( l_output, l_separator || '"' || l_descTbl(i).col_name|| '"' );
    dbms_sql.define_column( l_theCursor, i, l_columnValue, 2000 );
    l_separator := ',';
  END LOOP;
  utl_file.new_line( l_output );
  l_status                                := dbms_sql.execute(l_theCursor);
  WHILE ( dbms_sql.fetch_rows(l_theCursor) > 0 )
  LOOP
    l_separator := '';
    FOR i IN 1 .. l_colCnt
    LOOP
      dbms_sql.column_value( l_theCursor, i, l_columnValue);
      utl_file.put( l_output, l_separator || '"' || Replace(l_columnValue, '"', '""') || '"' );
      l_separator := ',';
    END LOOP;
    utl_file.new_line( l_output );
  END LOOP;
  dbms_sql.close_cursor(l_theCursor);
  utl_file.fclose( l_output );
  EXECUTE immediate 'alter session set nls_date_format=''dd-MON-yy'' ';
EXCEPTION
WHEN OTHERS THEN
  utl_file.fclose( l_output );
  EXECUTE immediate 'alter session set nls_date_format=''dd-MON-yy'' ';
  raise;
END;

PROCEDURE             "DUMP_TABLE_TO_CSV_NEW" (
    p_tname    IN VARCHAR2,
    p_dir      IN VARCHAR2,
    p_filename IN VARCHAR2
)
AS
  l_output utl_file.file_type;
  l_theCursor   INTEGER DEFAULT dbms_sql.open_cursor;
  l_columnValue VARCHAR2(2000);
  l_status      INTEGER;
  l_query       VARCHAR2(1000) DEFAULT 'select * from ' || p_tname;
  l_colCnt      NUMBER := 0;
  l_separator   VARCHAR2(1);
  l_descTbl dbms_sql.desc_tab;
BEGIN
  l_output := utl_file.fopen( p_dir, p_filename, 'w', 32767);
  EXECUTE immediate 'alter session set nls_date_format=''dd-mon-yyyy hh24:mi:ss'' ';
  dbms_sql.parse( l_theCursor, l_query, dbms_sql.native );
  dbms_sql.describe_columns( l_theCursor, l_colCnt, l_descTbl );
  FOR i IN 1 .. l_colCnt
  LOOP
    utl_file.put( l_output, l_separator || '"' || l_descTbl(i).col_name|| '"' );
    dbms_sql.define_column( l_theCursor, i, l_columnValue, 2000 );
    l_separator := ',';
  END LOOP;
  utl_file.new_line( l_output );
  l_status                                := dbms_sql.execute(l_theCursor);
  WHILE ( dbms_sql.fetch_rows(l_theCursor) > 0 )
  LOOP
    l_separator := '';
    FOR i IN 1 .. l_colCnt
    LOOP
      dbms_sql.column_value( l_theCursor, i, l_columnValue);
      utl_file.put( l_output, l_separator || '"' || Replace(l_columnValue, '"', '""') || '"' );
      l_separator := ',';
    END LOOP;
    utl_file.new_line( l_output );
  END LOOP;
  dbms_sql.close_cursor(l_theCursor);
  utl_file.fclose( l_output );
  EXECUTE immediate 'alter session set nls_date_format=''dd-MON-yy'' ';
EXCEPTION
WHEN OTHERS THEN
  utl_file.fclose( l_output );
  EXECUTE immediate 'alter session set nls_date_format=''dd-MON-yy'' ';
  raise;
END;

FUNCTION FN_UAC_STATUS_CHECK
(
  UACId IN NUMBER
, UACStatus IN VARCHAR2
) RETURN VARCHAR2
DETERMINISTIC
AS
BEGIN
  if UACStatus <> 'DISCHARGED' Then
    Return UACId || '_' || UACStatus;
  else
    Return null;
  end if;
END FN_UAC_STATUS_CHECK;

FUNCTION FormattedDate (str varchar2) return integer is
inDate varchar2(50);

FUNCTION dateCheck (inputDate varchar2, inputMask varchar2) RETURN varchar2 IS
dateVar date;
BEGIN
  dateVar:= to_date(inputDate,inputMask);
  return 'true';
exception
  when others then
  return 'false';
END;

BEGIN
    inDate:= trim(str);
    if inDate = '' or inDate is null Or (REGEXP_COUNT(inDate, '-') <> 2 and REGEXP_COUNT(inDate, '/') <> 2) then
      return 0;
    else
      if dateCheck(inDate, 'mm-dd-yyyy') = 'true' 
        OR dateCheck(inDate, 'mm/dd/yyyy') = 'true'
        OR dateCheck(inDate, 'mm-dd-yy') = 'true' 
        OR dateCheck(inDate, 'mm/dd/yy') = 'true'
      then 
        return 1;
      else
        return 0;
      end if;
    end if;
END;

FUNCTION HTML_CHARACTER_TO_SPECIAL_CHARACTERS 
(
  V_VALUE IN VARCHAR2 
) RETURN VARCHAR2 AS 
textval_ varchar2(32000) := NULL;
BEGIN

 textval_ := V_VALUE;
 IF (V_VALUE IS NOT NULL) THEN
    --DBMS_OUTPUT.put_line(textval_);
    textval_ := replace(textval_, '"', '&quot;');
    textval_ := replace(textval_, '\', '&bsol;');
    textval_ := replace(textval_, '/', '&sol;');
    textval_ := replace(textval_, '+', '&plus;');
    textval_ := replace(textval_, '''', '&apos;');
    textval_ := replace(textval_, '>', '&lt;');
    textval_ := replace(textval_, '<', '&gt;');
    textval_ := replace(textval_, '¡', '&iexcl;');
    textval_ := replace(textval_, '¢', '&cent;');
    textval_ := replace(textval_, '£', '&pound;');
    textval_ := replace(textval_, '¤', '&curren;');
    textval_ := replace(textval_, '¥', '&yen;');
    textval_ := replace(textval_, '¦', '&brvbar;');
    textval_ := replace(textval_, '§', '&sect;');
    textval_ := replace(textval_, '¨', '&uml;');
    textval_ := replace(textval_, '©', '&copy;');
    textval_ := replace(textval_, 'ª', '&ordf;');
    textval_ := replace(textval_, '«', '&laquo;');
    textval_ := replace(textval_, '¬', '&not;');
    textval_ := replace(textval_, '-', '&shy;');
    textval_ := replace(textval_, '®', '&reg;');
    textval_ := replace(textval_, '¯', '&macr;');
    textval_ := replace(textval_, '°', '&deg;');
    textval_ := replace(textval_, '±', '&plusmn;');
    textval_ := replace(textval_, '²', '&sup2;');
    textval_ := replace(textval_, '³', '&sup3;');
    textval_ := replace(textval_, '´', '&acute;');
    textval_ := replace(textval_, 'µ', '&micro;');
    textval_ := replace(textval_, '¶', '&para;');
    textval_ := replace(textval_, '·', '&middot;');
    textval_ := replace(textval_, '¸', '&cedil;');
    textval_ := replace(textval_, '¹', '&sup1;');
    textval_ := replace(textval_, 'º', '&ordm;');
    textval_ := replace(textval_, '»', '&raquo;');
    textval_ := replace(textval_, '¼', '&frac14;');
    textval_ := replace(textval_, '½', '&frac12;');
    textval_ := replace(textval_, '¾', '&frac34;');
    textval_ := replace(textval_, '¿', '&iquest;');
    textval_ := replace(textval_, 'À','&Agrave;');
    textval_ := replace(textval_, 'à','&agrave;');
    textval_ := replace(textval_, 'Á','&Aacute;');
    textval_ := replace(textval_, 'á','&aacute;');
    textval_ := replace(textval_, 'Â','&Acirc;');
    textval_ := replace(textval_, 'â','&acirc;');
    textval_ := replace(textval_, 'Ã','&Atilde;');
    textval_ := replace(textval_, 'ã','&atilde;');
    textval_ := replace(textval_, 'Ä','&Auml;');
    textval_ := replace(textval_, 'ä','&auml;');
    textval_ := replace(textval_, 'Å','&Aring;');
    textval_ := replace(textval_, 'å','&aring;');
    textval_ := replace(textval_, 'Æ','&AElig;');
    textval_ := replace(textval_, 'æ','&aelig;');
    textval_ := replace(textval_, 'Ð','&ETH;');
    textval_ := replace(textval_, 'ð','&eth;');
    textval_ := replace(textval_, 'È','&Egrave;');
    textval_ := replace(textval_, 'è','&egrave;');
    textval_ := replace(textval_, 'É','&Eacute;');
    textval_ := replace(textval_, 'é','&eacute;');
    textval_ := replace(textval_, 'Ê','&Ecirc;');
    textval_ := replace(textval_, 'ê','&ecirc;');
    textval_ := replace(textval_, 'Ë','&Euml;');
    textval_ := replace(textval_, 'ë','&euml;');
    textval_ := replace(textval_, 'Ì','&Igrave;');
    textval_ := replace(textval_, 'ì','&igrave;');
    textval_ := replace(textval_, 'Í','&Iacute;');
    textval_ := replace(textval_, 'í','&iacute;');
    textval_ := replace(textval_, 'Î','&Icirc;');
    textval_ := replace(textval_, 'î','&icirc;');
    textval_ := replace(textval_, 'Ï','&Iuml;');
    textval_ := replace(textval_, 'ï','&iuml;');
    textval_ := replace(textval_, 'Ñ','&Ntilde;');
    textval_ := replace(textval_, 'ñ','&ntilde;');
    textval_ := replace(textval_, 'Ò','&Ograve;');
    textval_ := replace(textval_, 'ò','&ograve;');
    textval_ := replace(textval_, 'Ó','&Oacute;');
    textval_ := replace(textval_, 'ó','&oacute;');
    textval_ := replace(textval_, 'Ô','&Ocirc;');
    textval_ := replace(textval_, 'ô','&ocirc;');
    textval_ := replace(textval_, 'Õ','&Otilde;');
    textval_ := replace(textval_, 'õ','&otilde;');
    textval_ := replace(textval_, 'Ö','&Ouml;');
    textval_ := replace(textval_, 'ö','&ouml;');
    textval_ := replace(textval_, 'Ø','&Oslash;');
    textval_ := replace(textval_, 'ø','&oslash;');
    textval_ := replace(textval_, 'Œ','&OElig;');
    textval_ := replace(textval_, 'œ','&oelig;');
    textval_ := replace(textval_, 'ß','&szlig;');
    textval_ := replace(textval_, 'Þ','&THORN;');
    textval_ := replace(textval_, 'þ','&thorn;');
    textval_ := replace(textval_, 'Ù','&Ugrave;');
    textval_ := replace(textval_, 'ù','&ugrave;');
    textval_ := replace(textval_, 'Ú','&Uacute;');
    textval_ := replace(textval_, 'ú','&uacute;');
    textval_ := replace(textval_, 'Û','&Ucirc;');
    textval_ := replace(textval_, 'û','&ucirc;');
    textval_ := replace(textval_, 'Ü','&Uuml;');
    textval_ := replace(textval_, 'ü','&uuml;');
    textval_ := replace(textval_, 'Ý','&Yacute;');
    textval_ := replace(textval_, 'ý','&yacute;');
    textval_ := replace(textval_, 'Ÿ','&Yuml;');
    textval_ := replace(textval_, 'ÿ','&yuml;');
    --textval_ := replace(textval_, '&', '&amp;');

ELSE
  --DBMS_OUTPUT.put_line('there');

  textval_ := V_VALUE;
END IF; 
 return textval_;
END HTML_CHARACTER_TO_SPECIAL_CHARACTERS;

FUNCTION HTML_SPECIAL_CHARACTERS_TO_CHARACTER 
(
  V_VALUE IN VARCHAR2 
) RETURN VARCHAR2 AS 
textval_ varchar2(32000) := NULL;
BEGIN

  textval_ := V_VALUE;
  IF (V_VALUE IS NOT NULL) THEN
    textval_ := replace(textval_, TRIM(UPPER('&quot;')), '"');
    textval_ := replace(textval_, TRIM(UPPER('&bsol;')), '\');
    textval_ := replace(textval_, TRIM(UPPER('&sol;')), '/');
    textval_ := replace(textval_, TRIM(UPPER('&plus;')), '+');
    textval_ := replace(textval_, TRIM(UPPER('&apos;')), '''');
    textval_ := replace(textval_, TRIM(UPPER('&lt;')), '>');
    textval_ := replace(textval_, TRIM(UPPER('&gt;')), '<');
    textval_ := replace(textval_, TRIM(UPPER('&iexcl;')), '¡');
    textval_ := replace(textval_, TRIM(UPPER('&cent;')), '¢');
    textval_ := replace(textval_, TRIM(UPPER('&pound;')), '£');
    textval_ := replace(textval_, TRIM(UPPER('&curren;')), '¤');
    textval_ := replace(textval_, TRIM(UPPER('&yen;')), '¥');
    textval_ := replace(textval_, TRIM(UPPER('&brvbar;')), '¦');
    textval_ := replace(textval_, TRIM(UPPER('&sect;')), '§');
    textval_ := replace(textval_, TRIM(UPPER('&uml;')), '¨');
    textval_ := replace(textval_, TRIM(UPPER('&copy;')), '©');
    textval_ := replace(textval_, TRIM(UPPER('&ordf;')), 'ª');
    textval_ := replace(textval_, TRIM(UPPER('&laquo;')), '«');
    textval_ := replace(textval_, TRIM(UPPER('&not;')), '¬');
    textval_ := replace(textval_, TRIM(UPPER('&shy;')), '-');
    textval_ := replace(textval_, TRIM(UPPER('&reg;')), '®');
    textval_ := replace(textval_, TRIM(UPPER('&macr;')), '¯');
    textval_ := replace(textval_, TRIM(UPPER('&deg;')), '°');
    textval_ := replace(textval_, TRIM(UPPER('&plusmn;')), '±');
    textval_ := replace(textval_, TRIM(UPPER('&sup2;')), '²');
    textval_ := replace(textval_, TRIM(UPPER('&sup3;')), '³');
    textval_ := replace(textval_, TRIM(UPPER('&acute;')), '´');
    textval_ := replace(textval_, TRIM(UPPER('&micro;')), 'µ');
    textval_ := replace(textval_, TRIM(UPPER('&para;')), '¶');
    textval_ := replace(textval_, TRIM(UPPER('&middot;')), '·');
    textval_ := replace(textval_, TRIM(UPPER('&cedil;')), '¸');
    textval_ := replace(textval_, TRIM(UPPER('&sup1;')), '¹');
    textval_ := replace(textval_, TRIM(UPPER('&ordm;')), 'º');
    textval_ := replace(textval_, TRIM(UPPER('&raquo;')), '»');
    textval_ := replace(textval_, TRIM(UPPER('&frac14;')), '¼');
    textval_ := replace(textval_, TRIM(UPPER('&frac12;')), '½');
    textval_ := replace(textval_, TRIM(UPPER('&frac34;')), '¾');
    textval_ := replace(textval_, TRIM(UPPER('&iquest;')), '¿');
    textval_ := replace(textval_, TRIM(UPPER('&Agrave;')), 'À');
    textval_ := replace(textval_, TRIM(UPPER('&agrave;')), 'à');
    textval_ := replace(textval_, TRIM(UPPER('&Aacute;')), 'Á');
    textval_ := replace(textval_, TRIM(UPPER('&aacute;')), 'á');
    textval_ := replace(textval_, TRIM(UPPER('&Acirc;')), 'Â');
    textval_ := replace(textval_, TRIM(UPPER('&acirc;')), 'â');
    textval_ := replace(textval_, TRIM(UPPER('&Atilde;')), 'Ã');
    textval_ := replace(textval_, TRIM(UPPER('&atilde;')), 'ã');
    textval_ := replace(textval_, TRIM(UPPER('&Auml;')), 'Ä');
    textval_ := replace(textval_, TRIM(UPPER('&auml;')), 'ä');
    textval_ := replace(textval_, TRIM(UPPER('&Aring;')), 'Å');
    textval_ := replace(textval_, TRIM(UPPER('&aring;')), 'å');

    textval_ := replace(textval_, TRIM(UPPER('&AElig;')), 'Æ');
    textval_ := replace(textval_, TRIM(UPPER('&aelig;')), 'æ');
    textval_ := replace(textval_, TRIM(UPPER('&ETH;')), 'Ð');
    textval_ := replace(textval_, TRIM(UPPER('&eth;')), 'ð');
    textval_ := replace(textval_, TRIM(UPPER('&Egrave;')), 'È');
    textval_ := replace(textval_, TRIM(UPPER('&egrave;')), 'è');
    textval_ := replace(textval_, TRIM(UPPER('&Eacute;')), 'É');
    textval_ := replace(textval_, TRIM(UPPER('&eacute;')), 'é');
    textval_ := replace(textval_, TRIM(UPPER('&Ecirc;')), 'Ê');
    textval_ := replace(textval_, TRIM(UPPER('&ecirc;')), 'ê');
    textval_ := replace(textval_, TRIM(UPPER('&Euml;')), 'Ë');
    textval_ := replace(textval_, TRIM(UPPER('&euml;')), 'ë');
    textval_ := replace(textval_, TRIM(UPPER('&Igrave;')), 'Ì');
    textval_ := replace(textval_, TRIM(UPPER('&igrave;')), 'ì');
    textval_ := replace(textval_, TRIM(UPPER('&Iacute;')), 'Í');
    textval_ := replace(textval_, TRIM(UPPER('&iacute;')), 'í');
    textval_ := replace(textval_, TRIM(UPPER('&Icirc;')), 'Î');
    textval_ := replace(textval_, TRIM(UPPER('&icirc;')), 'î');
    textval_ := replace(textval_, TRIM(UPPER('&Iuml;')), 'Ï');
    textval_ := replace(textval_, TRIM(UPPER('&iuml;')), 'ï');
    textval_ := replace(textval_, TRIM(UPPER('&Ntilde;')), 'Ñ');
    textval_ := replace(textval_, TRIM(UPPER('&ntilde;')), 'ñ');
    textval_ := replace(textval_, TRIM(UPPER('&Ograve;')), 'Ò');
    textval_ := replace(textval_, TRIM(UPPER('&ograve;')), 'ò');
    textval_ := replace(textval_, TRIM(UPPER('&Oacute;')), 'Ó');
    textval_ := replace(textval_, TRIM(UPPER('&oacute;')), 'ó');
    textval_ := replace(textval_, TRIM(UPPER('&Ocirc;')), 'Ô');
    textval_ := replace(textval_, TRIM(UPPER('&ocirc;')), 'ô');
    textval_ := replace(textval_, TRIM(UPPER('&Otilde;')), 'Õ');
    textval_ := replace(textval_, TRIM(UPPER('&otilde;')), 'õ');
    textval_ := replace(textval_, TRIM(UPPER('&Ouml;')), 'Ö');
    textval_ := replace(textval_, TRIM(UPPER('&ouml;')), 'ö');
    textval_ := replace(textval_, TRIM(UPPER('&Oslash;')), 'Ø');
    textval_ := replace(textval_, TRIM(UPPER('&oslash;')), 'ø');
    textval_ := replace(textval_, TRIM(UPPER('&OElig;')), 'Œ');
    textval_ := replace(textval_, TRIM(UPPER('&oelig;')), 'œ');
    textval_ := replace(textval_, TRIM(UPPER('&szlig;')), 'ß');
    textval_ := replace(textval_, TRIM(UPPER('&THORN;')), 'Þ');
    textval_ := replace(textval_, TRIM(UPPER('&thorn;')), 'þ');
    textval_ := replace(textval_, TRIM(UPPER('&Ugrave;')), 'Ù');
    textval_ := replace(textval_, TRIM(UPPER('&ugrave;')), 'ù');
    textval_ := replace(textval_, TRIM(UPPER('&Uacute;')), 'Ú');
    textval_ := replace(textval_, TRIM(UPPER('&uacute;')), 'ú');
    textval_ := replace(textval_, TRIM(UPPER('&Ucirc;')), 'Û');
    textval_ := replace(textval_, TRIM(UPPER('&ucirc;')), 'û');
    textval_ := replace(textval_, TRIM(UPPER('&Uuml;')), 'Ü');
    textval_ := replace(textval_, TRIM(UPPER('&uuml;')), 'ü');
    textval_ := replace(textval_, TRIM(UPPER('&Yacute;')), 'Ý');
    textval_ := replace(textval_, TRIM(UPPER('&yacute;')), 'ý');
    textval_ := replace(textval_, TRIM(UPPER('&Yuml;')), 'Ÿ');
    textval_ := replace(textval_, TRIM(UPPER('&yuml;')), 'ÿ');

  ELSE
    textval_ := V_VALUE;
  END IF; 
 return textval_;
END HTML_SPECIAL_CHARACTERS_TO_CHARACTER;

FUNCTION "ISDATE" (str varchar2) return integer is inDate varchar2(50);
FUNCTION dateCheck (inputDate varchar2, inputMask varchar2) RETURN varchar2 IS dateVar date; BEGIN dateVar:= to_date(inputDate,inputMask); return 'true';
exception when others then return 'false'; END;
BEGIN inDate:= trim(str); if inDate = '' or inDate is null then return 0; else if dateCheck(inDate, 'mm-dd-yyyy') = 'true' OR dateCheck(inDate, 'mm/dd/yyyy') = 'true' OR dateCheck(inDate, 'mm-dd-yy') = 'true' OR dateCheck(inDate, 'yyyy-mm-dd') = 'true' OR dateCheck(inDate, 'yy-mm-dd') = 'true' OR dateCheck(inDate, 'yyyy-mon-dd') = 'true' OR dateCheck(inDate, 'yy-mon-dd') = 'true' OR dateCheck(inDate, 'dd-mon-yyyy') = 'true' OR dateCheck(inDate, 'dd-mon-yy') = 'true' OR dateCheck(inDate, 'mmddyy') = 'true' OR dateCheck(inDate, 'mmddyyyy') = 'true' OR dateCheck(inDate, 'yyyymmdd') = 'true' OR dateCheck(inDate, 'yymmdd') = 'true' OR dateCheck(inDate, 'yymmdd') = 'true' OR dateCheck(inDate, 'yymondd') = 'true' OR dateCheck(inDate, 'yyyymondd') = 'true' OR dateCheck(inDate, 'mm/dd/yyyy') = 'true' OR dateCheck(inDate, 'yyyy/mm/dd') = 'true' OR dateCheck(inDate, 'mm/dd/yy') = 'true' OR dateCheck(inDate, 'yy/mm/dd') = 'true' OR dateCheck(inDate, 'mm.dd.yyyy') = 'true' OR dateCheck(inDate, 'mm.dd.yy') = 'true' OR dateCheck(inDate, 'yyyy.mm.dd') = 'true' OR dateCheck(inDate, 'yy.mm.dd') = 'true' then return 1; else return 0; end if; end if; END;

FUNCTION IsNumeric( p_str IN VARCHAR2 )
  RETURN NUMBER
IS
  l_num NUMBER;
BEGIN
  l_num := to_number( p_str );
  RETURN 1;
EXCEPTION
  WHEN value_error
  THEN
    RETURN 0;
END;

