FUNCTION "JOIN" 
 (
     p_cursor sys_refcursor,
     p_del varchar2 := ','
 ) return varchar2
 is
     l_value   varchar2(32767);
     l_result  varchar2(32767);
 begin
     loop
         fetch p_cursor into l_value;
         exit when p_cursor%notfound;
         if l_result is not null then
             l_result := l_result || p_del;
         end if;
         l_result := l_result || l_value;
     end loop;
     return l_result;
 end JOIN;

procedure parallel_dump_table_to_csv 
(
  p_tname in varchar2 
  , p_dir in varchar2 
  , p_filename in varchar2 
  , taskname in varchar2
  , s3BucketName in varchar2
  , s3Prifix    in varchar2
  , cur_OUT OUT SYS_REFCURSOR
) as 
task varchar2(30) := taskname; --'parallel_unload_data0';
plsql   varchar2(1000);
qryString   VARCHAR2(1000);
subPlsql    varchar2(1000);
task_status number;
msg varchar2(2000);
message_to_return       CLOB;
rowHeader varchar2(2000);
taskId  varchar2(30);
begin  
    message_to_return := 'Success';
    dbms_parallel_execute.create_task( task_name => task );
    qryString := 'with grps as (
        select ntile(10) over (order by SID) grp, ' || p_tname || '.*
        from ' || p_tname || '
    )
    select min(SID) start_id, max(SID) end_id from grps
    group  by grp';

    dbms_parallel_execute.create_chunks_by_sql(
        task_name   => task,
        sql_stmt    => qryString,
        by_rowid    => false
    );

    plsql := '
    begin 
        dump_table_to_csv( ''' || p_tname || ''',''' || p_dir || ''', ''' || p_filename || ''', :start_id, :end_id, ''' || s3BucketName || ''', ''' || s3Prifix || ''');
    end;
    ';

    dbms_parallel_execute.run_task(
        task_name        => task,
        sql_stmt         => plsql,
        language_flag    => dbms_sql.native,
        parallel_level   => 5
    );
    task_status := dbms_parallel_execute.task_status (task_name => task);

    If task_status = dbms_parallel_execute.finished_with_error Then
          FOR messages IN (SELECT error_message
                            FROM user_parallel_execute_chunks
                            WHERE task_name = task AND status = 'PROCESSED_WITH_ERROR')
          LOOP
                message_to_return := message_to_return || 'ERROR: '||messages.error_message|| (CHR (13) || CHR (10));
          END LOOP;
    End If;
    dbms_parallel_execute.drop_task (task_name => task);

    for x in (Select * from TMP_DIR_CLEANUP where File_Prefix = p_filename)
    Loop
        dbms_lock.sleep(2);
        utl_file.fremove(p_dir, x.file_name);
        Delete from TMP_DIR_CLEANUP where file_name = x.file_name;
    End Loop;
    --msg := message_to_return;
    --  dbms_output.put_line(msg);
    Select '"' || LISTAGG(Column_name, '","') within group (Order by Column_id ) || '"' into rowHeader from ALL_TAB_COLUMNS  where Table_Name = p_tname  And Column_name <> 'SID';

    Open cur_OUT for
    Select message_to_return as errMsg, rowHeader as "rowHeader" from dual;
    --select * from TMP_DIR_CLEANUP;
end parallel_dump_table_to_csv;

FUNCTION "SPLIT" /*
 prerequisite: create or replace type split_tbl as table of varchar2(32767);
 Created by: Xiang Li
 How to use: select column_value from Table(split(strval,delemeter));
 *****************************************************************/
 (
    p_list varchar2,
    p_del varchar2 := ','
) return split_tbl pipelined
is
    l_idx    pls_integer;
    l_list    varchar2(32767) := p_list;
    l_value    varchar2(32767);
begin
    loop
        l_idx := instr(l_list,p_del);
        if l_idx > 0 then
            pipe row(substr(l_list,1,l_idx-1));
            l_list := substr(l_list,l_idx+length(p_del));

        else
            pipe row(l_list);
            exit;
        end if;
    end loop;
    return;
end split;

PROCEDURE           SP_ASSIGN_RELATIONSHIP_GROUP
(
  v_UACID NUMBER,
  v_ALIEN_NOs VARCHAR2,
  v_Upuser  VARCHAR2,
  v_RELATIONSHIP_GRP_ID  VARCHAR2,
  v_status OUT NOCOPY VARCHAR2

) AS

Cursor csVar  is Select Column_Value as a_number from Table(Split(v_ALIEN_NOs));
csVal  csVar%ROWTYPE;

BEGIN

Update UAC_INFO Set RELATIONSHIP_GRP_ID = v_RELATIONSHIP_GRP_ID, Uptime = Current_TIMESTAMP, Upuser = v_Upuser Where UAC_ID = v_UACID;


OPEN csVar;

Loop
    FETCH csVar Into csVal;
    EXIT WHEN csVar%NOTFOUND;

    Update UAC_INFO Set RELATIONSHIP_GRP_ID = v_RELATIONSHIP_GRP_ID, Uptime = Current_TIMESTAMP, Upuser = v_Upuser Where ALIEN_NO = csVal.a_number;

End Loop;
  Close csVar;
  commit;
  v_status := 'SUCCESS SUCCESS';

 END SP_ASSIGN_RELATIONSHIP_GROUP;

PROCEDURE SP_AUTOCANCEL_ENROUTE AS 
Cursor uacRows is
  SELECT DISTINCT UI.UAC_ID
  FROM UAC_PROGRAM_INFO UPI
  INNER JOIN UAC_INFO UI ON UPI.UAC_ID = UI.UAC_ID
  WHERE UPPER(UPI.CURRENT_STATUS)  = 'ENROUTE' 
  AND UPPER(UI.FIRST_NAME) not like '%FAKE%' 
  AND UPPER(UI.LAST_NAME) not like '%FAKE%' 
   --Make sure to exclude UAC's that are being transfered i.e have more than 1 program
  AND NOT EXISTS (
    SELECT 1 FROM (
      SELECT UPI2.UAC_ID, COUNT(UPI2.UAC_ID) 
      FROM UAC_PROGRAM_INFO UPI2 
      GROUP BY UPI2.UAC_ID 
      HAVING COUNT(UPI2.UAC_ID) > 1  
      ORDER BY UAC_ID) UPI3 WHERE UPI3.UAC_ID = UPI.UAC_ID

  )
  --AND DateDiff('Day',UPI.DATE_CREATED,sysdate) > 7;
  AND DateDiff('Day',UPI.DATE_CREATED,sysdate) > (SELECT to_number(PARAM_VALUE) from UACPORTAL_APP_PARAMS where PARAM_NAME = 'CANCEL_ENROUTE_PERIOD');
BEGIN
  For uacRow in uacRows
  Loop
    INSERT INTO UAC_PROGRAM_INFO_BACKUP Select * From UAC_PROGRAM_INFO Where UAC_ID = uacRow.UAC_ID and UPPER(CURRENT_STATUS) = 'ENROUTE';
	Delete from UAC_DOCUMENTS WHERE UAC_PROGRAM_ID IN (SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO WHERE UAC_ID = uacRow.UAC_ID and UPPER(CURRENT_STATUS) = 'ENROUTE');
	Delete from UAC_SPONSOR_LIST WHERE UAC_ID = uacRow.UAC_ID and IS_DELETED =0;
    Delete from UAC_PROGRAM_INFO Where UAC_ID = uacRow.UAC_ID and UPPER(CURRENT_STATUS) = 'ENROUTE';
    UPDATE UAC_INFO SET UAC_STATUS = 'CANCELLED', DATE_CANCELLED = Sysdate, CANCELLED_BY='SYSTEM', UPTIME=CURRENT_TIMESTAMP Where UAC_ID = uacRow.UAC_ID;
  End Loop;
  COMMIT;
END SP_AUTOCANCEL_ENROUTE;

PROCEDURE "SP_BACKUP_FACILITY_INFO" AS
arcTime_ TIMESTAMP(6) := CURRENT_TIMESTAMP; BEGIN
Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED From FACILITY_INFO;
Update TT_FACILITY_INFO F Set (IN_TRANSFER_M, IN_TRANSFER_F) = ( Select SUM(CASE WHEN (C.GENDER='M' OR C.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (C.GENDER='F' OR C.GENDER='Female') THEN 1 ELSE 0 End) from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER' ) Where Exists(Select 1 from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER');
Update TT_FACILITY_INFO F Set (IN_CARE_M, IN_CARE_F) = ( select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID);
Update TT_FACILITY_INFO F Set (ENROUTE_M, ENROUTE_F) = (Select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID);
UPDATE TT_FACILITY_INFO SET BEDS_IN_RESERVE_M = FUNDED_CAPACITY_M - (NVL(IN_TRANSFER_M,0) + NVL(ENROUTE_M,0) + NVL(IN_CARE_M,0) + NVL(BEDS_UNAVAILABLE_M,0)), BEDS_IN_RESERVE_F = FUNDED_CAPACITY_F - (NVL(IN_TRANSFER_F,0) + NVL(ENROUTE_F,0) + NVL(IN_CARE_F,0) + NVL(BEDS_UNAVAILABLE_F,0));
Insert into FACILITY_INFO_ARC( ARC_TIME, FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, IN_CARE_M, IN_CARE_F, BEDS_IN_RESERVE_M, BEDS_IN_RESERVE_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, ENROUTE_M, ENROUTE_F, IN_TRANSFER_M, IN_TRANSFER_F ) SELECT arcTime_, FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, IN_CARE_M, IN_CARE_F, BEDS_IN_RESERVE_M, BEDS_IN_RESERVE_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, ENROUTE_M, ENROUTE_F, IN_TRANSFER_M, IN_TRANSFER_F FROM TT_FACILITY_INFO;
Update FACILITY_INFO SET COMMENTS = null, INSTRUCTIONS = null, CONFIRMED_BY = null, VERIFIED = 0, CONFIRMED_TIME = null; END SP_BACKUP_FACILITY_INFO;

PROCEDURE "SP_CANCEL_HSPRS_OVER_18" ( PASTDATE DATE ) AS v_pastdate DATE:= PASTDATE; BEGIN /* Cancel Pending cases at 18 years and Active at 18 years and 30 days old The input here is the current date minus 18 years and 30 days */ Update UAC_HOMESTUDY_CASE_REFERRAL Set REFERRAL_STATUS = '4', CANCELLATION_DATE = to_timestamp(to_char(SYSDATE, 'DD/MON/RR HH24:MI:SS')) WHERE HOMESTUDY_ID IN ( SELECT C.HOMESTUDY_ID FROM UAC_PROGRAM_INFO A INNER JOIN UAC_INFO B ON A.UAC_ID = B.UAC_ID INNER JOIN UAC_HOMESTUDY_CASE_REFERRAL C ON A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID WHERE C.REFERRAL_STATUS = 1 AND B.DOB < v_pastdate - 30 ); COMMIT;
Update UAC_POST_RELEASE_SERVICE Set REFERRAL_STATUS = '4', CANCELLATION_DATE = to_timestamp(to_char(SYSDATE, 'DD/MON/RR HH24:MI:SS')) WHERE PRS_ID IN ( SELECT C.PRS_ID FROM UAC_PROGRAM_INFO A INNER JOIN UAC_INFO B ON A.UAC_ID = B.UAC_ID INNER JOIN UAC_POST_RELEASE_SERVICE C ON A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID WHERE C.REFERRAL_STATUS = 1 AND B.DOB < v_pastdate - 30 ) /* Exclued cases that were re-activated*/ --AND NOT EXISTS (SELECT 1 FROM TT_BK_POST_RELEASE_SERVICE C1 WHERE C1.PRS_ID = PRS_ID) -- AND PRS_ID not in (SELECT C1.PRS_ID FROM TT_BK_POST_RELEASE_SERVICE C1) ; COMMIT;
Update UAC_HOMESTUDY_CASE_REFERRAL Set REFERRAL_STATUS = '4', CANCELLATION_DATE = to_timestamp(to_char(SYSDATE, 'DD/MON/RR HH24:MI:SS')) WHERE HOMESTUDY_ID IN ( SELECT C.HOMESTUDY_ID FROM UAC_PROGRAM_INFO A INNER JOIN UAC_INFO B ON A.UAC_ID = B.UAC_ID INNER JOIN UAC_HOMESTUDY_CASE_REFERRAL C ON A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID WHERE C.REFERRAL_STATUS = 2 AND B.DOB < v_pastdate ); COMMIT;
Update UAC_POST_RELEASE_SERVICE Set REFERRAL_STATUS = '4', CANCELLATION_DATE = to_timestamp(to_char(SYSDATE, 'DD/MON/RR HH24:MI:SS')) WHERE PRS_ID IN ( SELECT C.PRS_ID FROM UAC_PROGRAM_INFO A INNER JOIN UAC_INFO B ON A.UAC_ID = B.UAC_ID INNER JOIN UAC_POST_RELEASE_SERVICE C ON A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID WHERE C.REFERRAL_STATUS = 2 AND B.DOB < v_pastdate ); COMMIT;
END SP_CANCEL_HSPRS_OVER_18;

