PROCEDURE "SP_CHECK_PRS_FOR_PROGRAM_EXIT" 
(
  v_UAC_PROGRAM_ID NUMBER,
  cur_OUT OUT Sys_Refcursor
) AS 
uacPROGRAM_ID NUMBER(10) := v_UAC_PROGRAM_ID;
n NUMBER := 0;
BEGIN
  -- Check if there is an active RR with
  -- ORR Decision = Approve with Post-release Only Services
  -- or ORR Decision After HS = Approve with Post-Release Services  or Approve with Post Release Services-TVPRA
  SELECT COUNT(*) INTO n  
  FROM UAC_RELEASE_REQUEST
  WHERE (ORR_DECISION = 2 OR ORR_DECISION_AFTER_HS IN (1,5))
  AND (ISDELETED = 0 OR ISDELETED Is NUll)
  AND UAC_PROGRAM_ID = uacPROGRAM_ID;

  --if there is , run the following checks
  if n > 0 Then

    -- Check if there is a PRS-TVPRA that is Pending or Active
    SELECT COUNT(*) INTO n
    FROM UAC_POST_RELEASE_SERVICE
    WHERE REFERRAL_STATUS IN (1,2)
    AND PRS_TYPE = 0
    AND UAC_PROGRAM_ID = uacPROGRAM_ID;

    -- if there is a PRS-TVPRA that is Pending or Active
    if n > 0  Then
      -- Check if there is a closed HS
      SELECT COUNT(*) INTO n
      FROM UAC_HOMESTUDY_CASE_REFERRAL A
      WHERE REFERRAL_STATUS = 3
      AND UAC_PROGRAM_ID = uacPROGRAM_ID;

      -- if not return false so that kid cannot be program exited
      if n < 1 Then
      Open cur_OUT For
          Select 0 as ReturnMessage from dual;
        Return;
      End If; 
    Else
      -- Check if there is a PRS that is Pending or Active
      SELECT COUNT(*) INTO n
      FROM UAC_POST_RELEASE_SERVICE
      WHERE REFERRAL_STATUS IN (1,2)
      AND PRS_TYPE = 1
      AND UAC_PROGRAM_ID = uacPROGRAM_ID;

      -- if not return false so that kid cannot be porgram exited
      if n < 1 Then
      Open cur_OUT For
          Select 0 as ReturnMessage from dual;
        Return;
      End If;
    End If;

    Open cur_OUT For
        Select 1 as ReturnMessage from dual;
  ELSE
    Open cur_OUT For
        Select 1 as ReturnMessage from dual;
  End If;

END SP_CHECK_PRS_FOR_PROGRAM_EXIT;

PROCEDURE             "SP_CHECK_QUERYID" (
QueryId IN VARCHAR2,
QueryType IN VARCHAR2,
FirstName IN VARCHAR2,
LastName IN VARCHAR2,
DateOfBirth IN VARCHAR2,
cur_OUT OUT Sys_Refcursor
)
AS 
n NUMBER := 0;
returnMessage varchar2(500) := 'SUCCESS';
BEGIN
  If Upper(QueryType) = 'SPONSOR' Then
    -- Check if there is a record with the query id in Sponsor Table
    Select count(*) into n from (
      Select 1 from UAC_SPONSOR_LIST where QUERY_ID = QueryId
      Union
      Select 0 from dual
    );

    if n > 1 then
      -- Check if the Sposnor is the same one who exists in the system.
      Select count(*) into n from (
        Select 1 from SPONSOR_INFO A 
        Inner Join UAC_SPONSOR_LIST B On A.SPONSOR_ID = B.SPONSOR_ID and a.IS_DELETED = 0 AND B.IS_DELETED = 0
        Where B.QUERY_ID = QueryId
        And Trim(Lower(A.FIRST_NAME)) = Trim(Lower(FirstName))
        And Trim(Lower(A.LAST_NAME)) = Trim(Lower(LastName))
        And DateDiff('day',A.DOB,  TO_DATE(DateOfBirth, 'mm/dd/yyyy')) = 0
        UNION
        Select 0 from dual
      );
      -- if the First Name, Last Name, and DOB doesn't match, then the Query ID is already used by someone else. 
      if n = 1 Then
        returnMessage := 'The Query ID mismatch with Sponsor ' || firstName || ' ' || lastName ||'.';
      end if;
    else
      -- Check if there is any record with the query id in HOUSEHOLD table
      Select count(*) into n from (
        Select 1 from SPONSOR_HOUSEHOLD_OCCUPANTS where QUERY_ID = QueryId
        Union
        Select 0 from dual
      );
      if n > 1 then
        -- If there is a record in the Household table, then check if it is the same person or not.
        Select count(*) into n from (
          Select 1 from SPONSOR_HOUSEHOLD_OCCUPANTS Where QUERY_ID = QueryId
          And Trim(Lower(FIRST_NAME)) = Trim(Lower(FirstName))
          And Trim(Lower(LAST_NAME)) = Trim(Lower(LastName))
          And DateDiff('day',DOB,  TO_DATE(DateOfBirth, 'mm/dd/yyyy')) = 0
          UNION
          Select 0 from dual
        );
        if n = 1 then
          returnMessage := 'The Query ID mismatch with Sponsor ' || firstName || ' ' || lastName ||'.';
        end if;
      end if;
    end if;
  else
    -- Check if there any record in Household table
    Select count(*) into n from (
      Select 1 from SPONSOR_HOUSEHOLD_OCCUPANTS where QUERY_ID = QueryId
      Union
      Select 0 from dual
    );

    if n = 1 then
      -- If there is no record in Household table, then check uac_sponsor_list table.
      Select count(*) into n from (
        Select 1 from UAC_SPONSOR_LIST where QUERY_ID = QueryId and is_deleted = 0
        Union
        Select 0 from dual
      );

      if n > 1 then
        -- If there is a record in Sponsor table, then check if it is the same person or not.
        Select count(*) into n from (
          Select 1 from SPONSOR_INFO A 
          Inner Join UAC_SPONSOR_LIST B On A.SPONSOR_ID = B.SPONSOR_ID and a.IS_DELETED = 0 AND B.IS_DELETED = 0
          Where B.QUERY_ID = QueryId
          And Trim(Lower(A.FIRST_NAME)) = Trim(Lower(FirstName))
          And Trim(Lower(A.LAST_NAME)) = Trim(Lower(LastName))
          And DateDiff('day',A.DOB,  TO_DATE(DateOfBirth, 'mm/dd/yyyy')) = 0
          UNION
          Select 0 from dual
        );
        if n = 1 then
          returnMessage := 'The Query ID mismatch with Household  ' || firstName || ' ' || lastName ||'.';
        end if;
      end if;
    else
      -- If there is a record in Household table, then check if it is the same person or not
      Select count(*) into n from (
        Select 1 from SPONSOR_HOUSEHOLD_OCCUPANTS Where QUERY_ID = QueryId
        And Trim(Lower(FIRST_NAME)) = Trim(Lower(FirstName))
        And Trim(Lower(LAST_NAME)) = Trim(Lower(LastName))
        And DateDiff('day',DOB,  TO_DATE(DateOfBirth, 'mm/dd/yyyy')) = 0
        UNION
        Select 0 from dual
      );
      if n = 1 then
        returnMessage := 'The Query ID mismatch with Household  ' || firstName || ' ' || lastName ||'.';
      end if;
    end if;
  end if;
  Open cur_OUT For
  Select returnMessage as ReturnMessage from dual;
END SP_CHECK_QUERYID;

PROCEDURE           "SP_COPY_SIR_TO_ADDENDUM" (
vSIR_ID IN NUMERIC
)
AS 
BEGIN
  for r1 in (SELECT * FROM UAC_SIR_NEW WHERE SIR_ID = vSIR_ID)
  loop
  r1.ADDENDUM_ID := 1;
  INSERT INTO UAC_SIR_NEW VALUES r1;
  INSERT INTO UAC_SIR_FIELDS_HISTORY(HISTORY_ID,SIR_ID, DATE_COMPLETED, STAFF, DESCRIPTION_OF_INCIDENT, STAFF_RESPONSE_INTERVENTION, 
  FOLLOW_UP_RESOLUTION, UPDATED_RECOMMENDATIONS,PROGRESS_OF_SL_INVESTIGATION,RESULTS_OF_SL_INVESTIGATION,PROGRESS_OF_CPS_INVESTIGATION,RESULTS_OF_CPS_INVESTIGATION,PROGRESS_OF_LE_INVESTIGATION,RESULTS_OF_LE_INVESTIGATION,
  SA_ACTION_FOR_VICTIM,SA_ACTION_FOR_ALLEGEDPERP,SA_ACTION_FOR_INDINVOLVED) VALUES (SEQ_UAC_FIELDS_HISTORY.NEXTVAL,r1.SIR_ID, r1.DATE_CREATED, r1.USER_CREATED, r1.DESCRIPTION_OF_INCIDENT, r1.STAFF_RESPONSE_INTERVENTION, 
  r1.FOLLOW_UP_RESOLUTION, r1.UPDATED_RECOMMENDATIONS,r1.PROGRESS_OF_SL_INVESTIGATION,r1.RESULTS_OF_SL_INVESTIGATION,r1.PROGRESS_OF_CPS_INVESTIGATION,r1.RESULTS_OF_CPS_INVESTIGATION,r1.PROGRESS_OF_LE_INVESTIGATION,r1.RESULTS_OF_LE_INVESTIGATION,
  r1.SA_ACTION_FOR_VICTIM,r1.SA_ACTION_FOR_ALLEGEDPERP,r1.SA_ACTION_FOR_INDINVOLVED);
  end loop;

  for r2 in (SELECT * FROM UAC_SIR_CATEGORY_MAP WHERE SIR_ID = vSIR_ID)
  loop
  r2.ADDENDUM_ID := 1;
  INSERT INTO UAC_SIR_CATEGORY_MAP VALUES r2;
  end loop;

  for r3 in (SELECT * FROM UAC_SIR_UCINVOLVED_LIST WHERE UAC_SIR_ID = vSIR_ID)
  loop
  r3.ADDENDUM_ID := 1;
  INSERT INTO UAC_SIR_UCINVOLVED_LIST VALUES r3;
  end loop;

  for r4 in (SELECT * FROM UAC_SIR_NOTIFICATION_LIST WHERE UAC_SIR_ID = vSIR_ID)
  loop
  r4.ADDENDUM_ID := 1;
  INSERT INTO UAC_SIR_NOTIFICATION_LIST VALUES r4;
  end loop;

END SP_COPY_SIR_TO_ADDENDUM;

PROCEDURE SP_COUNT_DUP_SPONSOR_INSERT 
(

vFName IN VARCHAR2,
vLName IN VARCHAR2,
vUser IN VARCHAR2,
vcounter OUT NOCOPY NUMBER,
vSponsorIdOut OUT NOCOPY NUMBER
-- cur_OUT OUT Sys_Refcursor

) AS


nothing number :=0;

BEGIN


SELECT Count(*)
INTO vcounter
FROM Sponsor_info where FIRST_NAME=vFName   and LAST_NAME = vLName   and USER_CREATED = vUser;

if (vcounter > 0) then
SELECT sponsor_id 
INTO vSponsorIdOut
FROM Sponsor_info where FIRST_NAME=vFName   and LAST_NAME = vLName   and USER_CREATED = vUser;
else
vSponsorIdOut := 0;
end if;
END SP_COUNT_DUP_SPONSOR_INSERT;

PROCEDURE           "SP_DELETE_ADDENDUM" 
(
  VSIR_ID IN NUMBER  
) AS 
BEGIN
  UPDATE UAC_SIR_NEW SET ISDELETED = 1 WHERE SIR_ID = vSIR_ID AND ADDENDUM_ID = 1;
  UPDATE UAC_SIR_CATEGORY_MAP SET ISDELETED = 1 WHERE SIR_ID = vSIR_ID AND ADDENDUM_ID = 1;
  UPDATE UAC_SIR_UCINVOLVED_LIST SET ISDELETED = 1 WHERE UAC_SIR_ID = vSIR_ID AND ADDENDUM_ID = 1;
  UPDATE UAC_SIR_NOTIFICATION_LIST SET ISDELETED = 1 WHERE UAC_SIR_ID = vSIR_ID AND ADDENDUM_ID = 1;
END SP_DELETE_ADDENDUM;

PROCEDURE SP_DELETE_CATEGORY_MAP_RECORDS (
vSirID    number,
cur_Out  OUT SYS_REFCURSOR 
)
AS 

BEGIN
DELETE from uac_sir_category_map
   where vSirID = Sir_ID and addendum_id = 1;
    OPEN cur_Out For
  Select 'OK' as RETURNVAL from dual;
END SP_DELETE_CATEGORY_MAP_RECORDS;

PROCEDURE "SP_DELETE_HOTLINE_ALERT" 
(
  V_HOTLINE_ALERT_ID IN NUMBER 
) AS 
BEGIN
   update UAC_HOTLINE_ALERT
     set ISDELETED = 1
     where HOTLINE_ALERT_ID = V_HOTLINE_ALERT_ID;

     update UAC_HOTLINE_REPORTING
     set ISDELETED = 1
     where HOTLINE_ALERT_ID = V_HOTLINE_ALERT_ID;
END SP_DELETE_HOTLINE_ALERT;

PROCEDURE "SP_DELETE_INPROGRESS_CLE_ADDENDUM" 
(
  vEVENT_ID IN NUMBER  
) AS 
v_count NUMBER;
BEGIN

    SELECT COUNT(*) INTO v_count
    FROM inprogress_cle_addendum
    WHERE EVENT_ID = vEVENT_ID;

    IF v_count > 0 THEN
      DELETE FROM inprogress_cle_addendum WHERE EVENT_ID = vEVENT_ID;
      Commit;
    END IF;  
END SP_DELETE_INPROGRESS_CLE_ADDENDUM;

PROCEDURE "SP_DELETE_RECORD" 
(
  RECORD_TYPE IN VARCHAR2, 
  RECORD_ID IN VARCHAR2, 
  UPUSER  IN VARCHAR2,
  v_ProgId IN NUMBER DEFAULT NULL, 
  v_Gender IN VARCHAR2 DEFAULT NULL, 
  cur_Out  OUT SYS_REFCURSOR 
) AS 

recType_  VARCHAR2(112) := Upper(RECORD_TYPE);
recId_    VARCHAR2(60) := RECORD_ID;
upUser_   VARCHAR2(112) := UPUSER;
dest_ProgId number;
BEGIN
  If recType_ = 'SPONSOR' Then
    Delete From UAC_SPONSOR_LIST Where SPONSOR_ID = SubStr(recId_, 0,InStr(recId_, '_') - 1) 
    And UAC_ID = SubStr(recId_, InStr(recId_, '_') + 1) and IS_DELETED = 0;
    Update SPONSOR_INFO SET P_COUNTER = P_COUNTER - 1 Where SPONSOR_ID = SubStr(recId_, 0,InStr(recId_, '_') - 1) And P_COUNTER > 0 and IS_DELETED = 0;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'DCS' Then
    Update UAC_ASSESSMENT Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where UAC_ASSESSMENT_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'NEWASSESSMENT' Then
    Update UAC_INITIAL_ASSESSMENT Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where INITIAL_INTAKES_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'RISKASSESSMENT' Then
    Update UAC_RISK_ASSESSMENT Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where RISK_ASSESSMENT_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'CASEREVIEW' Then
     Update UAC_CASE_REVIEW Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where UAC_CASE_REVIEW_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'SIR' Then
    Update UAC_SIR Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where SIR_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'SERVICEPLAN' Then
    Update UAC_INDIVIDUAL_SERVICE_PLAN Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where SERVICE_PLAN_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'MEDICALEXAM' Then
    Update UAC_MEDICAL_SCREENING Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where SCREENING_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'EDUCATIONPLAN' Then
    Update UAC_EDUCATION_PLACEMENT Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where PLACEMENT_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'TRAVELREQUEST' Then
    Update UAC_FOSTER_CARE_TRAVEL_REQUEST Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where TRAVEL_REQUEST_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'TRANSFER' Then
    Update UAC_TRANSFER_REQUEST Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where TRANSFER_REQUEST_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'DISCHARGENOTIFICATION' Then
    Update UAC_DISCHARGE_NOTIFICATION Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where DISCHARGE_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
   elsif recType_ = 'RELEASEREQUEST' Then
    Update UAC_RELEASE_REQUEST Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where RELEASE_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'SECUREREVIEW' Then
    Update UAC_SECURE_REVIEW Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where SECURE_REVIEW_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'UACDOCUMENTS' Then
    Update UAC_DOCUMENTS Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where DOC_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'UACINTAKESDOCLIST' Then
    Update UAC_INTAKES_DOC_LIST Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where DOC_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'UACINIDOCS' Then
    Update UAC_INI_DOCUMENTS Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where DOC_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'UAC_SIR_DOCUMENTS' Then
    Update UAC_SIR_DOCUMENTS Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where DOC_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'STATELICENSING' Then
    Update UAC_EVENT_REPORTS Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where REPORT_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'ENFORCEMENT' Then
    Update UAC_EVENT_REPORTS Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where REPORT_ID = recId_;
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  --elsif recType_ = 'SPONSORDOCS' Then
    --Update UAC_SPONSOR_DOC_LIST Set ISDELETED = 1, UPTIME=sysdate, Upuser=upUser_ Where DOC_ID = recId_;
    --OPEN cur_Out For
    --Select 'OK' as RETURNVAL from dual;
  elsif recType_ = 'CHILD_CURRENT_LOCATION' Then
    Update UAC_CHILD_LOCATION_HIST Set ISDELETED = 1, IS_CURRENT = 0 Where CHILD_LCTN_HIST_ID = recId_;
	Update UAC_CHILD_LOCATION_HIST UCLH1 Set UCLH1.IS_CURRENT = 1 
	WHERE UCLH1.CHILD_LCTN_HIST_ID in 
		(select max(UCLH2.CHILD_LCTN_HIST_ID) from UAC_CHILD_LOCATION_HIST UCLH2 
      WHERE (UCLH2.ISDELETED = 0 OR UCLH2.ISDELETED is null) 
            AND (UCLH2.IS_CURRENT = 0 OR UCLH2.IS_CURRENT is null) 
            AND UCLH2.UAC_ID in 
              (SELECT UCLH3.UAC_ID from UAC_CHILD_LOCATION_HIST UCLH3 WHERE UCLH3.CHILD_LCTN_HIST_ID = recId_)
		);
    OPEN cur_Out For
    Select 'OK' as RETURNVAL from dual;
  End If;
END SP_DELETE_RECORD;

PROCEDURE             "SP_DISCHARGE_UAC" 
(
  v_uacprogId IN NUMBER, 
  v_DischargedDate IN Date,
  v_DischargeType IN NUMBER,
  v_Upuser    VARCHAR2,
  v_status OUT NOCOPY VARCHAR2
) AS 

gender_ varchar2(10);
uacId_  NUMBER(10);
progId_ NUMBER(10);
newProgId_  NUMBER(10);
currTimeStamp TIMESTAMP(6) := Current_TimeStamp;
current_status_ varchar2(20);
BEGIN
  SAVEPOINT start_tran;

  Select UAC_ID, PROGRAM_ID, CURRENT_STATUS into uacId_, progId_,current_status_ from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = v_uacprogId;
  Select GENDER into gender_ from UAC_INFO Where UAC_ID = uacId_;

  -- Update Location History, if currently admitted in program
	if current_status_ = 'ADMITTED' then
		INSERT INTO UAC_CHILD_LOCATION_HIST (
		UAC_ID, 
		DISCHARGE_TYPE_ID,
		DISCHARGE_TYPE,
		LOCATION_TYPE_ID, 
		LOCATION_TYPE,
		LOCATION_NAME,
		LOCATION_ADDRESS, 
		LOCATION_ADDRESS_SECONDARY,
		LOCATION_CITY, 
		LOCATION_STATE, 
		LOCATION_ZIPCODE,
		DATE_LAST_UPDATED, 
		USER_LAST_UPDATED,
		ISDELETED,
		PROGRAM_ID,
		UAC_PROGRAM_ID)
		SELECT
		uacId_,
		null,
		null,
		null,
		'Program',
		C.PROGRAM_NAME,
		C.ADDRESS,
		null,
		C.CITY,
		C.STATE,
		C.ZIPCODE,
		to_timestamp(to_char(B.DATE_ADMITTED, 'DD-MON-RR HH.MI.SS AM'),'DD-MON-RR HH.MI.SSXFF AM'),
		B.UPUSER,
		0,
		B.PROGRAM_ID,
		B.UAC_PROGRAM_ID
		FROM UAC_PROGRAM_INFO B
		INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID
		WHERE B.UAC_PROGRAM_ID = v_uacprogId;
	End if;

  Insert Into UAC_PROGRAM_EXIT (PROGRAM_EXIT_ID, UAC_PROGRAM_ID, DATE_OF_DISCHARGE, TYPE_OF_DISCHARGE, DATE_CREATED, USER_CREATED, UPTIME, UPUSER)
  Values (SEQ_PROGRAM_EXIT.NextVal, v_uacprogId, v_DischargedDate, v_DischargeType, currTimeStamp, v_Upuser, currTimeStamp, v_Upuser);

  Update UAC_PROGRAM_INFO Set CURRENT_STATUS = 'DISCHARGED',DATE_DISCHARGED = v_DischargedDate, UPTIME = currTimeStamp, UPUSER=v_Upuser
  Where UAC_PROGRAM_ID = v_uacprogId;

  Update UAC_INFO Set UAC_STATUS = 'DISCHARGED', UPTIME = currTimeStamp, UPUSER=v_Upuser
  Where UAC_ID = uacId_;

  if v_DischargeType = 3 then
    Update SPONSOR_INFO A Set A_COUNTER = (nvl(A_COUNTER,0) + 1)
    Where SPONSOR_ID in (Select SPONSOR_ID From UAC_SPONSOR_LIST Where SPONSOR_ID = A.SPONSOR_ID And UAC_ID = uacId_ And CURRENT_SPONSOR = 1 and IS_deleted = 0) and Is_deleted = 0;
  end if;
  if v_DischargeType = 9 then
    Select PROPOSED_PROGRAM_ID into newProgId_ from (
      Select PROPOSED_PROGRAM_ID From UAC_TRANSFER_REQUEST Where UAC_PROGRAM_ID = v_uacprogId  And nvl(ISDELETED, 0) = 0 Order by DATE_CREATED Desc
    ) Where RowNum <= 1;

    Insert Into UAC_PROGRAM_INFO (UAC_PROGRAM_ID, UAC_ID, PROGRAM_ID, DATE_REFERRED, CURRENT_STATUS, DATE_CREATED, USER_CREATED, UPTIME, UPUSER)
    Values (SEQ_UAC_PROGRAM_INFO.nextval, uacId_, newProgId_, sysdate, 'ENROUTE', sysdate, v_Upuser, sysdate, v_Upuser);

    Update UAC_INFO Set UAC_STATUS = 'ENROUTE' Where UAC_ID = uacId_;


  end if;

  v_status := 'SUCCESS SUCCESS';
EXCEPTION
  WHEN OTHERS THEN
    ROLLBACK TO start_tran;
    v_status := SQLERRM;
END SP_DISCHARGE_UAC;

PROCEDURE "SP_EMAIL_ALERTS" (
cur_Out  OUT SYS_REFCURSOR,
cur_Out2  OUT SYS_REFCURSOR
)
AS 
BEGIN

        Open cur_Out For
      select ALW.EMAIL,ALW.FIRST_NAME,ALW.LAST_NAME FROM ADM_LIST_WEB_USER ALW        
        WHERE (extract( day from SYSDATE-ALW.LASTLOGIN))=25;

        Open cur_Out2 For
      select ALW.EMAIL,ALW.FIRST_NAME,ALW.LAST_NAME FROM ADM_LIST_WEB_USER ALW        
        WHERE (extract( day from SYSDATE-ALW.LASTLOGIN))=30;

        Update ADM_LIST_WEB_USER SET SUSPENDED=1 where (extract( day from SYSDATE-LASTLOGIN))=30;
            COMMIT;            
END SP_EMAIL_ALERTS;

PROCEDURE "SP_EXT_CODE_MAPPING" (
countryCode  IN  VARCHAR2,
relationCode  IN  VARCHAR2,
locationCode  IN  VARCHAR2,
cur_Out  OUT SYS_REFCURSOR
)
AS 

countryCode_   VARCHAR2(20) := upper(countryCode);
relationCode_   VARCHAR2(20) := upper(relationCode);
locationCode_   VARCHAR2(20) := upper(locationCode);
BEGIN

    Open cur_Out For
      Select CM.COUNTRY_NAME,RM.RELATION_NAME,LM.LOCATION_NAME,LM.SECTOR_CODE From EXT_COUNTRY_MAPPING CM
      CROSS JOIN EXT_RELATIONSHIP_MAPPING RM 
      CROSS JOIN EXT_LOCATION_MAPPING LM 
      WHERE
      CM.CBP_CODE = countryCode_ AND RM.CBP_CODE = relationCode_ AND LM.CBP_CODE = locationCode_;
END SP_EXT_CODE_MAPPING;

PROCEDURE "SP_FORGOT_USERNAME" ( vLAST_NAME IN VARCHAR2, vFIRST_NAME in VARCHAR2, vEMAIL IN VARCHAR2, cur_OUT OUT SYS_REFCURSOR ) AS
BEGIN Open cur_Out For
Select USER_ID, PASSWORD, FIRST_NAME, LAST_NAME, ADDRESS, CITY, STATE, ZIP_CODE, PHONE, WORK_PHONE, WORK_FAX, EMAIL, SUSPENDED, ROLE_TYPE, USER_PRIVELEGE, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, GROUPEMAILS, PASSWORDRESET, LOGINCOUNT, LASTLOGIN, CARRIER_ID, PROVIDER, BLOCKED From ADM_LIST_WEB_USER
where lower(vLAST_NAME) = Lower(LAST_NAME) and lower(vFIRST_NAME) = Lower(FIRST_NAME) and lower(vEMAIL) = Lower(EMAIL);
END SP_FORGOT_USERNAME;

PROCEDURE sp_GetSponsorAddresses ( p_SponsorId IN NUMBER, cur_Out OUT SYS_REFCURSOR ) AS BEGIN OPEN cur_Out FOR SELECT ROW_NUMBER() OVER (ORDER BY SponsorAddress) AS ROW_NUM, SponsorAddress FROM ( SELECT DISTINCT -- Ensures no duplicate addresses decodetext( TRIM( COALESCE(SPONSOR_ADDRESS, '') || ' ' || COALESCE(SPONSOR_ADDRESS_SECONDARY, '') || ', ' || COALESCE(SPONSOR_CITY, '') || ', ' || COALESCE(SPONSOR_STATE, '') || ' ' || COALESCE(SPONSOR_ZIPCODE, '') ) ) AS SponsorAddress FROM uac_sponsor_list WHERE sponsor_id = p_SponsorId
UNION -- Removes duplicates while ensuring efficiency
SELECT DISTINCT decodetext( TRIM( COALESCE(SPONSOR_ADDRESS, '') || ' ' || COALESCE(SPONSOR_ADDRESS_SECONDARY, '') || ', ' || COALESCE(SPONSOR_CITY, '') || ', ' || COALESCE(SPONSOR_STATE, '') || ' ' || COALESCE(SPONSOR_ZIPCODE, '') ) ) AS SponsorAddress FROM uac_sponsor_address_history WHERE sponsor_id = p_SponsorId ); END sp_GetSponsorAddresses;

PROCEDURE sp_GetSponsorFlagDetails ( p_SponsorId IN NUMBER, o_Flags OUT SYS_REFCURSOR, o_Categories OUT SYS_REFCURSOR, o_Comments OUT SYS_REFCURSOR ) AS BEGIN -- Cursor 1: Main Flag Details OPEN o_Flags FOR SELECT sf.SponsorFlagId, sf.FlaggedEntityId, sf.FlaggedEntity, (LFT.FLAGTYPE || ' Flag: ' || sf.FLAGGEDENTITY) AS FLAGTYPEANDENTITY, sf.FlagDescription, sf.UserCreated, TO_CHAR(sf.DateCreated, 'YYYY-MM-DD') AS DateCreated, --sf.FlagToken, (U.FIRST_NAME || ' ' || U.LAST_NAME) AS CREATEDBY, U.EMAIL AS CREATEDBYEMAIL, sf.UserCreated AS LastUpdatedBy, TO_CHAR(sf.DateCreated, 'YYYY-MM-DD') AS LastUpdatedDate, CASE WHEN shc.IsDeleted = 1 OR ac.IsDeleted = 1 THEN '1' ELSE '0' END AS IsDeleted FROM SponsorFlag sf LEFT JOIN ADM_LIST_WEB_USER U ON U.USER_ID = sf.USERCREATED LEFT JOIN LIST_FLAGTYPE LFT ON LFT.FLAGTYPEID = sf.FLAGTYPEID LEFT JOIN SPONSOR_HOUSEHOLD_OCCUPANTS shc ON sf.FlagTypeId = 2 AND REGEXP_LIKE(sf.FlaggedEntityId, '^\d+$') AND TO_NUMBER(sf.FlaggedEntityId) = shc.OCCUPANT_ID LEFT JOIN SPONSOR_ALTERNATE_CAREGIVER ac ON sf.FlagTypeId = 3 AND REGEXP_LIKE(sf.FlaggedEntityId, '^\d+$') AND TO_NUMBER(sf.FlaggedEntityId) = ac.SPONSOR_ALTERNATE_CAREGIVER_ID WHERE sf.SponsorId = p_SponsorId ORDER BY sf.SPONSORFLAGID DESC;
-- Cursor 2: Flag Categories OPEN o_Categories FOR SELECT sfc.SponsorFlagId, lfc.FlagCategory AS Category FROM SponsorFlagCategory sfc JOIN SponsorFlag sf ON sfc.SponsorFlagId = sf.SponsorFlagId JOIN LIST_FLAGCATEGORY lfc ON sfc.FlagCategoryId = lfc.FlagCategoryId WHERE sf.SponsorId = p_SponsorId;
-- Cursor 3: Flag Comments OPEN o_Comments FOR SELECT sfc.SponsorFlagCommentId, sfc.SponsorFlagId, sfc.FlagComment, sfc.UserCreated, sfc.CreatedBy, TO_CHAR(sfc.DateCreated, 'YYYY-MM-DD') AS DateCreated, sfc.CreatedByEmail FROM SponsorFlagComment sfc JOIN SponsorFlag sf ON sfc.SponsorFlagId = sf.SponsorFlagId WHERE sf.SponsorId = p_SponsorId ORDER BY sfc.SPONSORFLAGCOMMENTID DESC; END sp_GetSponsorFlagDetails;

PROCEDURE "SP_GETUACINFO" ( infoType VARCHAR2, UAC_PROGRAM_ID NUMBER, HistoryInfo NUMBER, cur_Out OUT SYS_REFCURSOR ) AS infoType_ VARCHAR2(30) := Upper(infoType); uacProgId_ NUMBER(10,0) := UAC_PROGRAM_ID; isHist_ NUMBER(1) := HistoryInfo; -- 1 is to select all the info. included historical data. uacId_ NUMBER(10,0); loc_ NUMBER(10) :=0; n NUMBER(4); uacImage VARCHAR2(112) := 'NOIMAGE'; BEGIN If infoType_ = 'BASIC' Then select count(*) into n from ( Select 1 from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = uacProgId_ Union Select 0 from dual ); if n > 1 then select UAC_ID into uacId_ from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = uacProgId_; Select CASE WHEN B.UAC_STATUS = 'ADMITTED' THEN DateDiff('day', A.DATE_ADMITTED, SysDate) WHEN B.UAC_STATUS = 'DISCHARGED' THEN DateDiff('day', A.DAte_ADMITTED, DATE_DISCHARGED) WHEN B.UAC_STATUS = 'IN-TRANSFER' THEN DateDiff('day', A.DATE_ADMITTED, SysDate) ELSE 0 END AS LOC into loc_ From (Select UAC_ID, MIN(DATE_ADMITTED) as DATE_ADMITTED, MAX(DATE_DISCHARGED) as DATE_DISCHARGED FROM UAC_PROGRAM_INFO A WHERE UAC_ID = uacId_ GROUP By UAC_ID) A INNER JOIN UAC_INFO B On A.UAC_ID = B.UAC_ID WHERE B.UAC_ID = uacId_; else uacId_ := -1; End if;
Select count(*) into n from ( Select DISTINCT FILE_NAME from UAC_IMAGES A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where C.UAC_ID = uacId_ ); if n > 0 Then Select FILE_NAME into uacImage From ( Select DISTINCT FILE_NAME, DATE_UPLOADED from UAC_IMAGES A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where C.UAC_ID = uacId_ Order by A.DATE_UPLOADED Desc ) where rownum = 1; end if; Open cur_Out For Select A.UAC_ID,A.ALIEN_NO, A.FIRST_NAME, A.LAST_NAME, A.AKA, A.DOB, Trunc(months_between(sysdate, A.DOB) /12) as Age, A.COB, A.GENDER, CURRENT_STATUS as UAC_STATUS,DATE_ADMITTED, B.PROGRAM_ID, C.PROGRAM_NAME, D.PROGRAM_TYPE, E.OFFICE_NAME as APPROVED_FIELD_OFFICE, DATE_ORR_APPROVED as ORR_APPROVED_DATE, Case When CURRENT_STATUS = 'ADMITTED' Or CURRENT_STATUS = 'IN-TRANSFER' Then DateDiff('day',DATE_ADMITTED, SysDate) When CURRENT_STATUS = 'ENROUTE' Then 0 When CURRENT_STATUS = 'DISCHARGED' Then DateDiff('day',DATE_ADMITTED, DATE_DISCHARGED) End as LOS, B.DATE_DISCHARGED, C.PROGRAM_TYPE_ID, G.IMAGE_PATH || G.FILE_NAME as UAC_PHOTO, F.FIRST_NAME || ' ' || F.LAST_NAME as Sponsor_NAME, F.SSN as Sponsor_SSN, F.ALIEN_NO as Sponsor_ANo, F.SPONSOR_ADDRESS, --|| ' ' || F.ADDRESS_SECONDARY AS SPONSOR_ADDRESS , F.Sponsor_City, F.Sponsor_State, F.Sponsor_ZipCode, F.Sponsor_Home_Phone as Sponsor_Phone, F.Relationship, A.DATE_ORR_APPROVED, C.CITY, C.STATE, loc_ as LOC, uacImage as ImageName From UAC_INFO A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Inner join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID Left Join LIST_PROGRAM_TYPE D On C.PROGRAM_TYPE_ID = D.PROGRAM_TYPE_ID Left Join List_REFERRING_FIELD_OFFICES E On A.REFERRING_OFFICE = E.OFFICE_ID Left Join ( Select B.UAC_ID, CONCAT(CONCAT(B.SPONSOR_ADDRESS, ' '), B.SPONSOR_ADDRESS_SECONDARY) as Sponsor_Address, B.Sponsor_City, B.Sponsor_State, B.Sponsor_ZipCode, B.Sponsor_Home_Phone, A.*, C.Relationship From SPONSOR_INFO A Inner Join UAC_SPONSOR_LIST B On A.SPONSOR_ID = B.SPONSOR_ID and a.is_deleted = 0 and b.is_deleted = 0 Left Join LIST_RELATIONSHIP C On B.SPONSOR_RELATIONSHIP_TO_UAC = C.RELATIONSHIP_ID Where B.CURRENT_SPONSOR = 1 ) F On A.UAC_ID = F.UAC_ID Left Join UAC_IMAGES G On B.UAC_PROGRAM_ID = G.UAC_PROGRAM_ID Where B.UAC_PROGRAM_ID = uacProgId_; Elsif infoType_ = 'ASSESSMENT' Then Open cur_Out For Select UAC_ASSESSMENT_ID, To_Char(DATE_OF_INTAKE, 'mm/dd/yyyy') as DATE_COMPLETED, To_Char(A.UPTIME, 'mm/dd/yyyy') as LAST_UPDATED, CASE_MGT_NAME as STAFF, B.Program_ID, PROGRAM_NAME From UAC_ASSESSMENT A INNER JOIN UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID Where B.UAC_PROGRAM_ID = uacProgId_ And A.ISDELETED = 0 Order by A.Uptime Desc; Elsif infoType_ = 'SERVICEPLAN' Then Open cur_Out For Select C.UAC_PROGRAM_ID, SERVICE_PLAN_ID as ISP_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, PROGRAM_NAME From UAC_INDIVIDUAL_SERVICE_PLAN A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join LIST_PROGRAMS B On C.PROGRAM_ID = B.PROGRAM_ID Where C.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'SIR' Then Open cur_Out For Select C.UAC_PROGRAM_ID,C.UAC_ID, SIR_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, PROGRAM_NAME From UAC_SIR A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where C.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) <> 1 Order by DATE_COMPLETED DESC,LAST_UPDATED DESC;
Elsif infoType_ = 'EDUCATIONPLAN' Then Open cur_Out For Select C.UAC_PROGRAM_ID, PLACEMENT_ID, To_Char(A.UPTIME, 'mm/dd/yyyy') as LAST_UPDATED, UPLOADED_BY as STAFF, FILE_NAME, ORG_FILE_NAME, REPORT_TYPE as FILE_TYPE, nvl(Round(FILE_SIZE/1024,0), null) as FILE_SIZE, B.Program_ID, PROGRAM_NAME From UAC_EDUCATION_PLACEMENT A Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where C.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'KYR' Then Open cur_Out For Select C.UAC_PROGRAM_ID, EFFORT_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, PROGRAM_NAME From UAC_EFFORT A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where C.UAC_PROGRAM_ID = uacProgId_ And A.EFFORT_TYPE_ID = 1 Order by A.Uptime Desc; Elsif infoType_ = 'DISCHARGENOTIFICATION' Then Open cur_Out For Select C.UAC_PROGRAM_ID, DISCHARGE_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, PROGRAM_NAME, A.TYPE_OF_DISCHARGE From UAC_DISCHARGE_NOTIFICATION A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where C.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'TRANSFER' Or infoType_ = 'RPTTRANSFER' Then Open cur_Out For Select C.UAC_PROGRAM_ID, TRANSFER_REQUEST_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED,A.PROPOSED_PROGRAM_ID, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, PROGRAM_NAME From UAC_TRANSFER_REQUEST A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where C.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'SPONSOR' Then Open cur_Out For Select E.UAC_ID, C.UAC_PROGRAM_ID, A.SPONSOR_ID, A.FIRST_NAME, A.LAST_NAME, A.SSN, /* CASE When E.SPONSOR_ADDRESS is null then A.Address ELSE E.SPONSOR_ADDRESS End as Address, CASE WHEN E.SPONSOR_CITY is null Then A.City Else E.SPONSOR_CITY End as City, CASE WHEN E.SPONSOR_STATE is null Then A.STATE Else E.SPONSOR_STATE End as State, */ CONCAT(CONCAT(E.SPONSOR_ADDRESS, ' '), E.SPONSOR_ADDRESS_SECONDARY) as ADDRESS, E.SPONSOR_CITY as city, E.SPONSOR_STATE as state, CASE WHEN nvl(E.CURRENT_SPONSOR,0) = 1 Then 'Yes' Else 'No' End as Curr_Sponsor, P_Counter, A_Counter, B.Program_ID, PROGRAM_NAME ,
-- CASE WHEN nvl(A.FLAG,0) = 1 Then 'Yes' Else 'No' End as Flag, /*FLAG_NOTE,*/ -- CASE WHEN nvl(A.FLAG_ADDRESS,0) = 1 Then 'Yes' Else 'No' End as Flag_Address, /*FLAG_ADDRESS_NOTE,*/
CASE WHEN NVL(A.FLAG, 0) = 1 OR NVL(A.FLAG_ADDRESS, 0) = 1 OR EXISTS ( SELECT 1 FROM SPONSORFLAG SF WHERE SF.SPONSORID = A.SPONSOR_ID ) THEN 'YES' ELSE 'NO' END AS FLAG,
CASE WHEN TO_DATE(E.DATE_CREATED, 'DD-MON-RR') <= (SELECT TO_DATE(PARAM_VALUE, 'DD-MON-RR') FROm UACPORTAL_APP_PARAMS WHERE PARAM_NAME = 'OLD_SPONSOR_FORM_DATE') OR E.DATE_CREATED IS NULL THEN 'YES' ELSE 'NO' END AS SHOW_OLD_SPONSOR_FORM From SPONSOR_INFO A Inner Join UAC_SPONSOR_LIST E On A.SPONSOR_ID = E.SPONSOR_ID and a.is_deleted = 0 and e.is_deleted = 0 Inner Join UAC_PROGRAM_INFO C On E.UAC_ID = C.UAC_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where C.UAC_PROGRAM_ID = uacProgId_ Order by A.Uptime Desc; Elsif infoType_ = 'DCS' Then Open cur_Out For Select A.UAC_PROGRAM_ID, A.UAC_ASSESSMENT_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, PROGRAM_NAME From UAC_ASSESSMENT A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_PROGRAM_ID = uacProgId_ And ISDELETED = 0 Order by A.Uptime Desc; Elsif infoType_ = 'RELEASEREQUEST' Then Open cur_Out For Select A.UAC_PROGRAM_ID, A.RELEASE_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, B.PROGRAM_NAME, E.Reason Cancellation_Reason, (SELECT nvl(Max(1),0) FROM UAC_HOMESTUDY_CASE_REFERRAL HS WHERE HS.RELEASE_ID = A.RELEASE_ID) as HAS_HS From UAC_RELEASE_REQUEST A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Left Join List_Deny_Reason E on A.CW_Cancellation_Reason_ID=E.Reason_ID Where A.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'HOMESTUDYCASEREFERRAL' Then Open cur_Out For Select A.UAC_PROGRAM_ID, A.HOMESTUDY_ID, A.REFERRAL_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, E.FIRST_NAME || ' ' || E.LAST_NAME as SPONSOR_NAME, CASE WHEN e.is_Deleted = 0 THEN CONCAT(CONCAT(F.SPONSOR_ADDRESS, ' '), F.SPONSOR_ADDRESS_SECONDARY) ELSE '' END as SPONSOR_ADDRESS, CASE WHEN e.is_Deleted = 0 THEN F.SPONSOR_CITY ELSE '' END as SPONSOR_CITY, CASE WHEN e.is_Deleted = 0 THEN F.SPONSOR_STATE ELSE '' END as SPONSOR_STATE, CASE WHEN e.is_Deleted = 0 THEN F.SPONSOR_ZIPCODE ELSE '' END as SPONSOR_ZIPCODE, G.REFERRAL_STATUS From UAC_HOMESTUDY_CASE_REFERRAL A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID INNER JOIN LIST_REFERRAL_STATUS G ON G.REFERRAL_STATUS_ID = A.REFERRAL_STATUS LEFt JOIN UAC_SPONSOR_LIST F On F.UAC_ID = C.UAC_ID and F.SPONSOR_ID = A.ORIG_SPONSOR_ID and f.is_deleted = 0 LEFT JOIN SPONSOR_INFO E ON F.SPONSOR_ID = E.SPONSOR_ID and e.is_deleted = 0 Where A.UAC_PROGRAM_ID = uacProgId_ --AND (F.CURRENT_SPONSOR = 1 OR F.SPONSOR_ID Is NUll) Order by A.Uptime Desc; Elsif infoType_ = 'POSTRELEASESERVICESREFERRAL' Then Open cur_Out For Select A.UAC_PROGRAM_ID, A.PRS_ID, A.REFERRAL_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, E.FIRST_NAME || ' ' || E.LAST_NAME as SPONSOR_NAME, CASE WHEN e.is_Deleted = 0 THEN CONCAT(CONCAT(F.SPONSOR_ADDRESS, ' '), F.SPONSOR_ADDRESS_SECONDARY) ELSE '' END as SPONSOR_ADDRESS, CASE WHEN e.is_Deleted = 0 THEN F.SPONSOR_CITY ELSE '' END as SPONSOR_CITY, CASE WHEN e.is_Deleted = 0 THEN F.SPONSOR_STATE ELSE '' END as SPONSOR_STATE, CASE WHEN e.is_Deleted = 0 THEN F.SPONSOR_ZIPCODE ELSE '' END as SPONSOR_ZIPCODE, G.REFERRAL_STATUS From UAC_POST_RELEASE_SERVICE A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID INNER JOIN LIST_REFERRAL_STATUS G ON G.REFERRAL_STATUS_ID = A.REFERRAL_STATUS LEFt JOIN UAC_SPONSOR_LIST F On F.UAC_ID = C.UAC_ID and F.SPONSOR_ID = A.ORIG_SPONSOR_ID and f.is_deleted = 0 LEFT JOIN SPONSOR_INFO E ON F.SPONSOR_ID = E.SPONSOR_ID and e.is_deleted = 0 Where A.UAC_PROGRAM_ID = uacProgId_ --AND (F.CURRENT_SPONSOR = 1 OR F.SPONSOR_ID Is NUll) Order by A.Uptime Desc; Elsif infoType_ = 'ARNOTIFICATION' Then Open cur_Out For Select A.UAC_PROGRAM_ID, A.AR_NOTIFICATION_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, B.PROGRAM_NAME From UAC_AR_NOTIFICATION A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_PROGRAM_ID = uacProgId_ Order by A.Uptime Desc; Elsif infoType_ = 'MEDICALEXAM' Then Open cur_Out For Select A.UAC_PROGRAM_ID, A.SCREENING_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, B.PROGRAM_NAME From UAC_MEDICAL_SCREENING A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'NEWASSESSMENT' Then Open cur_OUT For Select A.UAC_PROGRAM_ID, A.INITIAL_INTAKES_ID, To_Char(A.DATE_CREATED, 'mm/dd/yyyy') as DATE_COMPLETED, To_Char(A.UPTIME, 'mm/dd/yyyy') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, B.PROGRAM_NAME From UAC_INITIAL_ASSESSMENT A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_PROGRAM_ID = uacProgId_ And nvl(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'RISKASSESSMENT' Then Open cur_OUT For Select A.UAC_PROGRAM_ID, A.RISK_ASSESSMENT_ID, To_Char(A.DATE_CREATED, 'mm/dd/yyyy') as DATE_COMPLETED, To_Char(A.UPTIME, 'mm/dd/yyyy') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, B.PROGRAM_NAME From UAC_RISK_ASSESSMENT A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_PROGRAM_ID = uacProgId_ And nvl(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'TRAVELREQUEST' Then Open cur_OUT For Select A.UAC_PROGRAM_ID, A.TRAVEL_REQUEST_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, B.PROGRAM_NAME From UAC_FOSTER_CARE_TRAVEL_REQUEST A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'CASEREVIEW' Then Open cur_OUT For Select A.UAC_PROGRAM_ID, A.UAC_CASE_REVIEW_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, B.PROGRAM_NAME From UAC_CASE_REVIEW A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO E on A.UAC_PROGRAM_ID = E.UAC_PROGRAM_ID Inner Join List_Programs B On E.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'SECUREREVIEW' Then Open cur_OUT For Select A.UAC_PROGRAM_ID, A.SECURE_REVIEW_ID, TO_CHAR(A.DATE_CREATED,'MM/DD/YYYY') as DATE_COMPLETED, TO_CHAR(A.UPTIME,'MM/DD/YYYY') as LAST_UPDATED, D.LAST_NAME || ', ' || D.First_Name as Staff, B.Program_ID, B.PROGRAM_NAME From UAC_SECURE_REVIEW A Inner Join ADM_LIST_WEB_USER D ON Upper(A.UPUSER) = Upper(D.User_ID) Inner Join UAC_PROGRAM_INFO E on A.UAC_PROGRAM_ID = E.UAC_PROGRAM_ID Inner Join List_Programs B On E.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; Elsif infoType_ = 'UACDOCUMENTS' Then Open cur_OUT For Select C.UAC_PROGRAM_ID, A.DOC_ID, To_Char(A.UPTIME, 'mm/dd/yyyy') as LAST_UPDATED, UPLOADED_BY as STAFF, FILE_NAME, A.FILE_TYPE, A.FILE_SIZE, B.Program_ID, PROGRAM_NAME, A.ORG_FILE_NAME, A.Remarks, dt.document_type From UAC_DOCUMENTS A Inner Join UAC_PROGRAM_INFO C On A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID Inner Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID LEFT JOIN LIST_DOCUMENT_TYPE dt on A.document_type_id = dt.document_type_id Where C.UAC_PROGRAM_ID = uacProgId_ And NVL(A.ISDELETED,0) = 0 Order by A.Uptime Desc; End If;
END SP_GETUACINFO;

PROCEDURE "SP_GETUAC_CASEMANAGERS" ( UAC_PROGRAM_ID NUMBER, cur_Out OUT SYS_REFCURSOR ) AS uacProgId_ NUMBER(10,0) := UAC_PROGRAM_ID; BEGIN
Open cur_Out For Select A.MGR_ID, A.UAC_ID,A.UAC_PROGRAM_ID, A.FIRST_NAME, A.LAST_NAME, A.PHONE, A.IS_PRIMARY, B.ORG_NAME, A.EMAIL, a.date_created, A.UPUSER FROM UAC_CASE_MANAGERS A LEFT OUTER JOIN LIST_CASE_MANAGER_ORGANIZATIONS B ON A.ORGANIZATION_ID = B.ORG_ID Where A.UAC_PROGRAM_ID = uacProgId_ ORDER BY MGR_ID DESC FETCH FIRST 2 ROWS ONLY;
END SP_GETUAC_CASEMANAGERS;

PROCEDURE "SP_GETUAC_UNIFICATION_SPECIALIST" ( UAC_PROGRAM_ID NUMBER, cur_Out OUT SYS_REFCURSOR ) AS uacProgId_ NUMBER(10,0) := UAC_PROGRAM_ID; BEGIN
Open cur_Out For Select A.US_ID, A.UAC_PROGRAM_ID, C.FIRST_NAME, C.LAST_NAME, A.PHONE, A.IS_PRIMARY, B.ORG_NAME, C.EMAIL, a.date_created, A.UPUSER FROM UAC_UNIFICATION_SPECIALIST A INNER JOIN ADM_LIST_WEB_USER C On A.UPUSER = C.USER_ID LEFT OUTER JOIN LIST_CASE_MANAGER_ORGANIZATIONS B ON A.ORGANIZATION_ID = B.ORG_ID Where A.UAC_PROGRAM_ID = uacProgId_ ORDER BY US_ID DESC FETCH FIRST 2 ROWS ONLY;
END SP_GETUAC_UNIFICATION_SPECIALIST;

PROCEDURE "SP_GET_AGENCY" 
( 
  cur_OUT OUT SYS_REFCURSOR 
) AS 

BEGIN
  Open CUR_OUT for 
  SELECT AGENCY_ID,AGENCY_NAME
  FROM LIST_AGENCY
  ORDER BY AGENCY_ID;
END SP_GET_AGENCY;

PROCEDURE "SP_GET_APP_LAUNCHER_SETTINGS" 
(
   CUR_OUT  OUT SYS_REFCURSOR
) AS


BEGIN
  OPEN CUR_OUT For
		Select Param_VALUE from uacportal.uacportal_App_params 
        where  param_name = 'APP_LAUNCHER_ENVIRONMENT_URL' and App_type = 'SSO';


END SP_GET_APP_LAUNCHER_SETTINGS;

PROCEDURE             "SP_GET_ASSESSMENT_DATA" 
(
  uacAssessmentId IN NUMBER 
, uacProgId IN NUMBER
, userId    IN  VARCHAR2
, CUR_OUT1 OUT SYS_REFCURSOR 
, CUR_OUT2 OUT SYS_REFCURSOR 
, CUR_OUT3 OUT SYS_REFCURSOR 
, CUR_OUT4 OUT SYS_REFCURSOR
, CUR_OUT5 OUT SYS_REFCURSOR 
, CUR_OUT6 OUT SYS_REFCURSOR 
, CUR_OUT7 OUT SYS_REFCURSOR
, CUR_OUT8 OUT SYS_REFCURSOR
, CUR_OUT9 OUT SYS_REFCURSOR
, CUR_OUT10 OUT SYS_REFCURSOR
, CUR_OUT11 OUT SYS_REFCURSOR
, CUR_OUT12 OUT SYS_REFCURSOR
, CUR_OUT13 OUT SYS_REFCURSOR
) AS 

uacAssId_  NUMBER := uacAssessmentId;
uacProgId_    NUMBER := uacProgId;
uacId_    NUMBER;
currentSponsorId  NUMBER := -1;
entryLocation VARCHAR2(200) := '';
n NUMBER(2) := 0;
BEGIN
  Select UAC_ID into uacId_ from UAC_PROGRAM_INFO where UAC_PROGRAM_ID = uacProgId_;
  OPEN CUR_OUT1 For
  Select * from UAC_FAMILY_RELATIONS WHERE UAC_ASSESSMENT_ID = uacAssId_;

  OPEN CUR_OUT2 For
  Select * from UAC_CHILDREN Where UAC_ASSESSMENT_ID = uacAssId_;

  OPEN CUR_OUT3 For
  Select * from LIST_MEDICAL_MENTAL_CONDITION;

  OPEN CUR_OUT4 For
  Select * from UAC_MEDICAL_HISTORY Where UAC_ASSESSMENT_ID = uacAssId_;

  OPEN CUR_OUT5 For
  Select * from UAC_MEDICATION_HISTORY Where UAC_ASSESSMENT_ID = uacAssId_;

  OPEN CUR_OUT6 For
  Select * from UAC_CRIMINAL_HISTORY Where UAC_ASSESSMENT_ID = uacAssId_;

  OPEN CUR_OUT7 For
  Select * From UAC_SUBSTANCE_USE_HISTORY Where UAC_ASSESSMENT_ID = uacAssId_;

  OPEN CUR_OUT8 For
  Select * From V_UAC_SPONSOR_INFO Where UAC_ID = uacId_;

  OPEN CUR_OUT9 For
  Select * from LIST_SUBSTANCES;

  Select count(*) into n From (
    Select 1 from UAC_SPONSOR_LIST Where UAC_ID = uacId_ And CURRENT_SPONSOR = 1 and IS_DELETED=0
    UNION
    Select 0 From Dual
  );

  if n > 1 Then
    Select Distinct SPONSOR_ID into currentSponsorId from UAC_SPONSOR_LIST Where UAC_ID = uacId_ And CURRENT_SPONSOR = 1 and is_deleted = 0 and rownum = 1;
  Else
    currentSponsorId := -1;
  End If;

  OPEN CUR_OUT10 For
  Select * From V_SPONSOR_SPONSORED_UACS Where SPONSOR_ID = currentSponsorId;

  OPEN CUR_OUT11 For
  Select * From LIST_RELATIONSHIP WHERE LIST_ID = 1
  /*Where ISACTIVE = 1*/
  ORDER BY RELATIONSHIP;

  OPEN CUR_OUT12 For
  Select * from SPONSOR_CRIMINAL_HISTORY Where SPONSOR_ID = currentSponsorId;

  Select count(*) into n From (
    Select A.City_OF_ENTRY || ', ' || A.STATE_OF_ENTRY 
    from UAC_INFO A 
    Where A.CITY_OF_ENTRY is not null And A.UAC_ID = uacId_
    UNION
    Select '' From dual
  );
  if n > 1 then

    Select A.City_OF_ENTRY || ', ' || A.STATE_OF_ENTRY into entryLocation 
    from UAC_INFO A 
    Where A.CITY_OF_ENTRY is not null And A.UAC_ID = uacId_;
  End If;
  if uacAssId_ = -100 then
    uacAssId_ := SEQ_UAC_ASSESSMENT.NextVal;
    Insert into UAC_ASSESSMENT (UAC_ASSESSMENT_ID, UAC_PROGRAM_ID, LOS, DATE_OF_INTAKE, UPTIME, UPUSER)
    Select uacAssId_, uacProgId_, 
    Case When CURRENT_STATUS = 'ADMITTED'  Or CURRENT_STATUS = 'IN-TRANSFER' Then DateDiff('day',DATE_ADMITTED, SysDate)
        When  CURRENT_STATUS = 'ENROUTE' Then 0
        When  CURRENT_STATUS = 'DISCHARGED' Then DateDiff('day',DATE_ADMITTED, DATE_DISCHARGED)
    End,
    DATE_ADMITTED, CURRENT_TIMESTAMP, userId  From UAC_PROGRAM_INFO where UAC_PROGRAM_ID = uacProgId_;
    commit;
    --end if;
  end if;
  OPEN CUR_OUT13 For
  Select entryLocation as Entry_Location, A.* from UAC_ASSESSMENT A Where UAC_ASSESSMENT_ID = uacAssId_;
END SP_GET_ASSESSMENT_DATA;

PROCEDURE SP_GET_BWA_MATCHES ( vName varchar2, vRecordID in Number, CUR_OUT1 OUT SYS_REFCURSOR, CUR_OUT2 OUT SYS_REFCURSOR, CUR_OUT3 OUT SYS_REFCURSOR ) AS
BEGIN
OPEN CUR_OUT1 For Select sponsor_Id from sponsor_info where UPPER(REPLACE(vName,' ','')) = UPPER(replace(CONCAT(first_name, Last_name), ' ' , '')) and dob = (select dob from bwa_staging where bwa_staging_id = vRecordID) ; OPEN CUR_OUT2 For select Sponsor_id,occupant_id,uac_id from Sponsor_HOUSEHOLD_OCCUPANTS where UPPER(REPLACE(vName,' ','')) = UPPER(replace(CONCAT(first_name, Last_name), ' ' , '')) and dob = (select dob from bwa_staging where bwa_staging_id = vRecordID) ; OPEN CUR_OUT3 For select Sponsor_id,sponsor_alternate_caregiver_id,uac_id from sponsor_alternate_caregiver where UPPER(REPLACE(vName,' ','')) = UPPER(replace(name, ' ' , '')) and dob = (select dob from bwa_staging where bwa_staging_id = vRecordID) ; END SP_GET_BWA_MATCHES;

PROCEDURE "SP_GET_BWA_SETTINGS" 
(
   CUR_OUT  OUT SYS_REFCURSOR
) AS 


BEGIN
  OPEN CUR_OUT For
		Select param_Name, Param_VALUE from uacportal_App_params 
        where  App_type = 'BWADALTONAPI';


END SP_GET_BWA_SETTINGS;

PROCEDURE "SP_GET_CHILD_CURRENT_LOCATION" ( vUAC_ID NUMBER, cur_Out OUT SYS_REFCURSOR ) AS uacId_ NUMBER(10,0) := vUAC_ID; uacProgId_ NUMBER(10,0); n NUMBER(4); uacStatus_ VARCHAR2(112); currentStatus_ VARCHAR2(112); programExitID_ NUMBER(10,0); dischargeID_ NUMBER(4); livingWithSponsor_ NUMBER(4); BEGIN /* -- Used to get row for Current Location of Child section on DischargeDetail.aspx page */ select count(*) into n from ( Select 1 from UAC_CHILD_LOCATION_HIST Where UAC_ID = uacId_ AND IS_CURRENT = 1 AND ISDELETED = 0 Union Select 0 from dual ); if n > 1 then Select LIVING_WITH_SPONSOR into livingWithSponsor_ from UAC_CHILD_LOCATION_HIST Where UAC_ID = uacId_ AND IS_CURRENT = 1 AND ISDELETED = 0; --If UC is currently living with Sponsor if livingWithSponsor_ = 1 then -- Look up and display Current Sponsor Info Open cur_Out For SELECT UCLH.CHILD_LCTN_HIST_ID, 'Sponsor' as LOCATION_TYPE, E.FIRST_NAME || ' ' || E.LAST_NAME as SPONSOR_OR_PROGRAM_NAME, CASE WHEN D.SPONSOR_ADDRESS is null then '' Else D.SPONSOR_ADDRESS || ', ' END || CASE WHEN D.SPONSOR_ADDRESS_SECONDARY is null THEN '' ELSE D.SPONSOR_ADDRESS_SECONDARY || ', ' END || CASE WHEN D.SPONSOR_CITY is null Then '' Else D.SPONSOR_CITY || ', ' END || D.SPONSOR_STATE || ' ' || D.SPONSOR_ZIPCODE as ADDRESS, --CASE WHEN SATH.UPDATE_DATE IS NULL THEN U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(D.UPTIME, 'MM/DD/YYYY HH12:MI AM') ELSE U1.FIRST_NAME || ' ' || U1.LAST_NAME || ' ' || to_char(SATH.UPDATE_DATE, 'MM/DD/YYYY HH12:MI AM') END as LAST_UPDATED, --U2.FIRST_NAME || ' ' || U2.LAST_NAME || to_char(UCLH.DATE_LAST_UPDATED, 'MM/DD/YYYY HH12:MI AM') as LAST_UPDATED, CASE WHEN SATH.UPDATE_DATE IS NULL THEN CASE WHEN D.UPTIME > UCLH.DATE_LAST_UPDATED THEN U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(D.UPTIME, 'MM/DD/YYYY HH12:MI AM') ELSE U2.FIRST_NAME || ' ' || U2.LAST_NAME || ' ' || to_char(UCLH.DATE_LAST_UPDATED, 'MM/DD/YYYY HH12:MI AM') END ELSE CASE WHEN SATH.UPDATE_DATE > UCLH.DATE_LAST_UPDATED THEN U1.FIRST_NAME || ' ' || U1.LAST_NAME || ' ' || to_char(SATH.UPDATE_DATE, 'MM/DD/YYYY HH12:MI AM') ELSE U2.FIRST_NAME || ' ' || U2.LAST_NAME || ' ' || to_char(UCLH.DATE_LAST_UPDATED, 'MM/DD/YYYY HH12:MI AM') END END as LAST_UPDATED, UCLH.DISCHARGE_TYPE_ID, nvl(UCLH.REASON_FOR_UPDATE_ID,1) AS REASON_FOR_UPDATE_ID FROM UAC_SPONSOR_LIST D LEFT JOIN SPONSOR_INFO E On D.SPONSOR_ID = E.SPONSOR_ID and d.is_deleted = 0 and e.is_deleted = 0 LEFT JOIN SPONSOR_ASSESS_TAB_HISTORY SATH ON D.UAC_ID = SATH.UAC_ID AND D.SPONSOR_ID = SATH.SPONSOR_ID AND SATH.ASSESSMENT_TAB = 'ContactInformation' LEFT JOIN ADM_LIST_WEB_USER U On UPPER(D.UPUSER) = UPPER(U.USER_ID) LEFT JOIN ADM_LIST_WEB_USER U1 On UPPER(SATH.UPUSER) = UPPER(U1.USER_ID) LEFT JOIN UAC_CHILD_LOCATION_HIST UCLH ON D.UAC_ID = UCLH.UAC_ID AND UCLH.IS_CURRENT = 1 AND UCLH.ISDELETED = 0 LEFT JOIN ADM_LIST_WEB_USER U2 ON UPPER(UCLH.USER_LAST_UPDATED) = UPPER(U2.USER_ID) WHERE D.UAC_ID = uacId_ AND D.CURRENT_SPONSOR = 1 ; Else --Else display Current Location Open cur_Out For SELECT A.CHILD_LCTN_HIST_ID, /*C.CAREGIVER_TYPE as LOCATION_TYPE, A.CAREGIVER_FIRST_NAME || ' ' || A.CAREGIVER_LAST_NAME as SPONSOR_OR_PROGRAM_NAME, CASE WHEN A.LOCATION_ADDRESS is null then '' ELSE A.LOCATION_ADDRESS || ', ' END || CASE WHEN A.LOCATION_ADDRESS_SECONDARY is null then '' else A.LOCATION_ADDRESS_SECONDARY || ', ' END || CASE WHEN A.LOCATION_CITY is null then '' else A.LOCATION_CITY || ', ' END || Case when A.LOCATION_STATE is null then '' else A.LOCATION_STATE || ' ' END || A.LOCATION_ZIPCODE as ADDRESS, U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(A.DATE_LAST_UPDATED, 'MM/DD/YYYY HH12:MI AM') as LAST_UPDATED*/ CASE WHEN A.PRIMARY_CAREGIVER_TYPE is not null then C.CAREGIVER_TYPE ELSE A.LOCATION_TYPE END as LOCATION_TYPE, CASE WHEN A.PRIMARY_CAREGIVER_TYPE is not null then A.CAREGIVER_FIRST_NAME || ' ' || A.CAREGIVER_LAST_NAME Else A.LOCATION_NAME END as SPONSOR_OR_PROGRAM_NAME, CASE WHEN A.LOCATION_ADDRESS is null then '' ELSE A.LOCATION_ADDRESS || ', ' END || CASE WHEN A.LOCATION_ADDRESS_SECONDARY is null then '' else A.LOCATION_ADDRESS_SECONDARY || ', ' END || CASE WHEN A.LOCATION_CITY is null then '' else A.LOCATION_CITY || ', ' END || Case when A.LOCATION_STATE is null then '' else A.LOCATION_STATE || ' ' END || A.LOCATION_ZIPCODE as ADDRESS, U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(A.DATE_LAST_UPDATED, 'MM/DD/YYYY HH12:MI AM') as LAST_UPDATED, A.DISCHARGE_TYPE_ID, nvl(A.REASON_FOR_UPDATE_ID,1) AS REASON_FOR_UPDATE_ID, RFU.REASON_FOR_UPDATE_TYPE FROM UAC_CHILD_LOCATION_HIST A LEFT JOIN LIST_CURRENT_LOCATION_TYPE B On A.CHILD_LOCATION_TYPE = B.CURRENT_LCTN_TYPE_ID LEFT JOIN LIST_CAREGIVER_TYPE C On A.PRIMARY_CAREGIVER_TYPE = C.CAREGIVER_TYPE_ID LEFT JOIN ADM_LIST_WEB_USER U On UPPER(A.USER_LAST_UPDATED) = UPPER(U.USER_ID) LEFT JOIN LIST_REASON_FOR_UPDATE RFU ON A.REASON_FOR_UPDATE_ID = RFU.REASON_FOR_UPDATE_ID Where UAC_ID = uacId_ AND IS_CURRENT = 1; End If; ELSE select count(*) into n from ( Select 1 from UAC_PROGRAM_INFO Where UAC_ID = uacId_ Union Select 0 from dual ); if n > 1 then
-- Get latest UAC_PROGRAM_INFO record for UCs SELECT max(UAC_PROGRAM_ID) into uacProgId_ FROM UAC_PROGRAM_INFO WHERE UAC_ID = uacId_ AND CURRENT_STATUS <> 'ENROUTE';
Select UAC_STATUS, CURRENT_STATUS into uacStatus_, currentStatus_ FROM UAC_INFO A INNER JOIN UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID WHERE B.UAC_PROGRAM_ID = uacProgId_;
-- currently admitted in program or in-transfer if currentStatus_ = 'ADMITTED' OR currentStatus_ = 'IN-TRANSFER' then Open cur_Out For SELECT null as CHILD_LCTN_HIST_ID, 'Currently admitted in program' as ADMISSION_DISCHARGE_STATUS, 'Program' as LOCATION_TYPE, C.PROGRAM_NAME as SPONSOR_OR_PROGRAM_NAME, CASE WHEN C.ADDRESS is null then '' ELSE C.ADDRESS || ', ' END || CASE WHEN C.CITY is null then '' else C.CITY || ', ' END || Case when C.STATE is null then '' else C.STATE || ' ' END || C.ZIPCODE as ADDRESS, U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(B.DATE_ADMITTED, 'MM/DD/YYYY HH12:MI AM') as LAST_UPDATED, 0 AS REASON_FOR_UPDATE_ID, null as REASON_FOR_UPDATE_TYPE FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID LEFT JOIN ADM_LIST_WEB_USER U On UPPER(B.UPUSER) = UPPER(U.USER_ID) WHERE B.UAC_PROGRAM_ID = uacProgId_; Elsif currentStatus_ = 'DISCHARGED' then
select count(*) into n from ( Select 1 from UAC_PROGRAM_EXIT Where UAC_PROGRAM_ID = uacProgId_ Union Select 0 from dual );
if n > 1 then SELECT PROGRAM_EXIT_ID, TYPE_OF_DISCHARGE into programExitID_, dischargeID_ FROM UAC_PROGRAM_EXIT WHERE UAC_PROGRAM_ID = uacProgId_ ORDER BY PROGRAM_EXIT_ID DESC FETCH first 1 row only;
if dischargeID_ = 1 then -- Discharged to Program/Facility Open cur_Out For SELECT null as CHILD_LCTN_HIST_ID, DT.DISCHARGE_TYPE as ADMISSION_DISCHARGE_STATUS, F.LOCATION_TYPE, RR.PROGRAM_NAME as SPONSOR_OR_PROGRAM_NAME, Case When RR.PROGRAM_ADDRESS1 is null Then '' Else RR.PROGRAM_ADDRESS1 || ', ' End || CASE WHEN RR.PROGRAM_ADDRESS2 is null THEN '' ELSE RR.PROGRAM_ADDRESS2 || ', ' END || Case When RR.PROGRAM_CITY is null then '' Else RR.PROGRAM_CITY || ', ' End || RR.PROGRAM_STATE || ' ' || RR.PROGRAM_ZIPCODE as ADDRESS, U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(PE.UPTIME, 'MM/DD/YYYY HH12:MI AM') as LAST_UPDATED, 0 AS REASON_FOR_UPDATE_ID, null as REASON_FOR_UPDATE_TYPE FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_PROGRAM_EXIT PE On B.UAC_PROGRAM_ID = PE.UAC_PROGRAM_ID INNER JOIN LIST_DISCHARGE_TYPE DT On PE.TYPE_OF_DISCHARGE = DT.DISCHARGE_TYPE_ID INNER JOIN LIST_DISCHARGE_TYPE_CHILD_LOCATION E ON DT.DISCHARGE_TYPE_ID = E.DISCHARGE_TYPE_ID INNER JOIN LIST_CHILD_LOCATION_TYPE F ON E.LOCATION_TYPE_ID = F.LOCATION_TYPE_ID INNER JOIN UACPORTAL.UAC_RELEASE_REQUEST RR On RR.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID INNER JOIN (SELECT RR1.UAC_PROGRAM_ID, MAX(RR1.RELEASE_ID) as MAX_RR_ID FROM UACPORTAL.UAC_RELEASE_REQUEST RR1 WHERE (RR1.ISDELETED = 0 OR RR1.ISDELETED Is NUll) AND RR1.UAC_PROGRAM_ID = uacProgId_ GROUP BY RR1.UAC_PROGRAM_ID ) Z On RR.RELEASE_ID = Z.MAX_RR_ID LEFT JOIN ADM_LIST_WEB_USER U On UPPER(PE.UPUSER) = UPPER(U.USER_ID) WHERE B.UAC_PROGRAM_ID = uacProgId_ AND PE.PROGRAM_EXIT_ID = programExitID_; ELSIF dischargeID_ in (2,4,7,8,13,14,15,16) then Open cur_Out For SELECT null as CHILD_LCTN_HIST_ID, DT.DISCHARGE_TYPE as ADMISSION_DISCHARGE_STATUS, F.LOCATION_TYPE, F.LOCATION as SPONSOR_OR_PROGRAM_NAME, F.LOCATION as ADDRESS, U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(PE.UPTIME, 'MM/DD/YYYY HH12:MI AM') as LAST_UPDATED, 0 AS REASON_FOR_UPDATE_ID, null as REASON_FOR_UPDATE_TYPE FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_PROGRAM_EXIT PE On B.UAC_PROGRAM_ID = PE.UAC_PROGRAM_ID INNER JOIN LIST_DISCHARGE_TYPE DT On PE.TYPE_OF_DISCHARGE = DT.DISCHARGE_TYPE_ID INNER JOIN LIST_DISCHARGE_TYPE_CHILD_LOCATION E ON DT.DISCHARGE_TYPE_ID = E.DISCHARGE_TYPE_ID INNER JOIN LIST_CHILD_LOCATION_TYPE F ON E.LOCATION_TYPE_ID = F.LOCATION_TYPE_ID LEFT JOIN ADM_LIST_WEB_USER U On UPPER(PE.UPUSER) = UPPER(U.USER_ID) WHERE B.UAC_PROGRAM_ID = uacProgId_ AND PE.PROGRAM_EXIT_ID = programExitID_; ELSIF dischargeID_ = 3 then -- Reunification (Individual Sponsor) Open cur_Out For SELECT null as CHILD_LCTN_HIST_ID, DT.DISCHARGE_TYPE as ADMISSION_DISCHARGE_STATUS, F.LOCATION_TYPE, E.FIRST_NAME || ' ' || E.LAST_NAME as SPONSOR_OR_PROGRAM_NAME, CASE WHEN D.SPONSOR_ADDRESS is null then '' Else D.SPONSOR_ADDRESS || ', ' END || CASE WHEN D.SPONSOR_ADDRESS_SECONDARY is null THEN '' ELSE D.SPONSOR_ADDRESS_SECONDARY || ', ' END || CASE WHEN D.SPONSOR_CITY is null Then '' Else D.SPONSOR_CITY || ', ' END || D.SPONSOR_STATE || ' ' || D.SPONSOR_ZIPCODE as ADDRESS, CASE WHEN SATH.UPDATE_DATE IS NULL THEN CASE WHEN D.UPTIME > PE.UPTIME THEN U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(D.UPTIME, 'MM/DD/YYYY HH12:MI AM') ELSE UPE.FIRST_NAME || ' ' || UPE.LAST_NAME || ' ' || to_char(PE.UPTIME, 'MM/DD/YYYY HH12:MI AM') END ELSE CASE WHEN SATH.UPDATE_DATE > PE.UPTIME THEN U1.FIRST_NAME || ' ' || U1.LAST_NAME || ' ' || to_char(SATH.UPDATE_DATE, 'MM/DD/YYYY HH12:MI AM') ELSE UPE.FIRST_NAME || ' ' || UPE.LAST_NAME || ' ' || to_char(PE.UPTIME, 'MM/DD/YYYY HH12:MI AM') END END as LAST_UPDATED, 0 AS REASON_FOR_UPDATE_ID, null as REASON_FOR_UPDATE_TYPE FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_PROGRAM_EXIT PE On B.UAC_PROGRAM_ID = PE.UAC_PROGRAM_ID INNER JOIN LIST_DISCHARGE_TYPE DT On PE.TYPE_OF_DISCHARGE = DT.DISCHARGE_TYPE_ID INNER JOIN LIST_DISCHARGE_TYPE_CHILD_LOCATION DTCL ON DT.DISCHARGE_TYPE_ID = DTCL.DISCHARGE_TYPE_ID INNER JOIN LIST_CHILD_LOCATION_TYPE F ON DTCL.LOCATION_TYPE_ID = F.LOCATION_TYPE_ID LEFT JOIN UAC_SPONSOR_LIST D on B.UAC_ID = D.UAC_ID AND D.CURRENT_SPONSOR = 1 and d.is_deleted = 0 LEFT JOIN SPONSOR_INFO E On D.SPONSOR_ID = E.SPONSOR_ID LEFT JOIN ( SELECT UPE1.PROGRAM_EXIT_ID, UPE1.UAC_PROGRAM_ID, UPE1.TYPE_OF_DISCHARGE, DT1.DISCHARGE_TYPE FROM UAC_PROGRAM_EXIT UPE1 INNER JOIN LIST_DISCHARGE_TYPE DT1 On UPE1.TYPE_OF_DISCHARGE = DT1.DISCHARGE_TYPE_ID
) PE1 ON B.UAC_PROGRAM_ID = PE1.UAC_PROGRAM_ID LEFT JOIN SPONSOR_ASSESS_TAB_HISTORY SATH ON D.UAC_ID = SATH.UAC_ID AND D.SPONSOR_ID = SATH.SPONSOR_ID AND SATH.ASSESSMENT_TAB = 'ContactInformation' LEFT JOIN ADM_LIST_WEB_USER U On UPPER(D.UPUSER) = UPPER(U.USER_ID) LEFT JOIN ADM_LIST_WEB_USER U1 On UPPER(SATH.UPUSER) = UPPER(U1.USER_ID) LEFT JOIN ADM_LIST_WEB_USER UPE On UPPER(PE.UPUSER) = UPPER(UPE.USER_ID) WHERE B.UAC_PROGRAM_ID = uacProgId_ AND PE.PROGRAM_EXIT_ID = programExitID_; ELSIF dischargeID_ IN (6,10,11,12) then Open cur_Out For SELECT null as CHILD_LCTN_HIST_ID, DT.DISCHARGE_TYPE as ADMISSION_DISCHARGE_STATUS, F.LOCATION_TYPE, F.LOCATION as SPONSOR_OR_PROGRAM_NAME, '' as ADDRESS, U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(PE.UPTIME, 'MM/DD/YYYY HH12:MI AM') as LAST_UPDATED, 0 AS REASON_FOR_UPDATE_ID, null as REASON_FOR_UPDATE_TYPE FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_PROGRAM_EXIT PE On B.UAC_PROGRAM_ID = PE.UAC_PROGRAM_ID INNER JOIN LIST_DISCHARGE_TYPE DT On PE.TYPE_OF_DISCHARGE = DT.DISCHARGE_TYPE_ID INNER JOIN LIST_DISCHARGE_TYPE_CHILD_LOCATION E ON DT.DISCHARGE_TYPE_ID = E.DISCHARGE_TYPE_ID INNER JOIN LIST_CHILD_LOCATION_TYPE F ON E.LOCATION_TYPE_ID = F.LOCATION_TYPE_ID LEFT JOIN ADM_LIST_WEB_USER U On UPPER(PE.UPUSER) = UPPER(U.USER_ID) WHERE B.UAC_PROGRAM_ID = uacProgId_ AND PE.PROGRAM_EXIT_ID = programExitID_; ELSIF dischargeID_ = 9 then Open cur_Out For SELECT null as CHILD_LCTN_HIST_ID, 'Transferred' as ADMISSION_DISCHARGE_STATUS, 'Program' as LOCATION_TYPE, C.PROGRAM_NAME as SPONSOR_OR_PROGRAM_NAME, C.ADDRESS || ', ' || C.CITY || ', ' || C.STATE || ' ' || C.ZIPCODE as ADDRESS, U.FIRST_NAME || ' ' || U.LAST_NAME || ' ' || to_char(B.DATE_ADMITTED, 'MM/DD/YYYY HH12:MI AM') as LAST_UPDATED, 0 AS REASON_FOR_UPDATE_ID, null as REASON_FOR_UPDATE_TYPE FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID LEFT JOIN ADM_LIST_WEB_USER U On UPPER(B.UPUSER) = UPPER(U.USER_ID) WHERE B.UAC_PROGRAM_ID = uacProgId_; ELSE Open cur_Out For SELECT '' as ADMISSION_DISCHARGE_STATUS, '' as LOCATION_TYPE, '' as SPONSOR_OR_PROGRAM_NAME, '' as ADDRESS, '' as LAST_UPDATED FROM DUAL; End If; End if; End if; End if; End if; END SP_GET_CHILD_CURRENT_LOCATION;

PROCEDURE "SP_GET_CHILD_LEVEL_EVENT_SETTING" 
(
   CUR_OUT  OUT SYS_REFCURSOR
) AS 


BEGIN
  OPEN CUR_OUT For
		Select param_value from uacportal_App_params 
        where PARAM_NAME = 'CHILD_EVENT_CUTOVER_DATE';


END SP_GET_CHILD_LEVEL_EVENT_SETTING;

PROCEDURE "SP_GET_CURRENT_CHILD_LOCATION" 
( 
  v_CHILD_LCTN_HIST_ID NUMBER,
  cur_OUT OUT SYS_REFCURSOR 
) AS 

BEGIN
  Open CUR_OUT for 
    SELECT * FROM UAC_CHILD_LOCATION_HIST WHERE CHILD_LCTN_HIST_ID = v_CHILD_LCTN_HIST_ID;
END SP_GET_CURRENT_CHILD_LOCATION;

PROCEDURE "SP_GET_EMAIL_NOTIFICATION_ADD" (
v_UAC_ID IN NUMBER,
v_PROGRAM_ID IN NUMBER,
cur_Out  OUT SYS_REFCURSOR
)
AS 
UACID   NUMBER(10) := v_UAC_ID;
PROGID   NUMBER(10) := v_PROGRAM_ID;
BEGIN
 /*
        Open cur_Out For            
        select  EMAIL,GROUPEMAILS
        from ADM_LIST_WEB_USER 
        where USER_ID in (select distinct USER_CREATED FROM UAC_INFO WHERE UAC_ID=UACID);
       */
        Open cur_Out For
      select LISTAGG(ALW.EMAIL, ', ') WITHIN GROUP (ORDER BY ALW.EMAIL) EMAIL,
        LP.MAIL_ADDRESS,LP.MAIL_CITY,LP.MAIL_STATE,LP.MAIL_ZIPCODE FROM ADM_LIST_WEB_USER ALW 
        JOIN ADM_USER_PROGRAM AUP ON ALW.USER_ID=AUP.USER_ID  
        JOIN LIST_PROGRAMS LP ON LP.PROGRAM_ID=AUP.PROGRAM_ID
        WHERE ALW.ROLE_TYPE='Program' AND ALW.USER_PRIVELEGE='Admin' AND AUP.PROGRAM_ID=PROGID And ALW.USER_ID = 'UACPORTAL'
        GROUP BY   LP.MAIL_ADDRESS,LP.MAIL_CITY,LP.MAIL_STATE,LP.MAIL_ZIPCODE;
        /* 
        Open cur_Out2 For
        select MAIL_ADDRESS,MAIL_CITY,MAIL_STATE,MAIL_ZIPCODE FROM LIST_PROGRAMS WHERE PROGRAM_ID=PROGID;
        */

END SP_GET_EMAIL_NOTIFICATION_ADD;

PROCEDURE "SP_GET_EMAIL_NOTIFICATION_INFO" (
v_UAC_ID IN NUMBER,
v_PROGRAM_ID IN NUMBER,
cur_Out  OUT SYS_REFCURSOR
)
AS 
UACID   NUMBER(10) := v_UAC_ID;
PROGID   NUMBER(10) := v_PROGRAM_ID;
BEGIN
        Open cur_Out For            
        select  EMAIL,GROUPEMAILS
        from ADM_LIST_WEB_USER 
        where USER_ID in (select distinct USER_CREATED FROM UAC_INFO WHERE UAC_ID=UACID);
     /*   
        Open cur_Out1 For
        select LISTAGG(EMAIL, ', ') WITHIN GROUP (ORDER BY EMAIL) EMAIL FROM ADM_LIST_WEB_USER ALW 
        JOIN ADM_USER_PROGRAM AUP ON ALW.USER_ID=AUP.USER_ID        
        WHERE ALW.ROLE_TYPE='Program' AND ALW.USER_PRIVELEGE='Admin' AND AUP.PROGRAM_ID=PROGID;

        Open cur_Out2 For
        select MAIL_ADDRESS,MAIL_CITY,MAIL_STATE,MAIL_ZIPCODE FROM LIST_PROGRAMS WHERE PROGRAM_ID=PROGID;
        */

END SP_GET_EMAIL_NOTIFICATION_INFO;

PROCEDURE SP_GET_ENROLLMENTRPTS_LIST ( 
cur_OUT OUT SYS_REFCURSOR
) 
as
rptStayingDays NUMBER(2);
rptRunTime    VARCHAR2(8);
BEGIN
  select Param_Value into rptStayingDays from UACPORTAL_APP_PARAMS Where APP_TYPE = 'VA/PC' And PARAM_NAME='gsDaysEnrollmentReportStays';
  select RUN_TIME into rptRunTime from UAC_JOBS where JOB_NAME = 'ENROLLMENT_REPORT';

  Open cur_OUT FOR 
  'Select Distinct :rptStayingDays as DaysStay, :rptRunTime as runTime, Rpt_Date from UAC_ENROLLMENT_RPTS_HISTORY where Rpt_Date >= (Sysdate - :rptStayDays) Order by RPT_DATE Desc' 
  using rptStayingDays, rptRunTime, rptStayingDays;

END SP_GET_ENROLLMENTRPTS_LIST;

PROCEDURE "SP_GET_EVENTS" ( vEVENT_DATE DATE default NULL, v_USER_ID IN VARCHAR2, v_Event_Type IN VARCHAR2, cur_OUT OUT SYS_REFCURSOR ) AS vUserRole VARCHAR2(50); BEGIN Select ROLE_TYPE into vUserRole From ADM_LIST_WEB_USER Where USER_ID = v_USER_ID; If vEVENT_DATE is null then if(v_Event_Type = 'Program') Then if(vUserRole = 'Program') Then Open CUR_OUT for select DISTINCT T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,T2.SYNOPSIS,T2.DATE_EVENT AS DATE_EVENT,T2.TIME_EVENT,L.PROGRAM_NAME from PROGRAM_LEVEL_EVENTS_INFO T2 join LIST_PROGRAMS L on T2.PROGRAM_ID=L.PROGRAM_ID where T2.PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID) order by T2.DATE_EVENT DESC; Else Open CUR_OUT for select DISTINCT T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,T2.SYNOPSIS,T2.DATE_EVENT AS DATE_EVENT,T2.TIME_EVENT,L.PROGRAM_NAME from PROGRAM_LEVEL_EVENTS_INFO T2 join LIST_PROGRAMS L on T2.PROGRAM_ID=L.PROGRAM_ID order by T2.DATE_EVENT DESC; End if;
Elsif (v_Event_Type = 'SIR') Then if(vUserRole = 'Program') Then Open CUR_OUT for select distinct T1.EVENT_ID,'SIR Event' as EVENT_TYPE, T1.SYNOPSIS_OF_EVENT as SYNOPSIS,T1.DATE_OF_EVENT AS DATE_EVENT, T1.TIME_OF_EVENT as TIME_EVENT,L.PROGRAM_NAME FROM UAC_SIR_EVENT T1 join LIST_PROGRAMS L on T1.PROGRAM_ID=L.PROGRAM_ID WHERE T1.ISDELETED = 0 AND T1.PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID) order by T1.DATE_OF_EVENT DESC; Else Open CUR_OUT for select distinct T1.EVENT_ID,'SIR Event' as EVENT_TYPE, T1.SYNOPSIS_OF_EVENT as SYNOPSIS, T1.DATE_OF_EVENT AS DATE_EVENT, T1.TIME_OF_EVENT as TIME_EVENT,L.PROGRAM_NAME FROM UAC_SIR_EVENT T1 join LIST_PROGRAMS L on T1.PROGRAM_ID=L.PROGRAM_ID WHERE T1.ISDELETED = 0 order by T1.DATE_OF_EVENT DESC; End if; Else if(vUserRole = 'Program') Then Open CUR_OUT for select DISTINCT T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,T2.SYNOPSIS,T2.DATE_EVENT AS DATE_EVENT,T2.TIME_EVENT,L.PROGRAM_NAME from PROGRAM_LEVEL_EVENTS_INFO T2 join LIST_PROGRAMS L on T2.PROGRAM_ID=L.PROGRAM_ID where T2.PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID) union select distinct T1.EVENT_ID,'SIR Event' as EVENT_TYPE, T1.SYNOPSIS_OF_EVENT, T1.DATE_OF_EVENT AS DATE_EVENT, T1.TIME_OF_EVENT AS TIME_EVENT,L.PROGRAM_NAME FROM UAC_SIR_EVENT T1 join LIST_PROGRAMS L on T1.PROGRAM_ID=L.PROGRAM_ID WHERE T1.ISDELETED = 0 AND T1.PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID) order by DATE_EVENT DESC; Else Open CUR_OUT for select DISTINCT T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,T2.SYNOPSIS,T2.DATE_EVENT AS DATE_EVENT,T2.TIME_EVENT,L.PROGRAM_NAME from PROGRAM_LEVEL_EVENTS_INFO T2 join LIST_PROGRAMS L on T2.PROGRAM_ID=L.PROGRAM_ID union select distinct T1.EVENT_ID,'SIR Event' as EVENT_TYPE, T1.SYNOPSIS_OF_EVENT, T1.DATE_OF_EVENT AS DATE_EVENT, T1.TIME_OF_EVENT AS TIME_EVENT,L.PROGRAM_NAME FROM UAC_SIR_EVENT T1 join LIST_PROGRAMS L on T1.PROGRAM_ID=L.PROGRAM_ID WHERE T1.ISDELETED = 0 order by DATE_EVENT DESC; End if; End if; Else if(v_Event_Type = 'Program') Then if(vUserRole = 'Program') Then Open CUR_OUT for select DISTINCT T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,T2.SYNOPSIS,T2.DATE_EVENT AS DATE_EVENT,T2.TIME_EVENT,L.PROGRAM_NAME from PROGRAM_LEVEL_EVENTS_INFO T2 join LIST_PROGRAMS L on T2.PROGRAM_ID=L.PROGRAM_ID where T2.PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID) and DateDiff('day',T2.DATE_EVENT , vEVENT_DATE) = 0 order by T2.DATE_EVENT DESC; Else Open CUR_OUT for select DISTINCT T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,T2.SYNOPSIS,T2.DATE_EVENT AS DATE_EVENT,T2.TIME_EVENT,L.PROGRAM_NAME from PROGRAM_LEVEL_EVENTS_INFO T2 join LIST_PROGRAMS L on T2.PROGRAM_ID=L.PROGRAM_ID where DateDiff('day',T2.DATE_EVENT , vEVENT_DATE) = 0 order by T2.DATE_EVENT DESC; End if;
Elsif (v_Event_Type = 'SIR') Then if(vUserRole = 'Program') Then Open CUR_OUT for select distinct T1.EVENT_ID,'SIR Event' as EVENT_TYPE, T1.SYNOPSIS_OF_EVENT as SYNOPSIS,T1.DATE_OF_EVENT AS DATE_EVENT, T1.TIME_OF_EVENT as TIME_EVENT,L.PROGRAM_NAME FROM UAC_SIR_EVENT T1 join LIST_PROGRAMS L on T1.PROGRAM_ID=L.PROGRAM_ID WHERE T1.ISDELETED = 0 AND T1.PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID) and DateDiff('day',T1.DATE_OF_EVENT , vEVENT_DATE) = 0 order by T1.DATE_OF_EVENT DESC; Else Open CUR_OUT for select distinct T1.EVENT_ID,'SIR Event' as EVENT_TYPE, T1.SYNOPSIS_OF_EVENT as SYNOPSIS, T1.DATE_OF_EVENT AS DATE_EVENT, T1.TIME_OF_EVENT as TIME_EVENT,L.PROGRAM_NAME FROM UAC_SIR_EVENT T1 join LIST_PROGRAMS L on T1.PROGRAM_ID=L.PROGRAM_ID WHERE T1.ISDELETED = 0 and DateDiff('day',T1.DATE_OF_EVENT , vEVENT_DATE) = 0 order by T1.DATE_OF_EVENT DESC; End if; Else if(vUserRole = 'Program') Then Open CUR_OUT for select DISTINCT T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,T2.SYNOPSIS,T2.DATE_EVENT AS DATE_EVENT,T2.TIME_EVENT,L.PROGRAM_NAME from PROGRAM_LEVEL_EVENTS_INFO T2 join LIST_PROGRAMS L on T2.PROGRAM_ID=L.PROGRAM_ID where T2.PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID) and DateDiff('day',T2.DATE_EVENT , vEVENT_DATE) = 0 union select distinct T1.EVENT_ID,'SIR Event' as EVENT_TYPE, T1.SYNOPSIS_OF_EVENT, T1.DATE_OF_EVENT AS DATE_EVENT, T1.TIME_OF_EVENT AS TIME_EVENT,L.PROGRAM_NAME FROM UAC_SIR_EVENT T1 join LIST_PROGRAMS L on T1.PROGRAM_ID=L.PROGRAM_ID WHERE T1.ISDELETED = 0 AND T1.PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID) and DateDiff('day',T1.DATE_OF_EVENT , vEVENT_DATE) = 0 order by DATE_EVENT DESC; Else Open CUR_OUT for select DISTINCT T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,T2.SYNOPSIS,T2.DATE_EVENT AS DATE_EVENT,T2.TIME_EVENT,L.PROGRAM_NAME from PROGRAM_LEVEL_EVENTS_INFO T2 join LIST_PROGRAMS L on T2.PROGRAM_ID=L.PROGRAM_ID where DateDiff('day',T2.DATE_EVENT , vEVENT_DATE) = 0 union select distinct T1.EVENT_ID,'SIR Event' as EVENT_TYPE, T1.SYNOPSIS_OF_EVENT, T1.DATE_OF_EVENT AS DATE_EVENT, T1.TIME_OF_EVENT AS TIME_EVENT,L.PROGRAM_NAME FROM UAC_SIR_EVENT T1 join LIST_PROGRAMS L on T1.PROGRAM_ID=L.PROGRAM_ID WHERE T1.ISDELETED = 0 and DateDiff('day',T1.DATE_OF_EVENT , vEVENT_DATE) = 0 order by DATE_EVENT DESC; End if; End if; End if; END SP_GET_EVENTS;

PROCEDURE SP_GET_EVENTS_BY_PROGID ( v_UAC_PROG_ID number, cur_Out OUT SYS_REFCURSOR ) AS
cutoverdate DATE := trunc(SYSDATE);
BEGIN
select PARAM_VALUE into cutoverdate from uacportal.uacportal_app_params where param_name = 'CHILD_EVENT_CUTOVER_DATE'; OPEN cur_Out for select T1.EVENT_ID as EVENT_ID, NVL(T1.EVENT_TYPE, 'SIR Event') as EVENT_TYPE, UCF.SIR_FOLLOWUP_REPORT_ID, t2.DATE_CREATED as DATE_OF_EVENT, T1.SIR_ID AS SIR_ID, T1.UAC_SIR_ID, T1.ABUSE_NEGLECT_TYPE, --to_char(T1.DATE_CREATED, 'MM/DD/YYYY') AS DATE_COMPLETED, T1.Date_Created as DATE_COMPLETED, T1.UPTIME AS DATE_UPDATED, T1.UPUSER AS STAFF, T1.ABUSE_NEGLECT_TYPE, -- (select D1.date_created from UACPORTAL.UAC_SIR_EVENTS D1 WHERE T1.SIR_id = D1.sir_id) as CUTOVERDATE, (case when T1.ABUSE_NEGLECT_TYPE is not null then T1.ABUSE_NEGLECT_TYPE when T1.SIR_TYPE_ID = 1 and t2.date_created < cutoverdate THEN 'Emergency SIR' else (select LISTAGG(T3.DESCRIPTION,',') WITHIN GROUP(ORDER BY T3.DESCRIPTION) AS TYPE1 from UAC_SIR_CATEGORY_MAP T4 INNER JOIN UAC_SIR_CATEGORY T3 on T4.CATEGORY_ID = T3.CATEGORY_ID WHERE T4.SIR_ID = T1.SIR_ID AND T4.ADDENDUM_ID IS NULL AND t3.parent_id is null) end) TYPE1, (select count(*) from UAC_SIR_NEW T2 where T2.SIR_ID = T1.SIR_ID AND T2.ADDENDUM_ID IS NOT NULL AND ISDELETED = 0) NUM_ADDENDUMS, to_char(T1.DATE_CREATED, 'MM/DD/YYYY HH12:MI AM') AS DATE_CREATED from UAC_SIR_NEW T1 Inner Join UAC_SIR_EVENT T2 on T1.EVENT_ID = T2.EVENT_ID Left Join UAC_SIR_FOLLOWUP_RPT UCF on T1.EVENT_ID=UCF.EVENT_ID where T1.UAC_PROGRAM_ID = v_UAC_PROG_ID AND T1.ADDENDUM_ID IS NULL AND T1.ISDELETED = 0; END SP_GET_EVENTS_BY_PROGID;

PROCEDURE SP_GET_EVENT_ID ( vUser IN VARCHAR2, vEventIdOut OUT NOCOPY NUMBER
) AS BEGIN --fetch first row when using uptime/dates select Event_ID into vEventIdOut from UAC_SIR_EVENT WHERE UPUSER = vUser order by uptime Desc fetch first 1 row only;
END SP_GET_EVENT_ID;

PROCEDURE "SP_GET_FACILITY_INFO" ( isDeleted IN NUMBER, v_USER_ID IN VARCHAR2, v_STATE IN VARCHAR2, cur_OUT OUT SYS_REFCURSOR ) AS isDeleted_ NUMBER(1) := isDeleted; vUserRole VARCHAR2(50); BEGIN
Delete from TT_FACILITY_INFO; Select ROLE_TYPE into vUserRole From ADM_LIST_WEB_USER Where USER_ID = v_USER_ID; IF (vUserRole = 'HQ' or vUserRole <> 'Program') THEN IF(v_STATE = 'DFFC') THEN Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select f.FACILITY_ID, f.FACILITY_NAME, f.FACILITY_TYPE, f.REGION, f.STATE, f.CITY, f.FUNDED_CAPACITY_M, f.FUNDED_CAPACITY_F, f.COMMENTS, f.INSTRUCTIONS, f.CONFIRMED_BY, f.CONFIRMED_TIME, f.ISDELETED, f.MEN_ONLY, f.REGION_ORDER, f.PROGRAM_ID, f.BEDS_UNAVAILABLE_M, f.BEDS_UNAVAILABLE_F, f.DFFC_TYPE, f.VERIFIED From FACILITY_INFO f Where ISDELETED = isDeleted_ AND f.STATE is null; ElsIf (v_STATE is null or v_STATE='') THEN Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select f.FACILITY_ID, f.FACILITY_NAME, f.FACILITY_TYPE, f.REGION, f.STATE, f.CITY, f.FUNDED_CAPACITY_M, f.FUNDED_CAPACITY_F, f.COMMENTS, f.INSTRUCTIONS, f.CONFIRMED_BY, f.CONFIRMED_TIME, f.ISDELETED, f.MEN_ONLY, f.REGION_ORDER, f.PROGRAM_ID, f.BEDS_UNAVAILABLE_M, f.BEDS_UNAVAILABLE_F, f.DFFC_TYPE, f.VERIFIED From FACILITY_INFO f Where ISDELETED = isDeleted_; Else Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select f.FACILITY_ID, f.FACILITY_NAME, f.FACILITY_TYPE, f.REGION, f.STATE, f.CITY, f.FUNDED_CAPACITY_M, f.FUNDED_CAPACITY_F, f.COMMENTS, f.INSTRUCTIONS, f.CONFIRMED_BY, f.CONFIRMED_TIME, f.ISDELETED, f.MEN_ONLY, f.REGION_ORDER, f.PROGRAM_ID, f.BEDS_UNAVAILABLE_M, f.BEDS_UNAVAILABLE_F, f.DFFC_TYPE, f.VERIFIED From FACILITY_INFO f where f.STATE=v_STATE And ISDELETED = isDeleted_; END IF; ELSIF(vUserRole = 'Program') THEN Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED From FACILITY_INFO Where ISDELETED = isDeleted_ and PROGRAM_ID IN (select PROGRAM_ID from ADM_USER_PROGRAM WHERE USER_ID = v_USER_ID); END IF;
Update TT_FACILITY_INFO F Set (IN_TRANSFER_M, IN_TRANSFER_F) = ( Select SUM(CASE WHEN (C.GENDER='M' OR C.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (C.GENDER='F' OR C.GENDER='Female') THEN 1 ELSE 0 End) from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER' ) Where Exists(Select 1 from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER');
Update TT_FACILITY_INFO F Set (IN_CARE_M, IN_CARE_F) = ( select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID);
Update TT_FACILITY_INFO F Set (ENROUTE_M, ENROUTE_F) = (Select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID);
UPDATE TT_FACILITY_INFO SET BEDS_IN_RESERVE_M = FUNDED_CAPACITY_M - (NVL(IN_TRANSFER_M,0) + NVL(ENROUTE_M,0) + NVL(IN_CARE_M,0) + NVL(BEDS_UNAVAILABLE_M,0)), BEDS_IN_RESERVE_F = FUNDED_CAPACITY_F - (NVL(IN_TRANSFER_F,0) + NVL(ENROUTE_F,0) + NVL(IN_CARE_F,0) + NVL(BEDS_UNAVAILABLE_F,0));
Open CUR_OUT for
SELECT F.*, S.STATENAME From TT_FACILITY_INFO F Left Join LIST_STATES S On F.STATE = S.STATE;
END SP_GET_FACILITY_INFO;

PROCEDURE SP_GET_FACILITY_SUMMARY ( ARCTIME IN DATE , cur_Out OUT SYS_REFCURSOR ) AS arcTime_ Date := ARCTIME; BEGIN if arcTime_ is null then Delete from TT_FACILITY_INFO; Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select f.FACILITY_ID, f.FACILITY_NAME, f.FACILITY_TYPE, f.REGION, f.STATE, f.CITY, f.FUNDED_CAPACITY_M, f.FUNDED_CAPACITY_F, f.COMMENTS, f.INSTRUCTIONS, f.CONFIRMED_BY, f.CONFIRMED_TIME, f.ISDELETED, f.MEN_ONLY, f.REGION_ORDER, f.PROGRAM_ID, f.BEDS_UNAVAILABLE_M, f.BEDS_UNAVAILABLE_F, f.DFFC_TYPE, f.VERIFIED From FACILITY_INFO f Where NVL(ISDELETED,0) = 0;
Update TT_FACILITY_INFO F Set (IN_TRANSFER_M, IN_TRANSFER_F) = ( Select SUM(CASE WHEN (C.GENDER='M' OR C.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (C.GENDER='F' OR C.GENDER='Female') THEN 1 ELSE 0 End) from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER' ) Where Exists(Select 1 from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER');
Update TT_FACILITY_INFO F Set (IN_CARE_M, IN_CARE_F) = ( select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO P Inner join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID);
Update TT_FACILITY_INFO F Set (ENROUTE_M, ENROUTE_F) = (Select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID); /* UPDATE TT_FACILITY_INFO SET BEDS_IN_RESERVE_M = FUNDED_CAPACITY_M - (NVL(IN_TRANSFER_M,0) + NVL(ENROUTE_M,0) + NVL(IN_CARE_M,0) + NVL(BEDS_UNAVAILABLE_M,0)), BEDS_IN_RESERVE_F = FUNDED_CAPACITY_F - (NVL(IN_TRANSFER_F,0) + NVL(ENROUTE_F,0) + NVL(IN_CARE_F,0) + NVL(BEDS_UNAVAILABLE_F,0)); */ UPDATE TT_FACILITY_INFO SET BEDS_IN_RESERVE_M = FUNDED_CAPACITY_M - ( NVL(ENROUTE_M,0) + NVL(IN_CARE_M,0) + NVL(BEDS_UNAVAILABLE_M,0)), BEDS_IN_RESERVE_F = FUNDED_CAPACITY_F - ( NVL(ENROUTE_F,0) + NVL(IN_CARE_F,0) + NVL(BEDS_UNAVAILABLE_F,0));
Open cur_Out for /* Select Case When FACILITY_TYPE is null then 'DFFC' Else FACILITY_TYPE End as Facility_Type, SUM(NVL(FUNDED_CAPACITY_M,0) + NVL(FUNDED_CAPACITY_F,0)) as Funded_Capacity, SUM(NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0)) as IN_CARE, SUM(NVL(BEDS_IN_RESERVE_M,0)+ NVL(BEDS_IN_RESERVE_F,0)) BEDS_IN_RESERVER, SUM((NVL(FUNDED_CAPACITY_M,0) + NVL(FUNDED_CAPACITY_F,0)) - (NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0) + NVL(BEDS_IN_RESERVE_M,0)+ NVL(BEDS_IN_RESERVE_F,0) + NVL(ENROUTE_M,0) + NVL(ENROUTE_F,0) + NVL(IN_TRANSFER_M,0) + NVL(IN_TRANSFER_F,0))) Not_FUNDED From TT_Facility_Info where IsDeleted=0 group by Facility_Type Order by 1; */ Select Case When FACILITY_TYPE is null then 'DFFC' Else FACILITY_TYPE End as Facility_Type, SUM(NVL(FUNDED_CAPACITY_M,0) + NVL(FUNDED_CAPACITY_F,0)) as Funded_Capacity, SUM(NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0)) as IN_CARE, SUM(NVL(ENROUTE_M,0) + NVL(ENROUTE_F,0)) as ENROUTE, SUM(NVL(BEDS_IN_RESERVE_M,0)+ NVL(BEDS_IN_RESERVE_F,0)) BEDS_IN_RESERVER, SUM((NVL(FUNDED_CAPACITY_M,0) + NVL(FUNDED_CAPACITY_F,0)) - (NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0) + NVL(BEDS_IN_RESERVE_M,0)+ NVL(BEDS_IN_RESERVE_F,0) + NVL(ENROUTE_M,0) + NVL(ENROUTE_F,0))) Not_FUNDED From TT_Facility_Info where IsDeleted=0 group by Facility_Type Order by 1; Else Open cur_Out for /* Select Case When FACILITY_TYPE is null then 'DFFC' Else FACILITY_TYPE End as Facility_Type, SUM(NVL(FUNDED_CAPACITY_M,0) + NVL(FUNDED_CAPACITY_F,0)) as Funded_Capacity, SUM(NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0)) as IN_CARE, SUM(NVL(BEDS_IN_RESERVE_M,0)+ NVL(BEDS_IN_RESERVE_F,0)) BEDS_IN_RESERVER, SUM((NVL(FUNDED_CAPACITY_M,0) + NVL(FUNDED_CAPACITY_F,0)) - (NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0) + NVL(BEDS_IN_RESERVE_M,0)+ NVL(BEDS_IN_RESERVE_F,0) + NVL(ENROUTE_M,0) + NVL(ENROUTE_F,0) + NVL(IN_TRANSFER_M,0) + NVL(IN_TRANSFER_F,0))) Not_FUNDED From Facility_Info_Arc where IsDeleted=0 And DateDiff('day',ARC_TIME,arcTime_) = 0 group by Facility_Type Order by 1; */ Select Case When FACILITY_TYPE is null then 'DFFC' Else FACILITY_TYPE End as Facility_Type, SUM(NVL(FUNDED_CAPACITY_M,0) + NVL(FUNDED_CAPACITY_F,0)) as Funded_Capacity, SUM(NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0)) as IN_CARE, SUM(NVL(ENROUTE_M,0) + NVL(ENROUTE_F,0)) as ENROUTE, SUM(NVL(BEDS_IN_RESERVE_M,0)+ NVL(BEDS_IN_RESERVE_F,0)) BEDS_IN_RESERVER, SUM((NVL(FUNDED_CAPACITY_M,0) + NVL(FUNDED_CAPACITY_F,0)) - (NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0) + NVL(BEDS_IN_RESERVE_M,0)+ NVL(BEDS_IN_RESERVE_F,0) + NVL(ENROUTE_M,0) + NVL(ENROUTE_F,0))) Not_FUNDED From Facility_Info_Arc where IsDeleted=0 And DateDiff('day',ARC_TIME,arcTime_) = 0 group by Facility_Type Order by 1; End If; END SP_GET_FACILITY_SUMMARY;

PROCEDURE SP_GET_FACILITY_SUM_BY_REGION ( ARCTIME IN DATE , RptType in Varchar2, cur_Out OUT SYS_REFCURSOR ) AS arcTime_ Date := ARCTIME; rptType_ VARCHAR2(20) := upper(RptType); BEGIN if arcTime_ is null then
Delete from TT_FACILITY_INFO; Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED From FACILITY_INFO;
Update TT_FACILITY_INFO F Set (IN_TRANSFER_M, IN_TRANSFER_F) = ( Select SUM(CASE WHEN (C.GENDER='M' OR C.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (C.GENDER='F' OR C.GENDER='Female') THEN 1 ELSE 0 End) from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER' ) Where Exists(Select 1 from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER');
Update TT_FACILITY_INFO F Set (IN_CARE_M, IN_CARE_F) = ( select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID);
Update TT_FACILITY_INFO F Set (ENROUTE_M, ENROUTE_F) = (Select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID); /* UPDATE TT_FACILITY_INFO SET BEDS_IN_RESERVE_M = FUNDED_CAPACITY_M - (NVL(IN_TRANSFER_M,0) + NVL(ENROUTE_M,0) + NVL(IN_CARE_M,0) + NVL(BEDS_UNAVAILABLE_M,0)), BEDS_IN_RESERVE_F = FUNDED_CAPACITY_F - (NVL(IN_TRANSFER_F,0) + NVL(ENROUTE_F,0) + NVL(IN_CARE_F,0) + NVL(BEDS_UNAVAILABLE_F,0)); */ UPDATE TT_FACILITY_INFO SET BEDS_IN_RESERVE_M = FUNDED_CAPACITY_M - ( NVL(ENROUTE_M,0) + NVL(IN_CARE_M,0) + NVL(BEDS_UNAVAILABLE_M,0)), BEDS_IN_RESERVE_F = FUNDED_CAPACITY_F - (NVL(ENROUTE_F,0) + NVL(IN_CARE_F,0) + NVL(BEDS_UNAVAILABLE_F,0));
if rptType_ = 'BYREGION' Then Open cur_Out for Select REGION, --SUM(NVL(FUNDED_CAPACITY,0)) as Funded_Capacity, Sum(nvl(FUNDED_CAPACITY_M,0) + nvl(FUNDED_CAPACITY_F,0)) as Funded_Capacity, Sum(nvl(BEDS_IN_RESERVE_M,0) + nvl(BEDS_IN_RESERVE_F,0)) as BEDS_IN_RESERVE From TT_FACILITY_INFO where IsDeleted=0 group by REGION Order by 1; --SUM(NVL(FUNDED_CAPACITY,0) - (NVL(IN_CARE_M,0) + NVL(IN_CARE_F,0) + NVL(BEDS_IN_RESERVE_M,0) + NVL(BEDS_IN_RESERVE_F,0))) as Funded_Capacity, Else OPen cur_Out For Select FACILITY_TYPE, Sum(nvl(BEDS_IN_RESERVE_M,0) + nvl(BEDS_IN_RESERVE_F,0)) as BEDS_IN_RESERVE From TT_FACILITY_INFO where IsDeleted=0 And FACILITY_TYPE is not null group by FACILITY_TYPE Order by 1; End If; Else if rptType_ = 'BYREGION' Then Open cur_Out for Select REGION, --SUM(NVL(FUNDED_CAPACITY,0)) as Funded_Capacity, Sum(nvl(FUNDED_CAPACITY_M,0) + nvl(FUNDED_CAPACITY_F,0)) as Funded_Capacity, Sum(nvl(BEDS_IN_RESERVE_M,0) + nvl(BEDS_IN_RESERVE_F,0)) as BEDS_IN_RESERVE From Facility_Info_Arc where IsDeleted=0 And DateDiff('day', Arc_TIME, arcTime_) = 0 and To_Char(Arc_Time, 'HH:mi:ss AM') = To_Char(arcTime_, 'HH:mi:ss AM') group by REGION Order by 1; else OPen cur_Out For Select FACILITY_TYPE, Sum(nvl(BEDS_IN_RESERVE_M,0) + nvl(BEDS_IN_RESERVE_F,0)) as BEDS_IN_RESERVE From Facility_Info_Arc where IsDeleted=0 And DateDiff('day', Arc_TIME, arcTime_) = 0 and To_Char(Arc_Time, 'HH:mi:ss AM') = To_Char(arcTime_, 'HH:mi:ss AM') And FACILITY_TYPE is not null group by FACILITY_TYPE Order by 1; end if; End If; END SP_GET_FACILITY_SUM_BY_REGION;

PROCEDURE "SP_GET_FC_TRAVEL_REQUEST" (
v_TRAVEL_REQUEST_ID NUMBER,
cur_Out  OUT SYS_REFCURSOR
)
AS 
BEGIN
      Open cur_Out For
          Select * 
          From UAC_FOSTER_CARE_TRAVEL_REQUEST Where TRAVEL_REQUEST_ID = v_TRAVEL_REQUEST_ID ;
END SP_GET_FC_TRAVEL_REQUEST;

PROCEDURE "SP_GET_FC_TRAVEL_REQUESTS" (
v_UAC_PROGRAM_ID NUMBER,
cur_Out  OUT SYS_REFCURSOR
)
AS 
BEGIN
      Open cur_Out For
          Select TRAVEL_REQUEST_ID, REQUESTOR_NAME || ' - ' || DATE_REQUESTED   REQUESTOR_NAME 
          From UAC_FOSTER_CARE_TRAVEL_REQUEST Where UAC_PROGRAM_ID = v_UAC_PROGRAM_ID ;
END SP_GET_FC_TRAVEL_REQUESTS;

PROCEDURE "SP_GET_HSPRS_DROPDOWNLIST" (

cur_Out1  OUT SYS_REFCURSOR,
cur_Out2  OUT SYS_REFCURSOR,
cur_Out3  OUT SYS_REFCURSOR,
cur_Out4  OUT SYS_REFCURSOR,
cur_Out5  OUT SYS_REFCURSOR
)
AS 

BEGIN
  Open cur_Out1 For
  SELECT * FROM LIST_REFERRAL_STATUS;

  Open cur_Out2 For
  SELECT * FROM LIST_REF_REASON;

  Open cur_Out3 For
  SELECT * FROM LIST_HS_SPECIAL_INSTR;

  Open cur_Out4 For
  SELECT * FROM LIST_HOMESTUDY_PROVIDER;

  Open cur_Out5 For
  SELECT * FROM LIST_REASON_EXPEDITED;

END SP_GET_HSPRS_DROPDOWNLIST;

PROCEDURE "SP_GET_INPROGRESS_CLE_ADDENDUM" 
(
  vEVENT_ID IN NUMBER,
  TOTAL  OUT NUMBER
) AS 

BEGIN

    SELECT COUNT(*) as EVENT_ID into TOTAL
    FROM inprogress_cle_addendum
    WHERE EVENT_ID = vEVENT_ID;

END SP_GET_INPROGRESS_CLE_ADDENDUM;

PROCEDURE SP_GET_INTAKE_CHECKBOXES ( v_UacId number, cur_OUT OUT SYS_REFCURSOR ) AS v_list NUMBER :=0; v_checkboxId NUMBER;
BEGIN for rec in( select CHECKBOXES_ID from INTAKE_POPULATION_TAG where UAC_ID = v_UacId)
loop --DBMS_OUTPUT.PUT_LINE('CHECKBOXES_ID: ' || rec.CHECKBOXES_ID); if rec.CHECKBOXES_ID > 0 then v_List := v_List + 1; end if; end loop;
--DBMS_OUTPUT.PUT_LINE('count: ' || v_list);
if v_list > 0 THEN if v_list = 1 THEN select CHECKBOXES_ID INTO v_checkboxId from INTAKE_POPULATION_TAG t where UAC_ID = v_UacId;
Open CUR_OUT for select ic.INTAKE_CHECKBOXES_ID ,ic.CHECKBOX_VALUE ,ic.ISACTIVE ,ic.ORDER_BY ,t.TAG_STATUS ,t.ISSELECTED ,t.UAC_ID from INTAKE_CHECKBOXES ic inner join INTAKE_POPULATION_TAG t on t.CHECKBOXES_ID = ic.INTAKE_CHECKBOXES_ID and ic.ISACTIVE = '1' where t.UAC_ID = v_UacId UNION select ic.INTAKE_CHECKBOXES_ID ,ic.CHECKBOX_VALUE ,ic.ISACTIVE ,ic.ORDER_BY , '' as TAG_STATUS , null AS ISSELECTED , 0 AS UAC_ID from INTAKE_CHECKBOXES ic where ic.ISACTIVE = 1 and ic.INTAKE_CHECKBOXES_ID <> v_checkboxId order by ORDER_BY ASC; ELSE Open CUR_OUT for select ic.INTAKE_CHECKBOXES_ID ,ic.CHECKBOX_VALUE ,ic.ISACTIVE ,ic.ORDER_BY ,t.TAG_STATUS ,t.ISSELECTED ,t.UAC_ID from INTAKE_CHECKBOXES ic inner join INTAKE_POPULATION_TAG t on t.CHECKBOXES_ID = ic.INTAKE_CHECKBOXES_ID and ic.ISACTIVE = '1' where t.UAC_ID = v_UacId order by ORDER_BY ASC; END IF; ELSE Open CUR_OUT for select A.INTAKE_CHECKBOXES_ID ,A.CHECKBOX_VALUE ,A.ISACTIVE ,A.ORDER_BY , '' as TAG_STATUS , 0 AS ISSELECTED , 0 AS UAC_ID from INTAKE_CHECKBOXES A where A.ISACTIVE = 1 order by A.ORDER_BY ASC; END IF;
END SP_GET_INTAKE_CHECKBOXES;

PROCEDURE "SP_GET_LIRS_USCCB_COUNT" (
cur_OUT OUT SYS_REFCURSOR
)
AS
lirsM_  NUMBER(4);
lirsF_  NUMBER(4);
usccbM_ NUMBER(4);
usccbF_ NUMBER(4);
BEGIN
  Select SUM(CASE When B.GENDER = 'M' Then 1 ELSE 0 END), SUM(CASE When B.GENDER = 'F' Then 1 ELSE 0 END) Into lirsM_, lirsF_ from UAC_Program_INFO A Inner Join UAC_INFO B On A.UAC_ID = B.UAC_ID Where Program_ID in (10,55,60, 111, 61, 64) and CURRENT_STATUS = 'ADMITTED';
  Select SUM(CASE When B.GENDER = 'M' Then 1 ELSE 0 END), SUM(CASE When B.GENDER = 'F' Then 1 ELSE 0 END) into usccbM_, usccbF_ from UAC_Program_INFO A Inner Join UAC_INFO B On A.UAC_ID = B.UAC_ID Where Program_ID in (108, 109, 18, 112, 132,110, 102, 114) and CURRENT_STATUS = 'ADMITTED';

  Open cur_OUT For
  Select lirsM_ as LIRS_M, lirsF_ as LIRS_F, usccbM_ as USCCB_M, usccbF_ as USCCB_F from dual;

END SP_GET_LIRS_USCCB_COUNT;

