PROCEDURE SP_Get_NEW_SIRID ( vUser IN VARCHAR2, vSirIDOut OUT NOCOPY NUMBER -- cur_OUT OUT Sys_Refcursor
) AS BEGIN
SELECT uac_Sir_ID INTO vSirIDOut FROM UAC_SIR_NEW info where upuser = vUser order by uac_sir_id desc fetch first 1 row only;
END SP_Get_NEW_SIRID;

PROCEDURE SP_Get_ORIGINAL_SIRID (
VUacProgramID in Number, vEventID in number, vSirIDOut OUT NOCOPY NUMBER -- cur_OUT OUT Sys_Refcursor
) AS BEGIN
SELECT Sir_ID INTO vSirIDOut FROM UAC_SIR_NEW info where info.UAC_PRogram_id = VUacProgramID and info.event_id = vEventID order by uac_sir_id desc fetch first 1 row only;
END SP_Get_ORIGINAL_SIRID;

PROCEDURE "SP_GET_PROGRAM_ADDRESS" ( v_PROGRAM_ID number, cur_Out OUT SYS_REFCURSOR ) AS BEGIN
Open cur_Out For select p.PROGRAM_ID, p.PROGRAM_NAME, p.PROGRAM_TYPE_ID, p.ADDRESS, p.CITY, p.STATE, p.ZIPCODE from LIST_PROGRAMS p where PROGRAM_ID=v_PROGRAM_ID;
END SP_GET_PROGRAM_ADDRESS;

PROCEDURE "SP_GET_PROGRAM_HIST" ( v_Anumber varchar2, cur_Out OUT SYS_REFCURSOR ) AS
BEGIN
Open cur_Out For Select A.UAC_PROGRAM_ID, B.PROGRAM_NAME || ' - ' || CURRENT_STATUS as PROGRAM_NAME From UAC_PROGRAM_INFO A Inner join LIST_PROGRAMS B On A.PROGRAM_ID = B.PROGRAM_ID Where A.UAC_ID in ( Select UAC_ID from UAC_INFO where ALIEN_NO = v_Anumber ) And A.CURRENT_STATUS in ('ADMITTED', 'DISCHARGED', 'IN-TRANSFER') Order By A.UPTIME DESC; END SP_GET_PROGRAM_HIST;

PROCEDURE "SP_GET_PROOF_OF_RELATIONSHIP_LIST" ( cur_Out OUT SYS_REFCURSOR ) AS BEGIN
Open cur_Out For select LIST_PROOF_RELATIONSHIP_DOCUMENT_ID, RELATIONSHIP_DOCUMENT from LIST_PROOF_RELATIONSHIP_DOCUMENT where ISACTIVE = 1;
END SP_GET_PROOF_OF_RELATIONSHIP_LIST;

PROCEDURE "SP_GET_SEQ_VALUE" 
(
  SEQ_NAME IN VARCHAR2 
, cur_OUT OUT Sys_Refcursor  
) AS 
seqName_  VARCHAR2(112) := SEQ_NAME;
strSql    VARCHAR2(250);
BEGIN
  strSql := 'Select ' || seqName_ || '.nextval as seqNextVal from dual';
  Open cur_OUT For (strSql);
END SP_GET_SEQ_VALUE;

PROCEDURE             "SP_GET_SIR_DOCLIST_BY_EVENT" (
v_EVENT_ID    number,
v_DOC_TYPE varchar2,
cur_Out  OUT SYS_REFCURSOR
)
AS 
BEGIN
  OPEN cur_Out for
  Select * from UAC_SIR_DOCUMENTS where SIR_DOC_TYPE=v_DOC_TYPE and UAC_SIR_ID IN (SELECT UAC_SIR_ID from UAC_SIR_NEW Where EVENT_ID=v_EVENT_ID);
END SP_GET_SIR_DOCLIST_BY_EVENT;

PROCEDURE SP_GET_SOUTHWEST_BORDER_SUMMARY ( ARCTIME IN DATE , cur_Out OUT SYS_REFCURSOR ) AS arcTime_ Date := ARCTIME; BEGIN if arcTime_ is null then Delete from TT_FACILITY_INFO; Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select f.FACILITY_ID, f.FACILITY_NAME, f.FACILITY_TYPE, f.REGION, f.STATE, f.CITY, f.FUNDED_CAPACITY_M, f.FUNDED_CAPACITY_F, f.COMMENTS, f.INSTRUCTIONS, f.CONFIRMED_BY, f.CONFIRMED_TIME, f.ISDELETED, f.MEN_ONLY, f.REGION_ORDER, f.PROGRAM_ID, f.BEDS_UNAVAILABLE_M, f.BEDS_UNAVAILABLE_F, f.DFFC_TYPE, f.VERIFIED From FACILITY_INFO f Where NVL(ISDELETED,0) = 0 AND EXISTS (SELECT 1 FROM FACILITY_BORDER_REGION fbr WHERE f.FACILITY_ID = fbr.FACILITY_ID);
Update TT_FACILITY_INFO F Set (IN_TRANSFER_M, IN_TRANSFER_F) = ( Select SUM(CASE WHEN (C.GENDER='M' OR C.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (C.GENDER='F' OR C.GENDER='Female') THEN 1 ELSE 0 End) from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER' ) Where Exists(Select 1 from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER');
Update TT_FACILITY_INFO F Set (IN_CARE_M, IN_CARE_F) = ( select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO P Inner join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID);
Update TT_FACILITY_INFO F Set (ENROUTE_M, ENROUTE_F) = (Select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID);
UPDATE TT_FACILITY_INFO SET BEDS_IN_RESERVE_M = FUNDED_CAPACITY_M - ( NVL(ENROUTE_M,0) + NVL(IN_CARE_M,0) + NVL(BEDS_UNAVAILABLE_M,0)), BEDS_IN_RESERVE_F = FUNDED_CAPACITY_F - ( NVL(ENROUTE_F,0) + NVL(IN_CARE_F,0) + NVL(BEDS_UNAVAILABLE_F,0));
Open cur_Out for
Select lbr.BORDER_REGION, SUM(NVL(f.FUNDED_CAPACITY_M,0) + NVL(f.FUNDED_CAPACITY_F,0)) as TOTAL_STANDARD_CAPACITY, SUM(NVL(f.IN_CARE_M,0) + NVL(f.IN_CARE_F,0) + NVL(f.ENROUTE_M,0) + NVL(f.ENROUTE_F,0)) as OCCUPIED_BEDS, SUM(NVL(f.BEDS_IN_RESERVE_M,0)+ NVL(f.BEDS_IN_RESERVE_F,0)) OPEN_BEDS, SUM(NVL(f.BEDS_UNAVAILABLE_M,0) + NVL(f.BEDS_UNAVAILABLE_F,0)) as UNAVAILABLE_BEDS From TT_Facility_Info f INNER JOIN FACILITY_BORDER_REGION fbr ON f.FACILITY_ID = fbr.FACILITY_ID INNER JOIN LIST_BORDER_REGION lbr ON fbr.REGION_ID = lbr.REGION_ID where f.IsDeleted=0 group by lbr.BORDER_REGION Order by 1;
Else Open cur_Out for Select lbr.BORDER_REGION, SUM(NVL(f.FUNDED_CAPACITY_M,0) + NVL(f.FUNDED_CAPACITY_F,0)) as TOTAL_STANDARD_CAPACITY, SUM(NVL(f.IN_CARE_M,0) + NVL(f.IN_CARE_F,0) + NVL(f.ENROUTE_M,0) + NVL(f.ENROUTE_F,0)) as OCCUPIED_BEDS, SUM(NVL(f.BEDS_IN_RESERVE_M,0)+ NVL(f.BEDS_IN_RESERVE_F,0)) OPEN_BEDS, SUM(NVL(f.BEDS_UNAVAILABLE_M,0) + NVL(f.BEDS_UNAVAILABLE_F,0)) as UNAVAILABLE_BEDS From Facility_Info_Arc f INNER JOIN FACILITY_BORDER_REGION fbr ON f.FACILITY_ID = fbr.FACILITY_ID INNER JOIN LIST_BORDER_REGION lbr ON fbr.REGION_ID = lbr.REGION_ID where f.IsDeleted=0 And DateDiff('day',ARC_TIME,arcTime_) = 0 group by lbr.BORDER_REGION Order by 1; End If; END SP_GET_SOUTHWEST_BORDER_SUMMARY;

PROCEDURE "SP_GET_SPONSOR_ASSESSMENT" ( vSPONSOR_ID NUMBER, vUACID IN NUMBER, cur_Out OUT SYS_REFCURSOR ) AS LOCKTEST number :=0; BEGIN
SELECT COUNT(*) INTO LOCKTEST FROM UAC_SPONSOR_LIST WHERE SPONSOR_ID = vSPONSOR_ID and UAC_ID = vUACID ; if LOCKTEST = 1 Then OPEN cur_Out For SELECT 1 As EXISTINGSPONSOR, A.UPTIME, A.First_Name, A.Last_Name, A.ALIEN_NO, A.DOB, A.COB, A.GENDER, A.LEGAL_STATUS_ID, A.COR, B.SPONSOR_HOME_PHONE , B.SPONSOR_WORK_PHONE , B.BACKUP_PHONE_TYPE, A.EMAIL , B.SPONSOR_ADDRESS , B.SPONSOR_ADDRESS_SECONDARY, B.SPONSOR_CITY , B.SPONSOR_STATE, B.SPONSOR_ZIPCODE , B.SPONSOR_COUNTY, B.SPONSOR_LATITUDE, B.SPONSOR_LONGITUDE, B.ADDRESS_VERIFIED_GOOGLE_EARTH , B.ADDRESS_VERIFIED_GOOGLE_MAPS , B.HOW_LONG_AT_CURRENT_ADDRESS , B.ADDITIONAL_ADDRESS_INFORMATION , B.VERIFIED_SMARTY_STREETS, B.SPONSOR_EMAIL , A.MARRIAGE_STATUS, B.IMMIGRATION_STATUS, B.LEGAL_STATUS_VERIFIED, B.SPONSOR_IDENTITY_VERIFIED, B.ADDITIONAL_IDENTITY, A.PRIMARY_LANGUAGE, A.OTHER_LANGUAGES, A.RELIGIOUS_AFFILIATION, B.ADDITIONAL_INFORMATION, B.CASE_MANAGEMENT_ASSESSMENT, A.Flag, A.FLAG_NOTE, a.Flag_address, A.FLAG_ADDRESS_NOTE, B.CASE_MANAGEMENT_ASSESSMENT, B.ASSESSMENT_STARTED, B.DATE_INITIATED, B.DATE_COMPLETE, B.SIGNATURE, B.TITLE, B.HAVE_JOB, B.WORK_SCHEDULE, B.MEET_MONTHLY_EXPENSES, B.SPONSOR_RELATIONSHIP_TO_UAC, B.OWE_MONEY, B.CHILD_JOURNEY, B.WHY_CHILD_LEAVE_COUNTRY, B.HAS_DNA_BEEN_USED, B.RELATIONSHIP_VERIFIED, B.SPONSOR_KNOW_CHILD, B.PRIMARY_SPONSOR, B.CURRENT_SPONSOR, B.SPONSOR_CATEGORY, B.ADD_REL_TO_CHILD, B.ADDITIONAL_FINANCIAL, B.SPONSOR_ATTEMPT_SPONSOR_ORR, B.CHILD_STILL_RESIDING_WITH_SPONSOR , B.WHY_CHILD_NOT_RESIDING_WITH_SPONSOR , B.SPONSOR_WITHDRAW_APP , B.WHY_WITHDRAW, B.SPONSOR_DENIED_SPOSORSHIP , B.WHY_DENIED , B.ADD_INFO_SPONSORSHIPS , B.SPONSOR_UNDERGO_HOME_STUDY, B.CHILD_POSTRELEASE_SERVICE, B.CHILD_ENROLLED_ATTENDING_SCHOOL, B.CHILD_COURT_DATE, B.SPONSOR_ATTEND_LOPC, B.FORMTYPE, FT.ADDITIONAL_INFO_ON_COERCION, FT.ADDITIONAL_INFO_ON_DEBT , FT.ADDITIONAL_INFO_ON_JOURNEY , FT.PRESENT_ABUSE_RISK, FT.PRESENT_ABUSE_RISK_YES, FT.PRESENT_BEING_ABUSED, FT.PRESENT_BEING_ABUSED_YES, FT.HOW_MUCH_DEBT_INCREASE, FT.ARRANGE_WORK, FT.ARRANGE_WORK_YES, FT.KEEP_DESTROY_DOCUMENTS, FT.KEEP_DESTROY_DOCUMENTS_YES, FT.THREATEN_TO_REPORT, FT.THREATEN_TO_REPORT_YES, FT.ANYONE_PAY_FOR_TRAVEL, FT.THREATEN_SPONSOR_FAMILY, FT.THREATEN_SPONSOR_FAMILY_YES, FT.ANYTHING_BAD_IF_LEAVE, FT.ANYTHING_BAD_IF_LEAVE_YES, FT.ARRANGEMENTS_CHANGE, FT.ARRANGEMENTS_CHANGE_YES, FT.DEPEND_UPON_NON_FAMILY_MEMBERS, FT.EXPERIENCE_ANY_CHALLENGES, FT.PERFORM_WORK, FT.RECEIVE_PAY, FT.WORK_CONDITIONS_CHANGE, FT.PAY_BACK, FT.FAMILY_FRIEND_OWE_MONEY, FT.FAMILY_FRIEND_OWE_MONEY_YES, FT.DEBT_AMOUNT_INCREASE, FT.PAY_WITHOUT_REPORT_ORR, FT.PAY_WITHOUT_REPORT_ORR_YES, FT.PAY_ON_RELEASE, FT.PAY_ON_RELEASE_YES, FT.THREATEN_OVER_PAYMENT, FT.TRAVELED_BACK, FT.HOW_ARRIVE_WORK_SITE, FT.PERFORM_WORK_FREQUENCY, FT.THREATEN_OVER_PAYMENT_YES, FT.WORRY_FIND, FT.WORRY_FIND_YES, FT.HAS_DEBT, FT.PLAN_FOR_PAY_BACK, FT.REFERRED_TO_OTIP, FT.ANYONE_AROUND_PHYSICALLY_HARMED, FT.ANYONE_AROUND_PHYSICALLY_HARMED_YES, FT.HELD_AGAINST_WILL, FT.HELD_AGAINST_WILL_YES, FT.DO_WHAT_THEY_DISLIKE, FT.DO_WHAT_THEY_DISLIKE_YES, FT.PHYSICALLY_HARMED, FT.PHYSICALLY_HARMED_YES, FT.COMMUNICATION_RESTRICTED, FT.PAID_AS_PROMISED, FT.MOVEMENT_RESTRICTED, FT.EXPENSES_OUT_OF_PAY, FT.EXPENSES_OUT_OF_PAY_YES, FT.HAPPEN_IF_LEFT_JOB, FT.HAPPEN_IF_NOT_PAY_BACK, FT.DEBT_AMOUNT, FT.PERFORM_WORK_TYPE, FT.FIRST_DECIDE_TO_US, FT.WHEN_DEBT_AMOUNT_INCREASE, FT.FIRST_LIVE_WHERE_WHOM, FT.WHERE_LIVE_WHILE_WORKING, FT.WHO_ARRANGE_WORK, FT.WHO_PLAN_JOURNEY, FT.WHY_DEBT_AMOUNT_INCREASE, FT.ADD_INFO_FRAUD_TRAFFICKING, CH.SPONSOR_DISCLOSE_CRIMINAL_HISTORY, CH.EXPLAIN_CRIMINAL_HISTORY, CH.FELONY , CH.MISDEMEANOR, CH.PROBATION_PAROLE, CH.CHILD_ABUSE_NEGLECT , CH.SUBSTANCE_ABUSE , CH.DOMESTIC_VIOLENCE, CH.CRIMINAL_ADDITIONAL_INFORMATION, CH.BACKGROUND_ADDITIONAL_INFORMATION, FR.SPONSOR_PARTNER, FR.LEAGLLY_MARRIED_COHABITATION, FR.SPONSOR_HAVE_CHILDREN, FR.CHILDREN_BEEN_IN_ORR_CARE, FR.WHO_CARING_FOR_CHILDREN, FR.COURT_ORDERED_FINANCIAL_SUPPORT, FR.SPONSOR_PARTNER_INTERACTED_CPS, FR.SPONSOR_PARTNER_CPS_INTERACTION, FR.CHILD_REMOVED_FROM_CUSTODY, FR.CHILD_REMOVED_EXPLAIN, FR.INVOLVED_IN_CHILD_SUPPORT, FR.INVOLVED_IN_CHILD_SUPPORT_EXPLAIN, FR.FAMILY_FRIENDS_IN_USA, FR.HAVE_FAMILY_HOME_COUNTRY, FR.RELATIONSHIP_WITH_FAMILY_HOME_COUNTRY, FR.FAMILY_RELATIONSHIPS_ADDITION_INFO, FR.HOW_SPONSOR_DISCIPLINE, FR.ANY_RELATIVES_IN_ORR_CARE, FR.WHERE_IN_ORR_CARE, SH.DESCRIBE_HOME, SH.WHERE_SLEEP, SH.CONTRIBUTE_TO_HOUSEHOLD, SH.HOUSING_CONDITIONS, SH.ADEQUATE_HOUSING, SH.ANYONE_ELSE_LIVE, SH.CONTAGIOUS_DISEASE, SH.CONTAGIOUS_DISEASE_YES, SH.CRIMINAL_CONVICTIONS, SH.CRIMINAL_CONVICTIONS_YES, SH.CHILD_REMOVED, SH.CHILD_REMOVED_YES, SH.ATTEMPT_TO_SPONSOR, SH.RESIDE_WITH_HOUSEHOLD, SH.RESIDE_WITH_HOUSEHOLD_NO, SH.UNDERGO_HS, SH.RECEIVED_PRS, SH.ENROLLED_SCHOOL, SH.COURT_DATE, SH.ATTEND_LOPC, SH.WITHDRAWN_APPLICATION, SH.WITHDRAWN_APPLICATION_YES, SH.DENY_SPONSORSHIP, SH.DENY_SPONSORSHIP_YES, SH.HOUSEHOLD_IDENTITIES_VERIFIED, SH.ADDITIONAL_INFO_HOUSEHOLD, SCP.SCHOOL_TO_ATTEND, SCP.UNDERSTAND_ENROLLMENT_PROCESS, SCP.SPONSOR_AWARE_MEDICAL_CONDITIONS, SCP.PLANS_TO_ADDRESS_MEDICAL_NEEDS, SCP.PLANS_TO_ADDRESS_MENTAL_HEALTH_NEEDS, SCP.SPONSOR_AWARE_CRIMINAL_BEHAVIOR, SCP.CHILD_DISCIPLINE_PLAN, SCP.CONCERNS_DISCIPLINE_PRACTICE, SCP.READ_SPONSOR_HANDBOOK, SCP.PLAN_TO_KEEP_SAFE, SCP.SPONSOR_SPECIAL_NEEDS_MENTAL_HEALTH, SCP.SPONSOR_FAMILY_COMMUNITY_SUPPOT, SCP.DESCRIBE_CHILD_SUPERVISION, SCP.FAMILY_FRIENDS_HELP_CHILDCARE, SCP.CARE_PLAN_ADDITIONAL_INFORMATION, SCP.ALTERNATE_CAREGIVER_VERIFIED, SCP.WHO_TRANSPORT_CHILD_SCHOOL FROM SPONSOR_INFO A INNER JOIN UAC_SPONSOR_LIST B ON a.sponsor_id = b.sponsor_id and a.is_deleted = 0 and b.is_deleted = 0 LEFT OUTER JOIN SPONSOR_FRAUD_TRAFFICKING FT ON B.SPONSOR_ID = FT.SPONSOR_ID and B.uac_id = FT.UAC_ID LEFT OUTER JOIN SPONSOR_CRIMINAL_HISTORY CH ON B.SPONSOR_ID = CH.SPONSOR_ID and B.uac_id = CH.UAC_ID LEFT OUTER JOIN SPONSOR_FAMILY_RELATIONSHIPS FR ON B.SPONSOR_ID = FR.SPONSOR_ID and B.uac_id = FR.UAC_ID LEFT OUTER JOIN SPONSOR_HOUSEHOLD SH ON B.SPONSOR_ID = SH.SPONSOR_ID and B.uac_id = SH.UAC_ID LEFT OUTER JOIN SPONSOR_CARE_PLAN SCP ON B.SPONSOR_ID = SCP.SPONSOR_ID and B.uac_id = SCP.UAC_ID WHERE B.SPONSOR_ID = vSPONSOR_ID and b.uac_id = vUACID; ELSE OPEN cur_Out For SELECT 0 AS EXISTINGSPONSOR, UPTIME, First_Name, Last_Name, DOB, COB, ALIEN_NO, GENDER, LEGAL_STATUS_ID, LEGAL_STATUS_VERIFIED, IMMIGRATION_STATUS, PRIMARY_LANGUAGE, OTHER_LANGUAGES, RELIGIOUS_AFFILIATION, HOME_PHONE , WORK_PHONE , BACKUP_PHONE_TYPE , EMAIL , ADDRESS , ADDRESS_SECONDARY, COUNTY, LATITUDE, LONGITUDE, COR, CITY , STATE , ZIP_CODE , FLAG, FLAG_NOTE, FLAG_ADDRESS, FLAG_ADDRESS_NOTE from sponsor_info where sponsor_id = vSPONSOR_ID; END IF;
END SP_GET_SPONSOR_ASSESSMENT;

PROCEDURE "SP_GET_SPONSOR_INFO" (
v_sponsorId    NUMBER,
v_sn      NUMBER,
cur_Out  OUT SYS_REFCURSOR
)
AS 
tCount  NUMBER(10);
BEGIN
  Select count(*) into tCount from UAC_SPONSOR_LIST Where SPONSOR_ID = v_sponsorId and is_deleted = 0;
  If tCount is not null and tCount > 0 Then

    OPEN cur_Out For 
    Select 1 from dual;
  Else
    --MV_SPONSOR_INFO_LIST
    OPEN cur_Out For 
    Select 1 from dual;
  End If;
END SP_GET_SPONSOR_INFO;

PROCEDURE SP_GET_SPONSOR_LIST ( SPONSOR_ID IN NUMBER , UAC_ID IN NUMBER , ADDRESS IN VARCHAR2 , CITY IN VARCHAR2 , STATE IN VARCHAR2 , cur_Out OUT SYS_REFCURSOR ) AS
sponsorId_ NUMBER(10) := SPONSOR_ID; uacId_ NUMBER(10) := UAC_ID; address_ VARCHAR2(250) := Upper(ADDRESS); city_ VARCHAR2(120) := Upper(City); state_ CHAR(2) := Upper(State); n Number(4) := 0; BEGIN select count(*) into n from ( Select 1 from UAC_SPONSOR_LIST Where UAC_ID = uacId_ And SPONSOR_ID = sponsorId_ and is_deleted = 0 Union Select 0 from dual );
If n > 1 Then OPEN cur_Out For Select A.UAC_ID, A.CURRENT_SPONSOR, B."SPONSOR_ID",B."FIRST_NAME",B."LAST_NAME",B."SSN",B."ALIEN_NO", A."SPONSOR_ADDRESS" as ADDRESS, A."SPONSOR_ADDRESS_SECONDARY" as SECONDARY, A.SPONSOR_CITY "CITY", A.SPONSOR_STATE "STATE", A.SPONSOR_ZIPCODE "ZIP_CODE", B."DOB", Trunc(months_between(sysdate, B.DOB) /12) as Age, B."COB", B."COR", B."MARRIAGE_STATUS", A.SPONSOR_HOME_PHONE "HOME_PHONE", A.BACKUP_PHONE_TYPE, A.SPONSOR_WORK_PHONE "WORK_PHONE", A.SPONSOR_FAX "FAX", A.SPONSOR_EMAIL "EMAIL", B."GENDER", B."LEGAL_STATUS_ID", A."SPONSOR_RELATIONSHIP_TO_UAC", B."HOUSEHOLD_HAS_DISEASE", B."HOUSEHOLD_HAS_CRIMINAL_HIST", nvl(A.COMMENTS,B.NOTES) as "NOTES",B."SPONSORTHIP_STATUS", B."P_COUNTER",B."A_COUNTER",B."DATE_CREATED",B."USER_CREATED",B."UPTIME" as S_UPTIME,B."UPUSER", A.UPTIME as U_UPTIME, D.LEGAL_STATUS, E.RELATIONSHIP, B.FLAG, B.FLAG_NOTE, B.FLAG_ADDRESS, B.FLAG_ADDRESS_NOTE, E.SPONSOR_CATEGORY || E.SPONSOR_SUB_CATEGORY As SPONSOR_CATEGORY, A.QUERY_ID From UAC_SPONSOR_LIST A Inner Join SPONSOR_INFO B On A.SPONSOR_ID = B.SPONSOR_ID and a.is_deleted = 0 and b.is_deleted = 0 Left Join LIST_LEGAL_STATUS D On B.LEGAL_STATUS_ID = D.LEGAL_STATUS_ID Left Join List_Relationship E On A.SPONSOR_RELATIONSHIP_TO_UAC = E.RELATIONSHIP_ID Where A.SPONSOR_ID = sponsorId_ And A.UAC_ID = uacId_; Else OPEN cur_Out For Select rownum as SN, A.* from ( Select null as CURRENT_SPONSOR, A.SPONSOR_ID, A.SSN, A.FIRST_NAME, A.LAST_NAME, A.ALIEN_NO, B.SPONSOR_ADDRESS as ADDRESS,B.SPONSOR_ADDRESS_SECONDARY as ADDRESS_SECONDARY, B.SPONSOR_CITY as CITY,B.SPONSOR_STATE as STATE, B.SPONSOR_ZIPCODE as ZIP_CODE, P_COUNTER, A_COUNTER, A.DOB,A.COB,A.COR, A.MARRIAGE_STATUS, B.SPONSOR_HOME_PHONE as HOME_PHONE, B.SPONSOR_WORK_PHONE as WORK_PHONE, B.BACKUP_PHONE_TYPE, B.SPONSOR_FAX as FAX, B.SPONSOR_EMAIL as EMAIL, A.GENDER, A.LEGAL_STATUS_ID, B.SPONSOR_RELATIONSHIP_TO_UAC, B.UAC_RELATIONSHIP_TO_SPONSOR, A.HOUSEHOLD_HAS_DISEASE, A.HOUSEHOLD_HAS_CRIMINAL_HIST, nvl(B.COMMENTS, A.NOTES) as NOTES, A.SPONSORTHIP_STATUS, CASE WHEN nvl(A.FLAG,0) = 0 Then 'No' Else 'Yes' End as Flag, Flag_Note, C.SPONSOR_CATEGORY || C.SPONSOR_SUB_CATEGORY As SPONSOR_CATEGORY, A.FLAG_ADDRESS, A.FLAG_ADDRESS_NOTE, A.UPTIME as S_UPTIME, B.UPTIME as U_UPTIME, B.QUERY_ID From SPONSOR_INFO A INNER join UAC_SPONSOR_LIST B on A.SPONSOR_ID=B.SPONSOR_ID and a.is_deleted = 0 and b.is_deleted = 0 Left join List_Relationship C on B.SPONSOR_RELATIONSHIP_TO_UAC = C.RELATIONSHIP_ID UNION Select null as CURRENT_SPONSOR,SPONSOR_ID, SSN,FIRST_NAME, LAST_NAME,ALIEN_NO, ADDRESS, ADDRESS_SECONDARY, CITY, STATE, ZIP_CODE, P_COUNTER, A_COUNTER, DOB, COB, COR, MARRIAGE_STATUS, HOME_PHONE, WORK_PHONE, 0 as BACKUP_PHONE_TYPE, FAX, EMAIL, GENDER, LEGAL_STATUS_ID, SPONSOR_RELATIONSHIP_TO_UAC, UAC_RELATIONSHIP_TO_SPONSOR, HOUSEHOLD_HAS_DISEASE, HOUSEHOLD_HAS_CRIMINAL_HIST, NOTES, SPONSORTHIP_STATUS, CASE WHEN nvl(FLAG,0) = 0 Then 'No' Else 'Yes' End as Flag, Flag_Note, null as SPONSOR_CATEGORY, FLAG_ADDRESS, FLAG_ADDRESS_NOTE, UPTIME as S_UPTIME, null as U_UPTIME, null as QUERY_ID From SPONSOR_INFO Where is_deleted = 0 and SPONSOR_ID Not in (select Distinct Sponsor_ID from UAC_SPONSOR_LIST where is_deleted = 0) ) A Where A.Sponsor_ID = sponsorId_ And (Upper(A.Address) = address_ Or Upper(A.City) = city_ Or Upper(A.State) = state_); End If; END SP_GET_SPONSOR_LIST;

PROCEDURE SP_GET_SPONSOR_PROFILE_FLD_LOCK_COUNT ( vUACID IN NUMBER , vSponsorId IN NUMBER , cur_Out OUT SYS_REFCURSOR ) AS
BEGIN
OPEN cur_Out For select count(*) AS IsNew from UAC_SPONSOR_LIST Where SPONSOR_ID = vSponsorId and vUACID = UAC_ID and is_deleted = 0;
END SP_GET_SPONSOR_PROFILE_FLD_LOCK_COUNT;

PROCEDURE SP_GET_SPONSOR_PROFILE_LOCK_COUNT ( vUACID IN NUMBER , vSponsorId IN NUMBER , cur_Out OUT SYS_REFCURSOR ) AS
BEGIN
OPEN cur_Out For select count(*) AS LOCKCOUNT from UAC_SPONSOR_LIST Where SPONSOR_ID = vSponsorId and vUACID <> UAC_ID and is_Deleted = 0 ;
END SP_GET_SPONSOR_PROFILE_LOCK_COUNT;

PROCEDURE             "SP_GET_SSO_SETTINGS" 
(
   CUR_OUT  OUT SYS_REFCURSOR
) AS 


BEGIN
  OPEN CUR_OUT For
		Select Param_VALUE from uacportal_App_params 
        where  param_name = 'USE_SSO' and App_type = 'SSO';


END SP_GET_SSO_SETTINGS;

PROCEDURE             "SP_GET_STATUS_INFO" (
vUacProgId    NUMBER default 0,
cur_Out  OUT SYS_REFCURSOR
)
AS
daysDischarged  number(10,0) := 0;
hasAssessment   Number(1,0) := 0;
hasIniAssessment Number(1,0) := 0;
hasTrnsfRequest  NUMBER(1,0) := 0;
hasReleaseRequest NUMBER(1,0) := 0;
hasDischargeNotification NUMBER(1,0) :=0;
hasProgramExit  NUMBER(1,0) :=0;
hasHomeStudyCaseReferral  NUMBER(1,0) :=0;
hasPRS  NUMBER(1,0) :=0;
hasPRSTVPRA  NUMBER(1,0) :=0;
cannotAddNewRelease NUMBER(1,0) :=0;
cannotAddNewHS NUMBER(1,0) :=0;
cannotAddNewPRS NUMBER(1,0) :=0;
cannotProgramExit NUMBER(1,0) :=0;
uacStatus   VARCHAR2(50) := '';
hasCurrentChildLocation NUMBER(1,0) :=0;
uacOverallStatus   VARCHAR2(50) := '';
n         NUMBER(1) :=0;
BEGIN
  select count(*) into n From (
   Select 1 from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = vUacProgId
    Union
    Select 0 from dual
    ) A;
  if n > 1 then
    Select CURRENT_STATUS into uacStatus from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = vUacProgId;
  End If;

  select nvl(Max(1),0) into hasAssessment from UAC_ASSESSMENT Where UAC_PROGRAM_ID = vUacProgId And ISDELETED = 0;
  Select nvl(Max(1),0) into hasIniAssessment from UAC_INITIAL_ASSESSMENT Where UAC_PROGRAM_ID = vUacProgId  And nvl(ISDELETED,0) = 0;
  if n > 1 Then
    Select nvl(DateDiff('day', DATE_DISCHARGED,  Sysdate),0) into daysDischarged from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = vUacProgId;
  End if;
  Select nvl(Max(1),0) into hasTrnsfRequest from UAC_TRANSFER_REQUEST Where UAC_PROGRAM_ID = vUacProgId And nvl(ISDELETED,0) = 0;
  Select nvl(Max(1),0) into hasReleaseRequest from UAC_RELEASE_REQUEST Where UAC_PROGRAM_ID = vUacProgId And nvl(ISDELETED,0) = 0;
  Select nvl(Max(1),0) into hasDischargeNotification from UAC_DISCHARGE_NOTIFICATION Where UAC_PROGRAM_ID = vUacProgId And nvl(ISDELETED,0) = 0;
  Select nvl(Max(1),0) into hasProgramExit from UAC_PROGRAM_EXIT Where UAC_PROGRAM_ID = vUacProgId;

  Select nvl(Max(1),0) into cannotProgramExit from UAC_RELEASE_REQUEST R INNER JOIN (
                   --Get most recent active Release Request
                   SELECT UAC_PROGRAM_ID,MAX(RELEASE_ID) as MAX_RELEASE_ID
                   FROM UAC_RELEASE_REQUEST
                   WHERE (ISDELETED = 0 OR ISDELETED is Null) AND UAC_PROGRAM_ID = vUacProgId
                   GROUP BY UAC_PROGRAM_ID
          ) RMax ON R.RELEASE_ID = RMax.MAX_RELEASE_ID
  Where R.ORR_DECISION IN (5,6,10,11,12,13,14,15,16, -1) OR 
          ( R.ORR_DECISION IN (7,8,9) AND R.ORR_DECISION_AFTER_HS IN (-1,3,4)
          );

  Select nvl(Max(1),0) into hasHomeStudyCaseReferral from UAC_RELEASE_REQUEST Where UAC_PROGRAM_ID = vUacProgId AND ORR_DECISION IN (7,8,9);

  -- if there is a HS has been moved with the kid to the current program, display the HS
  if hasHomeStudyCaseReferral = 0 then
	SELECT nvl(Max(1),0) into hasHomeStudyCaseReferral from UAC_HOMESTUDY_CASE_REFERRAL Where UAC_PROGRAM_ID = vUacProgId;
  End if;

  Select nvl(Max(1),0) into hasPRS from UAC_RELEASE_REQUEST Where UAC_PROGRAM_ID = vUacProgId AND (ORR_DECISION = 2
                                      OR (ORR_DECISION IN (8,9) AND ORR_DECISION_AFTER_HS = 1 AND EXISTS 
                                                (SELECT 1 FROM UAC_HOMESTUDY_CASE_REFERRAL H WHERE H.REFERRAL_STATUS = 3 AND H.UAC_PROGRAM_ID = vUacProgId ) ))
												AND (ISDELETED = 0 OR ISDELETED Is Null);

  if hasPRS = 0 then
	Select nvl(Max(1),0) into hasPRS from UAC_POST_RELEASE_SERVICE Where UAC_PROGRAM_ID = vUacProgId AND PRS_TYPE = 1;
  End if;

  Select nvl(Max(1),0) into hasPRSTVPRA from UAC_RELEASE_REQUEST A INNER JOIN 
              UAC_PROGRAM_INFO C ON A.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID INNER JOIN 
                               -- Check there is a closed HS
              (SELECT F.UAC_ID
                FROM UAC_HOMESTUDY_CASE_REFERRAL H INNER JOIN 
                  UAC_PROGRAM_INFO F ON F.UAC_PROGRAM_ID = H.UAC_PROGRAM_ID
                WHERE H.REFERRAL_STATUS = 3 
              ) B ON B.UAC_ID = C.UAC_ID

              WHERE A.UAC_PROGRAM_ID = vUacProgId AND A.ORR_DECISION_AFTER_HS = 5 AND (a.ISDELETED = 0 OR a.ISDELETED Is Null);
  if hasPRSTVPRA = 0 then
	Select nvl(Max(1),0) into hasPRSTVPRA from UAC_POST_RELEASE_SERVICE Where UAC_PROGRAM_ID = vUacProgId AND PRS_TYPE = 0;
  End if;

  Select nvl(Max(1),0) into cannotAddNewRelease from UAC_RELEASE_REQUEST a Where a.UAC_PROGRAM_ID = vUacProgId 
																			AND NOT a.ORR_DECISION IN (6,10,11,12,13,14,15,16)
																			AND NOT (a.ORR_DECISION IN (7,8,9) AND a.ORR_DECISION_AFTER_HS IN (4,6,7,8,9,10,11,12))
																			AND (a.ISDELETED = 0 OR a.ISDELETED Is Null) AND
                                                                             Not a.RELEASE_ID IN (SELECT b.RELEASE_ID FROM UAC_RELEASE_REQUEST b WHERE b.UAC_PROGRAM_ID = a.UAC_PROGRAM_ID AND b.ISDELETED = 1);

  Select nvl(Max(1),0) into cannotAddNewHS from UAC_HOMESTUDY_CASE_REFERRAL Where (UAC_PROGRAM_ID = vUacProgId AND (REFERRAL_STATUS = 1 OR REFERRAL_STATUS = 2 OR REFERRAL_STATUS = 5))
																				OR EXISTS (SELECT 1 from UAC_POST_RELEASE_SERVICE Where UAC_PROGRAM_ID = vUacProgId 
																							AND (REFERRAL_STATUS = 1 OR REFERRAL_STATUS = 2 OR REFERRAL_STATUS = 5))
																				OR NOT EXISTS (SELECT 1 FROM UAC_RELEASE_REQUEST Where UAC_PROGRAM_ID = vUacProgId
																																	AND ORR_DECISION IN (7,8,9))
																				OR NOT EXISTS (SELECT 1 FROM UAC_PROGRAM_INFO UPI INNER JOIN UAC_SPONSOR_LIST USL ON UPI.UAC_ID = USL.UAC_ID AND USL.is_dELETED= 0
																								WHERE UPI.UAC_PROGRAM_ID = vUacProgId AND USL.CURRENT_SPONSOR = 1)
																				;


  Select nvl(Max(1),0) into cannotAddNewPRS from UAC_POST_RELEASE_SERVICE Where (UAC_PROGRAM_ID = vUacProgId AND (REFERRAL_STATUS = 1 OR REFERRAL_STATUS = 2 OR REFERRAL_STATUS = 5))
																				OR NOT EXISTS (SELECT 1 FROM UAC_RELEASE_REQUEST Where UAC_PROGRAM_ID = vUacProgId
																																	AND ((ORR_DECISION IN (7) AND ORR_DECISION_AFTER_HS IN (5))
																																		OR (ORR_DECISION IN (8,9) AND ORR_DECISION_AFTER_HS IN (1))
																																		OR (ORR_DECISION IN (2))
																																	)
																								)
																				OR NOT EXISTS (SELECT 1 FROM UAC_PROGRAM_INFO UPI INNER JOIN UAC_SPONSOR_LIST USL ON UPI.UAC_ID = USL.UAC_ID AND USL.IS_DELETED=0
																								WHERE UPI.UAC_PROGRAM_ID = vUacProgId AND USL.CURRENT_SPONSOR = 1)
																				;
	Select nvl(Max(1), 0) into hasCurrentChildLocation from UAC_CHILD_LOCATION_HIST CLH Where CLH.IS_CURRENT = 1 
																								AND EXISTS (SELECT 1 FROM UAC_PROGRAM_INFO UPI
																											 WHERE UPI.UAC_ID = CLH.UAC_ID AND UPI.UAC_PROGRAM_ID = vUacProgId);
	Select UAC_STATUS into uacOverallStatus FROM UAC_INFO A LEFT JOIN UAC_PROGRAM_INFO B ON A.UAC_ID = B.UAC_ID WHERE B.UAC_PROGRAM_ID = vUacProgId;

  Open cur_Out For
      Select daysDischarged as "daysDischarged", hasAssessment as HasAssessment, hasIniAssessment as HasInitialAssessment,
      hasTrnsfRequest as hasTransferRequest, hasReleaseRequest as "hasReleaseRequest", hasDischargeNotification as "hasDischargeNotification",
      hasProgramExit as "hasProgramExit", 
      hasHomeStudyCaseReferral as "hasHomeStudyCaseReferral",
      hasPRS as "hasPRS",
      hasPRSTVPRA as "hasPRSTVPRA",
      cannotAddNewRelease as "cannotAddNewRelease",
      cannotAddNewHS as "cannotAddNewHS",
      cannotAddNewPRS as "cannotAddNewPRS",
	  hasCurrentChildLocation as "hasCurrentChildLocation",
      uacStatus as UAC_STATUS,
            cannotProgramExit as "cannotProgramExit",
	  uacOverallStatus as "uacOverallStatus"
      from dual;
END SP_GET_STATUS_INFO;

PROCEDURE "SP_GET_SUMMARY_RPT" (
RecordType  IN  NUMBER,
cur_Out OUT   SYS_REFCURSOR
--,
--cur_Out2 OUT   SYS_REFCURSOR,
--cur_Out3 OUT   SYS_REFCURSOR,
--cur_Out4 OUT   SYS_REFCURSOR
)
AS 

i_R1   NUMBER(6);
i_R2   NUMBER(6);
i_R3       NUMBER(6);
i_R4   NUMBER(6);
i_R5 NUMBER(6);
i_R6  NUMBER(6);

pregnantTotalCurr NUMBER(3);
pregrantORRShifted  NUMBER(3);
pregrantTotalPending  NUMBER(3);
UACTotalPending   NUMBER(4);
UACPendingLessThan24H   NUMBER(4);
UACPendingBetween24And48   NUMBER(4);
UACPendingBetween48And72   NUMBER(4);
UACPendingMoreThan72   NUMBER(4);
otherMCTotalCurr NUMBER(3);
otherMCORRShifted  NUMBER(3);
otherMCTotalPending  NUMBER(3);
UACTotalDHSCustody  NUMBER(6);

BEGIN
    --Referral and Placement ORR Shift Report
    select count(*) into i_R1 from uac_info where trunc(DATE_CREATED) = trunc(Sysdate);
    select count(*) into i_R2 from uac_info where trunc(date_ORR_APPROVED) = trunc(sysdate);
    select count(*) into i_R3 from uac_info where Trunc(months_between(sysdate, DOB) /12) <=12 and trunc(date_ORR_APPROVED) = trunc(sysdate);
    select count(*) into i_R4 from uac_info where UAC_STATUS = 'CANCELLED' and trunc(uptime) = trunc(sysdate);
    select count(*) into i_R6 from uac_info where Trunc(months_between(sysdate, DOB) /12) <=12 and trunc(date_ORR_APPROVED) = trunc(sysdate);
    select count(*) into UACTotalPending from uac_info where UAC_STATUS='PENDING';
    Select count(*) into UACTotalDHSCustody from uac_info where UAC_STATUS in ('PENDING', 'ENROUTE');
    select count(*) into pregnantTotalCurr from uac_info where PREGNANCY='1' and trunc(DATE_CREATED) = trunc(sysdate);
    select count(*) into pregrantORRShifted from uac_info where PREGNANCY='1' and Trunc(date_ORR_APPROVED) = trunc(sysdate);
    select count(*) into pregrantTotalPending from uac_info where PREGNANCY='1' and UAC_STATUS='PENDING';
    select count(*) into otherMCTotalCurr from uac_info where HC_OTHER='1' and trunc(DATE_CREATED) = trunc(sysdate);
    select count(*) into otherMCORRShifted from uac_info where HC_OTHER='1' and Trunc(date_ORR_APPROVED) = trunc(sysdate);
    select count(*) into otherMCTotalPending from uac_info where HC_OTHER='1' and UAC_STATUS='PENDING';


    Select Sum(Case When DateDiff('hour', DATE_CREATED, sysdate) <= 24 Then 1 Else 0 End),
    Sum(Case When DateDiff('hour', DATE_CREATED, sysdate) Between 25 and 48 Then 1 Else 0 End),
    Sum(Case When DateDiff('hour', DATE_CREATED, sysdate) Between 49 and 72 Then 1 Else 0 End),
    Sum(Case When DateDiff('hour', DATE_CREATED, sysdate) > 72 Then 1 Else 0 End)
    Into UACPendingLessThan24H, UACPendingBetween24And48, UACPendingBetween48And72, UACPendingMoreThan72
    From UAC_INFO Where UAC_STATUS = 'PENDING';

    If RecordType = 1 Then  
      Open cur_Out For
      Select i_R1 as "i_R1", i_R2 as "i_R2", i_R3 as "i_R3", i_R4 as "i_R4", i_R6 as "i_R6", 
      UACTotalPending as "UACTotalPending", pregnantTotalCurr as "pregnantTotalCurr", pregrantORRShifted as "pregrantORRShifted", pregrantTotalPending as "pregrantTotalPending"
      , UACPendingLessThan24H as "UACPendingLessThan24H", UACPendingBetween24And48 as "UACPendingBetween24And48", 
      UACPendingBetween48And72 as "UACPendingBetween48And72", UACPendingMoreThan72 as "UACPendingMoreThan72"
      , otherMCTotalCurr as "otherMCTotalCurr", otherMCORRShifted as "otherMCORRShifted", otherMCTotalPending as "otherMCTotalPending", UACTotalDHSCustody as "UACTotalDHSCustody"
      from  dual;
    elsif RecordType = 2 Then
      --Pending Placement Report
      Open cur_Out For
      select COB,count(*) NumberOfUACs, sum(Case when UAC_STATUS = 'CANCELLED' Then 1 Else 0 End) as NumberOfUACsCancelled from uac_info where trunc(DATE_CREATED) = trunc(sysdate) group by COB;
    elsif RecordType = 3 Then
      --Report #8:
      Open cur_Out For
      select Trunc(DATE_CREATED) Date_ORR_SHIFT,count(*) UACPending, Sum(Case When Trunc(months_between(sysdate, DOB) /12) <=12 Then 1 Else 0 End) PendingUACUnder12 from uac_info where UAC_STATUS='PENDING' and DateDiff('day',DATE_CREATED, sysdate) < 10 Group by Trunc(DATE_CREATED);
    elsif RecordType = 4 then
      --Report #16:
      Open cur_Out For
      select STATE_OF_ENTRY,count(*) UACTotalByState from uac_info where UAC_STATUS='PENDING' group by STATE_OF_ENTRY;
    end if;
END SP_GET_SUMMARY_RPT;

PROCEDURE SP_GET_UACSPONSORS_BY_ARRAY 
(
  UAC_PROGRAM_ID  IN NUMBER
, FIRSTNAME IN VARCHAR2
, LASTNAME IN VARCHAR2
, ALIEN_NO IN VARCHAR2 
, ADDRESS IN VARCHAR2 
, CITY IN VARCHAR2 
, STATEOF IN VARCHAR2 
, EMAIL IN VARCHAR2 
, HOMEPHONE IN VARCHAR2 
, WORKPHONE IN VARCHAR2 
, COB IN VARCHAR2
, DOB IN VARCHAR2
, v_Param1 IN VARCHAR2
,cur_Out  OUT SYS_REFCURSOR
)
AS

uacProgId_ NUMBER(10) := UAC_PROGRAM_ID;
dob_ DATE := NULL;

fName_  VARCHAR2(300) := Trim(Upper(nvl(regexp_replace (FIRSTNAME, '\s+', ' '), 'xxx')));
lName_  VARCHAR2(300) := Trim(Upper(nvl(regexp_replace (LASTNAME, '\s+', ' '),'xxx')));

searchnames_ VARCHAR2(500) := Trim(Upper(nvl(regexp_replace (FIRSTNAME, '\s+', ' '), ''))) || ' ' || Trim(Upper(nvl(regexp_replace (LASTNAME, '\s+', ' '), '')));
v_Param_Original VARCHAR2(500) := Trim(Upper(nvl(regexp_replace (FIRSTNAME, '\s+', ' '), ''))) || ' ' || Trim(Upper(nvl(regexp_replace (LASTNAME, '\s+', ' '), '')));

aNumber_  VARCHAR2(10) := Trim(Upper(nvl(ALIEN_NO, 'xxx')));
address_  VARCHAR2(500) := Trim(Upper(nvl(regexp_replace (ADDRESS, '\s+', ' '),'xxx')));
city_     VARCHAR2(500) := Trim(Upper(nvl(CITY, 'xxx')));
state_    CHAR(2) := Trim(Upper(nvl(STATEOF,'xx')));
email_ VARCHAR2(500) := Trim(Upper(nvl(EMAIL, 'xxx')));
homephone_ VARCHAR2(22) := Trim(Upper(nvl(HOMEPHONE,'xxx')));
workphone_ VARCHAR2(22) := Trim(Upper(nvl(WORKPHONE,'xxx')));
cob_      VARCHAR2(500) := Trim(Upper(nvl(COB,'xxx')));

v_wordCount NUMBER(10,0) := 0;
vSearchResultLimit NUMBER(10,0);
v_count number(10,0);
v_permutations NUMBER(10,0) := 0;

step_ VARCHAR2(200) := NULL;
errors_ VARCHAR2(200) := NULL;

v_replacedFirst_ VARCHAR2(200) := NULL;
v_replacedSecond_ VARCHAR2(200) := NULL;
v_firstAndSecond VARCHAR2(200) := NULL;

v_permutations1_ VARCHAR2(200) := NULL;
v_permutations2_ VARCHAR2(200) := NULL;
v_permutations3_ VARCHAR2(200) := NULL;
v_permutations4_ VARCHAR2(200) := NULL;
v_FirstName1_ VARCHAR2(200) := NULL;
v_LastName1_ VARCHAR2(200) := NULL;
v_FirstName2_ VARCHAR2(200) := NULL;
v_LastName2_ VARCHAR2(200) := NULL;

v_SpecialCharsFirstName_ VARCHAR(2000) := NULL;
v_SpecialCharsFirstName1_ VARCHAR(2000) := NULL;
v_SpecialCharsLastName_ VARCHAR(2000) := NULL;
v_SpecialCharsLastName1_ VARCHAR(2000) := NULL;
v_SpecialCharsAddress_ VARCHAR(2000) := NULL;
v_SpecialCharsAddress1_ VARCHAR(2000) := NULL;
v_SpecialCharsCity_ VARCHAR(2000) := NULL;
v_SpecialCharsCity1_ VARCHAR(2000) := NULL;

v_isSpecialCharsFirstName_ NUMBER(10,0) :=0;
v_isSpecialCharsLastName_ NUMBER(10,0) :=0;
v_isSpecialCharsAddress_ NUMBER(10,0) :=0;
v_isSpecialCharsCity_ NUMBER(10,0) :=0;

BEGIN

     IF (EMAIL IS NOT NULL) THEN
        email_ := REGEXP_REPLACE(TRIM(UPPER(EMAIL)), '[^0-9A-Za-z @.!#$%&*+-/=?^_`{|}~(),:;<>]', '');
     END IF;
     IF (v_Param_Original = ' ') THEN
        v_Param_Original := NULL;

     END IF;

-- for SoundEx. also SoundEx works better with Lower, not Upper
  IF (FIRSTNAME IS NOT NULL) THEN
   v_isSpecialCharsFirstName_ := IS_SPECIAL_CHARACTER(fName_);

   IF (v_isSpecialCharsFirstName_ = 1) THEN
        v_SpecialCharsFirstName_ := trim(upper(HTML_CHARACTER_TO_SPECIAL_CHARACTERS(fName_)));
        v_SpecialCharsFirstName1_ := trim(upper(HTML_SPECIAL_CHARACTERS_TO_CHARACTER(UPPER(fName_))));
        --DBMS_OUTPUT.put_line('firstName - ' || v_SpecialCharsFirstName_);  
        --DBMS_OUTPUT.put_line('firstName - ' || v_SpecialCharsFirstName1_);          
   END IF;

   select REPLACE(LOWER(trim(fName_)), 'in', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'and', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'of', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'the', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'to', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'str', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'drv', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'st', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'dr', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'place', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), 'blvd', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), '.', '') into v_replacedFirst_ from dual;
   select REPLACE(LOWER(v_replacedFirst_), '#', '') into v_replacedFirst_ from dual;
  END IF;
  IF (LASTNAME IS NOT NULL) THEN
   v_isSpecialCharsLastName_ := IS_SPECIAL_CHARACTER(lName_);

   IF (v_isSpecialCharsLastName_ = 1) THEN
        v_SpecialCharsLastName_ := trim(upper(HTML_CHARACTER_TO_SPECIAL_CHARACTERS(lName_)));   
        v_SpecialCharsLastName1_ := trim(upper(HTML_SPECIAL_CHARACTERS_TO_CHARACTER(UPPER(lName_))));
        --DBMS_OUTPUT.put_line('lastName - ' || v_SpecialCharsLastName_);  
        --DBMS_OUTPUT.put_line('lastName - ' || v_SpecialCharsLastName1_);  
   END IF;

   select REPLACE(LOWER(trim(lName_)), 'in', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'and', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'of', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'the', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'to', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'str', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'drv', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'st', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'dr', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'place', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), 'blvd', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), '.', '') into v_replacedSecond_ from dual;
   select REPLACE(LOWER(v_replacedSecond_), '#', '') into v_replacedSecond_ from dual;
  END IF;

  select v_replacedFirst_ || ' ' || v_replacedSecond_ into v_firstAndSecond from dual;

    IF (ADDRESS IS NOT NULL) THEN
       v_isSpecialCharsAddress_ := IS_SPECIAL_CHARACTER(address_);

       IF (v_isSpecialCharsAddress_ = 1) THEN
            v_SpecialCharsAddress_ := trim(upper(HTML_CHARACTER_TO_SPECIAL_CHARACTERS(address_)));   
            v_SpecialCharsAddress1_ := trim(upper(HTML_SPECIAL_CHARACTERS_TO_CHARACTER(UPPER(address_))));
       END IF;        
       address_ := REGEXP_REPLACE(address_, '/', ' ');
       address_ := CONCAT(CONCAT('%',replace(Trim(UPPER(address_)), ' ', '%')), '%');
       -- DBMS_OUTPUT.put_line(address_); 
    END IF;

    IF (CITY IS NOT NULL) THEN
       v_isSpecialCharsCity_ := IS_SPECIAL_CHARACTER(city_);

       IF (v_isSpecialCharsCity_ = 1) THEN
            v_SpecialCharsCity_ := trim(upper(HTML_CHARACTER_TO_SPECIAL_CHARACTERS(city_)));   
            v_SpecialCharsCity1_ := trim(upper(HTML_SPECIAL_CHARACTERS_TO_CHARACTER(UPPER(city_))));
       END IF;
    END IF;

    IF (DOB IS NOT NULL) THEN
        BEGIN
            Select to_date(DOB,'mm/dd/yyyy') INTO dob_ from dual;
        EXCEPTION WHEN OTHERS THEN dob_ := NULL;
        END;
    END IF;

    select NVL ((SELECT to_number(nvl(PARAM_VALUE, '150')) --into vSearchResultLimit
                 from UACPORTAL_APP_PARAMS where PARAM_NAME = 'SearchResultsLimit1'
                ),
                to_number('150')
               ) AS PARAM_VALUE into vSearchResultLimit
    from   dual;

    select length(v_Param_Original) - length(replace(v_Param_Original,' ',null)) 
    INTO v_wordCount from dual;
    --DBMS_OUTPUT.put_line(v_Param_Original);  
    --DBMS_OUTPUT.put_line(v_wordCount);  
    v_wordCount  := 0;

    IF (v_Param_Original IS NOT NULL
        AND v_wordCount > 1) THEN
        SELECT REGEXP_SUBSTR(TRIM(v_Param_Original), '[^ ]+') INTO v_permutations1_ FROM dual;
        SELECT REGEXP_SUBSTR(TRIM(v_Param_Original), '[^ ]+', 1, 2) INTO v_permutations2_ FROM  dual;
        SELECT REGEXP_SUBSTR(TRIM(v_Param_Original), '[^ ]+', 1, 3) INTO v_permutations3_ FROM  dual;
        SELECT REGEXP_SUBSTR(TRIM(v_Param_Original), '[^ ]+', 1, 4) INTO v_permutations4_ FROM  dual;

        --DBMS_OUTPUT.put_line(v_permutations1_);
        --DBMS_OUTPUT.put_line(v_permutations2_);
        --DBMS_OUTPUT.put_line(v_permutations3_);
        --DBMS_OUTPUT.put_line(v_permutations4_);
        v_FirstName1_ := '';
        v_FirstName2_ := '';

        IF (v_permutations1_ IS NOT NULL) THEN
            v_FirstName1_ := v_permutations1_;
        END IF;
        IF (v_permutations2_ IS NOT NULL) THEN
            v_FirstName2_ := v_permutations2_;
        END IF;
        IF (v_permutations3_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations3_;
        END IF;
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations4_;
        END IF;

        --DBMS_OUTPUT.put_line(v_FirstName1_);
        --DBMS_OUTPUT.put_line(v_FirstName2_);

        IF (v_FirstName1_ IS NOT NULL) THEN
            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(v_FirstName1_)),TRIM( UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' )))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;

            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' ))), TRIM(UPPER(v_FirstName1_))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;  
        END IF;

        v_FirstName1_ := '';
        v_FirstName2_ := '';
        IF (v_permutations2_ IS NOT NULL) THEN
            v_FirstName1_ := v_permutations2_;
        END IF;
        IF (v_permutations1_ IS NOT NULL) THEN
            v_FirstName2_ := v_permutations1_;
        END IF;
        IF (v_permutations3_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations3_;
        END IF;
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations4_;
        END IF;

        --DBMS_OUTPUT.put_line(v_FirstName1_);
        --DBMS_OUTPUT.put_line(v_FirstName2_);

        IF (v_FirstName1_ IS NOT NULL) THEN
            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(v_FirstName1_)),TRIM( UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' )))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;

            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' ))), TRIM(UPPER(v_FirstName1_))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;  
        END IF;

        v_FirstName1_ := '';
        v_FirstName2_ := '';
        IF (v_permutations3_ IS NOT NULL) THEN
            v_FirstName1_ := v_permutations3_;
        END IF;
        IF (v_permutations1_ IS NOT NULL) THEN
            v_FirstName2_ := v_permutations1_;
        END IF;
        IF (v_permutations2_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations2_;
        END IF;
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations4_;
        END IF;

        --DBMS_OUTPUT.put_line(v_FirstName1_);
        --DBMS_OUTPUT.put_line(v_FirstName2_);

        IF (v_FirstName1_ IS NOT NULL) THEN
            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(v_FirstName1_)),TRIM( UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' )))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;

            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' ))), TRIM(UPPER(v_FirstName1_))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;  
        END IF;

        v_FirstName1_ := '';
        v_FirstName2_ := '';
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName1_ := v_permutations4_;
        END IF;
        IF (v_permutations1_ IS NOT NULL) THEN
            v_FirstName2_ := v_permutations1_;
        END IF;
        IF (v_permutations2_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations2_;
        END IF;
        IF (v_permutations3_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations3_;
        END IF;
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations4_;
        END IF;

        --DBMS_OUTPUT.put_line(v_FirstName1_);
        --DBMS_OUTPUT.put_line(v_FirstName2_);

        IF (v_FirstName1_ IS NOT NULL) THEN
            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(v_FirstName1_)),TRIM( UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' )))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;

            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' ))), TRIM(UPPER(v_FirstName1_))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;  
        END IF;

        -- Remove items that are like FNAME := 'JOSE' AND LNAME := 'SMITH'
        -- in both the first and last names
        IF (v_wordCount = 2) THEN
            DELETE FROM SPONSOR_INFO_PERMUTATIONS 
            WHERE  PERMUTATIONS_KEY = v_Param1
                AND (length(FNAME) - length(replace(FNAME,' ',null)) = 0)
                AND (length(LNAME) - length(replace(LNAME,' ',null)) = 0);
        ELSIF (v_wordCount = 3) THEN
            DELETE FROM SPONSOR_INFO_PERMUTATIONS 
            WHERE  PERMUTATIONS_KEY = '123456789oiuytgb'
                AND (length((FNAME || ' ' || LNAME)) - length(replace((FNAME || ' ' || LNAME),' ',null)) < 3);
        END IF;

    END IF;

   Select count(*) into v_permutations from SPONSOR_INFO_PERMUTATIONS where PERMUTATIONS_KEY = v_Param1;

   --select count(*) as countItem into v_count from SPONSOR_INFO_PERMUTATIONS where PERMUTATIONS_KEY = v_Param1;
   --DBMS_OUTPUT.put_line(fName_);   

   --v_permutations := 1;

BEGIN
    IF (DOB IS NULL) THEN
     step_ := 'ROWLEVEL-1, NULL DOB - LAST and FIRST';

     --DBMS_OUTPUT.put_line(fName_);
     step_ := 'ROWLEVEL-1, NULL DOB - LAST and FIRST';
     INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
          A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0), 
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
          A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

         1 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
        From V_SPONSOR_INFO A
		  Where
            --((CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.first_name)) || ' ' || TRIM(UPPER(A.last_name)) = searchNames_) THEN 1 ELSE 0 END) = 1
            --  OR (CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.last_name)) || ' ' || TRIM(UPPER(A.first_name)) = searchNames_) THEN 1 ELSE 0 END) = 1
            --)
            (
            CASE WHEN fName_ = 'XXX' THEN 1 WHEN (trim(UPPER(REPLACE(A.LAST_NAME, '''', ''))) = REPLACE(trim(fName_), '''', '')) THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN lName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.FIRST_NAME,''), '''', '')) = REPLACE(lName_, '''', '')) THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN city_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.CITY,'')) = city_) THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN address_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.ADDRESS,''), '''', '')) = REPLACE(address_, '''', '')) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN homephone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.HOME_PHONE,'')) = homephone_) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN workphone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.WORK_PHONE,'')) = workphone_) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN email_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.EMAIL,'')) = REPLACE(email_, '''', '')) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN aNumber_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.ALIEN_NO, '')) = aNumber_) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN state_ = 'XX' THEN 1 WHEN (UPPER(nvl(A."STATE",'')) = state_) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN cob_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.COB,''))= cob_) THEN 1 ELSE 0 END) = 1

		    And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
            And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID
            )

           Order by A.First_Name, A.Last_Name 
           FETCH FIRST vSearchResultLimit ROWS ONLY 
      );

     step_ := 'ROWLEVEL-1, NULL DOB - FIRST and LAST';
     INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
          A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
          A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

         1 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
        From V_SPONSOR_INFO A
		  Where
            --((CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.first_name)) || ' ' || TRIM(UPPER(A.last_name)) = searchNames_) THEN 1 ELSE 0 END) = 1
            --  OR (CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.last_name)) || ' ' || TRIM(UPPER(A.first_name)) = searchNames_) THEN 1 ELSE 0 END) = 1
            --)
            (CASE WHEN fName_ = 'XXX' THEN 1 WHEN (trim(UPPER(REPLACE(A.FIRST_NAME, '''', ''))) = trim(REPLACE(fName_, '''', ''))) THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN lName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.LAST_NAME,''), '''', '')) = REPLACE(lName_, '''', '')) THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN city_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.CITY,'')) = city_) THEN 1 ELSE 0 END) = 1
