            And (CASE WHEN workphone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.WORK_PHONE,'')) = workphone_) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN email_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.EMAIL,'')) = REPLACE(email_, '''', '')) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN aNumber_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.ALIEN_NO, '')) = aNumber_) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN state_ = 'XX' THEN 1 WHEN (UPPER(nvl(A."STATE",'')) = state_) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN cob_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.COB,''))= cob_) THEN 1 ELSE 0 END) = 1

		    And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
            And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
           Order by A.First_Name, A.Last_Name 
           FETCH FIRST vSearchResultLimit ROWS ONLY 
      );

    if (v_isSpecialCharsFirstName_ = 1
        OR v_isSpecialCharsLastName_ = 1
        OR v_isSpecialCharsCity_ = 1
        OR v_isSpecialCharsAddress_ = 1) THEN

        step_ := 'ROWLEVEL-1, NULL DOB - FIRST and LAST - SPECIAL CHARACTERS';
        INSERT INTO SPONSOR_INFO_SEARCH_TEMP
         (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
              A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
              A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
              A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
              A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
              A.State, NVL(A.P_COUNTER, 0), 
             REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
             REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
              A.EMAIL,
             A.COB,
             NVL(A.A_COUNTER, 0), 

             CASE 
                WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                     OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
                THEN 'Yes'
                ELSE 'No'
            END AS Flag,

            --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
            A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

             1 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
            From V_SPONSOR_INFO A
              Where
                --((CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.first_name)) || ' ' || TRIM(UPPER(A.last_name)) = searchNames_) THEN 1 ELSE 0 END) = 1
                --  OR (CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.last_name)) || ' ' || TRIM(UPPER(A.first_name)) = searchNames_) THEN 1 ELSE 0 END) = 1
                --)
                (CASE WHEN v_isSpecialCharsFirstName_ = 0 THEN 1 WHEN (trim(UPPER(A.FIRST_NAME)) = trim(v_SpecialCharsFirstName_)) THEN 1 ELSE 0 END) = 1
                AND (CASE WHEN v_isSpecialCharsLastName_ = 0 THEN 1 WHEN (UPPER(nvl(A.LAST_NAME,'')) = v_SpecialCharsLastName_) THEN 1 ELSE 0 END) = 1
                AND (CASE WHEN v_isSpecialCharsCity_ = 0 THEN 1 WHEN (UPPER(nvl(A.CITY,'')) = v_SpecialCharsCity_) THEN 1 ELSE 0 END) = 1
                AND (CASE WHEN v_isSpecialCharsAddress_ = 0 THEN 1 WHEN (UPPER(nvl(A.ADDRESS,'')) like '%' || v_SpecialCharsAddress_ || '%') THEN 1 ELSE 0 END) = 1
                And (CASE WHEN homephone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.HOME_PHONE,'')) = homephone_) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN workphone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.WORK_PHONE,'')) = workphone_) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN email_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.EMAIL,'')) = REPLACE(email_, '''', '')) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN aNumber_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.ALIEN_NO, '')) = aNumber_) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN state_ = 'XX' THEN 1 WHEN (UPPER(nvl(A."STATE",'')) = state_) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN cob_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.COB,''))= cob_) THEN 1 ELSE 0 END) = 1

                And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
                And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
               Order by A.First_Name, A.Last_Name 
               FETCH FIRST vSearchResultLimit ROWS ONLY 
          );

        step_ := 'ROWLEVEL-1, DOB IS NOT NULL - FIRST and LAST - SPECIAL CHARACTERS';
        INSERT INTO SPONSOR_INFO_SEARCH_TEMP
         (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
              A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
              A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
              A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
              A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
              A.State, NVL(A.P_COUNTER, 0),  
             REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
             REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
              A.EMAIL,
             A.COB,
             NVL(A.A_COUNTER, 0), 

            CASE 
                WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                     OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
                THEN 'Yes'
                ELSE 'No'
            END AS Flag,

            --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
            A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

             1 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
            From V_SPONSOR_INFO A
              Where
                --((CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.first_name)) || ' ' || TRIM(UPPER(A.last_name)) = searchNames_) THEN 1 ELSE 0 END) = 1
                --  OR (CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.last_name)) || ' ' || TRIM(UPPER(A.first_name)) = searchNames_) THEN 1 ELSE 0 END) = 1
                --)
                (CASE WHEN v_isSpecialCharsFirstName_ = 0 THEN 1 WHEN (trim(UPPER(A.FIRST_NAME)) = trim(v_SpecialCharsFirstName1_)) THEN 1 ELSE 0 END) = 1
                AND (CASE WHEN v_isSpecialCharsLastName_ = 0 THEN 1 WHEN (UPPER(nvl(A.LAST_NAME,'')) = v_SpecialCharsLastName1_) THEN 1 ELSE 0 END) = 1
                AND (CASE WHEN v_isSpecialCharsCity_ = 0 THEN 1 WHEN (UPPER(nvl(A.CITY,'')) = v_SpecialCharsCity1_) THEN 1 ELSE 0 END) = 1
                AND (CASE WHEN v_isSpecialCharsAddress_ = 0 THEN 1 WHEN (UPPER(nvl(A.ADDRESS,'')) like '%' || v_SpecialCharsAddress1_ || '%') THEN 1 ELSE 0 END) = 1
                And (CASE WHEN homephone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.HOME_PHONE,'')) = homephone_) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN workphone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.WORK_PHONE,'')) = workphone_) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN email_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.EMAIL,'')) = REPLACE(email_, '''', '')) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN aNumber_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.ALIEN_NO, '')) = aNumber_) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN state_ = 'XX' THEN 1 WHEN (UPPER(nvl(A."STATE",'')) = state_) THEN 1 ELSE 0 END) = 1
                And (CASE WHEN cob_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.COB,''))= cob_) THEN 1 ELSE 0 END) = 1

                And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
                And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
               Order by A.First_Name, A.Last_Name 
               FETCH FIRST vSearchResultLimit ROWS ONLY 
          );
        IF (DOB IS NOT NULL) THEN
        step_ := 'ROWLEVEL-1, NULL DOB - FIRST and LAST - SPECIAL CHARACTERS';
        INSERT INTO SPONSOR_INFO_SEARCH_TEMP
