                (CASE WHEN v_isSpecialCharsFirstName_ = 0 THEN 1 WHEN (trim(UPPER(A.FIRST_NAME)) = trim(v_SpecialCharsFirstName1_)) THEN 1 ELSE 0 END) = 1
                AND (CASE WHEN v_isSpecialCharsLastName_ = 0 THEN 1 WHEN (UPPER(nvl(A.LAST_NAME,'')) = v_SpecialCharsLastName1_) THEN 1 ELSE 0 END) = 1
