      INSERT INTO SPONSOR_INFO_SEARCH_TEMP
      (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address ,
          A.City,--REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0),  

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

         2 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
        join SPONSOR_INFO_PERMUTATIONS P  ON UPPER(TRIM(P.PERMUTATIONS_KEY)) = UPPER(TRIM(v_Param1))
           AND TRIM(UPPER(A.LAST_NAME)) = UPPER(TRIM(P.FNAME)) || ' ' || UPPER(TRIM(P.LNAME))
      And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
      --Order by A.First_Name, A.Last_Name 
      FETCH FIRST vSearchResultLimit ROWS ONLY );

    END IF;
   END IF;

   IF (DOB IS NULL) THEN
     step_ := 'ROWLEVEL-3 DOB IS NULL';
    INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
          A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0), 
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
          A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

         3 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
        From V_SPONSOR_INFO A
		  Where
            --((CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.first_name)) || ' ' || TRIM(UPPER(A.last_name)) = searchNames_ || '%') THEN 1 ELSE 0 END) = 1
            -- OR (CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.last_name)) || ' ' || TRIM(UPPER(A.first_name)) = searchNames_ || '%') THEN 1 ELSE 0 END) = 1
            --)
            (CASE WHEN fName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.FIRST_NAME,''), '''', '')) LIKE REPLACE(fName_ , '''', '')|| '%') THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN lName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.LAST_NAME,''), '''', '')) LIKE REPLACE(lName_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN address_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.ADDRESS,''), '''', '')) LIKE REPLACE(address_, '''', '')) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN homephone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.HOME_PHONE,'')) LIKE homephone_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN workphone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.WORK_PHONE,'')) LIKE workphone_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN email_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.EMAIL,'')) LIKE REPLACE(email_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN aNumber_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.ALIEN_NO, '')) LIKE aNumber_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN city_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.CITY,'')) LIKE city_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN state_ = 'XX' THEN 1 WHEN (UPPER(nvl(A."STATE",'')) LIKE state_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN cob_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.COB,'')) LIKE cob_ || '%') THEN 1 ELSE 0 END) = 1

		    And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
            And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
          Order by A.First_Name, A.Last_Name 
          FETCH FIRST vSearchResultLimit ROWS ONLY 
      );
  ELSE
    step_ := 'ROWLEVEL-3 DOB IS NULL';
    INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
          A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
          A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

         3 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
        From V_SPONSOR_INFO A
		  Where
            --((CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.first_name)) || ' ' || TRIM(UPPER(A.last_name)) = searchNames_ || '%') THEN 1 ELSE 0 END) = 1
            -- OR (CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.last_name)) || ' ' || TRIM(UPPER(A.first_name)) = searchNames_ || '%') THEN 1 ELSE 0 END) = 1
            --)
            (CASE WHEN fName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.FIRST_NAME,''), '''', '')) LIKE REPLACE(fName_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN lName_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.LAST_NAME,'')) LIKE lName_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN address_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.ADDRESS,''), '''', '')) LIKE REPLACE(address_, '''', '')) THEN 1 ELSE 0 END) = 1
            And (CASE WHEN homephone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.HOME_PHONE,'')) LIKE homephone_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN workphone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.WORK_PHONE,'')) LIKE workphone_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN email_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.EMAIL,'')) LIKE REPLACE(email_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN aNumber_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.ALIEN_NO, '')) LIKE aNumber_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN city_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.CITY,'')) LIKE city_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN state_ = 'XX' THEN 1 WHEN (UPPER(nvl(A."STATE",'')) LIKE state_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN cob_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.COB,'')) LIKE cob_ || '%') THEN 1 ELSE 0 END) = 1
            And To_Date(DOB, 'DD-MON-RR') = To_Date(dob_, 'DD-MON-RR')

		    And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
            And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
          Order by A.First_Name, A.Last_Name 
          FETCH FIRST vSearchResultLimit ROWS ONLY 
      );

  END IF;

   IF (DOB IS NULL) THEN 

     step_ := 'ROWLEVEL-4 DOB IS NULL';
     INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
          A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
          A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

         4 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
        From V_SPONSOR_INFO A
		  Where
            --((CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.first_name)) || ' ' || TRIM(UPPER(A.last_name)) Like ' ' ||  searchNames_ || '%') THEN 1 ELSE 0 END) = 1
            -- OR (CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.last_name)) || ' ' || TRIM(UPPER(A.first_name)) Like ' ' ||  searchNames_ || '%') THEN 1 ELSE 0 END) = 1
            --)
            (CASE WHEN fName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.FIRST_NAME,''), '''', '')) like '%' || REPLACE(fName_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN lName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.LAST_NAME,''), '''', '')) like '%' || REPLACE(lName_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN address_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.ADDRESS,''), '''', '')) like REPLACE(address_, '''', '')) THEN 1 ELSE 0 END) = 1

            And (CASE WHEN homephone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.HOME_PHONE,'')) like '%' ||  homephone_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN workphone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.WORK_PHONE,'')) like '%' ||  workphone_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN email_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.EMAIL,'')) like '%' ||  REPLACE(email_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN aNumber_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.ALIEN_NO, '')) like '%' ||  aNumber_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN city_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.CITY,'')) like '%' ||  city_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN state_ = 'XX' THEN 1 WHEN (UPPER(nvl(A."STATE",'')) like '%' ||  state_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN cob_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.COB,'')) like '%' ||  cob_ || '%') THEN 1 ELSE 0 END) = 1

		    And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
            And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
          Order by A.First_Name, A.Last_Name 
          FETCH FIRST vSearchResultLimit ROWS ONLY 
      ); 
   ELSE
     step_ := 'ROWLEVEL-4 DOB IS NOT NULL';
     INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
         A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
          A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
          A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

         4 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
        From V_SPONSOR_INFO A
		  Where
            --((CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.first_name)) || ' ' || TRIM(UPPER(A.last_name)) Like ' ' ||  searchNames_ || '%') THEN 1 ELSE 0 END) = 1
            -- OR (CASE WHEN searchNames_ = 'XXX XXX' THEN 1 WHEN (TRIM(UPPER(A.last_name)) || ' ' || TRIM(UPPER(A.first_name)) Like ' ' ||  searchNames_ || '%') THEN 1 ELSE 0 END) = 1
            --)
            (CASE WHEN fName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.FIRST_NAME,''), '''', '')) like '%' || REPLACE(fName_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN lName_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.LAST_NAME,''), '''', '')) like '%' || REPLACE(lName_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            AND (CASE WHEN address_ = 'XXX' THEN 1 WHEN (UPPER(REPLACE(nvl(A.ADDRESS,''), '''', '')) like REPLACE(address_, '''', '')) THEN 1 ELSE 0 END) = 1

            And (CASE WHEN homephone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.HOME_PHONE,'')) like '%' ||  homephone_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN workphone_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.WORK_PHONE,'')) like '%' ||  workphone_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN email_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.EMAIL,'')) like '%' ||  REPLACE(email_, '''', '') || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN aNumber_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.ALIEN_NO, '')) like '%' ||  aNumber_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN city_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.CITY,'')) like '%' ||  city_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN state_ = 'XX' THEN 1 WHEN (UPPER(nvl(A."STATE",'')) like '%' ||  state_ || '%') THEN 1 ELSE 0 END) = 1
            And (CASE WHEN cob_ = 'XXX' THEN 1 WHEN (UPPER(nvl(A.COB,'')) like '%' ||  cob_ || '%') THEN 1 ELSE 0 END) = 1
            And To_Date(DOB, 'DD-MON-RR') = To_Date(dob_, 'DD-MON-RR')

		    And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
            And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
          Order by A.First_Name, A.Last_Name 
          FETCH FIRST vSearchResultLimit ROWS ONLY 
      ); 
   END IF;

   IF (v_firstAndSecond IS NOT NULL) THEN 
     step_ := 'ROWLEVEL-5 ane is not null';
    INSERT INTO SPONSOR_INFO_SEARCH_TEMP
          (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address , 
          A.City, --REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1') 
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,

         5 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
      where 
          SOUNDEX(LOWER(trim(A.First_Name || ' ' || A.Last_Name))) = SOUNDEX(LOWER(v_firstAndSecond))
          And SPONSOR_ID Not In (Select SPONSOR_ID from UAC_SPONSOR_LIST A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Where B.UAC_PROGRAM_ID = uacProgId_)
          And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
        Order by A.First_Name, A.Last_Name 
        FETCH FIRST vSearchResultLimit ROWS ONLY 
       );  
   END IF;

   EXCEPTION 
        WHEN OTHERS THEN errors_ := 'Database error: ' || ' ' || SQLERRM;
   END; 

   --- retrieve all of the records
   IF (errors_ IS NULL) THEN
        Open cur_Out For
          SELECT DISTINCT rowC, SPONSOR_ID, ALIEN_NO, DOB, First_Name, Last_Name,Address, City, State, P_COUNTER, 
          HOME_PHONE, WORK_PHONE, EMAIL,COB,
           A_COUNTER, FLAG, FLAG_NOTE, ROWLEVEL, SPONSOR_STATUS, ADD_SPONSOR 
          FROM SPONSOR_INFO_SEARCH_TEMP 
          WHERE SEARCH_KEY = v_Param1
          ORDER BY ROWLEVEL;
   ELSE
      -- INSERT INTO SPONSOR_INFO_SEARCH_TEMP
      -- Select DISTINCT v_Param1, 0 as rowC,  0 AS SPONSOR_ID, '' AS ALIEN_NO, NULL AS DOB, 
          --'Database_Error' AS FIRST_NAME, 
       --   'SP_GET_UACSPONSORS_BY_ARRAY' AS Last_Name, 
        --  errors_ || ' IN ' || step_ AS Address, 
        --  '' AS City, 
        --  '' AS State, 0 AS P_COUNTER, 
        --  '0000000000' as HOME_PHONE, '0000000000'  as WORK_PHONE,
        -- '' AS EMAIL,
      --   '' AS COB,
      --   0 AS A_COUNTER, '' AS FLAG, '' as FLAG_NOTE, -1 AS ROWLEVEL, '' as SPONSOR_STATUS,'' as ADD_SPONSOR
     --   FROM DUAL;

        Open cur_Out For
          SELECT DISTINCT rowC, SPONSOR_ID, ALIEN_NO, DOB, First_Name, Last_Name,Address, City, State, P_COUNTER, 
          HOME_PHONE, WORK_PHONE, EMAIL,COB,
           A_COUNTER, FLAG, FLAG_NOTE, ROWLEVEL, SPONSOR_STATUS, ADD_SPONSOR 
          FROM SPONSOR_INFO_SEARCH_TEMP 
          WHERE SEARCH_KEY = v_Param1
          ORDER BY ROWLEVEL;        
   END IF;

   -- delete the values from the temp table based on the v_Param1
   DELETE FROM SPONSOR_INFO_SEARCH_TEMP WHERE SEARCH_KEY = v_Param1;
   DELETE FROM SPONSOR_INFO_PERMUTATIONS WHERE PERMUTATIONS_KEY = v_Param1;

END SP_GET_UACSPONSORS_BY_ARRAY;

PROCEDURE "SP_GET_UAC_CASESTATUS_ALT_CAREGIVER" ( UAC_ID NUMBER, cur_Out OUT SYS_REFCURSOR ) AS uacId_ NUMBER(10,0) := UAC_ID; SponsorID NUMBER;
BEGIN
Open CUR_OUT for select distinct e.UAC_ID,e.SPONSOR_ID,e.OCCUPANT_ID,e.SPONSOR_ALTERNATE_CAREGIVER_ID, (select NAME from SPONSOR_ALTERNATE_CAREGIVER where e.SPONSOR_ALTERNATE_CAREGIVER_ID = SPONSOR_ALTERNATE_CAREGIVER_ID AND Age > 17 and ISDELETED = 0) as NAME , a.DATE_REQUESTED as BG_PUBLIC_REQUESTED,a.DATE_RECEIVED as BG_PUBLIC_RECiEVED,(select DESCRIPTION from LIST_BG_CHECK_RESULT_TYPE where a.result_id = result_id) as BG_PUBLIC_RESULTS, b.DATE_REQUESTED as BG_SEX_REQUESTED,b.DATE_RECEIVED as BG_SEX_RECIEVED,(select DESCRIPTION from LIST_BG_CHECK_RESULT_TYPE where b.result_id = result_id) as BG_SEX_RESULTS, c.DATE_REQUESTED as BG_CAN_REQUESTED,c.DATE_RECEIVED as BG_CAN_RECIEVED,(select DESCRIPTION from LIST_BG_CHECK_RESULT_TYPE where c.result_id = result_id) as BG_CAN_RESULTS, d.DATE_REQUESTED as BG_FBICH_REQUESTED,d.DATE_RECEIVED as BG_FBICH_RECIEVED,(select DESCRIPTION from LIST_BG_CHECK_RESULT_TYPE where d.result_id = result_id) as BG_FBICH_RESULTS, d.FINGER_PRINT_METHOD,d.FINGER_PRINT_DATE_AVAILABLE,d.FINGER_PRINT_APPOINTMENT, d.ID_DATE_SENT ,d.ARI_SENT_TO_ORR,d.FIELD_PRINTDATE_AVAILABLE,d.FIELD_PRINT_APPOINTMENT, d.CARD_PRINT_SENT_DATE,d.CARD_PRINT_DATE_RECEIVED, case when d.BG_FBICH is null then 0 else 1 end as FBI_SHOW from SPONSOR_BACKGROUND_CHECK e LEFT OUTER join SPONSOR_BACKGROUND_CHECK a on a.BG_PUBLIC=1 and a.sponsor_id = e.sponsor_id and a.occupant_id is null And e.SPONSOR_ALTERNATE_CAREGIVER_ID = a.SPONSOR_ALTERNATE_CAREGIVER_ID LEFT OUTER join SPONSOR_BACKGROUND_CHECK b on b.BG_SEX = 1 and b.sponsor_id = e.sponsor_id and b.occupant_id is null And e.SPONSOR_ALTERNATE_CAREGIVER_ID = b.SPONSOR_ALTERNATE_CAREGIVER_ID LEFT OUTER join SPONSOR_BACKGROUND_CHECK c on c.BG_CAN = 1 and c.sponsor_id = e.sponsor_id and c.occupant_id is null And e.SPONSOR_ALTERNATE_CAREGIVER_ID = c.SPONSOR_ALTERNATE_CAREGIVER_ID LEFT OUTER join SPONSOR_BACKGROUND_CHECK d on d.BG_FBICH = 1 and d.sponsor_id = e.sponsor_id and d.occupant_id is null And e.SPONSOR_ALTERNATE_CAREGIVER_ID = d.SPONSOR_ALTERNATE_CAREGIVER_ID where e.UAC_ID = UACID_ and e.occupant_id is null and e.SPONSOR_ALTERNATE_CAREGIVER_ID is not null and (select age from SPONSOR_ALTERNATE_CAREGIVER where e.SPONSOR_ALTERNATE_CAREGIVER_ID = SPONSOR_ALTERNATE_CAREGIVER_ID) > 17 and e.SPONSOR_ALTERNATE_CAREGIVER_ID not in( select SPONSOR_ALTERNATE_CAREGIVER_ID from SPONSOR_ALTERNATE_CAREGIVER where e.SPONSOR_ALTERNATE_CAREGIVER_ID = SPONSOR_ALTERNATE_CAREGIVER_ID and ISDELETED = 1) and e.SPONSOR_ALTERNATE_CAREGIVER_ID not in( select SPONSOR_ALTERNATE_CAREGIVER_ID from SPONSOR_ALTERNATE_CAREGIVER where e.SPONSOR_ALTERNATE_CAREGIVER_ID = SPONSOR_ALTERNATE_CAREGIVER_ID and AGE < 18) and e.sponsor_id = (Select sponsor_id from Uac_sponsor_list where e.sponsor_id = sponsor_id and is_deleted = 0 and e.UAC_ID = UAC_ID and current_sponsor=1 and rownum = 1) and e.SPONSOR_ALTERNATE_CAREGIVER_ID not in ( select SPAC.SPONSOR_ALTERNATE_CAREGIVER_ID from SPONSOR_ALTERNATE_CAREGIVER SPAC inner join SPONSOR_HOUSEHOLD_OCCUPANTS SHO on SHO.uac_id = SPAC.UAC_ID and SHO.SPONSOR_ID = SPAC.Sponsor_id and SHO.ISDELETED = 0 where SHO.DOB=SPAC.DOB and (UPPER(sho.first_name)||' ' || UPPER(sho.last_name) = Upper(SPAC.name))); END SP_GET_UAC_CASESTATUS_ALT_CAREGIVER;

PROCEDURE "SP_GET_UAC_CASESTATUS_HH" ( vUAC_ID NUMBER, cur_Out OUT SYS_REFCURSOR ) AS UACID_ NUMBER(10,0) := vUAC_ID; SponsorID NUMBER;
BEGIN
Open CUR_OUT for select distinct e.UAC_ID,e.SPONSOR_ID,e.OCCUPANT_ID,e.SPONSOR_ALTERNATE_CAREGIVER_ID, (select NAME_of_OCCUPANT from SPONSOR_HOUSEHOLD_OCCUPANTS where e.OCCUPANT_ID = Occupant_id AND Age > 17 and ISDELETED = 0) as OCCUPANT_NAME , a.DATE_REQUESTED as BG_PUBLIC_REQUESTED,a.DATE_RECEIVED as BG_PUBLIC_RECiEVED,(select DESCRIPTION from LIST_BG_CHECK_RESULT_TYPE where a.result_id = result_id) as BG_PUBLIC_RESULTS, b.DATE_REQUESTED as BG_SEX_REQUESTED,b.DATE_RECEIVED as BG_SEX_RECIEVED,(select DESCRIPTION from LIST_BG_CHECK_RESULT_TYPE where b.result_id = result_id) as BG_SEX_RESULTS, c.DATE_REQUESTED as BG_CAN_REQUESTED,c.DATE_RECEIVED as BG_CAN_RECIEVED,(select DESCRIPTION from LIST_BG_CHECK_RESULT_TYPE where c.result_id = result_id) as BG_CAN_RESULTS, d.DATE_REQUESTED as BG_FBICH_REQUESTED,d.DATE_RECEIVED as BG_FBICH_RECIEVED,(select DESCRIPTION from LIST_BG_CHECK_RESULT_TYPE where d.result_id = result_id) as BG_FBICH_RESULTS, d.FINGER_PRINT_METHOD,d.FINGER_PRINT_DATE_AVAILABLE,d.FINGER_PRINT_APPOINTMENT, d.ID_DATE_SENT ,d.ARI_SENT_TO_ORR,d.FIELD_PRINTDATE_AVAILABLE,d.FIELD_PRINT_APPOINTMENT, d.CARD_PRINT_SENT_DATE,d.CARD_PRINT_DATE_RECEIVED, case when d.BG_FBICH is null then 0 else 1 end as FBI_SHOW from SPONSOR_BACKGROUND_CHECK e LEFT OUTER join SPONSOR_BACKGROUND_CHECK a on a.BG_PUBLIC=1 and a.sponsor_id = e.sponsor_id and a.occupant_id = e.occupant_id And a.SPONSOR_ALTERNATE_CAREGIVER_ID is null LEFT OUTER join SPONSOR_BACKGROUND_CHECK b on b.BG_SEX = 1 and b.sponsor_id = e.sponsor_id and b.occupant_id =e.occupant_id and b.SPONSOR_ALTERNATE_CAREGIVER_ID is null LEFT OUTER join SPONSOR_BACKGROUND_CHECK c on c.BG_CAN = 1 and c.sponsor_id = e.sponsor_id and c.occupant_id =e.occupant_id and c.SPONSOR_ALTERNATE_CAREGIVER_ID is null LEFT OUTER join SPONSOR_BACKGROUND_CHECK d on d.BG_FBICH = 1 and d.sponsor_id = e.sponsor_id and d.occupant_id =e.occupant_id and d.SPONSOR_ALTERNATE_CAREGIVER_ID is null where e.UAC_ID = UACID_ and e.occupant_id is not null and e.SPONSOR_ALTERNATE_CAREGIVER_ID is null and (select age from SPONSOR_HOUSEHOLD_OCCUPANTS where e.OCCUPANT_ID = OCCUPANT_ID) > 17 and e.occupant_ID not in( select OCCUPANT_ID from SPONSOR_HOUSEHOLD_OCCUPANTS where e.OCCUPANT_ID = Occupant_id and ISDELETED = 1) and e.occupant_ID not in( select OCCUPANT_ID from SPONSOR_HOUSEHOLD_OCCUPANTS where e.OCCUPANT_ID = Occupant_id and age <18) and e.sponsor_id = (Select sponsor_id from Uac_sponsor_list where e.sponsor_id = sponsor_id and is_deleted = 0 and e.UAC_ID = UAC_ID and current_sponsor =1 and rownum = 1);
END SP_GET_UAC_CASESTATUS_HH;

PROCEDURE "SP_GET_UAC_CASESTATUS_INFO" ( UAC_ID NUMBER, UAC_PROGRAM_ID NUMBER, cur_Out OUT SYS_REFCURSOR ) AS uacId_ NUMBER(10,0) := UAC_ID; uacProgId_ NUMBER(10,0) := UAC_PROGRAM_ID; BEGIN DELETE FROM TT_UAC_CASESTATUS_INFO;
INSERT INTO TT_UAC_CASESTATUS_INFO(UAC_ID, UAC_PROGRAM_ID) VALUES (uacId_, uacProgId_);
Update TT_UAC_CASESTATUS_INFO SET INIT_DATE_COMPLETED = (Select To_Char(MAX(B.UPTIME), 'mm/dd/yyyy') From UAC_INITIAL_ASSESSMENT B Where B.UAC_PROGRAM_ID = uacProgId_ And nvl(B.ISDELETED,0) = 0), RISK_DATE_COMPLETED = (SELECT To_Char(MAX(UPTIME), 'mm/dd/yyyy') AS MAX_UPTIME FROM UAC_RISK_ASSESSMENT WHERE UAC_PROGRAM_ID = uacProgId_ And nvl(ISDELETED, 0) = 0), ASSESSMNT_DATE_COMPLETED = (SELECT TO_CHAR(Max(UPTIME),'MM/DD/YYYY') FROM UAC_ASSESSMENT WHERE UAC_PROGRAM_ID = uacProgId_ And nvl(ISDELETED, 0) = 0), --******************* HS *********************************** (REFERRAL_DATE_HS, ACCEPTANCE_DATE_HS, TYPE_OF_HOMESTUDY) = (Select DISTINCT To_Char(REFERRAL_DATE, 'mm/dd/yyyy'), To_Char(ACCEPTANCE_DATE, 'mm/dd/yyyy'), Case When ORR_DECISION = 7 Then 'TVPRA' When ORR_DECISION = 8 Then 'Discretionary' When ORR_DECISION = 9 Then 'ORR' ELSE 'N/A' End as TYPE_OF_HOMESTUDY FROM (Select A.REFERRAL_DATE, A.ACCEPTANCE_DATE, E.ORR_DECISION, A.HOMESTUDY_ID From UAC_HOMESTUDY_CASE_REFERRAL A Inner Join UAC_RELEASE_REQUEST E on A.UAC_PROGRAM_ID = E.UAC_PROGRAM_ID INNER JOIN (SELECT MAX(DATE_CREATED) AS MAX_DATE_CREATED FROM UAC_RELEASE_REQUEST WHERE ORR_DECISION <> 6 AND nvl(ISDELETED,0) = 0 AND UAC_PROGRAM_ID = uacProgId_) R2 ON E.DATE_CREATED = R2.MAX_DATE_CREATED WHERE A.REFERRAL_STATUS IN (1,2,3) AND A.UAC_PROGRAM_ID = uacProgId_ AND E.ORR_DECISION IN (7,8,9) ORDER BY A.HOMESTUDY_ID DESC FETCH FIRST 1 ROWS ONLY)), --******************* PRS *********************************** (REFERRAL_DATE_PRS, ACCEPTANCE_DATE_PRS, TYPE_OF_PRS) = (Select DISTINCT To_Char(REFERRAL_DATE, 'mm/dd/yyyy'), To_Char(ACCEPTANCE_DATE, 'mm/dd/yyyy'), CASE WHEN PRS_Type=0 AND ORR_DECISION_AFTER_HS = 1 THEN 'PRS Non TVPRA' WHEN PRS_Type=0 AND ORR_DECISION_AFTER_HS = 5 THEN 'PRS-TVPRA' WHEN PRS_Type=1 THEN 'PRS ONLY' ELSE 'N/A' End AS TYPE_OF_PRS FROM (SELECT A.REFERRAL_DATE, A.ACCEPTANCE_DATE, A.PRS_TYPE, E.ORR_DECISION_AFTER_HS, A.PRS_ID From UAC_POST_RELEASE_SERVICE A Inner Join UAC_RELEASE_REQUEST E on A.UAC_PROGRAM_ID = E.UAC_PROGRAM_ID INNER JOIN (SELECT MAX(DATE_CREATED) AS MAX_DATE_CREATED FROM UAC_RELEASE_REQUEST WHERE ORR_DECISION <> 6 AND nvl(ISDELETED,0) = 0 AND UAC_PROGRAM_ID = uacProgId_) R2 ON E.DATE_CREATED = R2.MAX_DATE_CREATED WHERE A.REFERRAL_STATUS IN (1,2,3) AND A.UAC_PROGRAM_ID = uacProgId_ AND ((E.ORR_DECISION_AFTER_HS IN (1,5) AND E.ORR_DECISION <> 6 AND A.PRS_Type = 0) OR (E.ORR_DECISION = 2 AND A.PRS_Type = 1)) ORDER BY A.PRS_ID DESC FETCH FIRST 1 ROWS ONLY )), --******************* Release Request *********************************** (CASEMANAGER_DATE_SUBMITTED, CASECOORD_DATE_SUBMITTED, ORR_DATE_SUBMITTED, RELEASE_APPROVED_DATE) = (Select To_Char(A.CW_UPTIME, 'mm/dd/yyyy'), To_Char(A.CC_UPTIME, 'mm/dd/yyyy'), TO_CHAR(A.ORR_UPTIME,'MM/DD/YYYY'), TO_CHAR(RELEASE_APPROVED_DATE, 'MM/DD/YYYY') From UAC_RELEASE_REQUEST A INNER JOIN (SELECT UAC_PROGRAM_ID, MAX(UPTIME) AS RR_MAX_UPTIME FROM UAC_RELEASE_REQUEST WHERE ORR_DECISION <> 6 AND nvl(ISDELETED, 0) = 0 AND UAC_PROGRAM_ID = uacProgId_ GROUP BY UAC_PROGRAM_ID ) MR ON A.UPTIME = MR.RR_MAX_UPTIME WHERE A.ORR_DECISION <> 6 AND nvl(A.ISDELETED, 0) = 0 AND A.UAC_PROGRAM_ID = uacProgId_), --******************* Legal *********************************** LO_END_DATE = (SELECT To_Char(I.LO_END_DATE, 'mm/dd/yyyy') FROM UAC_INDIVIDUAL_SERVICE_PLAN I INNER JOIN (SELECT MAX(I1.UPTIME) as LAST_UPTIME FROM UAC_INDIVIDUAL_SERVICE_PLAN I1 WHERE I1.UAC_PROGRAM_ID = uacProgId_) ispLatest ON ispLatest.LAST_UPTIME = I.UPTIME WHERE I.UAC_PROGRAM_ID = uacProgId_ and rownum = 1) WHERE UAC_ID = uacId_ AND UAC_PROGRAM_ID = uacProgId_;
Open CUR_OUT for SELECT "UAC_ID", "UAC_PROGRAM_ID", "INIT_DATE_COMPLETED", "RISK_DATE_COMPLETED", "ASSESSMNT_DATE_COMPLETED", "TYPE_OF_HOMESTUDY", "REFERRAL_DATE_HS", "ACCEPTANCE_DATE_HS", "TYPE_OF_PRS", "REFERRAL_DATE_PRS", "ACCEPTANCE_DATE_PRS", "LO_END_DATE", "CASEMANAGER_DATE_SUBMITTED", "CASECOORD_DATE_SUBMITTED", "ORR_DATE_SUBMITTED", "RELEASE_APPROVED_DATE" FROM TT_UAC_CASESTATUS_INFO WHERE UAC_ID = uacId_ AND UAC_PROGRAM_ID = uacProgId_; END SP_GET_UAC_CASESTATUS_INFO;

PROCEDURE "SP_GET_USER_INFO" 
(
  USERID IN VARCHAR2  
, CUR_OUT OUT SYS_REFCURSOR 
) AS 
userId_    VARCHAR2(250) := Replace(Upper(Trim(USERID)), ' ', '');
v_passwordresetexpired VARCHAR2(10) := 'NO';
BEGIN
  -- Variables for checking user password reset expiration
  DECLARE v_hr_diff NUMBER;
  v_hr_limit NUMBER; 
  -- Check and suspend user if user has not reset password within duration specified in UACPORTAL_APP_PARAMS
  BEGIN

    -- Get temp password expiration limit
    SELECT PARAM_VALUE into v_hr_limit FROM UACPORTAL_APP_PARAMS WHERE PARAM_NAME = 'passwordResetLock';
    BEGIN      
      -- Get difference in hours of current time with time password was created
      SELECT datediff('Hour', M.MAX_PASSWORDLASTCHANGED, CURRENT_TIMESTAMP) into v_hr_diff
      FROM ADM_LIST_WEB_USER U 
      INNER JOIN
              (SELECT UPPER(Trim(USER_ID)) as USER_ID, max(PASSWORDLASTCHANGED) as MAX_PASSWORDLASTCHANGED 
                FROM ADM_WEB_USER_PWD  
                GROUP BY UPPER(Trim(USER_ID))) M ON Upper(Trim(M.USER_ID)) = Upper(Trim(U.USER_ID))
      WHERE  U.PASSWORDRESET = 1
      AND Upper(Trim(U.USER_ID)) = Upper(Trim(userId_));
      exception
        WHEN TOO_MANY_ROWS
        Then SELECT 0 into v_hr_diff from dual;
        WHEN NO_DATA_FOUND
        Then SELECT 0 into v_hr_diff from dual;
    END;
    --if password was created more that v_hr_limit hours ago then suspend user
    If v_hr_diff > v_hr_limit Then
      Update ADM_LIST_WEB_USER
      SET SUSPENDED = 1
      WHERE Upper(Trim(USER_ID)) = Upper(Trim(userId_));
      COMMIT;
      v_passwordresetexpired := 'YES';
    End if;
  END;
  --Return user info
  If v_passwordresetexpired = 'YES' Then
    OPEN CUR_OUT FOR
    Select U.*, 'YES'as PWD_RESET_EXP 
    from ADM_LIST_WEB_USER U Where Replace(Upper(Trim(U.USER_ID)), ' ', '') = userId_ ORDER BY U.UPTIME DESC;
  ELSE
    OPEN CUR_OUT FOR
    Select U.*, 'NO'as PWD_RESET_EXP 
    from ADM_LIST_WEB_USER U Where Replace(Upper(Trim(U.USER_ID)), ' ', '') = userId_ ORDER BY U.UPTIME DESC;
  End if;

END SP_GET_USER_INFO;

PROCEDURE "SP_HD_DELETE_A_NUMBER" ( v_UAC_ID VARCHAR2, v_USER_ID VARCHAR2 ) AS oldValue_ VARCHAR2(255) := ''; uacProgID_ VARCHAR2(10) := ''; uacID_ VARCHAR2(10) := ''; errorMsg_ VARCHAR2(100) := ''; errorLocation_ VARCHAR2(50) := ''; sqlString_ VARCHAR2(500) := ''; userId_ VARCHAR2(255) := ''; backString_ VARCHAR2(32767) := ''; n NUMBER := 0; n1 NUMBER := 0; n2 NUMBER := 0; BEGIN
SAVEPOINT save1;
userId_ := v_USER_ID; uacID_ := v_UAC_ID; if userId_ = '001' OR userId_ = '002' OR userId_ = '007' OR userId_ = '003' Then
-- +-------------------------------------------------------------+ -- | Check if ALIEN_NO is valid | -- +-------------------------------------------------------------+ BEGIN sqlString_ := 'Select ALIEN_NO FROM UAC_INFO WHERE UAC_ID = ' || v_UAC_ID; execute immediate sqlString_ into oldValue_; EXCEPTION WHEN no_data_found THEN uacProgID_ := ''; errorMsg_ := 'NO records were found for UAC_ID: ' || v_UAC_ID || '!'; dbms_output.put_line(errorMsg_); RETURN; END;
BEGIN Select count(*) into n from ( SELECT 1 FROM SPONSOR_BACKGROUND_CHECK WHERE UAC_ID = uacID_ UNION SELECT 1 FROM SPONSOR_HOUSEHOLD_OCCUPANTS WHERE UAC_ID = uacID_ UNION SELECT 1 FROM SPONSOR_BACKGROUND_CHECK WHERE UAC_ID = uacID_ UNION SELECT 1 FROM SPONSOR_HOUSEHOLD_OCCUPANTS WHERE UAC_ID = uacID_ UNION SELECT 1 FROM UAC_FAST WHERE UAC_ID = uacID_ UNION SELECT 1 FROM UAC_GANG_DETERMINATION_BY WHERE UAC_ID = uacID_ UNION SELECT 1 FROM UAC_INTAKES_DOC_LIST WHERE UAC_ID = uacID_ UNION SELECT 1 FROM UAC_SPONSOR_LIST WHERE UAC_ID = uacID_ UNION Select 0 from dual);
FOR ind IN (SELECT UAC_PROGRAM_ID FROm UAC_PROGRAM_INFO Where UAC_ID = uacID_) LOOP Select count(*) into n1 from ( SELECT 1 FROM UAC_ASSESSMENT WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_CASE_REVIEW WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_DISCHARGE_NOTIFICATION WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_DOCTOR_VISIT WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_DOCUMENTS WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_EDUCATION_PLACEMENT WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_EFFORT WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_FOSTER_CARE_TRAVEL_REQUEST WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_HOMESTUDY_CASE_REFERRAL WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_IMAGES WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_IMMUNIZATION WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_INDIVIDUAL_SERVICE_PLAN WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_INITIAL_ASSESSMENT WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_MEDICAL_SCREENING WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_MEDICAL_SCREENING_NOTES WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_MEDICAL_VISIT WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_MEDICATION_INFO WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_POST_RELEASE_SERVICE WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_PROGRAM_EXIT WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_RELEASE_REQUEST WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_RISK_ASSESSMENT WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_SECURE_REVIEW WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_SIR_NEW WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION SELECT 1 FROM UAC_TRANSFER_REQUEST WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID UNION Select 0 from dual );
if n1 > 1 then n2 := n1; end if;
END LOOP;
if n > 1 OR n2 > 1 Then --SELECT 'Please delete the related records for this UAC before first.' as ERROR_MSSGE from dual; errorMsg_ := 'There are multiple child reocrds associated with this UAC_ID.'; dbms_output.put_line(errorMsg_); RETURN; end if; END;
-- +-------------------------------------------------------------+ -- | BACKUP DATA, THEN DELETE | -- +-------------------------------------------------------------+ FOR ind IN (SELECT APPREHENSION_ID FROm UAC_APPREHENSION_INFO Where UAC_ID = uacID_) LOOP SELECT ' Insert into UAC_APPREHENSION_INFO (APPREHENSION_ID, UAC_ID, NAME, ANUMBER, PHONE, RELATIONSHIP_ID, ADDRESS, APPREHENSION_TYPE, UPTIME,UPUSER, AGE, IN_ORR_CARE ) Values (' || APPREHENSION_ID || ',' || UAC_ID || ',' || CASE WHEN NAME Is Null THEN 'null,' ELSE '''' || NAME || '''' || ',' END || CASE WHEN ANUMBER Is Null THEN 'null,' ELSE '''' || ANUMBER || '''' || ',' END || CASE WHEN PHONE Is Null THEN 'null,' ELSE '''' || PHONE || '''' || ',' END || RELATIONSHIP_ID || ',' || CASE WHEN ADDRESS Is Null THEN 'null,' ELSE '''' || ADDRESS || '''' || ',' END || CASE WHEN APPREHENSION_TYPE Is Null THEN 'null,' ELSE '''' || APPREHENSION_TYPE || '''' || ',' END || 'to_timestamp(''' || UPTIME || ''',''DD-MON-RR HH.MI.SSXFF AM''),' || CASE WHEN UPUSER Is Null THEN 'null,' ELSE '''' || UPUSER || '''' || ',' END || AGE || ',' || IN_ORR_CARE || ');' into backString_ FROm UAC_APPREHENSION_INFO Where APPREHENSION_ID = ind.APPREHENSION_ID;
DELETE FROM UAC_APPREHENSION_INFO Where APPREHENSION_ID = ind.APPREHENSION_ID;
INSERT INTO UACPORTAL.HD_TRANSACTION_LOG( USER_ID, TIME_STAMP, TRANS_TYPE, SP_NAME, TABLE_NAME, RECORD_ID, COLUMN_NAME, COLUMN_NEW_VALUE, COLUMN_OLD_VALUE ) VALUES ( userId_, SYSDATE, 'DELETE', 'SP_HD_DELETE_A_NUMBER', 'UAC_APPREHENSION_INFO', ind.APPREHENSION_ID, null, null, backString_ );
END LOOP;
FOR ind IN (SELECT UAC_PROGRAM_ID FROm UAC_PROGRAM_INFO Where UAC_ID = uacID_) LOOP SELECT 'Insert into UAC_PROGRAM_INFO (UAC_PROGRAM_ID, UAC_ID, PROGRAM_ID, DATE_REFERRED, BIO_VERIFIED, DATE_ADMITTED, CURRENT_STATUS, DATE_DISCHARGED, DATE_CREATED, USER_CREATED, UPUSER, UPTIME) values (' || UAC_PROGRAM_ID || ',' || UAC_ID || ',' || PROGRAM_ID || ',' || 'to_date(''' || DATE_REFERRED || ''',''DD-MON-RR''),' || BIO_VERIFIED || ',' || 'to_date(''' || DATE_ADMITTED || ''',''DD-MON-RR''),' || CASE WHEN CURRENT_STATUS Is Null THEN 'null,' ELSE '''' || CURRENT_STATUS || '''' || ',' END || 'to_date(''' || DATE_DISCHARGED || ''',''DD-MON-RR''),' || 'to_date(''' || DATE_CREATED || ''',''DD-MON-RR''),' || CASE WHEN USER_CREATED Is Null THEN 'null,' ELSE '''' || USER_CREATED || '''' || ',' END || CASE WHEN UPUSER Is Null THEN 'null,' ELSE '''' || UPUSER || '''' || ',' END || 'to_timestamp(''' || UPTIME || ''',''DD-MON-RR HH.MI.SSXFF AM''));' into backString_ FROM UAC_PROGRAM_INFO WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID;
DELETE FROM UAC_PROGRAM_INFO WHERE UAC_PROGRAM_ID = ind.UAC_PROGRAM_ID;
INSERT INTO UACPORTAL.HD_TRANSACTION_LOG( USER_ID, TIME_STAMP, TRANS_TYPE, SP_NAME, TABLE_NAME, RECORD_ID, COLUMN_NAME, COLUMN_NEW_VALUE, COLUMN_OLD_VALUE ) VALUES ( userId_, SYSDATE, 'DELETE', 'SP_HD_DELETE_A_NUMBER', 'UAC_PROGRAM_INFO', ind.UAC_PROGRAM_ID, null, null, backString_ );
END LOOP;
SELECT ' Insert into UAC_INFO (UAC_ID, ALIEN_NO, FIRST_NAME, LAST_NAME, AKA,DOB, COB, DATE_OF_ENTRY, MANNER_OF_ENTRY, CITY_OF_ENTRY, SPECIFY_CITY_OF_ENTRY, STATE_OF_ENTRY, DATE_OF_APPREHENSION, REFERRING_OFFICE, NOTIFICATION_CITY, SPECIFY_NOT_CITY, NOTIFICATION_STATE, DATE_ORR_NOTIFIED, TIME_ORR_NOTIFIED, DATE_ORR_APPROVED, TIME_ORR_APPROVED, PREGNANCY,INJURY, ILLNESS, BEHAVIOR_WO_MEDICATION, BEHAVIOR_W_MEDICATION, NOTE_BI_WO_MEDICATION, NOTE_FOR_W_MEDICATION, IMMIGRATION_STATUS, REFERRING_AGENCY, ORR_COMMENTS, UAC_STATUS, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, GENDER, UAC_FAST_STATUS, STATUS_ID, AGENCY_ID, MIDDLE_NAME, UAC_HEALTH_CONCERN, UAC_CRIMINAL_CHARGE, UAC_ADDITIONAL_INFO, HC_OTHER, HC_SUMMARY, HC_ERGENT_CONDITION, UAC_FLAG, PROCESSING_POC, PROCESSING_EMAIL, PROCESSING_PHONE, REFERRAL_NOTES, JUSTIFICATION_FOR_SS_PLACEMENT, DATE_OF_CURRENT_LOCATION, KNOWN_GANG_AFFILIATION, NAME_OF_GANG, CITY_OF_APPREHENSION, STATE_OF_APPREHENSION, CITY_OF_CURRENT_LOCATION, STATE_OF_CURRENT_LOCATION, GANG_AFFILIATION_SUMMARY, DETENTION_TYPE_OF_FACILITY, FACILITY_NAME, FACILITY_PHONE, FACILITY_POC, FACILITY_FAX, FACILITY_ADMISSION_DATE, FACILITY_DISCHARGED_DATE, INCIDENT_RPT_IN_DC, SUM_OF_MM_HEALTH_CONDITION, DATE_CANCELLED, CANCELLED_BY, REFERRAL_DATE, FAMILYGROUP, FOOT_GUIDE, REVIEWED_BY_ORR, CURRENT_LOCATION_OTHER, RELATED_TO_OTHER_UC, RELATIONSHIP_GRP_ID, UAC_SEPARATED_FROM_PARENTS) values (' || UAC_ID || ',' || CASE WHEN ALIEN_NO Is Null THEN 'null,' ELSE '''' || ALIEN_NO || '''' || ',' END || CASE WHEN FIRST_NAME Is Null THEN 'null,' ELSE '''' || FIRST_NAME || '''' || ',' END || CASE WHEN LAST_NAME Is Null THEN 'null,' ELSE '''' || LAST_NAME || '''' || ',' END || CASE WHEN AKA Is Null THEN 'null,' ELSE '''' || AKA || '''' || ',' END || 'to_date(''' || DOB || ''',''DD-MON-RR''),' || CASE WHEN COB Is Null THEN 'null,' ELSE '''' || COB || '''' || ',' END || DATE_OF_ENTRY || ''',''DD-MON-RR''),' || CASE WHEN MANNER_OF_ENTRY Is Null THEN 'null,' ELSE '''' || MANNER_OF_ENTRY || '''' || ',' END || CASE WHEN CITY_OF_ENTRY Is Null THEN 'null,' ELSE '''' || CITY_OF_ENTRY || '''' || ',' END || CASE WHEN SPECIFY_CITY_OF_ENTRY Is Null THEN 'null,' ELSE '''' || SPECIFY_CITY_OF_ENTRY || '''' || ',' END || CASE WHEN STATE_OF_ENTRY Is Null THEN 'null,' ELSE '''' || STATE_OF_ENTRY || '''' || ',' END || 'to_date(''' || DATE_OF_APPREHENSION || ''',''DD-MON-RR''),' || REFERRING_OFFICE || ',' || CASE WHEN NOTIFICATION_CITY Is Null THEN 'null,' ELSE '''' || NOTIFICATION_CITY || '''' || ',' END || CASE WHEN SPECIFY_NOT_CITY Is Null THEN 'null,' ELSE '''' || SPECIFY_NOT_CITY || '''' || ',' END || CASE WHEN NOTIFICATION_STATE Is Null THEN 'null,' ELSE '''' || NOTIFICATION_STATE || '''' || ',' END || 'to_date(''' || DATE_ORR_NOTIFIED || ''',''DD-MON-RR''),' || 'to_date(''' || TIME_ORR_NOTIFIED || ''',''DD-MON-RR''),' || 'to_date(''' || DATE_ORR_APPROVED || ''',''DD-MON-RR''),' || 'to_date(''' || TIME_ORR_APPROVED || ''',''DD-MON-RR''),' || PREGNANCY || ',' || INJURY || ',' || ILLNESS || ',' || BEHAVIOR_WO_MEDICATION || ',' || BEHAVIOR_W_MEDICATION || ',' || CASE WHEN NOTE_BI_WO_MEDICATION Is Null THEN 'null,' ELSE '''' || NOTE_BI_WO_MEDICATION || '''' || ',' END || CASE WHEN NOTE_FOR_W_MEDICATION Is Null THEN 'null,' ELSE '''' || NOTE_FOR_W_MEDICATION || '''' || ',' END || CASE WHEN IMMIGRATION_STATUS Is Null THEN 'null,' ELSE '''' || IMMIGRATION_STATUS || '''' || ',' END || REFERRING_AGENCY || ',' || CASE WHEN ORR_COMMENTS Is Null THEN 'null,' ELSE '''' || ORR_COMMENTS || '''' || ',' END || CASE WHEN UAC_STATUS Is Null THEN 'null,' ELSE '''' || UAC_STATUS || '''' || ',' END || 'to_date(''' || DATE_CREATED || ''',''DD-MON-RR''),' || CASE WHEN USER_CREATED Is Null THEN 'null,' ELSE '''' || USER_CREATED || '''' || ',' END || 'to_timestamp(''' || UPTIME || ''',''DD-MON-RR HH.MI.SSXFF AM''),' || CASE WHEN UPUSER Is Null THEN 'null,' ELSE '''' || UPUSER || '''' || ',' END || CASE WHEN GENDER Is Null THEN 'null,' ELSE '''' || GENDER || '''' || ',' END || CASE WHEN UAC_FAST_STATUS Is Null THEN 'null,' ELSE '''' || UAC_FAST_STATUS || '''' || ',' END || STATUS_ID || ',' || AGENCY_ID || ',' || CASE WHEN MIDDLE_NAME Is Null THEN 'null,' ELSE '''' || MIDDLE_NAME || '''' || ',' END || UAC_HEALTH_CONCERN || ',' || UAC_CRIMINAL_CHARGE || ',' || CASE WHEN UAC_ADDITIONAL_INFO Is Null THEN 'null,' ELSE '''' || UAC_ADDITIONAL_INFO || '''' || ',' END || HC_OTHER || ',' || CASE WHEN HC_SUMMARY Is Null THEN 'null,' ELSE '''' || HC_SUMMARY || '''' || ',' END || HC_ERGENT_CONDITION || ',' || CASE WHEN UAC_FLAG Is Null THEN 'null,' ELSE '''' || UAC_FLAG || '''' || ',' END || CASE WHEN PROCESSING_POC Is Null THEN 'null,' ELSE '''' || PROCESSING_POC || '''' || ',' END || CASE WHEN PROCESSING_EMAIL Is Null THEN 'null,' ELSE '''' || PROCESSING_EMAIL || '''' || ',' END || CASE WHEN PROCESSING_PHONE Is Null THEN 'null,' ELSE '''' || PROCESSING_PHONE || '''' || ',' END || CASE WHEN REFERRAL_NOTES Is Null THEN 'null,' ELSE '''' || REFERRAL_NOTES || '''' || ',' END || CASE WHEN JUSTIFICATION_FOR_SS_PLACEMENT Is Null THEN 'null,' ELSE '''' || JUSTIFICATION_FOR_SS_PLACEMENT || '''' || ',' END || 'to_date(''' || DATE_OF_CURRENT_LOCATION || ''',''DD-MON-RR''),' || KNOWN_GANG_AFFILIATION || ',' || CASE WHEN NAME_OF_GANG Is Null THEN 'null,' ELSE '''' || NAME_OF_GANG || '''' || ',' END || CASE WHEN CITY_OF_APPREHENSION Is Null THEN 'null,' ELSE '''' || CITY_OF_APPREHENSION || '''' || ',' END || CASE WHEN STATE_OF_APPREHENSION Is Null THEN 'null,' ELSE '''' || STATE_OF_APPREHENSION || '''' || ',' END || CASE WHEN CITY_OF_CURRENT_LOCATION Is Null THEN 'null,' ELSE '''' || CITY_OF_CURRENT_LOCATION || '''' || ',' END || CASE WHEN STATE_OF_CURRENT_LOCATION Is Null THEN 'null,' ELSE '''' || STATE_OF_CURRENT_LOCATION || '''' || ',' END || CASE WHEN GANG_AFFILIATION_SUMMARY Is Null THEN 'null,' ELSE '''' || GANG_AFFILIATION_SUMMARY || '''' || ',' END || DETENTION_TYPE_OF_FACILITY || ',' || CASE WHEN FACILITY_NAME Is Null THEN 'null,' ELSE '''' || FACILITY_NAME || '''' || ',' END || CASE WHEN FACILITY_PHONE Is Null THEN 'null,' ELSE '''' || FACILITY_PHONE || '''' || ',' END || CASE WHEN FACILITY_POC Is Null THEN 'null,' ELSE '''' || FACILITY_POC || '''' || ',' END || CASE WHEN FACILITY_FAX Is Null THEN 'null,' ELSE '''' || FACILITY_FAX || '''' || ',' END || 'to_date(''' || FACILITY_ADMISSION_DATE || ''',''DD-MON-RR''),' || 'to_date(''' || FACILITY_DISCHARGED_DATE || ''',''DD-MON-RR''),' || CASE WHEN INCIDENT_RPT_IN_DC Is Null THEN 'null,' ELSE '''' || INCIDENT_RPT_IN_DC || '''' || ',' END || CASE WHEN SUM_OF_MM_HEALTH_CONDITION Is Null THEN 'null,' ELSE '''' || SUM_OF_MM_HEALTH_CONDITION || '''' || ',' END || 'to_date(''' || DATE_CANCELLED || ''',''DD-MON-RR''),' || CASE WHEN CANCELLED_BY Is Null THEN 'null,' ELSE '''' || CANCELLED_BY || '''' || ',' END || 'to_timestamp(''' || REFERRAL_DATE || ''',''DD-MON-RR HH.MI.SSXFF AM''),' || CASE WHEN FAMILYGROUP Is Null THEN 'null,' ELSE '''' || FAMILYGROUP || '''' || ',' END || FOOT_GUIDE || ',' || REVIEWED_BY_ORR || ',' || CASE WHEN CURRENT_LOCATION_OTHER Is Null THEN 'null,' ELSE '''' || CURRENT_LOCATION_OTHER || '''' || ',' END || RELATED_TO_OTHER_UC || ',' || CASE WHEN RELATIONSHIP_GRP_ID Is Null THEN 'null,' ELSE '''' || RELATIONSHIP_GRP_ID || '''' || ',' END || UAC_SEPARATED_FROM_PARENTS || ');' into backString_ FROM UAC_INFO WHERE UAC_ID = uacID_;
DELETE FROM UAC_INFO WHERE UAC_ID = uacID_;
INSERT INTO UACPORTAL.HD_TRANSACTION_LOG( USER_ID, TIME_STAMP, TRANS_TYPE, SP_NAME, TABLE_NAME, RECORD_ID, COLUMN_NAME, COLUMN_NEW_VALUE, COLUMN_OLD_VALUE ) VALUES ( userId_, SYSDATE, 'DELETE', 'SP_HD_DELETE_A_NUMBER', 'UAC_INFO', uacID_, null, null, backString_ );
COMMIT;
else errorMsg_ := 'The USER_ID is invalid!'; dbms_output.put_line(errorMsg_); RETURN; end if;
EXCEPTION WHEN OTHERS THEN --errorMsg_ := 'Unable to complete delete operation!'; errorMsg_ := 'Unable to complete Update operation! Error when updating DOB'; dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150)); -- Rollback everything which was made after `SAVEPOINT save1` ROLLBACK TO save1; RAISE;
END SP_HD_DELETE_A_NUMBER;

PROCEDURE SP_HD_DELETE_FAKE_KID 
(
  vALIEN_NO VARCHAR2,
  CUR_OUT OUT SYS_REFCURSOR
) AS 
vUAC_ID_ Number;
vLastName_ VARCHAR2(2000);
vReturnMsg_ VARCHAR2(2000);
n Number := 0;
BEGIN
	SELECT UAC_ID, LAST_NAME into vUAC_ID_,vLastName_  FROM UAC_INFO WHERE ALIEN_NO = vALIEN_NO;
	If UPPER(vLastName_) LIKE 'FAKE'  Then




			DELETE FROM UAC_SCHEDULED_HEARING WHERE UAC_ASSESSMENT_ID IN
			(
				SELECT UAC_ASSESSMENT_ID FROM UAC_ASSESSMENT WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
				)
			);

			DELETE FROM UAC_FAMILY_RELATIONS WHERE UAC_ASSESSMENT_ID IN
			(
				SELECT UAC_ASSESSMENT_ID FROM UAC_ASSESSMENT WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
				)
			);

			DELETE FROM UAC_ASSESSMENT WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
				WHERE UAC_ID IN (vUAC_ID_)
			);

			DELETE FROM uac_medical_concern WHERE initial_intakes_id IN
			(
				SELECT initial_intakes_id FROM UAC_INITIAL_ASSESSMENT WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
				)
			);

			DELETE FROM uac_emotional_concerns WHERE initial_intakes_id IN
			(
				SELECT initial_intakes_id FROM UAC_INITIAL_ASSESSMENT WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
				)
			);

			DELETE FROM UAC_MENTAL_ISSUES WHERE initial_intakes_id IN
			(
				SELECT initial_intakes_id FROM UAC_INITIAL_ASSESSMENT WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
				)
			);
			DELETE FROM UAC_FAMILY_INFO WHERE initial_intakes_id IN
			(
				SELECT initial_intakes_id FROM UAC_INITIAL_ASSESSMENT WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
				)
			);
			DELETE FROM UAC_IIA_SUMMARY WHERE initial_intakes_id IN
			(
				SELECT initial_intakes_id FROM UAC_INITIAL_ASSESSMENT WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (692145)
				)
			);
			DELETE FROM UAC_INITIAL_ASSESSMENT WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
				WHERE UAC_ID IN (vUAC_ID_)
			);



			DELETE FROM uac_ini_documents WHERE risk_assessment_id IN
			(
				SELECT risk_assessment_id FROM uac_risk_assessment WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
				)
			);
			DELETE FROM uac_risk_assessment WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			);



			DELETE FROM UAC_ISP_CERTIFICATE WHERE SERVICE_PLAN_ID IN 
			(
				SELECT SERVICE_PLAN_ID FROM uac_individual_service_plan WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
				)

			);

			DELETE FROM UAC_ISP_SUB WHERE SERVICE_PLAN_ID IN 
			(
				SELECT SERVICE_PLAN_ID FROM uac_individual_service_plan WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
				)

			);
			DELETE FROM uac_individual_service_plan WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			);



			DELETE FROM uac_case_review WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			);


			DELETE FROM UAC_EDUCATION_PLACEMENT WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			);



			DELETE FROM uac_documents WHERE  UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
