			DELETE FROM UAC_IMAGES WHERE  UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			);


			DELETE FROM uac_homestudy_case_referral WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			);



			DELETE FROM uac_post_release_service WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			);

			/******************************** DISCHARGE *********************************/



			DELETE FROM UAC_TRANSFER_REQUEST WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			);


			DELETE FROM UAC_RELEASE_REQUEST WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			); 



			DELETE FROM UAC_DISCHARGE_NOTIFICATION WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			); 



			DELETE FROM UAC_PROGRAM_EXIT WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
					WHERE UAC_ID IN (vUAC_ID_)
			); 


			/******************************* SIR ************************************/


			DELETE FROM UAC_SIR_CATEGORY_MAP WHERE SIR_ID IN
			(
				SELECT SIR_ID FROM UAC_SIR_NEW WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
				)
			);



			DELETE FROM UAC_SIR_FIELDS_HISTORY WHERE SIR_ID IN
			(
				SELECT SIR_ID FROM UAC_SIR_NEW WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
				)
			);


			DELETE FROM UAC_SIR_MEDIA_CONTACTS WHERE SIR_ID IN
			(
				SELECT SIR_ID FROM UAC_SIR_NEW WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
				)
			);


			DELETE FROM UAC_SIR_NOTIFICATION_LIST WHERE UAC_SIR_ID IN
			(
				SELECT UAC_SIR_ID FROM UAC_SIR_NEW WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
				)
			);



			DELETE FROM UAC_SIR_UCINVOLVED_LIST WHERE UAC_SIR_ID IN 
			(
				SELECT UAC_SIR_ID FROM UAC_SIR_NEW WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
				)
			);


			DELETE FROm UAC_SIR_DOCUMENTS WHERE UAC_SIR_ID IN 
			(
				SELECT UAC_SIR_ID FROM UAC_SIR_NEW WHERE UAC_PROGRAM_ID IN
				(
					SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
				)
			);


			DELETE FROM UAC_SIR_FOLLOWUP_RPT WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
			); 

			DELETE FROM UAC_SIR_NEW WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
			); 

			/******************************************************/


			DELETE FROM sponsor_background_check WHERE UAC_ID IN
			(
				vUAC_ID_
			);


			DELETE FROM uac_sponsor_list WHERE UAC_ID IN
			(
				vUAC_ID_
			);


			DELETE FROM UAC_MEDICAL_SCREENING WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
			); 


			DELETE FROM UAC_SECURE_REVIEW WHERE UAC_PROGRAM_ID IN
			(
				SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO
						WHERE UAC_ID IN (vUAC_ID_)
			); 



			DELETE FROM UAC_PROGRAM_INFO WHERE UAC_ID IN
			(
				vUAC_ID_
			);



			DELETE FROM UAC_INFO WHERE UAC_ID IN
			(
				vUAC_ID_
			);
			COMMIT;
			SELECT COUNT(*) into n FROM UAC_INFO WHERE ALIEN_NO = vALIEN_NO;
			if n > 0 THEN
				SELECT 'A# ' || vALIEN_NO || ' could not be deleted. Please check the reocrds for this A#' into vReturnMsg_ from dual;
			Else
				SELECT 'SUCCESS' into vReturnMsg_ from dual;
			End if;
	ELSE
		SELECT 'A# ' || vALIEN_NO || ' is not a FAKE kid. Record was not deleted' into vReturnMsg_ from dual;
	End if;

	if vReturnMsg_ = 'SUCCESS' THEN
		OPEN CUR_OUT For
			SELECT 'A# ' || vALIEN_NO || ' succesfully deleted' as RETURN_MESSAGE from dual;
	Else
		OPEN CUR_OUT For
			SELECT vReturnMsg_ as RETURN_MESSAGE, A.* FROM UAC_INFO A WHERE ALIEN_NO = vALIEN_NO;
	End If;
END SP_HD_DELETE_FAKE_KID;

PROCEDURE "SP_HD_DELETE_SIR_EVENT"
(
	v_EVENT_ID VARCHAR2, 
  v_USER_ID VARCHAR2
) AS 
oldValue_ VARCHAR2(255) := '';
eventID_  NUMBER(10) := 0;
errorMsg_  VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n         NUMBER := 0;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  eventID_ := v_EVENT_ID;
  if userId_ = '001' OR userId_ = '002' OR userId_ =  '007' Then

    -- +-------------------------------------------------------------+
			-- | Check if GENERAL_INFO_ID is valid                                |
			-- +-------------------------------------------------------------+
			BEGIN
			  sqlString_ := 'Select EVENT_ID FROM UAC_SIR_EVENT WHERE EVENT_ID = ' ||  v_EVENT_ID;
			  execute immediate sqlString_ into oldValue_;
			EXCEPTION
			  WHEN no_data_found
			  THEN
				eventID_ := 0;
				errorMsg_ := 'NO UAC_SIR_EVENT records were found for EVENT_ID: ' || v_EVENT_ID || '!';
				dbms_output.put_line(errorMsg_);
				RETURN;
			END;

      BEGIN
        Select count(*) into n from (
          SELECT 1 from UAC_SIR_NEW WHERE (ISDELETED = 0 OR ISDELETED is NUll) AND EVENT_ID = eventID_
          UNION
          Select 0 from dual
          );
        if n > 1 Then
          --SELECT 'Please delete the SIR''s using this EVENT_ID first.' as ERROR_MSSGE from dual;
          errorMsg_ := 'Please delete the SIR''s using this EVENT_ID first.';
          dbms_output.put_line(errorMsg_);
          RETURN;
        end if;
      END;


       -- +-------------------------------------------------------------+
			-- | BACKUP DATA, THEN DELETE                 |
			-- +-------------------------------------------------------------+
    BEGIN
      FOR ind IN 
        (SELECT EVENT_ID 
          FROm UAC_SIR_EVENT Where EVENT_ID =  eventID_)
      LOOP     
        SELECT 
        'Insert into UAC_SIR_EVENT (
          EVENT_ID,
          DATE_OF_EVENT,
          SYNOPSIS_OF_EVENT,
          DATE_CREATED,
          USER_CREATED,
          UPTIME,
          UPUSER,
          ISDELETED,
          TIME_OF_EVENT,PROGRAM_ID)
        Values(' 
			|| EVENT_ID || ',' || 
			'to_date(''' || DATE_OF_EVENT || ''',''DD-MON-RR''),' ||
          CASE WHEN SYNOPSIS_OF_EVENT Is Null THEN 'null,' ELSE '''' || SYNOPSIS_OF_EVENT || '''' || ',' END ||
          'to_date(''' || DATE_CREATED || ''',''DD-MON-RR''),' ||
          CASE WHEN USER_CREATED Is Null THEN 'null,' ELSE '''' || USER_CREATED || '''' || ',' END ||
          'to_timestamp(''' || UPTIME || ''',''DD-MON-RR HH.MI.SSXFF AM''),' ||
          CASE WHEN UPUSER Is Null THEN 'null,' ELSE '''' || UPUSER || '''' || ',' END ||
          CASE WHEN ISDELETED Is Null THEN 'null,' ELSE '''' || ISDELETED || '''' || ',' END ||
          CASE WHEN TIME_OF_EVENT Is Null THEN 'null,' ELSE '''' || TIME_OF_EVENT || '''' || ',' END ||
          PROGRAM_ID ||  ');' INTO backString_
        FROM UAC_SIR_EVENT
        Where EVENT_ID = ind.EVENT_ID;

        Delete From UAC_SIR_EVENT
        Where EVENT_ID = ind.EVENT_ID;

        INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
          USER_ID,
          TIME_STAMP,
          TRANS_TYPE,
          SP_NAME,
          TABLE_NAME,
          RECORD_ID,
          COLUMN_NAME,
          COLUMN_NEW_VALUE,
          COLUMN_OLD_VALUE
        )
        VALUES
        (
          userId_,
          SYSDATE,
          'DELETE',
          'SP_HD_DELETE_SIR_EVENT',
          'UAC_SIR_EVENT',
          eventID_,
          null,
          null,
          backString_
        );

    END LOOP;
    END;

    COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    --errorMsg_ := 'Unable to complete delete operation!';
    errorMsg_ := 'Unable to complete delete operation! Error when deleting ' || errorLocation_;
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_DELETE_SIR_EVENT;

PROCEDURE "SP_HD_ENROUTE" 
(
	v_UAC_PROG_ID VARCHAR2, 
    v_USER_ID VARCHAR2
) AS 
oldValue_   VARCHAR2(255) := '';
uacProgID_  VARCHAR2(10) := '';
errorMsg_   VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_     VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n           NUMBER := 0;
cnt         PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  uacProgID_ := v_UAC_PROG_ID;
  select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    -- +-------------------------------------------------------------+
    -- | Check if XXXX is valid
    -- +-------------------------------------------------------------+
    BEGIN
        sqlString_ := 'SELECT CURRENT_STATUS FROM UAC_PROGRAM_INFO WHERE UAC_PROGRAM_ID = :1';
        EXECUTE IMMEDIATE sqlString_ INTO oldValue_ USING uacProgID_;
    EXCEPTION
      WHEN no_data_found
      THEN
        uacProgID_ := '';
        errorMsg_ := 'NO UAC records were found for UAC_PROGRAM_ID: ' || v_UAC_PROG_ID || '!';
        dbms_output.put_line(errorMsg_);
        RETURN;
    END;

       -- +-------------------------------------------------------------+
        -- | BACKUP DATA, THEN XXXX  
        -- +-------------------------------------------------------------+
        UPDATE UACPORTAL.UAC_PROGRAM_INFO SET CURRENT_STATUS='ENROUTE' WHERE UAC_PROGRAM_ID = uacProgID_;

        -- Log the transaction
        INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
            USER_ID,
            TIME_STAMP,
            TRANS_TYPE,
            SP_NAME,
            TABLE_NAME,
            RECORD_ID,
            COLUMN_NAME,
            COLUMN_NEW_VALUE,
            COLUMN_OLD_VALUE
        )
        VALUES (
            userId_,
            SYSDATE,
            'UPDATE',
            'SP_HD_ENROUTE',
            'UAC_PROGRAM_INFO',
            uacProgID_,
            'CURRENT_STATUS',
            'ENROUTE',
            oldValue_
        );

        COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    errorMsg_ := 'Unable to complete SP_HD_ENROUTE operation! Error when updating CURRENT_STATUS';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_ENROUTE;

PROCEDURE "SP_HD_RECOVER_UAC_ASSESSMENT" 
(
    v_UAC_ASSESSMENT_ID VARCHAR2,
    v_USER_ID VARCHAR2
) AS 
oldValue_   VARCHAR2(255) := '';
errorMsg_   VARCHAR2(500) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_     VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n           NUMBER := 0;
cnt         PLS_INTEGER;
assessmentID_ NUMBER := 0;
userName    VARCHAR(255) := '';
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  assessmentID_ := v_UAC_ASSESSMENT_ID;
  select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    select TECH_NAME into userName from LIST_AIHD_TECHS where TECH_ID = userId_;
    -- +-------------------------------------------------------------+
    -- | Check if Referral is valid
    -- +-------------------------------------------------------------+
    BEGIN
        sqlString_ := 'SELECT UAC_ASSESSMENT_ID FROM UAC_ASSESSMENT WHERE UAC_ASSESSMENT_ID = :1';
        EXECUTE IMMEDIATE sqlString_ INTO oldValue_ USING assessmentID_;
    EXCEPTION
      WHEN no_data_found
      THEN
        assessmentID_ := '';
        errorMsg_ := 'NO UAC records were found for UAC_ASSESSMENT_ID: ' || assessmentID_ || '!';
        dbms_output.put_line(errorMsg_);
        RETURN;
    END;

       -- +-------------------------------------------------------------+
        -- | BACKUP DATA, THEN update  
        -- +-------------------------------------------------------------+
        if assessmentID_ > 0 THEN

           UPDATE UACPORTAL.UAC_ASSESSMENT
            SET ISDELETED='0', UPTIME = SYSDATE, UPUSER = userName
            WHERE UAC_ASSESSMENT_ID = assessmentID_;

            -- Log the transaction
            INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
                USER_ID,
                TIME_STAMP,
                TRANS_TYPE,
                SP_NAME,
                TABLE_NAME,
                RECORD_ID,
                COLUMN_NAME,
                COLUMN_NEW_VALUE,
                COLUMN_OLD_VALUE
            )
            VALUES (
                userId_,
                SYSDATE,
                'UPDATE',
                'SP_HD_RECOVER_UAC_ASSESSMENT',
                'UAC_ASSESSMENT',
                assessmentID_,
                'IS_DELETED',
                '1',
                '0'
            );

            COMMIT;
        ELSE
            errorMsg_ := 'The UAC_ASSESSMENT_ID is invalid!';
            dbms_output.put_line(errorMsg_);
            RETURN;      
        END IF;
  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    errorMsg_ := 'Unable to complete UAC Assessment Recovery operation! Error when updating IS_DELETED';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_RECOVER_UAC_ASSESSMENT;

PROCEDURE "SP_HD_REMOVE_HHM"
(
	v_OCC_ID VARCHAR2,	
	v_USER_ID VARCHAR2
) AS 
oldValue_ VARCHAR2(255) := '';
occID_  VARCHAR2(10) := '';
errorMsg_  VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n         NUMBER := 0;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  occID_ := v_OCC_ID;
  if userId_ = '001' OR userId_ = '002' OR userId_ =  '007' OR userId_ =  '003' Then

    -- +-------------------------------------------------------------+
			-- | Check if ALIEN_NO is valid                                |
			-- +-------------------------------------------------------------+
			BEGIN
			  sqlString_ := 'Select OCCUPANT_ID FROM SPONSOR_HOUSEHOLD_OCCUPANTS WHERE OCCUPANT_ID = ' ||  v_OCC_ID;
			  execute immediate sqlString_ into oldValue_;
			EXCEPTION
			  WHEN no_data_found
			  THEN
				occID_ := '';
				errorMsg_ := 'NO OCCUPANT records were found for OCCUPANT_ID: ' || v_OCC_ID || '!';
				dbms_output.put_line(errorMsg_);
				RETURN;
			END;


       -- +-------------------------------------------------------------+
			-- | BACKUP DATA, THEN DELETE                 |
			-- +-------------------------------------------------------------+

			SELECT 'Insert into SPONSOR_HOUSEHOLD_OCCUPANTS (OCCUPANT_ID,
				SPONSOR_ID,
				NAME_OF_OCCUPANT,
				AGE,
				RELATIONSHIP_TO_SPONSOR,
				RELATIONSHIP_TO_MINOR,
				DEPENDENT_OF_SPONSOR,
				USER_CREATED,
				DATE_CREATED,
				UPTIME,
				UPUSER,
				UAC_ID,
				FIRST_NAME,
				LAST_NAME,
				GENDER,
				DOB,
				QUERY_ID)
				Values (' ||
				OCCUPANT_ID || ',' ||
				SPONSOR_ID || ',' ||
				CASE WHEN NAME_OF_OCCUPANT Is Null THEN 'null,' ELSE '''' || NAME_OF_OCCUPANT || '''' || ',' END ||
				AGE || ',' ||
				RELATIONSHIP_TO_SPONSOR || ',' ||
				RELATIONSHIP_TO_MINOR || ',' ||
				DEPENDENT_OF_SPONSOR || ',' ||
				CASE WHEN USER_CREATED Is Null THEN 'null,' ELSE '''' || USER_CREATED || '''' || ',' END ||
				'to_date(''' || DATE_CREATED || ''',''DD-MON-RR''),' ||
				'to_timestamp(''' || UPTIME || ''',''DD-MON-RR HH.MI.SSXFF AM''),' ||
				CASE WHEN UPUSER Is Null THEN 'null,' ELSE '''' || UPUSER || '''' || ',' END ||
				UAC_ID || ',' ||
				CASE WHEN FIRST_NAME Is Null THEN 'null,' ELSE '''' || FIRST_NAME || '''' || ',' END ||
				CASE WHEN LAST_NAME Is Null THEN 'null,' ELSE '''' || LAST_NAME || '''' || ',' END ||
				CASE WHEN GENDER Is Null THEN 'null,' ELSE '''' || GENDER || '''' || ',' END ||
				'to_date(''' || DOB || ''',''DD-MON-RR''),' ||
				CASE WHEN QUERY_ID Is Null THEN 'null);' ELSE '''' || QUERY_ID || ''');' END
				into backString_
			FROM SPONSOR_HOUSEHOLD_OCCUPANTS
			WHERE OCCUPANT_ID = occID_;

			DELETE FROM SPONSOR_HOUSEHOLD_OCCUPANTS WHERE OCCUPANT_ID = occID_;

			INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
			  USER_ID,
			  TIME_STAMP,
			  TRANS_TYPE,
			  SP_NAME,
			  TABLE_NAME,
			  RECORD_ID,
			  COLUMN_NAME,
			  COLUMN_NEW_VALUE,
			  COLUMN_OLD_VALUE
			)
			VALUES
			(
			  userId_,
			  SYSDATE,
			  'DELETE',
			  'SP_HD_REMOVE_HHM',
			  'SPONSOR_HOUSEHOLD_OCCUPANTS',
			  occID_,
			  null,
			  null,
			  backString_
			);

    COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    --errorMsg_ := 'Unable to complete delete operation!';
    errorMsg_ := 'Unable to complete Update operation! Error when updating DOB';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_REMOVE_HHM;

PROCEDURE "SP_HD_UPDATE_DOB" 
(
	v_UAC_ID VARCHAR2, 
	v_DOB VARCHAR2, 
  v_USER_ID VARCHAR2
) AS 
oldValue_ VARCHAR2(255) := '';
uacID_  VARCHAR2(10) := '';
dob_ DATE := null;
errorMsg_  VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n         NUMBER := 0;
cnt PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  uacID_ := v_UAC_ID;
  select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    -- +-------------------------------------------------------------+
			-- | Check if ALIEN_NO is valid                                |
			-- +-------------------------------------------------------------+
			BEGIN
			  sqlString_ := 'Select TO_CHAR(DOB, ''DD-MON-RR'') FROM UAC_INFO WHERE UAC_ID = ' ||  v_UAC_ID;
			  execute immediate sqlString_ into oldValue_;
			EXCEPTION
			  WHEN no_data_found
			  THEN
				uacID_ := '';
				errorMsg_ := 'NO UAC records were found for UAC_ID: ' || v_UAC_ID || '!';
				dbms_output.put_line(errorMsg_);
				RETURN;
			END;

       -- +-------------------------------------------------------------+
			-- | BACKUP DATA, THEN DELETE                 |
			-- +-------------------------------------------------------------+
			if ISDATE(v_DOB) = 0 then
				errorMsg_ := 'Date format of DOB is incorrect!!';
				dbms_output.put_line(errorMsg_);
			ELSE

				Select to_date(v_DOB,'DD-MON-RR') INTO dob_ from dual;

				UPDATE UAC_INFO
				SET DOB = dob_, UPTIME=current_timestamp, UPUSER='Helpdesk'
				Where UAC_ID = uacID_;

				INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
				  USER_ID,
				  TIME_STAMP,
				  TRANS_TYPE,
				  SP_NAME,
				  TABLE_NAME,
				  RECORD_ID,
				  COLUMN_NAME,
				  COLUMN_NEW_VALUE,
				  COLUMN_OLD_VALUE
				)
				VALUES
				(
				  userId_,
				  SYSDATE,
				  'UPDATE',
				  'SP_HD_UPDATE_DOB',
				  'UAC_INFO',
				  uacID_,
				  'DOB',
				  TO_CHAR(dob_, 'DD-MON-RR'),
				  oldValue_
				);

			End if;
    COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    --errorMsg_ := 'Unable to complete delete operation!';
    errorMsg_ := 'Unable to complete Update operation! Error when updating DOB';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_UPDATE_DOB;

PROCEDURE "SP_HD_UPDATE_HS_REFERRAL_TO_ACTIVE" 
(
    v_REFERRAL_ID VARCHAR2,
    v_USER_ID VARCHAR2
) AS 
oldValue_   VARCHAR2(255) := '';
referralID_  VARCHAR2(100) := '';
errorMsg_   VARCHAR2(500) := '';
errorLocation_  VARCHAR2(50) := '';
