        if hsID_ > 0 THEN

            UPDATE UACPORTAL.UAC_HOMESTUDY_CASE_REFERRAL SET REFERRAL_STATUS = 1 WHERE REFERRAL_ID = referralID_;

            -- Log the transaction
            INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
                USER_ID,
                TIME_STAMP,
                TRANS_TYPE,
                SP_NAME,
                TABLE_NAME,
                RECORD_ID,
                COLUMN_NAME,
                COLUMN_NEW_VALUE,
                COLUMN_OLD_VALUE
            )
            VALUES (
                userId_,
