                '1',
                oldValue_
            );

            COMMIT;
        ELSE
            errorMsg_ := 'The REFERRAL_ID is invalid!';
            dbms_output.put_line(errorMsg_);
            RETURN;      
        END IF;
  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    errorMsg_ := 'Unable to complete SP_HD_UPDATE_HS_REFERRAL_TO_ACTIVE operation! Error when updating REFERRAL_STATUS';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
