PROCEDURE             "SP_HD_UPDATE_RISK_ASSESSMENT" 
(
	v_ALIEN_NUMBER VARCHAR2,
    v_USER_ID VARCHAR2
) AS 
oldValue_   VARCHAR2(255) := '';
alienNumber_ VARCHAR2(10) := '';
uacID_ VARCHAR2(25) := '';
uacProgID_ VARCHAR2(25) := '';
riskAssessmentID_ VARCHAR2(25) := '';
errorMsg_   VARCHAR2(500) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_     VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n           NUMBER := 0;
cnt         PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  alienNumber_ := v_ALIEN_NUMBER;
  select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    SELECT UAC_ID into uacID_ FROM UACPORTAL.UAC_INFO WHERE ALIEN_NO = alienNumber_;

    if uacID_ IS NOT  null Then
        SELECT UAC_PROGRAM_ID into uacProgID_ FROM UACPORTAL.UAC_PROGRAM_INFO WHERE UAC_ID  = uacID_;

        if uacProgID_ IS NOT null Then

            -- +-------------------------------------------------------------+
            -- | Check if XXXX is valid
            -- +-------------------------------------------------------------+
            BEGIN
                sqlString_ := 'SELECT RISK_ASSESSMENT_ID FROM UACPORTAL.UAC_RISK_ASSESSMENT  WHERE UAC_PROGRAM_ID = :1';
                EXECUTE IMMEDIATE sqlString_ INTO riskAssessmentID_ USING uacProgID_;
            EXCEPTION
              WHEN no_data_found
              THEN
                uacProgID_ := '';
                uacID_ := '';
                alienNumber_ := '';
                riskAssessmentID_ := '';

                errorMsg_ := 'NO RISK records were found for UAC_PROGRAM_ID: ' || uacProgID_ || '!';
                dbms_output.put_line(errorMsg_);
                RETURN;
            END;

            IF riskAssessmentID_ IS NOT null then
               -- +-------------------------------------------------------------+
                -- | BACKUP DATA, THEN XXXX  
                -- +-------------------------------------------------------------+
                UPDATE UACPORTAL.UAC_RISK_ASSESSMENT
                    SET ISDELETED = '1', UPTIME = SYSDATE, UPUSER = 'helpdesk'
                    WHERE RISK_ASSESSMENT_ID = riskAssessmentID_;

                -- Log the transaction
                INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
                    USER_ID,
                    TIME_STAMP,
                    TRANS_TYPE,
                    SP_NAME,
                    TABLE_NAME,
                    RECORD_ID,
                    COLUMN_NAME,
                    COLUMN_NEW_VALUE,
                    COLUMN_OLD_VALUE
                )
                VALUES (
                    userId_,
                    SYSDATE,
                    'UPDATE',
                    'SP_HD_UPDATE_RISK_ASSESSMENT',
                    'UAC_RISK_ASSESSMENT',
                    uacProgID_,
                    'ISDELETED',
                    '1',
                    '0'
                );

                COMMIT;

          else
            errorMsg_ := 'The RISK_ASSESSMENT_ID is invalid!';
            dbms_output.put_line(errorMsg_);
            RETURN;        
          end if;
        else
            errorMsg_ := 'The UAC_PROGRAM_ID is invalid!';
            dbms_output.put_line(errorMsg_);
            RETURN;     
        end if;
      end if;
    else
        errorMsg_ := 'The USER_ID is invalid!';
        dbms_output.put_line(errorMsg_);
        RETURN;

  end if;
EXCEPTION
  WHEN OTHERS THEN
    errorMsg_ := 'Unable to complete UPDATE_RISK_ASSESSMENT operation! Error when updating ISDELETED';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_UPDATE_RISK_ASSESSMENT;

PROCEDURE             "SP_HD_UPDATE_UNCERTIFIED_SPONSOR_ASSESSMENT" 
(
	v_UAC_ID VARCHAR2, 
    v_SPONSOR_ID VARCHAR2,
    v_USER_ID VARCHAR2
) AS 
oldValue_   VARCHAR2(255) := '';
uacID_  VARCHAR2(10) := '';
sponsorID_  VARCHAR2(10) := '';
errorMsg_   VARCHAR2(500) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_     VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n           NUMBER := 0;
cnt         PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  sponsorID_ := v_SPONSOR_ID;
  uacID_ := v_UAC_ID;
  select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    -- +-------------------------------------------------------------+
    -- | Check if XXXX is valid
    -- +-------------------------------------------------------------+
    BEGIN
        sqlString_ := 'SELECT DATE_COMPLETE FROM UAC_SPONSOR_LIST WHERE UAC_ID = :1 AND SPONSOR_ID = :2';
        EXECUTE IMMEDIATE sqlString_ INTO oldValue_ USING uacID_, sponsorID_;
    EXCEPTION
      WHEN no_data_found
      THEN
        uacID_ := '';
        sponsorID_ := '';
        errorMsg_ := 'NO UAC records were found for UAC_ID: ' || v_UAC_ID || '!';
        dbms_output.put_line(errorMsg_);
        RETURN;
    END;

       -- +-------------------------------------------------------------+
        -- | BACKUP DATA, THEN XXXX  
        -- +-------------------------------------------------------------+
        UPDATE UACPORTAL.UAC_SPONSOR_LIST set DATE_COMPLETE = null, UPTIME = SYSDATE, UPUSER = 'helpdesk'
        where UAC_ID = uacID_ and SPONSOR_ID = sponsorID_;

        -- Log the transaction
        INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
            USER_ID,
            TIME_STAMP,
            TRANS_TYPE,
            SP_NAME,
            TABLE_NAME,
            RECORD_ID,
            COLUMN_NAME,
            COLUMN_NEW_VALUE,
            COLUMN_OLD_VALUE
        )
        VALUES (
            userId_,
            SYSDATE,
            'UPDATE',
            'SP_HD_UPDATE_UNCERTIFIED_SPONSOR_ASSESSMENT',
            'UAC_SPONSOR_LIST',
            uacID_,
            'DATE_COMPLETE',
            'null',
            oldValue_
        );

        COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    errorMsg_ := 'Unable to complete UPDATE_UNCERTIFIED_SPONSOR_ASSESSMENT operation! Error when updating XXXX';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_UPDATE_UNCERTIFIED_SPONSOR_ASSESSMENT;

PROCEDURE "SP_HD_UPD_ADMITTED_DATE" 
(
	v_UAC_PROG_ID VARCHAR2, 
	v_ADMIT_DATE VARCHAR2, 
  v_USER_ID VARCHAR2
) AS 
oldValue_ VARCHAR2(255) := '';
uacProgID_  VARCHAR2(10) := '';
admitDate_ DATE := null;
errorMsg_  VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n         NUMBER := 0;
cnt PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  uacProgID_ := v_UAC_PROG_ID;
  select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    -- +-------------------------------------------------------------+
			-- | Check if ALIEN_NO is valid                                |
			-- +-------------------------------------------------------------+
			BEGIN
			  sqlString_ := 'Select DATE_ADMITTED FROM UAC_PROGRAM_INFO WHERE UAC_PROGRAM_ID = ' ||  v_UAC_PROG_ID;
			  execute immediate sqlString_ into oldValue_;
			EXCEPTION
			  WHEN no_data_found
			  THEN
				uacProgID_ := '';
				errorMsg_ := 'NO UAC_PROGRAM_INFO records were found for UAC_PROGRAM_ID: ' || v_UAC_PROG_ID || '!';
				dbms_output.put_line(errorMsg_);
				RETURN;
			END;

       -- +-------------------------------------------------------------+
			-- | BACKUP DATA, THEN DELETE                 |
			-- +-------------------------------------------------------------+
			if ISDATE(v_ADMIT_DATE) = 0 then
				errorMsg_ := 'Date format of DATE_ADMITTED DATE is incorrect!!';
				dbms_output.put_line(errorMsg_);
			ELSE

				Select to_date(v_ADMIT_DATE,'DD-MON-RR') INTO admitDate_ from dual;

				UPDATE UAC_PROGRAM_INFO
				SET DATE_ADMITTED = admitDate_, UPTIME=current_timestamp, UPUSER='Helpdesk'
				Where UAC_PROGRAM_ID = uacProgID_;

				INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
				  USER_ID,
				  TIME_STAMP,
				  TRANS_TYPE,
				  SP_NAME,
				  TABLE_NAME,
				  RECORD_ID,
				  COLUMN_NAME,
				  COLUMN_NEW_VALUE,
				  COLUMN_OLD_VALUE
				)
				VALUES
				(
				  userId_,
				  SYSDATE,
				  'UPDATE',
				  'SP_HD_UPD_ADMITTED_DATE',
				  'UAC_PROGRAM_INFO',
				  uacProgID_,
				  'DATE_ADMITTED',
				  TO_CHAR(admitDate_, 'DD-MON-RR'),
				  oldValue_
				);

			End if;
    COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    --errorMsg_ := 'Unable to complete delete operation!';
    errorMsg_ := 'Unable to complete Update operation! Error when updating DOB';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_UPD_ADMITTED_DATE;

PROCEDURE "SP_HD_UPD_CUR_STATUS_DSCHRG" 
(
	v_UAC_PROG_ID VARCHAR2, 
	v_USER_ID VARCHAR2
) AS 
oldValue_ VARCHAR2(255) := '';
uacProgID_  VARCHAR2(10) := '';
uacID_  VARCHAR2(10) := '';
admitDate_ DATE := null;
errorMsg_  VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n         NUMBER := 0;
cnt PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  uacProgID_ := v_UAC_PROG_ID;
  select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    -- +-------------------------------------------------------------+
			-- | Check if ALIEN_NO is valid                                |
			-- +-------------------------------------------------------------+
			BEGIN
			  sqlString_ := 'Select CURRENT_STATUS FROM UAC_PROGRAM_INFO WHERE UAC_PROGRAM_ID = ' ||  v_UAC_PROG_ID;
			  execute immediate sqlString_ into oldValue_;
			EXCEPTION
			  WHEN no_data_found
