			UPDATE UAC_PROGRAM_INFO SET CURRENT_STATUS = 'DISCHARGED', UPTIME=current_timestamp, UPUSER='Helpdesk' WHERE UAC_PROGRAM_ID = uacProgID_;

			INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
			  USER_ID,
			  TIME_STAMP,
			  TRANS_TYPE,
			  SP_NAME,
			  TABLE_NAME,
			  RECORD_ID,
			  COLUMN_NAME,
			  COLUMN_NEW_VALUE,
			  COLUMN_OLD_VALUE
			)
			VALUES
			(
			  userId_,
			  SYSDATE,
			  'UPDATE',
			  'SP_HD_UPD_CUR_STATUS_DSCHRG',
			  'UAC_INFO',
			  uacProgID_,
			  'CURRENT_STATUS',
			  'DISCHARGED',
			  oldValue_
			);



    COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    --errorMsg_ := 'Unable to complete delete operation!';
    errorMsg_ := 'Unable to complete Update operation! Error when updating DOB';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_UPD_CUR_STATUS_DSCHRG;

PROCEDURE "SP_HD_UPD_DISCHRG_DATE" 
(
	v_UAC_PROG_ID VARCHAR2, 
	v_DIS_DATE VARCHAR2, 
  v_USER_ID VARCHAR2
) AS 
oldValue_ VARCHAR2(255) := '';
uacProgID_  VARCHAR2(10) := '';
disDate_ DATE := null;
errorMsg_  VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n         NUMBER := 0;
cnt PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  uacProgID_ := v_UAC_PROG_ID;
   select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    -- +-------------------------------------------------------------+
			-- | Check if ALIEN_NO is valid                                |
			-- +-------------------------------------------------------------+
			BEGIN
			  sqlString_ := 'Select ''UAC_PROGRAM_INFO.DATE_DISCHARGED= '' || TO_CHAR(A.DATE_DISCHARGED, ''DD-MON-RR'') || '', UAC_PROGRAM_EXIT.DATE_OF_DISCHARGE= '' || TO_CHAR(B.DATE_OF_DISCHARGE, ''DD-MON-RR'') FROM UAC_PROGRAM_INFO A INNER JOIN UAC_PROGRAM_EXIT B ON A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID WHERE A.UAC_PROGRAM_ID = ' ||  v_UAC_PROG_ID;
			  execute immediate sqlString_ into backString_;
			EXCEPTION
			  WHEN no_data_found
			  THEN
				uacProgID_ := '';
				errorMsg_ := 'NO UAC records were found for UAC_PROGRAM_ID: ' || v_UAC_PROG_ID || '!';
				dbms_output.put_line(errorMsg_);
				RETURN;
			END;

       -- +-------------------------------------------------------------+
			-- | BACKUP DATA, THEN DELETE                 |
			-- +-------------------------------------------------------------+
			if ISDATE(v_DIS_DATE) = 0 then
				errorMsg_ := 'Date format of DISCHARGE DATE is incorrect!!';
				dbms_output.put_line(errorMsg_);
			ELSE

				Select to_date(v_DIS_DATE,'DD-MON-RR') INTO disDate_ from dual;

				UPDATE UAC_PROGRAM_INFO
				SET DATE_DISCHARGED = disDate_, UPTIME=current_timestamp, UPUSER='Helpdesk'
				Where UAC_PROGRAM_ID = uacProgID_;

				UPDATE UAC_PROGRAM_EXIT
				SET DATE_OF_DISCHARGE = disDate_, UPTIME=current_timestamp, UPUSER='Helpdesk'
				Where UAC_PROGRAM_ID = uacProgID_;

				INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
				  USER_ID,
				  TIME_STAMP,
				  TRANS_TYPE,
				  SP_NAME,
				  TABLE_NAME,
				  RECORD_ID,
				  COLUMN_NAME,
				  COLUMN_NEW_VALUE,
				  COLUMN_OLD_VALUE
				)
				VALUES
				(
				  userId_,
				  SYSDATE,
				  'UPDATE',
				  'SP_HD_UPD_DISCHRG_DATE',
				  'UAC_PROGRAM_INFO, UAC_PROGRAM_EXIT',
				  uacProgID_,
				  'DATE_DISCHARGED, DATE_OF_DISCHARGE',
				  TO_CHAR(disDate_, 'DD-MON-RR'),
				  backString_
				);

			End if;
    COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    --errorMsg_ := 'Unable to complete delete operation!';
    errorMsg_ := 'Unable to complete Update operation! Error when updating DOB';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_UPD_DISCHRG_DATE;

PROCEDURE "SP_HD_UPD_DISCHRG_TYPE" 
(
	v_UAC_PROG_ID VARCHAR2, 
	v_DIS_TYPE NUMBER, 
  v_USER_ID VARCHAR2
) AS 
oldValue_ VARCHAR2(255) := '';
uacProgID_  VARCHAR2(10) := '';
dischrgType_ Number := 0;
errorMsg_  VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n         NUMBER := 0;
cnt PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  uacProgID_ := v_UAC_PROG_ID;
  dischrgType_ := v_DIS_TYPE;
   select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    -- +-------------------------------------------------------------+
			-- | Check if UAC_PROGRAM_ID is valid                                |
			-- +-------------------------------------------------------------+
			BEGIN
			  sqlString_ := 'Select TYPE_OF_DISCHARGE FROM UAC_PROGRAM_EXIT WHERE ROWNUM = 1 AND UAC_PROGRAM_ID = ' ||  v_UAC_PROG_ID;
			  execute immediate sqlString_ into oldValue_;
			EXCEPTION
			  WHEN no_data_found
			  THEN
				uacProgID_ := '';
				errorMsg_ := 'NO UAC_PROGRAM_EXIT records were found for UAC_PROGRAM_ID: ' || v_UAC_PROG_ID || '!';
				dbms_output.put_line(errorMsg_);
				RETURN;
			END;

			BEGIN
				Select count(*) into n from (
				  SELECT 1 from UAC_PROGRAM_EXIT WHERE UAC_PROGRAM_ID = uacProgID_
				  UNION
				  Select 0 from dual
				  );
				if n > 2 Then
				  --SELECT 'Please delete the SIR''s using this EVENT_ID first.' as ERROR_MSSGE from dual;
				  errorMsg_ := 'There are multiple UAC_PROGRAM_EXITs with this UAC_PROGRAM_ID.';
				  dbms_output.put_line(errorMsg_);
				  RETURN;
				end if;
			  END;

       -- +-------------------------------------------------------------+
			-- | BACKUP DATA, THEN DELETE                 |
			-- +-------------------------------------------------------------+
			BEGIN
				Select count(*) into n from (
				  SELECT 1 from LIST_DISCHARGE_TYPE WHERE DISCHARGE_TYPE_ID = dischrgType_
				  UNION
				  Select 0 from dual
				  );
				if n < 1 then
					errorMsg_ := 'The DISCHARGE_TYPE_ID does not exist!!';
					dbms_output.put_line(errorMsg_);
				ELSE

					UPDATE UAC_PROGRAM_EXIT
					SET TYPE_OF_DISCHARGE = dischrgType_, UPTIME=current_timestamp, UPUSER='Helpdesk'
					Where UAC_PROGRAM_ID = uacProgID_;

					INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
					  USER_ID,
					  TIME_STAMP,
					  TRANS_TYPE,
					  SP_NAME,
					  TABLE_NAME,
					  RECORD_ID,
					  COLUMN_NAME,
					  COLUMN_NEW_VALUE,
					  COLUMN_OLD_VALUE
					)
					VALUES
					(
					  userId_,
					  SYSDATE,
					  'UPDATE',
					  'SP_HD_UPD_DISCHRG_TYPE',
					  'UAC_PROGRAM_INFO',
					  uacProgID_,
					  'TYPE_OF_DISCHARGE',
					  dischrgType_,
					  oldValue_
					);

				End if;
			COMMIT;
			END;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    --errorMsg_ := 'Unable to complete delete operation!';
    errorMsg_ := 'Unable to complete Update operation! Error when updating DOB';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_UPD_DISCHRG_TYPE;

PROCEDURE             "SP_HD_UPD_RE_ADMIT_MINOR"
(
	v_UAC_PROG_ID VARCHAR2,
	v_USER_ID VARCHAR2
) AS
oldValue_ VARCHAR2(255) := '';
uacProgID_  VARCHAR2(10) := '';
uacID_  VARCHAR2(10) := '';
progID_  VARCHAR2(10) := '';
childLCTNHIST_ID_ VARCHAR2(10) := '';
admitDate_ DATE := null;
errorMsg_  VARCHAR2(100) := '';
errorLocation_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
backString_ VARCHAR2(32767) := '';
n         NUMBER := 0;
cnt PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  userId_ := v_USER_ID;
  uacProgID_ := v_UAC_PROG_ID;
   select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then

    -- +-------------------------------------------------------------+
			-- | Check if ALIEN_NO is valid                                |
			-- +-------------------------------------------------------------+
			BEGIN
			  sqlString_ := 'Select CURRENT_STATUS FROM UAC_PROGRAM_INFO WHERE UAC_PROGRAM_ID = ' ||  v_UAC_PROG_ID;
			  execute immediate sqlString_ into oldValue_;
			EXCEPTION
			  WHEN no_data_found
			  THEN
				uacProgID_ := '';
				errorMsg_ := 'NO UAC_PROGRAM_INFO records were found for UAC_PROGRAM_ID: ' || v_UAC_PROG_ID || '!';
				dbms_output.put_line(errorMsg_);
				RETURN;
			END;

			BEGIN
				Select count(*) into n from (
				  SELECT 1 from UAC_PROGRAM_EXIT WHERE UAC_PROGRAM_ID = uacProgID_
				  UNION
				  Select 0 from dual
				  );
				if n > 2 Then
				  errorMsg_ := 'There are multiple UAC_PROGRAM_EXITs with this UAC_PROGRAM_ID.';
				  dbms_output.put_line(errorMsg_);
				  RETURN;
				end if;
			  END;

			-- +-------------------------------------------------------------+
			-- | BACKUP DATA, THEN DELETE                 |
			-- +-------------------------------------------------------------+



			if UPPER(oldValue_) != 'DISCHARGED' then
				errorMsg_ := 'Cannot Re-Admiit UAC because status is not DISCHARGED. Status is currently: ' || oldValue_;
				dbms_output.put_line(errorMsg_);
			Else
				SELECT UAC_ID, PROGRAM_ID into uacID_, progID_ FROM UAC_PROGRAM_INFO WHERE ROWNUM = 1 AND UAC_PROGRAM_ID = uacProgID_;

				SELECT UAC_STATUS into oldValue_ from UAC_INFO WHERE UAC_ID = uacID_;

				UPDATE UAC_INFO SET UAC_STATUS = 'ADMITTED' WHERE UAC_ID = uacID_;

				INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
				  USER_ID,
				  TIME_STAMP,
				  TRANS_TYPE,
				  SP_NAME,
				  TABLE_NAME,
				  RECORD_ID,
				  COLUMN_NAME,
				  COLUMN_NEW_VALUE,
				  COLUMN_OLD_VALUE
				)
				VALUES
				(
				  userId_,
				  SYSDATE,
				  'UPDATE',
				  'SP_HD_UPD_RE_ADMIT_MINOR',
				  'UAC_INFO',
				  uacID_,
				  'UAC_STATUS',
				  'ADMITTED',
				  oldValue_
				);

				SELECT 'DATE_DISCHARGED=' || TO_CHAR(DATE_DISCHARGED, 'DD-MON-RR') || ', CURRENT_STATUS=''' || CURRENT_STATUS || '''' into oldValue_ FROM UAC_PROGRAM_INFO WHERE UAC_PROGRAM_ID = uacProgID_;

				UPDATE UAC_PROGRAM_INFO SET DATE_DISCHARGED=NULL, CURRENT_STATUS='ADMITTED', UPTIME=current_timestamp, UPUSER='Helpdesk' WHERE UAC_PROGRAM_ID = uacProgID_;

				INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
				  USER_ID,
				  TIME_STAMP,
				  TRANS_TYPE,
				  SP_NAME,
				  TABLE_NAME,
				  RECORD_ID,
				  COLUMN_NAME,
				  COLUMN_NEW_VALUE,
				  COLUMN_OLD_VALUE
				)
				VALUES
				(
				  userId_,
				  SYSDATE,
				  'UPDATE',
				  'SP_HD_UPD_RE_ADMIT_MINOR',
				  'UAC_PROGRAM_INFO',
				  uacID_,
				  'DATE_DISCHARGED, CURRENT_STATUS',
				  'Null, ADMITTED',
				  oldValue_
				);

				SELECT '
					Insert into UAC_PROGRAM_EXIT (
					PROGRAM_EXIT_ID,
					UAC_PROGRAM_ID,
					DATE_OF_DISCHARGE,
					TYPE_OF_DISCHARGE,
					DATE_CREATED,
					USER_CREATED,
					UPTIME,
					UPUSER)
					values (' ||
					PROGRAM_EXIT_ID || ',' ||
					UAC_PROGRAM_ID || ',' ||
					'to_date(''' || DATE_OF_DISCHARGE || ''',''DD-MON-RR''),' ||
					TYPE_OF_DISCHARGE || ',' ||
					'to_date(''' || DATE_CREATED || ''',''DD-MON-RR''),' ||
					CASE WHEN USER_CREATED Is Null THEN 'null,' ELSE '''' || USER_CREATED || '''' || ',' END ||
					'to_timestamp(''' || UPTIME || ''',''DD-MON-RR HH.MI.SSXFF AM''),' ||
					CASE WHEN UPUSER Is Null THEN 'null);' ELSE '''' || UPUSER || ''');' END
					into backString_
				FROM UAC_PROGRAM_EXIT
				WHERE UAC_PROGRAM_ID = uacProgID_;


				DELETE FROM UAC_PROGRAM_EXIT WHERE UAC_PROGRAM_ID = uacProgID_;

				INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
				  USER_ID,
				  TIME_STAMP,
				  TRANS_TYPE,
				  SP_NAME,
				  TABLE_NAME,
				  RECORD_ID,
				  COLUMN_NAME,
				  COLUMN_NEW_VALUE,
				  COLUMN_OLD_VALUE
				)
				VALUES
				(
				  userId_,
				  SYSDATE,
				  'DELETE',
				  'SP_HD_UPD_RE_ADMIT_MINOR',
				  'UAC_PROGRAM_EXIT',
				  uacProgID_,
				  null,
				  null,
				  backString_
				);

				-- Get latest Child Location History for the program that UC is being re-admitted to.
				SELECT max(CHILD_LCTN_HIST_ID) into childLCTNHIST_ID_ FROM UAC_CHILD_LOCATION_HIST WHERE UAC_ID = uacID_ AND PROGRAM_ID = progID_ AND ISDELETED = 0;

				UPDATE UAC_CHILD_LOCATION_HIST SET ISDELETED = 1 WHERE CHILD_LCTN_HIST_ID = childLCTNHIST_ID_;

				INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
				  USER_ID,
				  TIME_STAMP,
				  TRANS_TYPE,
				  SP_NAME,
				  TABLE_NAME,
				  RECORD_ID,
				  COLUMN_NAME,
				  COLUMN_NEW_VALUE,
				  COLUMN_OLD_VALUE
				)
				VALUES
				(
				  userId_,
				  SYSDATE,
				  'DELETE',
				  'SP_HD_UPD_RE_ADMIT_MINOR',
				  'UAC_CHILD_LOCATION_HIST',
				  childLCTNHIST_ID_,
				  'ISDELETED',
				  '1',
				  'UPDATE UAC_CHILD_LOCATION_HIST SET ISDELETED = 1 WHERE CHILD_LCTN_HIST_ID = ' || childLCTNHIST_ID_
				);

				-- Get the next most recent program from the CHild Location History.
				SELECT max(CHILD_LCTN_HIST_ID) into childLCTNHIST_ID_ FROM UAC_CHILD_LOCATION_HIST 
				WHERE UAC_ID = uacID_ AND PROGRAM_ID is not null AND ISDELETED = 0; 

				UPDATE UAC_CHILD_LOCATION_HIST SET ISDELETED = 1 WHERE CHILD_LCTN_HIST_ID > childLCTNHIST_ID_ AND UAC_ID = uacID_ AND (ISDELETED = 0 OR ISDELETED is null);

				INSERT INTO UACPORTAL.HD_TRANSACTION_LOG(
				  USER_ID,
				  TIME_STAMP,
				  TRANS_TYPE,
				  SP_NAME,
				  TABLE_NAME,
				  RECORD_ID,
				  COLUMN_NAME,
				  COLUMN_NEW_VALUE,
				  COLUMN_OLD_VALUE
				)
				VALUES
				(
				  userId_,
				  SYSDATE,
				  'DELETE',
				  'SP_HD_UPD_RE_ADMIT_MINOR',
				  'UAC_CHILD_LOCATION_HIST',
				  childLCTNHIST_ID_,
				  'ISDELETED',
				  '1',
				  'UPDATE UAC_CHILD_LOCATION_HIST SET ISDELETED = 1 WHERE CHILD_LCTN_HIST_ID > ' || childLCTNHIST_ID_ || ' AND UAC_ID = ' || uacID_ || ' AND (ISDELETED = 0 OR ISDELETED is null);' 
				);

			End if;
    COMMIT;

  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;

EXCEPTION
  WHEN OTHERS THEN
    --errorMsg_ := 'Unable to complete delete operation!';
    errorMsg_ := 'Unable to complete Update operation! Error when updating DOB';
    dbms_output.put_line(errorMsg_ || '. ' || SUBSTR(SQLERRM, 1, 150));
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;

END SP_HD_UPD_RE_ADMIT_MINOR;

PROCEDURE             "SP_HD_UPD_SPONSOR"
(
	v_SPONSOR_ID VARCHAR2,
  v_COLUMN_NAME VARCHAR2,
  v_VALUE VARCHAR2,
  v_USER_ID VARCHAR2
) AS
columnName_  VARCHAR2(50) := '';
oldValue_ VARCHAR2(255) := '';
sponsorId_  NUMBER(10) := 0;
errorMsg_  VARCHAR2(50) := '';
sqlString_ VARCHAR2(500) := '';
userId_ VARCHAR2(255) := '';
cnt PLS_INTEGER;
BEGIN

  SAVEPOINT save1;

  -- +-------------------------------------------------------------+
  -- | Check if v_USER_ID is valid                                 |
  -- +-------------------------------------------------------------+
  userId_ := v_USER_ID;
  select nvl(count(*), 0) into cnt from LIST_AIHD_TECHS where TECH_ID = userId_;

  if cnt > 0 Then
        -- +-------------------------------------------------------------+
        -- | Check if COLUMN_NAME is valid                               |
        -- +-------------------------------------------------------------+
        BEGIN
          Select COLUMN_NAME into columnName_ FROM all_tab_columns WHERE UPPER(TABLE_NAME) = 'SPONSOR_INFO' AND UPPER(COLUMN_NAME) = UPPER(v_COLUMN_NAME);
        EXCEPTION
          WHEN no_data_found
          THEN
            --columnName_ := '';
            errorMsg_ := 'Column name: ' || v_COLUMN_NAME || ' is incorrect!';
            dbms_output.put_line(errorMsg_);
            RETURN;
        END;

        -- +-------------------------------------------------------------+
        -- | Check if SPONSOR_ID is valid                                |
        -- +-------------------------------------------------------------+
        BEGIN
          sqlString_ := 'Select ' || v_COLUMN_NAME ||  ' FROM SPONSOR_INFO WHERE SPONSOR_ID = ' ||  v_SPONSOR_ID;
          execute immediate sqlString_ into oldValue_;
        EXCEPTION
          WHEN no_data_found
          THEN
            columnName_ := '';
            sponsorId_ := 0;
            errorMsg_ := 'NO Sponsor found for SPONSOR_ID: ' || v_SPONSOR_ID || '!';
            dbms_output.put_line(errorMsg_);
            RETURN;
        END;

        if columnName_ = '' then
          errorMsg_ := 'Something went wrong!';
          dbms_output.put_line(errorMsg_);
        Else
          -- +-------------------------------------------------------------+
          -- | Run the update                                              |
          -- +-------------------------------------------------------------+
          sqlString_ := 'update SPONSOR_INFO set ' || columnName_
                      || ' = ''' || v_VALUE 
                      || ''', UPTIME = current_timestamp '
                      || ', UPUSER = ''Helpdesk'''
                      || ' where SPONSOR_ID = ' || v_SPONSOR_ID;
          execute immediate sqlString_;



          -- +-------------------------------------------------------------+
          -- | LOG TRANSACTION IN HD_TRANSACTION_LOG TABLE                 |
          -- +-------------------------------------------------------------+
          INSERT INTO HD_TRANSACTION_LOG(
              USER_ID,
              TIME_STAMP,
              TRANS_TYPE,
              SP_NAME,
              TABLE_NAME,
              RECORD_ID,
              COLUMN_NAME,
              COLUMN_NEW_VALUE,
              COLUMN_OLD_VALUE
            )
            VALUES
            (
              userId_,
              SYSDATE,
              'UPDATE',
              'SP_HD_UPD_SPONSOR',
              'SPONSOR_INFO',
              v_SPONSOR_ID,
              columnName_,
              v_VALUE,
              oldValue_
            );
            --commit;
        end if;
  else
    errorMsg_ := 'The USER_ID is invalid!';
    dbms_output.put_line(errorMsg_);
    RETURN;
  end if;
EXCEPTION
  WHEN OTHERS THEN
    -- Rollback everything which was made after `SAVEPOINT save1`
    ROLLBACK TO save1;
    RAISE;
END SP_HD_UPD_SPONSOR;

PROCEDURE "SP_INFLUX_RPT" AS BEGIN Delete from TT_INFLUX_RPT; Insert Into TT_INFLUX_RPT ("UAC_ID" , "First Name", "Last Name", "Alian No", "Gender", "DOB", "Age at Admitted", "UAC Status", "Date Admitted", "Program Name", "UAC Initial Assessment Date", "UAC Assessment Date", "UAC Risk Assessment Date", "ISP Date", "Sponsor Category") Select DISTINCT A.UAC_ID, A.First_Name, A.Last_Name, A.Alien_No,A.GENDER, A.DOB, floor(Months_Between(B.DATE_ADMITTED,A.DOB)/12) as "Age at Admitted", A.UAC_STATUS, B.DATE_ADMITTED, G.PROGRAM_NAME, TO_CHAR(D.UPTIME, 'mm/dd/yyyy') as INITIAL_ASSESSMENT_DATE, TO_CHAR(E.UPTIME, 'mm/dd/yyyy') as UAC_ASSESSMENT_DATE, TO_CHAR(F.UPTIME, 'mm/dd/yyyy') as UAC_RISK_ASSESSMENT_DATE, TO_CHAR(H.ORIENTATION_END_DATE, 'mm/dd/yyyy') as ISP_DATE, M.SPONSOR_CATEGORY as "Sponsor Category" From UAC_INFO A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID And A.UAC_STATUS = B.CURRENT_STATUS Left Join UAC_TRANSFER_REQUEST C On B.UAC_PROGRAM_ID = C.UAC_PROGRAM_ID And nvl(C.ISDELETED,0) = 0 Left Join (Select UAC_PROGRAM_ID, MAX(UPTIME) as UPTIME From UAC_INITIAL_ASSESSMENT Where nvl(ISDELETED,0) = 0 GROUP BY UAC_PROGRAM_ID) D On B.UAC_PROGRAM_ID = D.UAC_PROGRAM_ID Left Join (Select UAC_PROGRAM_ID, MAX(UPTIME) as UPTIME From UAC_ASSESSMENT Where nvl(ISDELETED,0) = 0 GROUP BY UAC_PROGRAM_ID) E On B.UAC_PROGRAM_ID = E.UAC_PROGRAM_ID Left Join (Select UAC_PROGRAM_ID, MAX(UPTIME) as UPTIME From UAC_RISK_ASSESSMENT Where nvl(ISDELETED,0) = 0 GROUP BY UAC_PROGRAM_ID) F On B.UAC_PROGRAM_ID = F.UAC_PROGRAM_ID Left Join ( Select UAC_PROGRAM_ID, ORIENTATION_END_DATE From UAC_INDIVIDUAL_SERVICE_PLAN A Where nvl(ISDELETED,0) = 0 And UPTIME = (Select Max(UPTIME) From UAC_INDIVIDUAL_SERVICE_PLAN Where nvl(ISDELETED,0) = 0 And UAC_PROGRAM_ID = A.UAC_PROGRAM_ID) ) H On B.UAC_PROGRAM_ID = H.UAC_PROGRAM_ID Inner Join LIST_PROGRAMS G On B.PROGRAM_ID = G.PROGRAM_ID Inner Join UAC_RELEASE_REQUEST L On L.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID And nvl(L.ISDELETED,0) = 0 Left Join (Select A.UAC_ID, B.SPONSOR_CATEGORY From UAC_SPONSOR_LIST A Inner Join LIST_RELATIONSHIP B On A.SPONSOR_RELATIONSHIP_TO_UAC = B.RELATIONSHIP_ID Where A.CURRENT_SPONSOR = 1) M On A.UAC_ID = M.UAC_ID Where A.UAC_STATUS in ('ADMITTED', 'IN-TRANFER') And A.DOB between Add_Months(Sysdate,-204) and Add_Months(sysdate, -156) And G.PROGRAM_TYPE_ID = 5 And G.PROGRAM_ID <> 244 And L.CC_RECOMMENDATION <> 6 And Exists(Select 1 from UAC_SPONSOR_LIST Where UAC_ID = A.UAC_ID And CURRENT_SPONSOR=1) Order by A.First_Name, A.Last_Name, A.Alien_No, A.DOB, A.UAC_STATUS;
-- Date Evaludated, CXR, and PPD/TST or IGRA UPDATE TT_INFLUX_RPT RPT SET ("Date Evaluated", "Chest X-Ray Findings","PPD/TST or IGRA Test Result") = ( Select Distinct A.DATE_EVALUATED, Q.TEST_RESULT as "Chest X-Ray Findings", Case When (nvl(N.PPD_NEGATIVECOUNT, 0) > 0 And nvl(N.PPD_NOTNETAGIVECOUNT,0) = 0) And (nvl(P.IGRA_NegativeCOUNT, 0) > 0 And nvl(P.IGRA_NotNegativeCount,0) = 0) Then 'Negative' When (nvl(N.PPD_NEGATIVECOUNT, 0) = 0 And nvl(N.PPD_NOTNETAGIVECOUNT,0) = 0) And (nvl(P.IGRA_NegativeCount,0) > 0 And nvl(P.IGRA_NotNegativeCount,0) = 0) Then 'Negative' When (nvl(N.PPD_NEGATIVECOUNT,0) > 0 And nvl(N.PPD_NOTNETAGIVECOUNT,0) = 0) And (nvl(P.IGRA_NegativeCount,0) = 0 And nvl(P.IGRA_NotNegativeCount, 0) = 0) Then 'Negative' When (nvl(N.PPD_NEGATIVECOUNT,0) = 0 And nvl(N.PPD_NOTNETAGIVECOUNT,0) = 0) And (nvl(P.IGRA_NegativeCount,0) = 0 And nvl(P.IGRA_NotNegativeCount, 0) = 0) Then '' End as "PPD/TST or IGRA Test Result" From UAC_HEALTH.HEALTH_GENERAL_INFO A INNER JOIN UAC_INFO UC On A.UAC_ID = UC.UAC_ID Left Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.GENERAL_INFO_ID = B.GENERAL_INFO_ID Left Join ( Select C.UAC_ID, Sum(Case When A.INTERPRETATION = 4 Then 1 Else 0 End) as PPD_NegativeCount, Sum(Case When A.INTERPRETATION <> 4 THEN 1 Else 0 End) as PPD_NOTNETAGIVECOUNT from UAC_HEALTH.UAC_HEALTH_TST A Inner Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.HEALTH_TB_SCREENING_ID = B.HEALTH_TB_SCREENING_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO C on B.GENERAL_INFO_ID = C.GENERAL_INFO_ID Group By C.UAC_ID ) N On UC.UAC_ID = N.UAC_ID Left Join ( Select C.UAC_ID, sum(Case when A.RESULT = 4 then 1 else 0 end) as IGRA_NegativeCount , sum(case when A.Result <> 4 then 1 else 0 end) as IGRA_NotNegativeCount from UAC_HEALTH.UAC_HEALTH_IGRA A Inner Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.HEALTH_TB_SCREENING_ID = B.HEALTH_TB_SCREENING_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO C On B.GENERAL_INFO_ID = C.GENERAL_INFO_ID group by C.UAC_ID ) P On UC.UAC_ID = P.UAC_ID Left Join ( Select Distinct C.UAC_ID, E.DESCRIPTION as TEST_RESULT from UAC_HEALTH.UAC_HEALTH_CXR A Inner Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.HEALTH_TB_SCREENING_ID = B.HEALTH_TB_SCREENING_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO C On B.GENERAL_INFO_ID = C.GENERAL_INFO_ID Inner Join UAC_INFO D On D.UAC_ID = C.UAC_ID Inner Join UAC_HEALTH.LIST_TEST_RESULT E On A.FINDINGS = E.RESULT_ID Where D.DOB between Add_Months(Sysdate,-204) and Add_Months(sysdate, -180) And A.DATE_UPDATED = (Select max(DATE_UPDATED) from UAC_HEALTH.UAC_HEALTH_CXR Where HEALTH_TB_SCREENING_ID = A.HEALTH_TB_SCREENING_ID) ) Q On UC.UAC_ID = Q.UAC_ID Where Date_EVALUATED is not null And nvl(INITIAL_EXAM,0) = 1 And UC.UAC_ID = RPT.UAC_ID And UC.ALIEN_NO = RPT."Alian No" ) Where Exists ( Select 1 From UAC_HEALTH.HEALTH_GENERAL_INFO A INNER JOIN UAC_INFO UC On A.UAC_ID = UC.UAC_ID Left Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.GENERAL_INFO_ID = B.GENERAL_INFO_ID Left Join ( Select C.UAC_ID, Sum(Case When A.INTERPRETATION = 4 Then 1 Else 0 End) as PPD_NegativeCount, Sum(Case When A.INTERPRETATION <> 4 THEN 1 Else 0 End) as PPD_NOTNETAGIVECOUNT from UAC_HEALTH.UAC_HEALTH_TST A Inner Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.HEALTH_TB_SCREENING_ID = B.HEALTH_TB_SCREENING_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO C on B.GENERAL_INFO_ID = C.GENERAL_INFO_ID Group By C.UAC_ID ) N On UC.UAC_ID = N.UAC_ID Left Join ( Select C.UAC_ID, sum(Case when A.RESULT = 4 then 1 else 0 end) as IGRA_NegativeCount , sum(case when A.Result <> 4 then 1 else 0 end) as IGRA_NotNegativeCount from UAC_HEALTH.UAC_HEALTH_IGRA A Inner Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.HEALTH_TB_SCREENING_ID = B.HEALTH_TB_SCREENING_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO C On B.GENERAL_INFO_ID = C.GENERAL_INFO_ID group by C.UAC_ID ) P On UC.UAC_ID = P.UAC_ID Left Join ( Select Distinct C.UAC_ID, E.DESCRIPTION as TEST_RESULT from UAC_HEALTH.UAC_HEALTH_CXR A Inner Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.HEALTH_TB_SCREENING_ID = B.HEALTH_TB_SCREENING_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO C On B.GENERAL_INFO_ID = C.GENERAL_INFO_ID Inner Join UAC_INFO D On D.UAC_ID = C.UAC_ID Inner Join UAC_HEALTH.LIST_TEST_RESULT E On A.FINDINGS = E.RESULT_ID Where D.DOB between Add_Months(Sysdate,-204) and Add_Months(sysdate, -180) And A.DATE_UPDATED = (Select max(DATE_UPDATED) from UAC_HEALTH.UAC_HEALTH_CXR Where HEALTH_TB_SCREENING_ID = A.HEALTH_TB_SCREENING_ID) ) Q On UC.UAC_ID = Q.UAC_ID Where Date_EVALUATED is not null And nvl(INITIAL_EXAM,0) = 1 And UC.UAC_ID = RPT.UAC_ID And UC.ALIEN_NO = RPT."Alian No" );
-- Pregnancy Test and HIV Test UPDATE TT_INFLUX_RPT RPT SET ("Pregnancy Test Result", "HIV Test Result") = ( Select "Pregnancy Test Result", "HIV Test Result" From ( Select DISTINCT A.UAC_ID, CASE WHEN INSTR(M.TEST_IDS, '18', 1,1) > 0 Then 'Negative' Else Case When UC.GENDER = 'F' Or UPPER(UC.GENDER)='FEMALE' Then '' Else 'N/A' End End as "Pregnancy Test Result", CASE WHEN INSTR(M.TEST_IDS, '17', 1,1) > 0 Then 'Negative' Else '' End as "HIV Test Result" From UAC_HEALTH.HEALTH_GENERAL_INFO A INNER JOIN UAC_INFO UC On A.UAC_ID = UC.UAC_ID INNER Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.GENERAL_INFO_ID = B.GENERAL_INFO_ID INNER Join ( Select GENERAL_INFO_ID, LISTAGG(TEST_ID, ',') WITHIN GROUP(ORDER BY TEST_ID) as TEST_IDS from UAC_HEALTH.HEALTH_LAB_TEST Where TEST_ID in (17,18) and TEST_RESULT_ID = 4 Group By GENERAL_INFO_ID ) M On A.GENERAL_INFO_ID = M.GENERAL_INFO_ID Where Date_EVALUATED is not null order by A.UAC_ID) A Where A.UAC_ID = RPT.UAC_ID ) Where Exists (Select 1 From ( Select DISTINCT A.UAC_ID, CASE WHEN INSTR(M.TEST_IDS, '18', 1,1) > 0 Then 'Negative' Else Case When UC.GENDER = 'F' Or UPPER(UC.GENDER)='FEMALE' Then '' Else 'N/A' End End as "Pregnancy Test Result", CASE WHEN INSTR(M.TEST_IDS, '17', 1,1) > 0 Then 'Negative' Else '' End as "HIV Test Result" From UAC_HEALTH.HEALTH_GENERAL_INFO A INNER JOIN UAC_INFO UC On A.UAC_ID = UC.UAC_ID INNER Join UAC_HEALTH.HEALTH_TB_SCREENING B On A.GENERAL_INFO_ID = B.GENERAL_INFO_ID INNER Join ( Select GENERAL_INFO_ID, LISTAGG(TEST_ID, ',') WITHIN GROUP(ORDER BY TEST_ID) as TEST_IDS from UAC_HEALTH.HEALTH_LAB_TEST Where TEST_ID in (17,18) and TEST_RESULT_ID = 4 Group By GENERAL_INFO_ID ) M On A.GENERAL_INFO_ID = M.GENERAL_INFO_ID Where Date_EVALUATED is not null order by A.UAC_ID) A Where A.UAC_ID = RPT.UAC_ID );
-- IMMUNIZATION
-- Tdap/Td UPDATE TT_INFLUX_RPT RPT SET ("TDap/Td Administered", "TDap/Td Administered Date") = ( Select 'YES', DATES From ( Select Distinct F.UAC_ID, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 2 And Date_EVALUATED is not null Group by F.UAC_ID ) N Where N.UAC_ID = RPT.UAC_ID ) Where Exists (Select 1 From ( Select Distinct F.UAC_ID, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 2 And Date_EVALUATED is not null Group by F.UAC_ID ) N Where N.UAC_ID = RPT.UAC_ID );
