--'IPV (Polio virus)' UPDATE TT_INFLUX_RPT RPT SET ("IPV Administered", "IPV Administered Date") = ( Select 'YES', DATES From ( Select Distinct F.UAC_ID, E.DESCRIPTION AS IMMUNIZATION, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 6 Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID ) Where exists (Select 1 From ( Select Distinct F.UAC_ID, E.DESCRIPTION AS IMMUNIZATION, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 6 Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID );
--'MMR (measles, mumps, rubella)' UPDATE TT_INFLUX_RPT RPT SET ("MMR Administered", "MMR Administered Date") = ( Select 'YES', DATES From ( Select Distinct F.UAC_ID, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 7 And Date_EVALUATED is not null Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID ) Where Exists (Select 1 From ( Select Distinct F.UAC_ID, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 7 And Date_EVALUATED is not null Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID );
-- 'Meningococcal' UPDATE TT_INFLUX_RPT RPT SET ("Meningococcal Administered", "Meningococcal Admin. Date") = ( Select 'YES', DATES From ( Select Distinct F.UAC_ID, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 12 And Date_EVALUATED is not null Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID ) Where Exists ( Select 1 From ( Select Distinct F.UAC_ID, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 12 And Date_EVALUATED is not null Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID );
--'HPV2/4 (Human Papilloma virus)' UPDATE TT_INFLUX_RPT RPT SET ("HPV Administered", "HPV Administered Date") = ( Select 'YES', DATES From ( Select Distinct F.UAC_ID, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 13 And Date_EVALUATED is not null Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID ) Where Exists (Select 1 From ( Select Distinct F.UAC_ID, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 13 And Date_EVALUATED is not null Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID );
--'Influenza' UPDATE TT_INFLUX_RPT RPT SET ("Flu Administered", "Flu Administered Date") = ( Select 'YES', DATES From ( Select Distinct F.UAC_ID, E.DESCRIPTION AS IMMUNIZATION, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 5 And Date_EVALUATED is not null Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID ) Where Exists (Select 1 From ( Select Distinct F.UAC_ID, E.DESCRIPTION AS IMMUNIZATION, LISTAGG(D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.IMMUNIZATION_DATE) as Dates From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 5 And Date_EVALUATED is not null Group by F.UAC_ID, E.DESCRIPTION ) N Where N.UAC_ID = RPT.UAC_ID );
-- Other UPDATE TT_INFLUX_RPT RPT SET ("Other Vaccine Administered", "Other Vaccine Name(s)/Date(s)") = ( Select 'YES', "Other Vaccine" From ( Select Distinct F.UAC_ID, LISTAGG(D.VACCINE_OTHER_EXP || ' - ' || D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.VACCINE_OTHER_EXP) as "Other Vaccine" From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 16 And Date_EVALUATED is not null Group by F.UAC_ID ) N Where N.UAC_ID = RPT.UAC_ID ) Where Exists (Select 1 From ( Select Distinct F.UAC_ID, LISTAGG(D.VACCINE_OTHER_EXP || ' - ' || D.IMMUNIZATION_DATE, ', ') WITHIN GROUP (ORDER BY D.VACCINE_OTHER_EXP) as "Other Vaccine" From UAC_HEALTH.HEALTH_ASSESSMENT A Inner Join UAC_HEALTH.UAC_HEALTH_IMMUNIZATION D On A.HEALTH_ASSESSMENT_ID = D.HEALTH_ASSESSMENT_ID Inner Join UAC_HEALTH.LIST_HEALTH_IMMUNIZATION E On D.VACCINE_ID = E.IMMUNIZATION_ID Inner Join UAC_HEALTH.HEALTH_GENERAL_INFO F On A.GENERAL_INFO_ID = F.GENERAL_INFO_ID Where E.IMMUNIZATION_ID = 16 And Date_EVALUATED is not null Group by F.UAC_ID ) N Where N.UAC_ID = RPT.UAC_ID );
-- Clean up data Delete from TT_INFLUX_RPT A Where Not Exists(Select 1 from UAC_SPONSOR_LIST Where UAC_ID = A.UAC_ID And CURRENT_SPONSOR=1) Or "PPD/TST or IGRA Test Result" is null Or "HIV Test Result" is null Or "Pregnancy Test Result" is null Or ("Chest X-Ray Findings" != 'Normal' And "Chest X-Ray Findings" is not null) Or "Date Evaluated" is null;
END SP_INFLUX_RPT;

PROCEDURE sp_InsertFlagComment (
    p_SponsorFlagId IN NUMBER,
    p_FlagComment IN VARCHAR2,
    p_UserName IN VARCHAR2,
    p_FullName IN VARCHAR2,
    p_UserEmail IN VARCHAR2
) IS
BEGIN
    INSERT INTO SponsorFlagComment (SPONSORFLAGID, FLAGCOMMENT, USERCREATED, DATECREATED, CREATEDBY, CREATEDBYEMAIL)
    VALUES (p_SponsorFlagId, p_FlagComment, p_UserName, SYSDATE, p_FullName, p_UserEmail);
    COMMIT;
END sp_InsertFlagComment;

PROCEDURE sp_InsertSponsorFlagCategories (
    p_SponsorFlagId IN NUMBER,
    p_CategoryIds IN VARCHAR2
) IS
    TYPE CategoryIdsArray IS TABLE OF NUMBER INDEX BY PLS_INTEGER;
    v_category_ids CategoryIdsArray;
    v_pos NUMBER := 1;
    v_comma_pos NUMBER;
    v_id VARCHAR2(10);
BEGIN
    -- Populate the array with category IDs
    LOOP
        v_comma_pos := INSTR(p_CategoryIds, ',', v_pos);
        IF v_comma_pos > 0 THEN
            v_id := SUBSTR(p_CategoryIds, v_pos, v_comma_pos - v_pos);
            v_pos := v_comma_pos + 1;
        ELSE
            v_id := SUBSTR(p_CategoryIds, v_pos);
            v_pos := LENGTH(p_CategoryIds) + 1;
        END IF;

        IF TRIM(v_id) IS NOT NULL THEN
            v_category_ids(v_category_ids.COUNT + 1) := TO_NUMBER(TRIM(v_id));
        END IF;

        EXIT WHEN v_pos > LENGTH(p_CategoryIds);
    END LOOP;

    -- Bulk insert using FORALL
    FORALL i IN 1..v_category_ids.COUNT
        INSERT INTO SponsorFlagCategory (SponsorFlagId, FlagCategoryId)
        VALUES (p_SponsorFlagId, v_category_ids(i));

    COMMIT;
END sp_InsertSponsorFlagCategories;

PROCEDURE             "SP_INSERT_CCR_HISTORY" 
(
  RELEASE_ID IN NUMBER 
, ORG_CCR_ID IN NUMBER 
, ORG_CCR IN VARCHAR2 
, CCR_ID IN NUMBER 
, CCR IN VARCHAR2 
, ORG_CCR_AHS_ID  IN NUMBER
, ORG_CCR_AHS   IN VARCHAR2
, CCR_AHS_ID    IN NUMBER
, CCR_AHS   IN VARCHAR2
, UPTIME IN DATE 
, UPUSER IN VARCHAR2 
) AS 
rr_Id NUMBER := RELEASE_ID;
org_ccr_id_ NUMBER(2) := ORG_CCR_ID;
org_ccr_    VARCHAR2(200) := ORG_CCR;
ccr_id_ NUMBer(2) := CCR_ID;
ccr_    VARCHAR2(200) := CCR;
org_ccr_ahs_id_ NUMBER(2) := ORG_CCR_AHS_ID;
org_ccr_ahs_    VARCHAR2(200) := ORG_CCR_AHS;
ccr_ahs_id_ NUMBer(2) := CCR_AHS_ID;
ccr_ahs_    VARCHAR2(200) := CCR_AHS;
uptime_ TIMESTAMP(6) := UPTIME;
upuser_ VARCHAR2(200) := UPUSER;
BEGIN
    Insert Into UAC_RR_CCR_HISTORY (ID, RELEASE_ID, ORG_CCR_ID, ORG_CCR, CCR_ID, CCR, ORG_CCR_AHS_ID, ORG_CCR_AHS, CCR_AHS_ID, CCR_AHS, UPTIME, UPUSER) 
    Select nvl(max(ID),0) + 1, rr_Id, org_ccr_id_, org_ccr_, ccr_id_, ccr_, org_ccr_ahs_id_, org_ccr_ahs_, ccr_ahs_id_, ccr_ahs_, uptime_, upuser_ from UAC_RR_CCR_HISTORY;

END SP_INSERT_CCR_HISTORY;

PROCEDURE             "SP_INSERT_CMR_HISTORY" 
(
  RELEASE_ID IN NUMBER 
, ORG_CMR_ID IN NUMBER 
, ORG_CMR IN VARCHAR2 
, CMR_ID IN NUMBER 
, CMR IN VARCHAR2 
, ORG_CMR_AHS_ID  IN NUMBER
, ORG_CMR_AHS   IN VARCHAR2
, CMR_AHS_ID    IN NUMBER
, CMR_AHS   IN VARCHAR2
, UPTIME IN DATE 
, UPUSER IN VARCHAR2 
) AS 
rr_Id NUMBER := RELEASE_ID;
org_cmr_id_ NUMBER(2) := ORG_CMR_ID;
org_cmr_    VARCHAR2(200) := ORG_CMR;
cmr_id_ NUMBer(2) := CMR_ID;
cmr_    VARCHAR2(200) := CMR;
org_cmr_ahs_id_ NUMBER(2) := ORG_CMR_AHS_ID;
org_cmr_ahs_    VARCHAR2(200) := ORG_CMR_AHS;
cmr_ahs_id_ NUMBer(2) := CMR_AHS_ID;
cmr_ahs_    VARCHAR2(200) := CMR_AHS;
uptime_ TIMESTAMP(6) := UPTIME;
upuser_ VARCHAR2(200) := UPUSER;
BEGIN
    Insert Into UAC_RR_CMR_HISTORY (ID, RELEASE_ID, ORG_CMR_ID, ORG_CMR, CMR_ID, CMR, ORG_CMR_AHS_ID, ORG_CMR_AHS, CMR_AHS_ID, CMR_AHS, UPTIME, UPUSER) 
    Select nvl(max(ID),0) + 1, rr_Id, org_cmr_id_, org_cmr_, cmr_id_, cmr_, org_cmr_ahs_id_, org_cmr_ahs_, cmr_ahs_id_, cmr_ahs_, uptime_, upuser_ from UAC_RR_CMR_HISTORY;

END SP_INSERT_CMR_HISTORY;

PROCEDURE "SP_INSERT_CURRENT_LOCATION" ( vUAC_ID IN NUMBER, vCHILD_LOCATION_TYPE IN NUMBER, vLIVING_WITH_SPONSOR IN NUMBER, vLIVING_WITH_CAREGIVER IN NUMBER, vADDRESS_KNOWN IN NUMBER, vPRIMARY_CAREGIVER_TYPE IN NUMBER, vPRIMARY_CAREGIVER_OTHER IN VARCHAR2 := null, vLOCATION_ADDRESS IN VARCHAR2, vLOCATION_ADDRESS_SECONDARY IN VARCHAR2 := null, vLOCATION_CITY IN VARCHAR2, vLOCATION_STATE IN VARCHAR2, vLOCATION_ZIPCODE IN VARCHAR2, vLOCATION_COUNTRY IN NUMBER, vLOCATION_COUNTY IN VARCHAR2, vLOCATION_LATITUDE IN VARCHAR2, vLOCATION_LONGITUDE IN VARCHAR2, vSMARTY_STREET_VALIDATION IN VARCHAR2, vCAREGIVER_FIRST_NAME IN VARCHAR2, vCAREGIVER_LAST_NAME IN VARCHAR2, vCAREGIVER_PHONE IN VARCHAR2, vNOTES IN VARCHAR2 := null, vUSER IN VARCHAR2, vREASON_FOR_UPDATE_ID IN NUMBER ) AS uacId_ NUMBER(10,0) := vUAC_ID; childLCTNHIST_Id NUMBER(10,0); uacProgId_ NUMBER(10,0); programExitID_ NUMBER(10,0); dischargeID_ NUMBER(4); livingWithSponsor_ NUMBER(4); sponsorName_ VARCHAR2(500); address_ VARCHAR2(2000); secondaryAddress_ VARCHAR2(500); city_ VARCHAR2(2000); state_ VARCHAR2(4); zipCode_ VARCHAR2(10); country_ NUMBER(4); dateLastUpdated timestamp; userupdated VARCHAR2(112); n NUMBER(4); BEGIN --Check if there is a UAC_CHILD_LOCATION_HIST record with select count(*) into n from ( Select 1 from UAC_CHILD_LOCATION_HIST WHERE UAC_ID = uacId_ AND IS_CURRENT = 1 AND ISDELETED = 0 Union Select 0 from dual ); if n > 1 then SELECT LIVING_WITH_SPONSOR, DATE_LAST_UPDATED, USER_LAST_UPDATED into livingWithSponsor_, dateLastUpdated, userupdated from UAC_CHILD_LOCATION_HIST WHERE UAC_ID = uacId_ AND IS_CURRENT = 1 AND ISDELETED = 0; -- If Current Location is Living with Sponsor, update it with Sponsor address details before moving it into Location History if livingWithSponsor_ = 1 then UPDATE UAC_CHILD_LOCATION_HIST SET IS_CURRENT = 0, ISDELETED = 1 WHERE UAC_ID = uacId_ AND IS_CURRENT = 1 AND ISDELETED = 0 AND LIVING_WITH_SPONSOR = 1;
INSERT INTO UAC_CHILD_LOCATION_HIST (UAC_ID, LOCATION_TYPE_ID, LOCATION_TYPE, LOCATION_NAME, LOCATION_ADDRESS, LOCATION_ADDRESS_SECONDARY, LOCATION_CITY, LOCATION_STATE, LOCATION_ZIPCODE, LOCATION_COUNTRY, LOCATION_COUNTY, LOCATION_LATITUDE, LOCATION_LONGITUDE, SMARTY_STREET_VALIDATION, DATE_LAST_UPDATED, USER_LAST_UPDATED, IS_CURRENT, CHILD_LOCATION_TYPE, LIVING_WITH_SPONSOR, REASON_FOR_UPDATE_ID ) SELECT uacId_, 3, 'Sponsor', E.FIRST_NAME || ' ' || E.LAST_NAME, D.SPONSOR_ADDRESS, D.SPONSOR_ADDRESS_SECONDARY, D.SPONSOR_CITY, D.SPONSOR_STATE, D.SPONSOR_ZIPCODE, (SELECT LC.COUNTRY_ID FROM LIST_COUNTRIES LC WHERE E.COR = LC.COUNTRY_NAME), D.SPONSOR_COUNTY, D.SPONSOR_LATITUDE, D.SPONSOR_LONGITUDE, D.SMARTY_STREET_VALIDATION, --CASE WHEN SATH.UPDATE_DATE IS NULL THEN D.UPTIME ELSE SATH.UPDATE_DATE END, --CASE WHEN SATH.UPDATE_DATE IS NULL THEN D.UPUSER ELSE SATH.UPUSER END, --dateLastUpdated, --userupdated, CASE WHEN SATH.UPDATE_DATE IS NULL THEN CASE WHEN D.UPTIME > dateLastUpdated THEN D.UPTIME ELSE dateLastUpdated END ELSE CASE WHEN SATH.UPDATE_DATE > dateLastUpdated THEN SATH.UPDATE_DATE ELSE dateLastUpdated END END, CASE WHEN SATH.UPDATE_DATE IS NULL THEN CASE WHEN D.UPTIME > dateLastUpdated THEN D.UPUSER ELSE userupdated END ELSE CASE WHEN SATH.UPDATE_DATE > dateLastUpdated THEN SATH.UPUSER ELSE userupdated END END, 0, 1, 1, vREASON_FOR_UPDATE_ID FROM UAC_SPONSOR_LIST D LEFT JOIN SPONSOR_INFO E On D.SPONSOR_ID = E.SPONSOR_ID and d.is_deleted = 0 and e.is_deleted = 0 LEFT JOIN SPONSOR_ASSESS_TAB_HISTORY SATH ON D.UAC_ID = SATH.UAC_ID AND D.SPONSOR_ID = SATH.SPONSOR_ID AND SATH.ASSESSMENT_TAB = 'ContactInformation' LEFT JOIN ADM_LIST_WEB_USER U On UPPER(D.UPUSER) = UPPER(U.USER_ID) LEFT JOIN ADM_LIST_WEB_USER U1 On UPPER(SATH.UPUSER) = UPPER(U1.USER_ID) WHERE D.UAC_ID = uacId_ AND D.CURRENT_SPONSOR = 1;
/*UPDATE UAC_CHILD_LOCATION_HIST SET LOCATION_TYPE_ID = 3, LOCATION_TYPE = 'Sponsor', LOCATION_NAME = sponsorName_, LOCATION_ADDRESS = address_, LOCATION_ADDRESS_SECONDARY = secondaryAddress_, LOCATION_CITY = city_, LOCATION_STATE = state_, LOCATION_ZIPCODE = zipCode_, LOCATION_COUNTRY = country_, DATE_LAST_UPDATED = dateLastUpdated, USER_LAST_UPDATED = userupdated, IS_CURRENT = 0 WHERE UAC_ID = uacId_ AND IS_CURRENT = 1 AND ISDELETED = 0 AND LIVING_WITH_SPONSOR = 1;*/ ELSE UPDATE UAC_CHILD_LOCATION_HIST SET IS_CURRENT = 0 WHERE UAC_ID = uacId_ AND IS_CURRENT = 1 AND ISDELETED = 0; End If; Else --If there is no Current Record in UAC_CHILD_LOCATION_HIST --Move Current Location into UAC_CHILD_LOCATION_HIST SELECT max(UAC_PROGRAM_ID) into uacProgId_ FROM UAC_PROGRAM_INFO WHERE UAC_ID = uacId_ AND CURRENT_STATUS <> 'ENROUTE'; select count(*) into n from ( Select 1 from UAC_PROGRAM_EXIT Where UAC_PROGRAM_ID = uacProgId_ Union Select 0 from dual ); if n > 1 then SELECT PROGRAM_EXIT_ID, TYPE_OF_DISCHARGE into programExitID_, dischargeID_ FROM UAC_PROGRAM_EXIT WHERE UAC_PROGRAM_ID = uacProgId_ ORDER BY PROGRAM_EXIT_ID DESC FETCH first 1 row only;
-- Discharged to Program/Facility if dischargeID_ = 1 then INSERT INTO UAC_CHILD_LOCATION_HIST( UAC_ID, DISCHARGE_TYPE_ID, LOCATION_TYPE_ID, LOCATION_TYPE, LOCATION_NAME, LOCATION_ADDRESS, LOCATION_ADDRESS_SECONDARY, LOCATION_CITY, LOCATION_STATE, LOCATION_ZIPCODE, DATE_LAST_UPDATED, USER_LAST_UPDATED, IS_CURRENT, REASON_FOR_UPDATE_ID ) SELECT uacId_, DT.DISCHARGE_TYPE_ID, F.LOCATION_TYPE_ID, F.LOCATION_TYPE, RR.PROGRAM_NAME, RR.PROGRAM_ADDRESS1, RR.PROGRAM_ADDRESS2, RR.PROGRAM_CITY, RR.PROGRAM_STATE, RR.PROGRAM_ZIPCODE, PE.UPTIME, PE.UPUSER, 0, vREASON_FOR_UPDATE_ID FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_PROGRAM_EXIT PE On B.UAC_PROGRAM_ID = PE.UAC_PROGRAM_ID INNER JOIN LIST_DISCHARGE_TYPE DT On PE.TYPE_OF_DISCHARGE = DT.DISCHARGE_TYPE_ID INNER JOIN LIST_DISCHARGE_TYPE_CHILD_LOCATION E ON DT.DISCHARGE_TYPE_ID = E.DISCHARGE_TYPE_ID INNER JOIN LIST_CHILD_LOCATION_TYPE F ON E.LOCATION_TYPE_ID = F.LOCATION_TYPE_ID INNER JOIN UACPORTAL.UAC_RELEASE_REQUEST RR On RR.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID INNER JOIN (SELECT RR1.UAC_PROGRAM_ID, MAX(RR1.RELEASE_ID) as MAX_RR_ID FROM UACPORTAL.UAC_RELEASE_REQUEST RR1 WHERE (RR1.ISDELETED = 0 OR RR1.ISDELETED Is NUll) AND RR1.UAC_PROGRAM_ID = uacProgId_ GROUP BY RR1.UAC_PROGRAM_ID ) Z On RR.RELEASE_ID = Z.MAX_RR_ID LEFT JOIN ADM_LIST_WEB_USER U On UPPER(PE.UPUSER) = UPPER(U.USER_ID) WHERE B.UAC_PROGRAM_ID = uacProgId_ AND PE.PROGRAM_EXIT_ID = programExitID_; ELSIF dischargeID_ in (2,4,7,8,13,14,15,16) then INSERT INTO UAC_CHILD_LOCATION_HIST( UAC_ID, DISCHARGE_TYPE_ID, LOCATION_TYPE_ID, LOCATION_TYPE, LOCATION_NAME, LOCATION_ADDRESS, DATE_LAST_UPDATED, USER_LAST_UPDATED, IS_CURRENT, REASON_FOR_UPDATE_ID ) SELECT uacId_, DT.DISCHARGE_TYPE_ID, F.LOCATION_TYPE_ID, F.LOCATION_TYPE, F.LOCATION, F.LOCATION, PE.UPTIME, PE.UPUSER, 0, vREASON_FOR_UPDATE_ID FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_PROGRAM_EXIT PE On B.UAC_PROGRAM_ID = PE.UAC_PROGRAM_ID INNER JOIN LIST_DISCHARGE_TYPE DT On PE.TYPE_OF_DISCHARGE = DT.DISCHARGE_TYPE_ID INNER JOIN LIST_DISCHARGE_TYPE_CHILD_LOCATION E ON DT.DISCHARGE_TYPE_ID = E.DISCHARGE_TYPE_ID INNER JOIN LIST_CHILD_LOCATION_TYPE F ON E.LOCATION_TYPE_ID = F.LOCATION_TYPE_ID WHERE B.UAC_PROGRAM_ID = uacProgId_ AND PE.PROGRAM_EXIT_ID = programExitID_; -- Reunification (Individual Sponsor) ELSIF dischargeID_ = 3 then INSERT INTO UAC_CHILD_LOCATION_HIST( UAC_ID, DISCHARGE_TYPE_ID, LOCATION_TYPE_ID, LOCATION_TYPE, LOCATION_NAME, LOCATION_ADDRESS, LOCATION_ADDRESS_SECONDARY, LOCATION_CITY, LOCATION_STATE, LOCATION_ZIPCODE, DATE_LAST_UPDATED, USER_LAST_UPDATED, IS_CURRENT, REASON_FOR_UPDATE_ID ) SELECT uacId_, DT.DISCHARGE_TYPE_ID, F.LOCATION_TYPE_ID, F.LOCATION_TYPE, E.FIRST_NAME || ' ' || E.LAST_NAME, D.SPONSOR_ADDRESS, D.SPONSOR_ADDRESS_SECONDARY, D.SPONSOR_CITY, D.SPONSOR_STATE, D.SPONSOR_ZIPCODE, /*CASE WHEN SATH.UPDATE_DATE IS NULL THEN D.UPTIME ELSE SATH.UPDATE_DATE END, CASE WHEN SATH.UPDATE_DATE IS NULL THEN D.UPUSER ELSE SATH.UPUSER END,*/ --Get the SPONSOR_ASSESS_TAB_HISTORY Last Updated Date or Pogram Exit Last Updated Date, which ever is more recent CASE WHEN SATH.UPDATE_DATE IS NULL THEN CASE WHEN D.UPTIME > PE.UPTIME THEN D.UPTIME ELSE PE.UPTIME END ELSE CASE WHEN SATH.UPDATE_DATE > PE.UPTIME THEN SATH.UPDATE_DATE ELSE PE.UPTIME END END, --Get the SPONSOR_ASSESS_TAB_HISTORY Last Updated User or Pogram Exit Last Updated User, which ever is more recent CASE WHEN SATH.UPDATE_DATE IS NULL THEN CASE WHEN D.UPTIME > PE.UPTIME THEN D.UPUSER ELSE PE.UPUSER END ELSE CASE WHEN SATH.UPDATE_DATE > PE.UPTIME THEN SATH.UPUSER ELSE PE.UPUSER END END, 0, vREASON_FOR_UPDATE_ID FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_PROGRAM_EXIT PE On B.UAC_PROGRAM_ID = PE.UAC_PROGRAM_ID INNER JOIN LIST_DISCHARGE_TYPE DT On PE.TYPE_OF_DISCHARGE = DT.DISCHARGE_TYPE_ID INNER JOIN LIST_DISCHARGE_TYPE_CHILD_LOCATION DTCL ON DT.DISCHARGE_TYPE_ID = DTCL.DISCHARGE_TYPE_ID INNER JOIN LIST_CHILD_LOCATION_TYPE F ON DTCL.LOCATION_TYPE_ID = F.LOCATION_TYPE_ID LEFT JOIN UAC_SPONSOR_LIST D on B.UAC_ID = D.UAC_ID AND D.CURRENT_SPONSOR = 1 and d.is_deleted = 0 LEFT JOIN SPONSOR_INFO E On D.SPONSOR_ID = E.SPONSOR_ID and e.is_deleted = 0 LEFT JOIN ( SELECT UPE1.PROGRAM_EXIT_ID, UPE1.UAC_PROGRAM_ID, UPE1.TYPE_OF_DISCHARGE, DT1.DISCHARGE_TYPE FROM UAC_PROGRAM_EXIT UPE1 INNER JOIN LIST_DISCHARGE_TYPE DT1 On UPE1.TYPE_OF_DISCHARGE = DT1.DISCHARGE_TYPE_ID
) PE1 ON B.UAC_PROGRAM_ID = PE1.UAC_PROGRAM_ID LEFT JOIN SPONSOR_ASSESS_TAB_HISTORY SATH ON D.UAC_ID = SATH.UAC_ID AND D.SPONSOR_ID = SATH.SPONSOR_ID AND SATH.ASSESSMENT_TAB = 'ContactInformation' WHERE B.UAC_PROGRAM_ID = uacProgId_ AND PE.PROGRAM_EXIT_ID = programExitID_; ELSIF dischargeID_ IN (6,10,11,12) then INSERT INTO UAC_CHILD_LOCATION_HIST( UAC_ID, DISCHARGE_TYPE_ID, LOCATION_TYPE_ID, LOCATION_TYPE, LOCATION_NAME, DATE_LAST_UPDATED, USER_LAST_UPDATED, IS_CURRENT, REASON_FOR_UPDATE_ID ) SELECT uacId_, DT.DISCHARGE_TYPE_ID, F.LOCATION_TYPE_ID, F.LOCATION_TYPE, F.LOCATION, PE.UPTIME, PE.UPUSER, 0, vREASON_FOR_UPDATE_ID FROM UAC_PROGRAM_INFO B INNER JOIN LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_PROGRAM_EXIT PE On B.UAC_PROGRAM_ID = PE.UAC_PROGRAM_ID INNER JOIN LIST_DISCHARGE_TYPE DT On PE.TYPE_OF_DISCHARGE = DT.DISCHARGE_TYPE_ID INNER JOIN LIST_DISCHARGE_TYPE_CHILD_LOCATION E ON DT.DISCHARGE_TYPE_ID = E.DISCHARGE_TYPE_ID INNER JOIN LIST_CHILD_LOCATION_TYPE F ON E.LOCATION_TYPE_ID = F.LOCATION_TYPE_ID LEFT JOIN ADM_LIST_WEB_USER U On UPPER(PE.UPUSER) = UPPER(U.USER_ID) WHERE B.UAC_PROGRAM_ID = uacProgId_ AND PE.PROGRAM_EXIT_ID = programExitID_; End If; End If; End If;
INSERT INTO UAC_CHILD_LOCATION_HIST ( UAC_ID, CHILD_LOCATION_TYPE, LIVING_WITH_SPONSOR, LIVING_WITH_CAREGIVER, ADDRESS_KNOWN, PRIMARY_CAREGIVER_TYPE, PRIMARY_CAREGIVER_OTHER, LOCATION_ADDRESS, LOCATION_ADDRESS_SECONDARY, LOCATION_CITY, LOCATION_STATE, LOCATION_ZIPCODE, LOCATION_COUNTRY, LOCATION_COUNTY, LOCATION_LATITUDE, LOCATION_LONGITUDE, SMARTY_STREET_VALIDATION, DATE_LAST_UPDATED, USER_LAST_UPDATED, CAREGIVER_FIRST_NAME, CAREGIVER_LAST_NAME, NOTES, IS_CURRENT, CAREGIVER_PHONE, REASON_FOR_UPDATE_ID ) VALUES ( uacId_, vCHILD_LOCATION_TYPE, vLIVING_WITH_SPONSOR, vLIVING_WITH_CAREGIVER, vADDRESS_KNOWN, vPRIMARY_CAREGIVER_TYPE, vPRIMARY_CAREGIVER_OTHER, vLOCATION_ADDRESS, vLOCATION_ADDRESS_SECONDARY, vLOCATION_CITY, vLOCATION_STATE, vLOCATION_ZIPCODE, vLOCATION_COUNTRY, vLOCATION_COUNTY, vLOCATION_LATITUDE, vLOCATION_LONGITUDE, vSMARTY_STREET_VALIDATION, SYSDATE, vUSER, vCAREGIVER_FIRST_NAME, vCAREGIVER_LAST_NAME, vNOTES, 1, vCAREGIVER_PHONE, vREASON_FOR_UPDATE_ID );
END SP_INSERT_CURRENT_LOCATION;

PROCEDURE "SP_INSERT_INPROGRESS_CLE_ADDENDUM" 
(
  vEVENT_ID IN NUMBER
) AS 
v_count NUMBER;
BEGIN

    SELECT COUNT(*) INTO v_count
    FROM inprogress_cle_addendum
    WHERE EVENT_ID = vEVENT_ID;

    IF v_count <= 0 THEN
      INSERT INTO inprogress_cle_addendum (EVENT_ID) VALUES (vEVENT_ID);
      Commit;
    END IF;  
END SP_INSERT_INPROGRESS_CLE_ADDENDUM;

PROCEDURE "SP_INSERT_SHADOW_PROGRAM" 
(
  v_UAC_ID NUMBER, 
  v_Upuser  VARCHAR2,
  CUR_OUT  OUT SYS_REFCURSOR
) AS 
uacStatus_  VARCHAR2(20);
n   NUMBER(4) := 0;
shdwProgId_  NUMBER(10) := -1;
BEGIN
	SELECT PROGRAM_ID INTO shdwProgId_ FROM LIST_PROGRAMS WHERE UPPER(PROGRAM_NAME) = UPPER('PENDING');
	SELECT UAC_STATUS INTO uacStatus_ FROM UAC_INFO WHERE UAC_ID = v_UAC_ID;
	SELECT COUNT(*) INTO n FROM UAC_PROGRAM_INFO WHERE UAC_ID = v_UAC_ID AND UPPER(CURRENT_STATUS) IN ('PENDING', 'ENROUTE', 'ADMITTED');
	if nvl(shdwProgId_,0) > 0  AND UPPER(uacStatus_) = 'PENDING' AND n < 1 Then
        Insert Into UAC_PROGRAM_INFO (UAC_PROGRAM_ID, UAC_ID, PROGRAM_ID, CURRENT_STATUS, DATE_REFERRED, DATE_CREATED, USER_CREATED, Uptime, UpUSER)
        VALUES (SEQ_UAC_PROGRAM_INFO.nextval, v_UAC_ID, shdwProgId_, 'PENDING', Sysdate, Sysdate, v_UPUSER, sysdate,v_UPUSER);
  End If;
  Open CUR_OUT for
    SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO WHERE UAC_ID = v_UAC_ID AND UPPER(CURRENT_STATUS) = UPPER(uacStatus_)
  ;

END SP_INSERT_SHADOW_PROGRAM;

PROCEDURE "SP_INSERT_SPONSOR_ADDRESS_HIST" ( v_UAC_ID IN NUMBER, v_SPONSOR_ID IN NUMBER, v_SPONSOR_ADDRESS IN VARCHAR2, v_SPONSOR_ADDRESS_SECONDARY IN VARCHAR2, v_SPONSOR_CITY IN VARCHAR2, v_SPONSOR_STATE IN VARCHAR2, v_SPONSOR_ZIPCODE IN VARCHAR2, v_COR IN VARCHAR2 ) AS n NUMBER(4); n1 NUMBER(4); uac_Status_ VARCHAR2(30); dischargeID_ NUMBER(4); current_SPONSOR_ NUMBER(1); BEGIN INSERT INTO UAC_SPONSOR_ADDRESS_HISTORY ( UAC_ID, SPONSOR_ID, SPONSOR_ADDRESS, SPONSOR_ADDRESS_SECONDARY, SPONSOR_CITY, SPONSOR_STATE, SPONSOR_ZIPCODE, COR, DATE_CREATED, USER_CREATED ) VALUES ( v_UAC_ID, v_SPONSOR_ID, v_SPONSOR_ADDRESS, v_SPONSOR_ADDRESS_SECONDARY, v_SPONSOR_CITY, v_SPONSOR_STATE, v_SPONSOR_ZIPCODE, v_COR, (SELECT CASE WHEN (SELECT UPDATE_DATE FROm SPONSOR_ASSESS_TAB_HISTORY WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND ASSESSMENT_TAB = 'ContactInformation') IS NOT NULL THEN (SELECT UPDATE_DATE FROm SPONSOR_ASSESS_TAB_HISTORY WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND ASSESSMENT_TAB = 'ContactInformation') ELSE (SELECT UPTIME FROM UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID and is_deleted = 0) END FROM dual), (SELECT CASE WHEN (SELECT UPUSER FROm SPONSOR_ASSESS_TAB_HISTORY WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND ASSESSMENT_TAB = 'ContactInformation') IS NOT NULL THEN (SELECT UPUSER FROm SPONSOR_ASSESS_TAB_HISTORY WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND ASSESSMENT_TAB = 'ContactInformation') ELSE (SELECT UPUSER FROM UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID and is_deleted = 0) END FROM dual) );
--Check if sponsor is primary sponsor for UC SELECT CURRENT_SPONSOR into current_SPONSOR_ from UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID and is_deleted = 0;
if current_SPONSOR_ = 1 then
--Check if UC is discharged with unified with Sponsor as discharge type
Select UAC_STATUS into uac_Status_ from UAC_INFO WHERE UAC_ID = v_UAC_ID;
if uac_Status_ = 'DISCHARGED' then select count(*) into n from ( Select 1 from UAC_PROGRAM_EXIT A INNER JOIN UAC_PROGRAM_INFO B ON A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Where B.UAC_ID = v_UAC_ID Union Select 0 from dual );
if n > 1 then SELECT A.TYPE_OF_DISCHARGE into dischargeID_ FROM UAC_PROGRAM_EXIT A INNER JOIN UAC_PROGRAM_INFO B ON A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Where B.UAC_ID = v_UAC_ID ORDER BY A.PROGRAM_EXIT_ID DESC FETCH first 1 row only; End If;
-- Check if there is a Current Location record for the Child select count(*) into n from ( Select 1 from UAC_CHILD_LOCATION_HIST Where UAC_ID = v_UAC_ID AND IS_CURRENT = 1 AND ISDELETED = 0 AND (LIVING_WITH_SPONSOR = 0 OR LIVING_WITH_SPONSOR is Null) Union Select 0 from dual );
-- Check if there is a Current Location record for the Child where Living with Sponsor = 1 select count(*) into n1 from ( Select 1 from UAC_CHILD_LOCATION_HIST Where UAC_ID = v_UAC_ID AND IS_CURRENT = 1 AND ISDELETED = 0 AND LIVING_WITH_SPONSOR = 1 Union Select 0 from dual );
-- If there is no current Location record for the Child i.e no post release address and Discharge Type is Reunified with Sponsor -- Or there is a Current Location record where IS_CURRENT = 1 and LIVING_WITH_SPONSOR = 1 ie The UC is currently living with the Sponsor if (dischargeID_ = 3 AND n < 2) OR n1 > 1 then --then current location is with Sponsor and we can insert old sponsor address into location history INSERT INTO UAC_CHILD_LOCATION_HIST ( UAC_ID, DISCHARGE_TYPE_ID, DISCHARGE_TYPE, LOCATION_TYPE_ID, LOCATION_TYPE, LOCATION_NAME, LOCATION_ADDRESS, LOCATION_ADDRESS_SECONDARY, LOCATION_CITY, LOCATION_STATE, LOCATION_ZIPCODE, LOCATION_COUNTRY, DATE_LAST_UPDATED, USER_LAST_UPDATED, ISDELETED, PROGRAM_ID, LOCATION_COUNTY, LOCATION_LATITUDE, LOCATION_LONGITUDE, SMARTY_STREET_VALIDATION) VALUES ( v_UAC_ID, 3, (SELECT DISCHARGE_TYPE FROM LIST_DISCHARGE_TYPE WHERE DISCHARGE_TYPE_ID = 3), 3, (SELECT LOCATION_TYPE FROM LIST_CHILD_LOCATION_TYPE WHERE LOCATION_TYPE_ID = 3), (SELECT FIRST_NAME || ' ' || LAST_NAME FROM SPONSOR_INFO WHERE SPONSOR_ID = v_SPONSOR_ID), v_SPONSOR_ADDRESS, v_SPONSOR_ADDRESS_SECONDARY, v_SPONSOR_CITY, v_SPONSOR_STATE, v_SPONSOR_ZIPCODE, (SELECT LC.COUNTRY_ID FROM SPONSOR_INFO SPI LEFT JOIN LIST_COUNTRIES LC ON SPI.COR = LC.COUNTRY_NAME WHERE SPI.SPONSOR_ID = v_SPONSOR_ID), (SELECT CASE WHEN (SELECT UPDATE_DATE FROm SPONSOR_ASSESS_TAB_HISTORY WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND ASSESSMENT_TAB = 'ContactInformation') IS NOT NULL THEN (SELECT UPDATE_DATE FROm SPONSOR_ASSESS_TAB_HISTORY WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND ASSESSMENT_TAB = 'ContactInformation') ELSE (SELECT UPTIME FROM UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID and is_deleted = 0) END FROM dual), (SELECT CASE WHEN (SELECT UPUSER FROm SPONSOR_ASSESS_TAB_HISTORY WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND ASSESSMENT_TAB = 'ContactInformation') IS NOT NULL THEN (SELECT UPUSER FROm SPONSOR_ASSESS_TAB_HISTORY WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND ASSESSMENT_TAB = 'ContactInformation') ELSE (SELECT UPUSER FROM UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID and is_deleted = 0) END FROM dual), 0, Null, (Select SPONSOR_COUNTY FROM UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID), (Select SPONSOR_LATITUDE FROM UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID), (Select SPONSOR_LONGITUDE FROM UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID), (Select SMARTY_STREET_VALIDATION FROM UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID) ); END If; End If; End If;
END SP_INSERT_SPONSOR_ADDRESS_HIST;

PROCEDURE SP_INSERT_SPONSOR_INFO (
vFName IN VARCHAR2, vLName IN VARCHAR2, vUser IN VARCHAR2,
vSponsorIdOut OUT NOCOPY NUMBER -- cur_OUT OUT Sys_Refcursor
) AS
BEGIN
INSERT INTO SPONSOR_INFO ( FIRST_NAME , LAST_NAME ,
DATE_CREATED , USER_CREATED , UPTIME , UPUSER , IS_Deleted ) VALUES ( vFName, vLName, Sysdate, vUser, Sysdate, vUser, 0 ); vSponsorIdOut := SEQ_SPONSOR_INFO.currval;
END SP_INSERT_SPONSOR_INFO;

PROCEDURE SP_INSERT_UAC_OUT_OF_PROGRAM_ACCESS ( v_A_NUMBER VARCHAR2, v_ACCOUNT_EMAIL VARCHAR2, v_ACCESS_REASON VARCHAR2, v_OTHER_TEXT VARCHAR2, v_CREATEDBY VARCHAR2
) --CREATEDDATE AS
BEGIN INSERT INTO UAC_OUT_OF_PROGRAM_ACCESS( ALIEN_NO, ACCOUNT_EMAIL, ACCESS_REASON, OTHER_TEXT, CREATEDDATE, CREATEDBY ) Values ( v_A_NUMBER, v_ACCOUNT_EMAIL, v_ACCESS_REASON, v_OTHER_TEXT, SYSTIMESTAMP, v_CREATEDBY );
END SP_INSERT_UAC_OUT_OF_PROGRAM_ACCESS;

