PROCEDURE SP_MASS_ASSIGNMENT
(
  v_UAC_IDs VARCHAR2, 
  v_ProgId  NUMBER,
  v_Current_Status  VARCHAR2,
  v_Upuser  VARCHAR2,
  v_DATE_ORR_APPROVED TIMESTAMP,
  v_Male NUMBER,
  v_Female NUMBER,
  v_status OUT NOCOPY VARCHAR2
) AS 



Cursor csVar  is Select regexp_substr(Column_Value ,'[^ ]+', 1, 1) AS uacId, regexp_substr(Column_Value ,'[^ ]+', 1, 2) as Gender from Table(Split(v_UAC_IDs));

csVal  csVar%ROWTYPE;
gender VARCHAR2(2);
bedsM number;
bedsF number;
funded_capacity_M number(4);
funded_capacity_F number(4);
in_care_M number(4);
in_care_F number(4);
in_transfer_M number(4);
in_transfer_F number(4);
beds_unavailable_M number(4);
beds_unavailable_F number(4);
enroute_M number(4);
enroute_F number(4);
uacPID VARCHAR2(10);
n number(2) := 0;
currStatus  varchar2(50);
BEGIN
SAVEPOINT notEnoughBeds;

SELECT 
    F.FUNDED_CAPACITY_M,
    F.FUNDED_CAPACITY_F,
    (select COUNT(*)
      from UAC_PROGRAM_INFO P
      join UAC_INFO I
      on P.UAC_ID=I.UAC_ID
      where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER')
      and (I.GENDER='M' or I.GENDER='Male')
      and P.PROGRAM_ID=F.PROGRAM_ID 
    ) AS IN_CARE_M,
    (select COUNT(*)
      from UAC_PROGRAM_INFO P
      join UAC_INFO I
      on P.UAC_ID=I.UAC_ID
      where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER')
      and (I.GENDER='F' or I.GENDER='Female')
      and P.PROGRAM_ID=F.PROGRAM_ID 
    ) AS IN_CARE_F,   
    F.BEDS_UNAVAILABLE_M,
    F.BEDS_UNAVAILABLE_F,
    (Select COUNT(*) 
      from UAC_TRANSFER_REQUEST A
      Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID 
      And nvl(A.ISDELETED,0) = 0 
      And Exists(Select 1 from UAC_PROGRAM_INFO U
                  JOIN UAC_INFO I
                    ON U.UAC_ID=I.UAC_ID
                  Where U.UAC_PROGRAM_ID = A.UAC_PROGRAM_ID 
                  And U.CURRENT_STATUS = 'IN-TRANSFER'
                  And (I.GENDER='M' OR I.GENDER='Male'))
    ) AS IN_TRANSFER_M,
    (Select COUNT(*) 
      from UAC_TRANSFER_REQUEST A
      Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID 
      And nvl(A.ISDELETED,0) = 0 
      And Exists(Select 1 from UAC_PROGRAM_INFO U
                  JOIN UAC_INFO I
                    ON U.UAC_ID=I.UAC_ID
                  Where U.UAC_PROGRAM_ID = A.UAC_PROGRAM_ID 
                  And U.CURRENT_STATUS = 'IN-TRANSFER'
                  And (I.GENDER='F' OR I.GENDER='Female'))
    ) AS IN_TRANSFER_F,
    (Select COUNT(*)
      from UAC_PROGRAM_INFO U
      JOIN UAC_INFO I
        ON U.UAC_ID=I.UAC_ID
      Where CURRENT_STATUS='ENROUTE'
      AND (I.GENDER='M' OR I.GENDER='Male')
      AND U.PROGRAM_ID=F.PROGRAM_ID) AS ENROUTE_M,
    (Select COUNT(*) 
      from UAC_PROGRAM_INFO U
      JOIN UAC_INFO I
        ON U.UAC_ID=I.UAC_ID
      Where CURRENT_STATUS='ENROUTE'
      AND (I.GENDER='F' OR I.GENDER='Female')
      AND U.PROGRAM_ID=F.PROGRAM_ID) AS ENROUTE_F
  into funded_capacity_M,funded_capacity_F,in_care_M,in_care_F,beds_unavailable_M,beds_unavailable_F,in_transfer_M,in_transfer_F,enroute_M,enroute_F
  FROM FACILITY_INFO F
  WHERE F.PROGRAM_ID=v_ProgId;

   bedsM := funded_capacity_M - (in_care_M + beds_unavailable_M + in_transfer_M + enroute_M);
   bedsF := funded_capacity_F - (in_care_F + beds_unavailable_F + in_transfer_F + enroute_F);

  OPEN csVar;
  If (bedsM < v_Male) Then
      v_status := 'NOT ENOUGH BEDS NOT ENOUGH BEDS';      
      RETURN;
  End if;
  If (bedsF < v_Female) Then
     v_status := 'NOT ENOUGH BEDS NOT ENOUGH BEDS';    
      RETURN;
  End if;

  Loop
    FETCH csVar Into csVal;
    EXIT WHEN csVar%NOTFOUND;    
    If v_CURRENT_STATUS = 'PENDING' Then
      Select Count(*) into n from UAC_PROGRAM_INFO where CURRENT_STATUS in ('ENROUTE', 'ADMITTED') And UAC_ID = csVal.uacId;
      If n = 0 Then
        Update UAC_INFO Set UAC_STATUS = 'ENROUTE', Uptime = Current_TIMESTAMP, Upuser = v_Upuser, DATE_ORR_APPROVED=v_DATE_ORR_APPROVED Where UAC_ID = csVal.uacId;
        Select count(*) into n from UAC_PROGRAM_INFO Where UAC_ID = csVal.uacId And CURRENT_STATUS = 'PENDING';
		If n > 0 Then
			UPDATE UAC_PROGRAM_INFO SET PROGRAM_ID = v_ProgId, CURRENT_STATUS = 'ENROUTE', DATE_REFERRED = Sysdate, Uptime = sysdate, UpUSER = v_Upuser Where UAC_ID = csVal.uacId And CURRENT_STATUS = 'PENDING';
		Else
			Insert Into UAC_PROGRAM_INFO (UAC_PROGRAM_ID, UAC_ID, PROGRAM_ID, CURRENT_STATUS, DATE_REFERRED, DATE_CREATED, USER_CREATED, Uptime, UpUSER)
			VALUES (SEQ_UAC_PROGRAM_INFO.nextval, csVal.uacId, v_ProgId, 'ENROUTE', Sysdate, Sysdate, v_UPUSER, sysdate,v_Upuser);
		END If;
      Else
        Select CURRENT_STATUS into currStatus from UAC_PROGRAM_INFO where CURRENT_STATUS in ('ENROUTE', 'ADMITTED') And UAC_ID = csVal.uacId;
        Update UAC_INFO Set UAC_STATUS = currStatus, Uptime = Current_TIMESTAMP, Upuser = v_Upuser, DATE_ORR_APPROVED=v_DATE_ORR_APPROVED Where UAC_ID = csVal.uacId;
      End If;
    Elsif v_CURRENT_STATUS = 'ENROUTE' Then
      Select CURRENT_STATUS into currStatus from UAC_PROGRAM_INFO where CURRENT_STATUS = 'ADMITTED' And UAC_ID = csVal.uacId;
      If currStatus is null Then
        Update UAC_PROGRAM_INFO Set (PROGRAM_ID, CURRENT_STATUS, DATE_REFERRED, UpTime, Upuser) = (select v_ProgId, 'ENROUTE', Sysdate, Sysdate, v_Upuser from dual)
        Where UAC_ID = csVal.uacId and CURRENT_STATUS = 'ENROUTE';
      End If;
    End If;
  End Loop;
  Close csVar;
  v_status := 'SUCCESS SUCCESS';

 END SP_MASS_ASSIGNMENT;

PROCEDURE SP_RECREATE_INDEXES 
AS
BEGIN
  Delete From TT_RECREATE_INDEXES;
  Insert Into TT_RECREATE_INDEXES 
  Select RowNum, 'CREATE INDEX ' || A.INDEX_NAME || ' On ' || A.TABLE_NAME || ' (' || B.COLUMN_NAME || ' ' || B.DESCEND || ') NOLOGGING'
  , A.INDEX_NAME
  from all_indexes A
  Inner Join all_ind_columns B
  On A.INDEX_NAME = B.INDEX_NAME
  Where A.TABLE_OWNER='UACPORTAL' AND A.UNIQUENESS='NONUNIQUE' And A.TABLE_NAME NOT LIKE 'LIS_%' And B.COLUMN_POSITION = 1 And A.INDEX_TYPE='NORMAL'
  Order By A.TABLE_NAME, A.INDEX_NAME;

  BEGIN
    FOR ind IN 
      (SELECT index_name FROM TT_RECREATE_INDEXES)
    LOOP
        execute immediate 'DROP INDEX ' || ind.index_name;
    END LOOP;
  END;

  BEGIN
    FOR ind IN 
      (SELECT index_query FROM TT_RECREATE_INDEXES)
    LOOP
      Begin
        execute immediate ind.index_query;
      Exception
        When OTHERS then
          Continue;
      end;
    END LOOP;
  END;

END SP_RECREATE_INDEXES;

PROCEDURE SP_REFERRAL_STATUS_MASSUPDATE ( v_REfIDs VARCHAR2, v_infoType VARCHAR2, v_RefStatus VARCHAR2, v_Provider VARCHAR2, v_Upuser VARCHAR2, v_expDate DATE, v_status OUT NOCOPY VARCHAR2
) AS
Cursor csVar is Select Column_Value as ref_number from Table(Split(v_REfIDs)); csVal csVar%ROWTYPE; infoType_ VARCHAR2(30) := Upper(v_infoType); refStatus_ VARCHAR2(10) := Upper(v_RefStatus); provider_ VARCHAR2(200) := Upper(v_Provider);
BEGIN
OPEN csVar; --For each record where the HOMESTUDY_ID/PRS_ID matches one of the --ID's passed in the v_REfIDs parameter --Update the ASSIGNED_PROVIDER and the Status Loop FETCH csVar Into csVal; EXIT WHEN csVar%NOTFOUND;
If infoType_ = 'HS' Then --For Home Studies
If refStatus_ = '1' Then -- If Accepting Referrals Update UAC_HOMESTUDY_CASE_REFERRAL Set REFERRAL_STATUS = refStatus_, ASSIGNED_PROVIDER = provider_, Uptime = Current_TIMESTAMP, Upuser = v_Upuser, ACCEPTANCE_DATE = CURRENT_DATE, EXP_CLOSURE_DATE = v_expDate, PROVIDER_USERID = v_Upuser Where HOMESTUDY_ID = csVal.ref_number AND REFERRAL_STATUS = 2; Else Update UAC_HOMESTUDY_CASE_REFERRAL Set REFERRAL_STATUS = refStatus_, ASSIGNED_PROVIDER = provider_, Uptime = Current_TIMESTAMP, Upuser = v_Upuser, ACCEPTANCE_DATE = Null Where HOMESTUDY_ID = csVal.ref_number; End If;
Else -- For PRS
If refStatus_ = '1' Then -- If Accepting Referrals Update UAC_POST_RELEASE_SERVICE Set REFERRAL_STATUS = refStatus_, ASSIGNED_PROVIDER = provider_, Uptime = Current_TIMESTAMP, Upuser = v_Upuser, ACCEPTANCE_DATE = CURRENT_DATE, EXP_CLOSURE_DATE = CASE WHEN PRS_SERVICE_LEVEL = 1 THEN CURRENT_DATE + 30 --If it's PRS Lvl 1 add 30 days ELSE CASE WHEN PRS_SERVICE_LEVEL = 2 THEN CURRENT_DATE + 180 --If it's PRS Lvl 2 add 180 days (6 months) ELSE CASE WHEN PRS_SERVICE_LEVEL = 3 THEN CURRENT_DATE + 180 --If it's PRS Lvl 3 add 180 days (6 months) END END END, PROVIDER_USERID = v_Upuser Where PRS_ID = csVal.ref_number AND REFERRAL_STATUS = 2; Else Update UAC_POST_RELEASE_SERVICE Set REFERRAL_STATUS = refStatus_, ASSIGNED_PROVIDER = provider_, Uptime = Current_TIMESTAMP, Upuser = v_Upuser, ACCEPTANCE_DATE = Null, EXP_CLOSURE_DATE = Null Where PRS_ID = csVal.ref_number; End If;
End if;
End Loop; Close csVar; v_status := 'SUCCESS SUCCESS';
END SP_REFERRAL_STATUS_MASSUPDATE;

PROCEDURE "SP_REFRESH_SPONSOR_MV" AS 
BEGIN
  DBMS_SNAPSHOT.REFRESH( 'MV_SPONSOR_INFO_LIST', '?');
END SP_REFRESH_SPONSOR_MV;

PROCEDURE "SP_RGET_ORR_NOTE_TO_ICE" ( UAC_PROGRAM_ID IN NUMBER , cur_OUT OUT SYS_REFCURSOR ) AS
uacProgId_ NUMBER(10) := UAC_PROGRAM_ID; BEGIN OPEN cur_OUT For Select Distinct A.REQUESTER_NAME, A.REQUESTER_TITLE,A.REQUESTER_PHONE, TRUNC(A.DATE_CREATED) as DATE_REQUESTED, C.FIRST_NAME, C.LAST_NAME, C.ALIEN_NO, C.AKA, C.DOB, C.COB, E.PROGRAM_NAME as Prev_PROGRAM_NAME, G.PROGRAM_TYPE as Prev_PROGRAM_TYPE, E.Address as Prev_Address, E.City as Prev_City, E.State as Prev_State, E.ZipCode as Prev_ZipCode, E.Phone as Prev_Phone, E.Mail_Address as Prev_Mail_Address, E.Mail_City as Prev_Mail_City, E.Mail_State as Prev_Mail_State, E.Mail_ZipCode as Prev_Mail_ZipCode, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.SPONSOR_ADDRESS Else A.Address End as Address, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.Sponsor_City Else A.City End as City, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.Sponsor_State Else A.State End as State, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.Sponsor_ZipCode Else A.ZipCode End as ZipCode, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.Sponsor_Home_Phone Else A.Phone End As Phone, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.Sponsor_Email Else A.Email End as Email, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then '' Else A.PROGRAM_NAME End PROGRAM_NAME, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then '' Else A.PROGRAM_TYPE End Program_Type, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.Custodian_Name Else A.Custodian_Name End as Custodian_Name, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.Custodian_Title Else A.Custodian_Title End as Custodian_Title, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.Custodian_Phone Else A.CUstodian_Phone End as Custodian_Phone, Case When A.TYPE_OF_RELEASE = 1 AND A.ORR_DECISION NOT IN (10,11,12,13,14,15,16) Then F.WORK_Phone Else '' End as Alternative_Phone, RELEASE_SCHEDULED_DATE, NEXT_SCH_COURT_APPEARANCE, DATE_SPONSOR_NOTIFIED From ( Select * from ( Select UAC_PROGRAM_ID, Type_of_Release, Sponsor_ID, REQUESTER_NAME, REQUESTER_TITLE, REQUESTER_PHONE, DATE_CREATED, Program_Address1 || Case When Program_Address2 is not null Then ', ' else '' End || Program_Address2 as Address, Program_City as City, Program_State as State, Program_ZipCode as Zipcode, Prog_PRIMARY_Email as email,Prog_PRIMARY_Phone as Phone, '' as Custodian_Phone, Program_Name, Program_Type, '' as Custodian_Name,'' Custodian_Title, RELEASE_SCHEDULED_DATE, NEXT_SCH_COURT_APPEARANCE, DATE_SPONSOR_NOTIFIED, ORR_DECISION From UAC_RELEASE_REQUEST Where UAC_PROGRAM_ID = uacProgId_ Order by Uptime Desc ) where RowNum <=1 ) A INNER JOIN UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID INNER JOIN UAC_INFO C On B.UAC_ID = C.UAC_ID Inner Join List_Programs E On B.Program_ID = E.Program_ID INNER JOIN LIST_PROGRAM_TYPE G On E.PROGRAM_TYPE_ID = G.PROGRAM_TYPE_ID Left join ( Select B.*, A.UAC_ID,A.SPONSOR_ADDRESS || ' ' || A.SPONSOR_ADDRESS_SECONDARY as SPONSOR_ADDRESS, A.SPONSOR_CITY, A.SPONSOR_STATE, A.SPONSOR_ZIPCODE, A.SPONSOR_HOME_PHONE, A.SPONSOR_EMAIL, B.FIRST_NAME || ' ' || B.LAST_NAME as Custodian_Name, C.Relationship as Custodian_Title, A.Sponsor_Home_Phone as Custodian_Phone from UAC_SPONSOR_LIST A Inner Join Sponsor_Info B On A.Sponsor_ID = B.Sponsor_ID and a.is_deleted = 0 and b.is_deleted = 0 Inner Join List_Relationship C On A.SPONSOR_RELATIONSHIP_TO_UAC = C.RELATIONSHIP_ID ) F On B.UAC_ID = F.UAC_ID And A.SPONSOR_ID = F.SPONSOR_ID Where A.UAC_PROGRAM_ID = uacProgId_;
END SP_RGET_ORR_NOTE_TO_ICE;

PROCEDURE "SP_ROLLBACK_ARCHIVED_SPONSORS" AS BEGIN
-- Insert the Orphan sponsor in the audit table INSERT into SPONSOR_INFO ( SPONSOR_ID, FIRST_NAME, LAST_NAME, SSN, ALIEN_NO, ADDRESS, CITY, "STATE", ZIP_CODE, DOB, COB, COR, MARRIAGE_STATUS, HOME_PHONE, WORK_PHONE, FAX, EMAIL, GENDER, LEGAL_STATUS_ID, SPONSOR_RELATIONSHIP_TO_UAC, UAC_RELATIONSHIP_TO_SPONSOR, HOUSEHOLD_HAS_DISEASE, HOUSEHOLD_HAS_CRIMINAL_HIST, NOTES, SPONSORTHIP_STATUS, P_COUNTER, A_COUNTER, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, FLAG, FLAG_NOTE, FLAG_ADDRESS, FLAG_ADDRESS_NOTE, SPONSOR_TOKEN, SMARTY_STREET_VALIDATION )
SELECT SPONSOR_ID, FIRST_NAME, LAST_NAME, SSN, ALIEN_NO, ADDRESS, CITY, "STATE", ZIP_CODE, DOB, COB, COR, MARRIAGE_STATUS, HOME_PHONE, WORK_PHONE, FAX, EMAIL, GENDER, LEGAL_STATUS_ID, SPONSOR_RELATIONSHIP_TO_UAC, UAC_RELATIONSHIP_TO_SPONSOR, HOUSEHOLD_HAS_DISEASE, HOUSEHOLD_HAS_CRIMINAL_HIST, NOTES, SPONSORTHIP_STATUS, P_COUNTER, A_COUNTER, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, FLAG, FLAG_NOTE, FLAG_ADDRESS, FLAG_ADDRESS_NOTE, SPONSOR_TOKEN, SMARTY_STREET_VALIDATION From SPONSOR_INFO_ARCHIVE;
-- Clean up the SPONSOR_INFO_ARCHIVE DELETE from SPONSOR_INFO_ARCHIVE;
COMMIT;
END SP_ROLLBACK_ARCHIVED_SPONSORS;

PROCEDURE SP_SEARCH_BY_REFERRAL_NO ( v_Search_Value VARCHAR2, cur_Out OUT SYS_REFCURSOR ) AS v_Param1 varchar2(300); BEGIN v_Param1 := CONCAT(CONCAT('%',UPPER(v_Search_Value)), '%');
Open cur_Out For Select A.*, Count(*) over() as rowC from( Select Distinct A.UAC_ID, B.UAC_PROGRAM_ID, ALIEN_NO, FIRST_NAME, LAST_NAME, AKA, DOB, COB, GENDER, Case When CURRENT_STATUS is null Then A.UAC_STATUS Else B.CURRENT_STATUS End as UAC_STATUS,B.DATE_ADMITTED, B.DATE_DISCHARGED, C.PROGRAM_NAME, '' as APPROVED_FIELD_OFFICE, '' as ORR_APPROVED_DATE, C.PROGRAM_ID, DATE_OF_APPREHENSION, trunc(sysdate) - trunc(B.DATE_ADMITTED) LOS, E.DROP_DOWN_DISPLAY as DISCHARGE_TYPE, RELATIONSHIP_GRP_ID, PRS.REFERRAL_ID , CASE WHEN PRS.PRS_Type = 0 THEN 'PRS-TVPRA' ELSE 'PRS' END as REFERRAL_TYPE From UAC_INFO A INNER Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID INNER Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_POST_RELEASE_SERVICE PRS ON B.UAC_PROGRAM_ID = PRS.UAC_PROGRAM_ID Left Join UAC_PROGRAM_EXIT D On B.UAC_PROGRAM_ID = D.UAC_PROGRAM_ID Left Join LIST_DISCHARGE_TYPE E On D.TYPE_OF_DISCHARGE = E.DISCHARGE_TYPE_ID Where PRS.REFERRAL_ID like v_Param1 UNION Select Distinct A.UAC_ID, B.UAC_PROGRAM_ID, ALIEN_NO, FIRST_NAME, LAST_NAME, AKA, DOB, COB, GENDER, Case When CURRENT_STATUS is null Then A.UAC_STATUS Else B.CURRENT_STATUS End as UAC_STATUS,B.DATE_ADMITTED, B.DATE_DISCHARGED, C.PROGRAM_NAME, '' as APPROVED_FIELD_OFFICE, '' as ORR_APPROVED_DATE, C.PROGRAM_ID, DATE_OF_APPREHENSION, trunc(sysdate) - trunc(B.DATE_ADMITTED) LOS, E.DROP_DOWN_DISPLAY as DISCHARGE_TYPE, RELATIONSHIP_GRP_ID, HS.REFERRAL_ID , 'Homestudy' as REFERRAL_TYPE From UAC_INFO A INNER Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID INNER Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID INNER JOIN UAC_HOMESTUDY_CASE_REFERRAL HS ON B.UAC_PROGRAM_ID = HS.UAC_PROGRAM_ID Left Join UAC_PROGRAM_EXIT D On B.UAC_PROGRAM_ID = D.UAC_PROGRAM_ID Left Join LIST_DISCHARGE_TYPE E On D.TYPE_OF_DISCHARGE = E.DISCHARGE_TYPE_ID Where HS.REFERRAL_ID like v_Param1
) A Order by DATE_ADMITTED Desc FETCH FIRST 150 ROWS ONLY;
END SP_SEARCH_BY_REFERRAL_NO;

PROCEDURE "SP_SEARCH_BY_SPONSORS_ADDRESS" ( v_Search_Value IN VARCHAR2, cur_Out OUT SYS_REFCURSOR ) AS
BEGIN
Open cur_Out For select DISTINCT A.Address as street_line, A.City as city, A.State as state, '' as zipcode, --A.Zip_Code as zipcode, '' as cob, --(select ISO_CODE from LIST_COUNTRIES where COUNTRY_NAME = A.COB) AS cob, 0 as entries, 'other' as source --A.EMAIL as email, --A.HOME_PHONE as homephone, --A.WORK_PHONE as workphone, --A.FIRST_NAME || ' ' || A.LAST_NAME as FullName From V_SPONSOR_INFO A where TRIM(UPPER(address)) like '%' || TRIM(UPPER(v_Search_Value)) || '%' FETCH FIRST 10 ROWS ONLY;
END SP_SEARCH_BY_SPONSORS_ADDRESS;

PROCEDURE SP_SEARCH_BY_SPONSORS_BY_ARRAY 
(
v_Search_Value  IN  VARCHAR2,
v_Param1 IN VARCHAR2,
cur_Out  OUT SYS_REFCURSOR
)
AS 

v_Param_Original varchar2(2000);
v_ParmCat varchar(2000);
vSearchResultLimit NUMBER(10,0);
v_count number(10,0);
dob_ DATE := NULL;
v_permutations NUMBER(10,0) := 0;
v_wordCount NUMBER(10,0) := 0;
v_isNumber NUMBER := 0;

step_ VARCHAR2(200) := NULL;
errors_ VARCHAR2(200) := NULL;

v_replaced_ VARCHAR2(200) := NULL;
v_firstOf NUMBER(1,0) := NULL;

v_permutations1_ VARCHAR2(200) := NULL;
v_permutations2_ VARCHAR2(200) := NULL;
v_permutations3_ VARCHAR2(200) := NULL;
v_permutations4_ VARCHAR2(200) := NULL;
v_FirstName1_ VARCHAR2(200) := NULL;
v_LastName1_ VARCHAR2(200) := NULL;
v_FirstName2_ VARCHAR2(200) := NULL;
v_LastName2_ VARCHAR2(200) := NULL;

v_SpecialCharacters_ VARCHAR(2000) := NULL;
v_SpecialCharacters1_ VARCHAR(2000) := NULL;

BEGIN
    -- is the input begin with a number... then treat it as an address
    -- no permutations
    SELECT REGEXP_SUBSTR(
             TRIM(v_Search_Value),
             '([0-9]+)\d',
             1,     -- Start at the first character
             1,     -- Get the first match
             NULL--,  -- Case sensitive
             --1      -- Return the first capture group
           ) INTO v_isNumber
    FROM   dual;

    -- get rid of multiple spaces between words leaving at least one space if it exists
    v_Param_Original := Trim(UPPER(regexp_replace (v_Search_Value, '\s+', ' ')));
    v_ParmCat := CONCAT(CONCAT('%',replace(Trim(UPPER(regexp_replace (v_Search_Value, '\s+', ' '))), ' ', '%')), '%');

    v_SpecialCharacters_ := trim(upper(HTML_CHARACTER_TO_SPECIAL_CHARACTERS(v_Param_Original)));
    v_SpecialCharacters1_ := trim(upper(HTML_SPECIAL_CHARACTERS_TO_CHARACTER(UPPER(v_Param_Original))));
    --DBMS_OUTPUT.put_line(v_SpecialCharacters_);
    --DBMS_OUTPUT.put_line(v_SpecialCharacters1_);

    -- for SoundEx. also SoundEx works better with Lower, not Upper
    select REPLACE(LOWER(TRIM(regexp_replace (regexp_replace (v_Search_Value, '\s+', ' '), '\s+', ' '))), 'in', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'and', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'of', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'the', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'to', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'str', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'drv', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'st', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'dr', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'place', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), 'blvd', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), '.', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), '#', '') into v_replaced_ from dual;
    select REPLACE(LOWER(v_replaced_), '''', '') into v_replaced_ from dual;

    BEGIN
        Select to_date(v_Search_Value,'mm/dd/yyyy') INTO dob_ from dual;
    EXCEPTION WHEN OTHERS THEN dob_ := NULL;
    END;

    select NVL ((SELECT to_number(nvl(PARAM_VALUE, '150')) --into vSearchResultLimit
                 from UACPORTAL_APP_PARAMS where PARAM_NAME = 'SearchResultsLimit'
                ),
                to_number('150')
               ) AS PARAM_VALUE into vSearchResultLimit
    from   dual;    
    --DBMS_OUTPUT.put_line(v_isNumber);
    --DBMS_OUTPUT.put_line(v_Search_Value);

    select length(v_Param_Original) - length(replace(v_Param_Original,' ',null)) 
    INTO v_wordCount from dual;
    --DBMS_OUTPUT.put_line(v_wordCount);

    IF (v_wordCount > 0 
        AND v_isNumber IS NULL) THEN
        SELECT REGEXP_SUBSTR(TRIM(v_Param_Original), '[^ ]+') INTO v_permutations1_ FROM dual;
        SELECT REGEXP_SUBSTR(TRIM(v_Param_Original), '[^ ]+', 1, 2) INTO v_permutations2_ FROM  dual;
        SELECT REGEXP_SUBSTR(TRIM(v_Param_Original), '[^ ]+', 1, 3) INTO v_permutations3_ FROM  dual;
        SELECT REGEXP_SUBSTR(TRIM(v_Param_Original), '[^ ]+', 1, 4) INTO v_permutations4_ FROM  dual;

        --DBMS_OUTPUT.put_line(v_permutations1_);
        --DBMS_OUTPUT.put_line(v_permutations2_);
        --DBMS_OUTPUT.put_line(v_permutations3_);
        --DBMS_OUTPUT.put_line(v_permutations4_);
        v_FirstName1_ := '';
        v_FirstName2_ := '';

        IF (v_permutations1_ IS NOT NULL) THEN
            v_FirstName1_ := v_permutations1_;
        END IF;
        IF (v_permutations2_ IS NOT NULL) THEN
            v_FirstName2_ := v_permutations2_;
        END IF;
        IF (v_permutations3_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations3_;
        END IF;
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations4_;
        END IF;

        --DBMS_OUTPUT.put_line(v_FirstName1_);
        --DBMS_OUTPUT.put_line(v_FirstName2_);

        IF (v_FirstName1_ IS NOT NULL) THEN
            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(v_FirstName1_)),TRIM( UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' )))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;

            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' ))), TRIM(UPPER(v_FirstName1_))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;  
        END IF;

        v_FirstName1_ := '';
        v_FirstName2_ := '';
        IF (v_permutations2_ IS NOT NULL) THEN
            v_FirstName1_ := v_permutations2_;
        END IF;
        IF (v_permutations1_ IS NOT NULL) THEN
            v_FirstName2_ := v_permutations1_;
        END IF;
        IF (v_permutations3_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations3_;
        END IF;
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations4_;
        END IF;

        --DBMS_OUTPUT.put_line(v_FirstName1_);
        --DBMS_OUTPUT.put_line(v_FirstName2_);

        IF (v_FirstName1_ IS NOT NULL) THEN
            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(v_FirstName1_)),TRIM( UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' )))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;

            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' ))), TRIM(UPPER(v_FirstName1_))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;  
        END IF;

        v_FirstName1_ := '';
        v_FirstName2_ := '';
        IF (v_permutations3_ IS NOT NULL) THEN
            v_FirstName1_ := v_permutations3_;
        END IF;
        IF (v_permutations1_ IS NOT NULL) THEN
            v_FirstName2_ := v_permutations1_;
        END IF;
        IF (v_permutations2_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations2_;
        END IF;
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations4_;
        END IF;

        --DBMS_OUTPUT.put_line(v_FirstName1_);
        --DBMS_OUTPUT.put_line(v_FirstName2_);

        IF (v_FirstName1_ IS NOT NULL) THEN
            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(v_FirstName1_)),TRIM( UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' )))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;

            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' ))), TRIM(UPPER(v_FirstName1_))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;  
        END IF;

        v_FirstName1_ := '';
        v_FirstName2_ := '';
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName1_ := v_permutations4_;
        END IF;
        IF (v_permutations1_ IS NOT NULL) THEN
            v_FirstName2_ := v_permutations1_;
        END IF;
        IF (v_permutations2_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations2_;
        END IF;
        IF (v_permutations3_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations3_;
        END IF;
        IF (v_permutations4_ IS NOT NULL) THEN
            v_FirstName2_ := v_FirstName2_ || ' ' || v_permutations4_;
        END IF;

        --DBMS_OUTPUT.put_line(v_FirstName1_);
        --DBMS_OUTPUT.put_line(v_FirstName2_);

        IF (v_FirstName1_ IS NOT NULL) THEN
            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(v_FirstName1_)),TRIM( UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' )))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;

            -- creates our list of names by switching multiple words into a temp table
            -- the key is v_Param1 - FIRST PERMUTATION
            INSERT INTO SPONSOR_INFO_PERMUTATIONS (PERMUTATIONS_KEY, FNAME, LNAME) 
            WITH words AS
            (   SELECT  v_FirstName1_
                    ,   REGEXP_SUBSTR( v_FirstName2_, '\S+', 1, LEVEL ) AS word
                    ,   LEVEL                                           AS num
                FROM    DUAL
                CONNECT BY LEVEL <= LENGTH( REGEXP_REPLACE( v_FirstName2_, '\S+\s*', 'X' ) )
            )
            SELECT  TRIM(v_Param1), TRIM(UPPER(SYS_CONNECT_BY_PATH( W.word, ' ' ))), TRIM(UPPER(v_FirstName1_))
            FROM    words   W
            CONNECT BY NOCYCLE PRIOR W.num != W.num;  
        END IF;

        -- Remove items that are like FNAME := 'JOSE' AND LNAME := 'SMITH'
        -- in both the first and last names
        IF (v_wordCount = 2) THEN
            DELETE FROM SPONSOR_INFO_PERMUTATIONS 
            WHERE  PERMUTATIONS_KEY = v_Param1
                AND (length(FNAME) - length(replace(FNAME,' ',null)) = 0)
                AND (length(LNAME) - length(replace(LNAME,' ',null)) = 0);
        ELSIF (v_wordCount = 3) THEN
            DELETE FROM SPONSOR_INFO_PERMUTATIONS 
            WHERE  PERMUTATIONS_KEY = v_Param1
                AND (length((FNAME || ' ' || LNAME)) - length(replace((FNAME || ' ' || LNAME),' ',null)) < 3);
        END IF;
    END IF;

    Select count(*) into v_permutations from SPONSOR_INFO_PERMUTATIONS where PERMUTATIONS_KEY = v_Param1;
    --select count(*) as countItem into v_count from SPONSOR_INFO_PERMUTATIONS where PERMUTATIONS_KEY = v_Param1;
    --DBMS_OUTPUT.put_line(v_permutations);
    --DBMS_OUTPUT.put_line(HTML_CHARACTER_TO_SPECIAL_CHARACTERS(v_Param_Original));
   --v_permutations := 1;

   IF (v_Search_Value IS NOT NULL) THEN   
   BEGIN
   step_ := 'ROWLEVEL-1';
    INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     Select DISTINCT * FROM
       (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address ,
          A.City,--REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0), 
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0),

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1')
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE, 

         1 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
      where (
            TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) = v_Param_Original
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) = v_Param_Original

            OR TRIM(UPPER(REPLACE(A.FIRST_NAME, '''',''))) = REPLACE(v_Param_Original, '''','')
            OR TRIM(UPPER(REPLACE(A.LAST_NAME, '''',''))) = REPLACE(v_Param_Original, '''','')
            OR TRIM(UPPER(REPLACE(A.ADDRESS, '''',''))) = REPLACE(v_Param_Original, '''','')
            OR TRIM(UPPER(A.EMAIL)) = v_Param_Original
            OR TRIM(UPPER(A.HOME_PHONE)) = v_Param_Original
            OR TRIM(UPPER(A.WORK_PHONE)) = v_Param_Original

            -- SPECIAL CHARACTERS
            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) = v_SpecialCharacters_
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) = v_SpecialCharacters_

            OR TRIM(UPPER(A.FIRST_NAME)) = v_SpecialCharacters_
            OR TRIM(UPPER(A.LAST_NAME)) = v_SpecialCharacters_
            OR TRIM(UPPER(A.ADDRESS)) = v_SpecialCharacters_
            OR TRIM(UPPER(A.EMAIL)) = v_SpecialCharacters_
            OR TRIM(UPPER(A.HOME_PHONE)) = v_SpecialCharacters_
            OR TRIM(UPPER(A.WORK_PHONE)) = v_SpecialCharacters_

            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) = v_SpecialCharacters1_
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) = v_SpecialCharacters1_

            OR TRIM(UPPER(A.FIRST_NAME)) = v_SpecialCharacters1_
            OR TRIM(UPPER(A.LAST_NAME)) = v_SpecialCharacters1_
            OR TRIM(UPPER(A.ADDRESS)) = v_SpecialCharacters1_
            OR TRIM(UPPER(A.EMAIL)) = v_SpecialCharacters1_
            OR TRIM(UPPER(A.HOME_PHONE)) = v_SpecialCharacters1_
            OR TRIM(UPPER(A.WORK_PHONE)) = v_SpecialCharacters1_
            )

            And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID)  
      ) 
      --Order by First_Name, Last_Name 
      FETCH FIRST vSearchResultLimit ROWS ONLY ;

    IF (v_permutations > 1) THEN
     step_ := 'ROWLEVEL-2 - EQUALS first and last name ' || v_Param_Original;
     --DBMS_OUTPUT.put_line(step_);
      INSERT INTO SPONSOR_INFO_SEARCH_TEMP
      (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address ,
          A.City,--REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0),

         CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1')
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE, 

         2 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
        join SPONSOR_INFO_PERMUTATIONS P  ON UPPER(TRIM(P.PERMUTATIONS_KEY)) = UPPER(TRIM(v_Param1))
           AND TRIM(UPPER(A.FIRST_NAME)) = UPPER(TRIM(P.FNAME))
           AND TRIM(UPPER(A.LAST_NAME)) = UPPER(TRIM(P.LNAME))
           --AND TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) = UPPER(TRIM(P.FNAME)) || ' ' || UPPER(TRIM(P.LNAME))
      And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
      --Order by A.First_Name, A.Last_Name 
      FETCH FIRST vSearchResultLimit ROWS ONLY );

     step_ := 'ROWLEVEL-2 - EQUALS first + last name ' || v_Param_Original;
     --DBMS_OUTPUT.put_line(step_);
      INSERT INTO SPONSOR_INFO_SEARCH_TEMP
      (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address ,
          A.City,--REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0),

         CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1')
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE,  

         2 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
        join SPONSOR_INFO_PERMUTATIONS P  ON UPPER(TRIM(P.PERMUTATIONS_KEY)) = UPPER(TRIM(v_Param1))
           AND TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) = UPPER(TRIM(P.FNAME)) || ' ' || UPPER(TRIM(P.LNAME))
      And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
      --Order by A.First_Name, A.Last_Name 
      FETCH FIRST vSearchResultLimit ROWS ONLY );


     step_ := 'ROWLEVEL-2 - EQUALS first name ' || v_Param_Original;
     --DBMS_OUTPUT.put_line(step_);
      INSERT INTO SPONSOR_INFO_SEARCH_TEMP
      (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
