      FETCH FIRST vSearchResultLimit ROWS ONLY );

    END IF;

     step_ := 'ROWLEVEL-2 - LIKE ' || v_Param_Original;
      INSERT INTO SPONSOR_INFO_SEARCH_TEMP
      (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address ,
          A.City,--REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0),

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1')
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE, 

         2 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
        join SPONSOR_INFO_PERMUTATIONS P  ON UPPER(TRIM(P.PERMUTATIONS_KEY)) = UPPER(TRIM(v_Param1))
           AND ('%' || TRIM(UPPER(A.FIRST_NAME)) || '%' like '%' || UPPER(TRIM(P.FNAME)) || '%'
           AND '%' || TRIM(UPPER(A.LAST_NAME)) || '%' like '%' || UPPER(TRIM(P.LNAME)) || '%')
      And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
      --Order by A.First_Name, A.Last_Name 
      FETCH FIRST vSearchResultLimit ROWS ONLY );

    step_ := 'ROWLEVEL-3';
    INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     Select DISTINCT * FROM
       (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address ,
          A.City,--REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0), 
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1')
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE, 

         3 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
      where (
            TRIM(UPPER(REPLACE(A.FIRST_NAME, '''',''))) Like REPLACE(v_Param_Original, '''','') || '%'
            OR TRIM(UPPER(REPLACE(A.LAST_NAME, '''',''))) Like REPLACE(v_Param_Original, '''','') || '%'
            OR TRIM(UPPER(REPLACE(A.ADDRESS, '''',''))) LIKE REPLACE(v_Param_Original, '''','') || '%'
            OR TRIM(UPPER(A.EMAIL)) Like v_Param_Original || '%'

            OR TRIM(UPPER(A.HOME_PHONE)) like v_Param_Original || '%'
            OR TRIM(UPPER(A.WORK_PHONE)) like v_Param_Original || '%'

            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) Like v_Param_Original || '%'
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) Like v_Param_Original || '%'

            -- SPECIAL CHARACTERS
            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) = v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) = v_SpecialCharacters_ || '%'

            OR TRIM(UPPER(A.FIRST_NAME)) like v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.LAST_NAME)) like v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.ADDRESS)) like v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.EMAIL)) like v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.HOME_PHONE)) like v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.WORK_PHONE)) like v_SpecialCharacters_ || '%'

            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) like v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) like v_SpecialCharacters1_ || '%'

            OR TRIM(UPPER(A.FIRST_NAME)) = v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.LAST_NAME)) = v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.ADDRESS)) = v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.EMAIL)) = v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.HOME_PHONE)) = v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.WORK_PHONE)) = v_SpecialCharacters1_  || '%'        ) 
     And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID)   
     --Order by A.First_Name, A.Last_Name 
     FETCH FIRST vSearchResultLimit ROWS ONLY );

    step_ := 'ROWLEVEL-4';
    INSERT INTO SPONSOR_INFO_SEARCH_TEMP
     Select DISTINCT * FROM
       (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address ,
          A.City,--REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0),  
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

         CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1')
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE, 

         4 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
      where (
            TRIM(UPPER(REPLACE(A.FIRST_NAME, '''',''))) Like '%' || REPLACE(v_Param_Original, '''','') || '%'
            OR TRIM(UPPER(REPLACE(A.LAST_NAME, '''',''))) Like '%' || REPLACE(v_Param_Original, '''','') || '%'
            OR TRIM(UPPER(REPLACE(A.ADDRESS, '''',''))) LIKE '%' || REPLACE(v_Param_Original, '''','') || '%'
            OR TRIM(UPPER(A.EMAIL)) Like '%' || v_Param_Original || '%'

            OR TRIM(UPPER(A.HOME_PHONE)) like '%' || v_Param_Original || '%'
            OR TRIM(UPPER(A.WORK_PHONE)) like '%' || v_Param_Original || '%'

            OR TRIM(UPPER(A.FIRST_NAME)) Like v_ParmCat
            OR TRIM(UPPER(A.LAST_NAME)) Like v_ParmCat
            OR TRIM(UPPER(A.ADDRESS)) Like v_ParmCat
            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) Like v_ParmCat

            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) Like '%' || v_Param_Original || '%'
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) Like '%' || v_Param_Original || '%'

            -- SPECIAL CHARACTERS
            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) = '%' || v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) = '%' || v_SpecialCharacters_ || '%'

            OR TRIM(UPPER(A.FIRST_NAME)) like '%' || v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.LAST_NAME)) like '%' || v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.ADDRESS)) like '%' || v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.EMAIL)) like '%' || v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.HOME_PHONE)) like '%' || v_SpecialCharacters_ || '%'
            OR TRIM(UPPER(A.WORK_PHONE)) like '%' || v_SpecialCharacters_ || '%'

            OR TRIM(UPPER(A.FIRST_NAME)) || ' ' || TRIM(UPPER(A.LAST_NAME)) like '%' || v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.LAST_NAME)) || ' ' || TRIM(UPPER(A.FIRST_NAME)) like '%' || v_SpecialCharacters1_ || '%'

            OR TRIM(UPPER(A.FIRST_NAME)) like '%' || v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.LAST_NAME)) like '%' || v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.ADDRESS)) like '%' || v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.EMAIL)) like '%' || v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.HOME_PHONE)) like '%' || v_SpecialCharacters1_ || '%'
            OR TRIM(UPPER(A.WORK_PHONE)) like '%' || v_SpecialCharacters1_  || '%'   
      ) 
      And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID)    
      --Order by A.First_Name, A.Last_Name 
      FETCH FIRST vSearchResultLimit ROWS ONLY );          

      step_ := 'ROWLEVEL-5';
      INSERT INTO SPONSOR_INFO_SEARCH_TEMP
       (Select DISTINCT v_Param1, Count(*) over() as rowC,  A.SPONSOR_ID, A.ALIEN_NO, A.DOB, 
          A.FIRST_NAME, --REGEXP_REPLACE(A.FIRST_NAME, '[^0-9A-Za-z ,.#;&-]', '') AS FIRST_NAME, 
          A.Last_Name,  --REGEXP_REPLACE(A.Last_Name, '[^0-9A-Za-z ,.#;&-]', '') AS Last_Name, 
          A.Address,--  REGEXP_REPLACE(A.Address, '[^0-9A-Za-z ,.#-&/\]', '') AS Address ,
          A.City,--REGEXP_REPLACE(A.City, '[^0-9A-Za-z ,.#]', '') AS City, 
          A.State, NVL(A.P_COUNTER, 0), 
         REGEXP_REPLACE(A.HOME_PHONE, '[^0-9]', '')  as HOME_PHONE, 
         REGEXP_REPLACE(A.WORK_PHONE, '[^0-9]', '')  as WORK_PHONE,
         A.EMAIL,
         A.COB,
         NVL(A.A_COUNTER, 0), 

        CASE 
            WHEN UPPER(A.FLAG) IN ('YES') OR A.FLAG_ADDRESS IN ('1')
                 OR EXISTS (SELECT 1 FROM SponsorFlag WHERE SPONSORID = A.SPONSOR_ID)
            THEN 'Yes'
            ELSE 'No'
        END AS Flag,

        --(CASE WHEN REGEXP_REPLACE(A.FLAG, '[^0-9A-Za-z ,.#]', '') = 'Yes' OR nvl(A.FLAG_ADDRESS, 0) = 1 THEN 'Yes' ELSE 'No' END) AS FLAG, 
        A.FLAG_NOTE, --REGEXP_REPLACE(A.FLAG_NOTE, '[^0-9A-Za-z ,.#]', '') as FLAG_NOTE, 

        5 AS ROWLEVEL, '' as SPONSOR_STATUS,'Add Sponsor' as ADD_SPONSOR
      From V_SPONSOR_INFO A
      where 
          --SOUNDEX(TRIM(UPPER(A.First_Name)) || ' ' || TRIM(UPPER(A.Last_Name))) = SOUNDEX(TRIM(UPPER(v_Search_Value)))
          --OR SOUNDEX(TRIM(UPPER(A.First_Name))) = SOUNDEX(TRIM(UPPER(v_Search_Value)))
          --OR SOUNDEX(TRIM(UPPER(A.LAST_Name))) = SOUNDEX(TRIM(UPPER(v_Search_Value)))      
          SOUNDEX(LOWER(A.First_Name || ' ' || A.Last_Name)) = SOUNDEX(LOWER(v_replaced_))
        And NOT EXISTS (Select SPONSOR_ID from SPONSOR_INFO_SEARCH_TEMP Where SPONSOR_ID = A.SPONSOR_ID) 
     --Order by  A.First_Name, A.Last_Name 
     FETCH FIRST vSearchResultLimit ROWS ONLY );

    EXCEPTION 
       WHEN OTHERS THEN errors_ := 'Database error: ' || ' ' || SQLERRM;
    END;        
   END IF;

    --- retrieve all of the records
    IF (errors_ IS NULL) THEN
        Open cur_Out For
          SELECT DISTINCT rowC, SPONSOR_ID, ALIEN_NO, DOB, First_Name, Last_Name,Address, City, State, P_COUNTER, 
          HOME_PHONE, WORK_PHONE, EMAIL,COB,
           A_COUNTER, FLAG, FLAG_NOTE, ROWLEVEL, SPONSOR_STATUS, ADD_SPONSOR 
          FROM SPONSOR_INFO_SEARCH_TEMP 
          WHERE SEARCH_KEY = v_Param1
          ORDER BY ROWLEVEL;
    ELSE
       --INSERT INTO SPONSOR_INFO_SEARCH_TEMP
       --Select DISTINCT v_Param1, 0 as rowC,  0 AS SPONSOR_ID, '' AS ALIEN_NO, NULL AS DOB, 
          --'Database_Error' AS FIRST_NAME, 
          --'SP_SEARCH_BY_SPONSORS_BY_ARRAY' AS Last_Name, 
         -- errors_ || ' IN ' || step_ AS Address, 
         -- '' AS City, 
         -- '' AS State, 0 AS P_COUNTER, 
         -- '0000000000' as HOME_PHONE, '0000000000'  as WORK_PHONE,
         --'' AS EMAIL,
         --'' AS COB,
       -- 0 AS A_COUNTER, '' AS FLAG, '' as FLAG_NOTE, -1 AS ROWLEVEL, '' as SPONSOR_STATUS,'' as ADD_SPONSOR
       -- FROM DUAL;

        Open cur_Out For
          SELECT DISTINCT rowC, SPONSOR_ID, ALIEN_NO, DOB, First_Name, Last_Name,Address, City, State, P_COUNTER, 
          HOME_PHONE, WORK_PHONE, EMAIL,COB,
           A_COUNTER, FLAG, FLAG_NOTE, ROWLEVEL, SPONSOR_STATUS, ADD_SPONSOR 
          FROM SPONSOR_INFO_SEARCH_TEMP 
          WHERE SEARCH_KEY = v_Param1
          ORDER BY ROWLEVEL;        
    END IF;

    -- delete the values from the temp table based on the v_Param1
    DELETE FROM SPONSOR_INFO_SEARCH_TEMP WHERE SEARCH_KEY = v_Param1;
    DELETE FROM SPONSOR_INFO_PERMUTATIONS WHERE PERMUTATIONS_KEY = v_Param1;

END SP_SEARCH_BY_SPONSORS_BY_ARRAY;

PROCEDURE "SP_SEARCH_BY_SPONSORS_FIRSTLAST_NAMES" (
v_Search_Value  IN  VARCHAR2,
v_Search_Type IN NUMBER,
cur_Out  OUT SYS_REFCURSOR
)
AS 

v_rows NUMBER(10,0) := 10;
v_Type_ NUMBER(10,0);
v_length NUMBER(10,2);

BEGIN

    IF (v_Search_Value IS NOT NULL) THEN
        SELECT LENGTH(v_Search_Value) INTO v_length FROM DUAL;

        IF (v_length > 1) THEN
            IF (v_Search_Type IS NULL) THEN
             SELECT 0 INTO v_Type_ FROM DUAL;
            ELSE
             SELECT v_search_type INTO v_type_ FROM DUAL;
            END IF;

            IF (v_Search_Type > 2) THEN
             SELECT 0 INTO v_Type_ FROM DUAL;
            END IF;

            --DBMS_OUTPUT.put_line(v_Type_);
            IF (v_Type_ = 0) THEN
               Open cur_Out For
                select DISTINCT FIRST_NAME || ' ' || LAST_NAME AS SEARCH_NAME
                From V_SPONSOR_INFO A  
                where TRIM(UPPER(FIRST_NAME || ' ' || LAST_NAME)) like TRIM(UPPER(v_Search_Value)) || '%'
                FETCH FIRST v_rows ROWS ONLY;
            END IF;

            IF (v_Type_ = 1) THEN
               Open cur_Out For
                select DISTINCT FIRST_NAME  AS SEARCH_NAME
                From V_SPONSOR_INFO A  
                where TRIM(UPPER(FIRST_NAME)) like TRIM(UPPER(v_Search_Value)) || '%'
                FETCH FIRST v_rows ROWS ONLY;
            END IF;

            IF (v_Type_ = 2) THEN
               Open cur_Out For
                select DISTINCT LAST_NAME  AS SEARCH_NAME
                From V_SPONSOR_INFO A  
                where TRIM(UPPER(LAST_NAME)) like TRIM(UPPER(v_Search_Value)) || '%'
                FETCH FIRST v_rows ROWS ONLY;
            END IF;
        ELSE
            Open cur_Out For
            select DISTINCT v_Search_Value  AS SEARCH_NAME
            From DUAL;
        END IF;
    END IF;

END SP_SEARCH_BY_SPONSORS_FIRSTLAST_NAMES;

PROCEDURE SP_SEARCH_BY_UACID ( v_Search_Value VARCHAR2, v_RoleType VARCHAR2, cur_Out OUT SYS_REFCURSOR ) AS v_Param1 varchar2(300); BEGIN v_Param1 := CONCAT(CONCAT('%',UPPER(v_Search_Value)), '%');
If v_RoleType = 'ChildAdvocade' Or v_RoleType = 'LegalServices' Then Open cur_Out For Select A.*, Count(*) over() as rowC from( Select Distinct A.UAC_ID, B.UAC_PROGRAM_ID, ALIEN_NO, FIRST_NAME, LAST_NAME, AKA, DOB, COB, GENDER, Case When CURRENT_STATUS is null Then A.UAC_STATUS Else B.CURRENT_STATUS End as UAC_STATUS,B.DATE_ADMITTED, B.DATE_DISCHARGED, C.PROGRAM_NAME, '' as APPROVED_FIELD_OFFICE, '' as ORR_APPROVED_DATE, C.PROGRAM_ID, DATE_OF_APPREHENSION, trunc(sysdate) - trunc(B.DATE_ADMITTED) LOS, E.DROP_DOWN_DISPLAY as DISCHARGE_TYPE, RELATIONSHIP_GRP_ID From UAC_INFO A Left Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Left join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID Left Join UAC_PROGRAM_EXIT D On B.UAC_PROGRAM_ID = D.UAC_PROGRAM_ID Left Join LIST_DISCHARGE_TYPE E On D.TYPE_OF_DISCHARGE = E.DISCHARGE_TYPE_ID Where A.ALIEN_NO in (Select ALIEN_NO from UAC_INFO Where UAC_ID like v_Param1) And A.UAC_STATUS in ('ADMITTED', 'IN-TRANSFER', 'DISCHARGED') ) A Order by DATE_ADMITTED Desc FETCH FIRST 150 ROWS ONLY; Else Open cur_Out For Select A.*, Count(*) over() as rowC from( Select Distinct A.UAC_ID, B.UAC_PROGRAM_ID, ALIEN_NO, FIRST_NAME, LAST_NAME, AKA, DOB, COB, GENDER, Case When CURRENT_STATUS is null Then A.UAC_STATUS Else B.CURRENT_STATUS End as UAC_STATUS,B.DATE_ADMITTED, B.DATE_DISCHARGED, C.PROGRAM_NAME, '' as APPROVED_FIELD_OFFICE, '' as ORR_APPROVED_DATE, C.PROGRAM_ID, DATE_OF_APPREHENSION, trunc(sysdate) - trunc(B.DATE_ADMITTED) LOS, E.DROP_DOWN_DISPLAY as DISCHARGE_TYPE, RELATIONSHIP_GRP_ID From UAC_INFO A Left Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Left join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID Left Join UAC_PROGRAM_EXIT D On B.UAC_PROGRAM_ID = D.UAC_PROGRAM_ID Left Join LIST_DISCHARGE_TYPE E On D.TYPE_OF_DISCHARGE = E.DISCHARGE_TYPE_ID Where A.ALIEN_NO in (Select ALIEN_NO from UAC_INFO Where UAC_ID like v_Param1) ) A Order by DATE_ADMITTED Desc FETCH FIRST 150 ROWS ONLY; End If;
END SP_SEARCH_BY_UACID;

PROCEDURE "SP_SEARCH_EVENTS_BY_DATE" ( vEVENT_DATE DATE default NULL, vPROG_ID NUMBER, cur_Out OUT SYS_REFCURSOR ) AS
progId_ NUMBER(3); childEventDate DATE; BEGIN select PROGRAM_ID into progId_ from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = vPROG_ID;
If vEVENT_DATE is null then OPEN cur_Out for select distinct T1.EVENT_ID, --'Child Level Event' as EVENT_TYPE, nvl((select distinct EVENT_TYPE from UAC_SIR_NEW where EVENT_ID = T1.EVENT_ID and EVENT_TYPE is not null and rownum = 1), 'SIR Event') as EVENT_TYPE, t1.date_created, T1.SYNOPSIS_OF_EVENT, CASE when APPROX_MONTH = '1' then 'Jan'||'-'|| APPROX_YEAR when APPROX_MONTH = '2' then 'Feb'||'-'||APPROX_YEAR when APPROX_MONTH = '3' then 'Mar'||'-'||APPROX_YEAR when APPROX_MONTH = '4' then 'Apr'||'-'||APPROX_YEAR when APPROX_MONTH = '5' then 'May'||'-'||APPROX_YEAR when APPROX_MONTH = '6' then 'June'||'-'||APPROX_YEAR when APPROX_MONTH = '7' then 'July'||'-'||APPROX_YEAR when APPROX_MONTH = '8' then 'Aug'||'-'||APPROX_YEAR when APPROX_MONTH = '9' then 'Sept'||'-'||APPROX_YEAR when APPROX_MONTH = '10' then 'Oct'||'-'||APPROX_YEAR when APPROX_MONTH = '11' then 'Nov'||'-'||APPROX_YEAR when APPROX_MONTH = '12' then 'Dec'||'-'||APPROX_YEAR else TO_CHAR(T1.DATE_OF_EVENT,'DD-MON-YYYY') END DATE_OF_EVENT, T1.TIME_OF_EVENT FROM UAC_SIR_EVENT T1 WHERE T1.PROGRAM_ID = progId_ AND T1.ISDELETED = 0 AND T1.EVENT_ID not in (Select EVENT_ID from UAC_SIR_NEW Where UAC_PROGRAM_ID = vPROG_ID) union select T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,t2.date_created,T2.SYNOPSIS, TO_CHAR(t2.date_event,'DD-MON-YYYY') AS DATE_OF_EVENT, T2.TIME_EVENT from PROGRAM_LEVEL_EVENTS_INFO T2 WHERE T2.PROGRAM_ID = progId_ AND T2.EVENT_ID not in (Select EVENT_ID from UAC_SIR_NEW Where UAC_PROGRAM_ID = vPROG_ID) order by EVENT_ID; Else OPEN cur_Out for select distinct T1.EVENT_ID, --'Child Level Event' as EVENT_TYPE, nvl((select distinct EVENT_TYPE from UAC_SIR_NEW where EVENT_ID = T1.EVENT_ID and EVENT_TYPE is not null and rownum = 1), 'SIR Event') as EVENT_TYPE, t1.date_created, T1.SYNOPSIS_OF_EVENT, CASE when APPROX_MONTH = '1' then 'Jan'||'-'|| APPROX_YEAR when APPROX_MONTH = '2' then 'Feb'||'-'||APPROX_YEAR when APPROX_MONTH = '3' then 'Mar'||'-'||APPROX_YEAR when APPROX_MONTH = '4' then 'Apr'||'-'||APPROX_YEAR when APPROX_MONTH = '5' then 'May'||'-'||APPROX_YEAR when APPROX_MONTH = '6' then 'June'||'-'||APPROX_YEAR when APPROX_MONTH = '7' then 'July'||'-'||APPROX_YEAR when APPROX_MONTH = '8' then 'Aug'||'-'||APPROX_YEAR when APPROX_MONTH = '9' then 'Sept'||'-'||APPROX_YEAR when APPROX_MONTH = '10' then 'Oct'||'-'||APPROX_YEAR when APPROX_MONTH = '11' then 'Nov'||'-'||APPROX_YEAR when APPROX_MONTH = '12' then 'Dec'||'-'||APPROX_YEAR else TO_CHAR(T1.DATE_OF_EVENT,'DD-MON-YYYY') END DATE_OF_EVENT, T1.TIME_OF_EVENT FROM UAC_SIR_EVENT T1 WHERE T1.PROGRAM_ID = progId_ And DateDiff('day',T1.DATE_OF_EVENT , vEVENT_DATE) = 0 AND T1.ISDELETED = 0 AND T1.EVENT_ID not in (Select EVENT_ID from UAC_SIR_NEW Where UAC_PROGRAM_ID = vPROG_ID) union select T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,t2.date_created,T2.SYNOPSIS, TO_CHAR(T2.DATE_EVENT,'DD-MON-YYYY'), T2.TIME_EVENT from PROGRAM_LEVEL_EVENTS_INFO T2 WHERE T2.PROGRAM_ID = progId_ AND T2.EVENT_ID not in (Select EVENT_ID from UAC_SIR_NEW Where UAC_PROGRAM_ID = vPROG_ID) And DateDiff('day',T2.DATE_EVENT , vEVENT_DATE) = 0 order by EVENT_ID; End If; END SP_SEARCH_EVENTS_BY_DATE;

PROCEDURE "SP_SEARCH_EVENTS_BY_EVENT_ID" ( vPROG_ID NUMBER, vEVENT_ID NUMBER, cur_Out OUT SYS_REFCURSOR ) AS
progId_ NUMBER(3); BEGIN select PROGRAM_ID into progId_ from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = vPROG_ID;
OPEN cur_Out for select distinct T1.EVENT_ID, nvl((select distinct EVENT_TYPE from UAC_SIR_NEW where EVENT_ID = T1.EVENT_ID and EVENT_TYPE is not null and rownum = 1), 'SIR Event') as EVENT_TYPE,t1.date_created, T1.SYNOPSIS_OF_EVENT, CASE when APPROX_MONTH = '1' then 'Jan'||'-'|| APPROX_YEAR when APPROX_MONTH = '2' then 'Feb'||'-'||APPROX_YEAR when APPROX_MONTH = '3' then 'Mar'||'-'||APPROX_YEAR when APPROX_MONTH = '4' then 'Apr'||'-'||APPROX_YEAR when APPROX_MONTH = '5' then 'May'||'-'||APPROX_YEAR when APPROX_MONTH = '6' then 'June'||'-'||APPROX_YEAR when APPROX_MONTH = '7' then 'July'||'-'||APPROX_YEAR when APPROX_MONTH = '8' then 'Aug'||'-'||APPROX_YEAR when APPROX_MONTH = '9' then 'Sept'||'-'||APPROX_YEAR when APPROX_MONTH = '10' then 'Oct'||'-'||APPROX_YEAR when APPROX_MONTH = '11' then 'Nov'||'-'||APPROX_YEAR when APPROX_MONTH = '12' then 'Dec'||'-'||APPROX_YEAR else TO_CHAR(T1.DATE_OF_EVENT,'DD-MON-YYYY') END DATE_OF_EVENT, T1.TIME_OF_EVENT FROM UAC_SIR_EVENT T1 WHERE T1.EVENT_ID = vEVENT_ID AND T1.ISDELETED = 0 AND T1.EVENT_ID not in (Select EVENT_ID from UAC_SIR_NEW Where UAC_PROGRAM_ID = vPROG_ID) AND T1.PROGRAM_ID = progId_ -- Fix for UCP-2585 union select T2.EVENT_ID,'Program Level Event' as EVENT_TYPE,t2.date_created,T2.SYNOPSIS,TO_CHAR(T2.DATE_EVENT,'DD-MON-YYYY') AS DATE_OF_EVENT, T2.TIME_EVENT from PROGRAM_LEVEL_EVENTS_INFO T2 WHERE T2.EVENT_ID = vEVENT_ID AND T2.EVENT_ID not in (Select EVENT_ID from UAC_SIR_NEW Where UAC_PROGRAM_ID = vPROG_ID) AND T2.PROGRAM_ID = progId_ -- Fix for UCP-2585 order by EVENT_ID;
END SP_SEARCH_EVENTS_BY_EVENT_ID;

PROCEDURE "SP_SEARCH_USER" ( v_UserID VARCHAR2, v_FirstName VARCHAR2, v_LastName VARCHAR2, v_Email VARCHAR2, v_Role_Type VARCHAR2, v_User_Privilege VARCHAR2, v_ProgramIDs VARCHAR2, v_CurrentUser VARCHAR2, v_CurrentRole VARCHAR2, v_CurrentProgList VARCHAR2, v_User_Active VARCHAR2, v_Provider VARCHAR2, cur_Out OUT SYS_REFCURSOR ) AS v_UserID2 VARCHAR2(100); v_FirstName2 VARCHAR2(100); v_LastName2 VARCHAR2(100); v_Email2 VARCHAR2(100); v_Role_Type2 VARCHAR2(100); v_User_Privilege2 VARCHAR2(100); v_Provider2 VARCHAR2(100);
tCount NUMBER(10); BEGIN v_UserID2 := CONCAT(CONCAT('%',UPPER(v_UserID)), '%'); v_FirstName2 := CONCAT(CONCAT('%',UPPER(v_FirstName)), '%'); v_LastName2 := CONCAT(CONCAT('%',UPPER(v_LastName)), '%'); v_Email2 := CONCAT(CONCAT('%',UPPER(v_Email)), '%'); v_Role_Type2 := UPPER(v_Role_Type); v_User_Privilege2 := CONCAT(CONCAT('%',UPPER(v_User_Privilege)), '%'); v_Provider2 := CONCAT(CONCAT('%',UPPER(v_Provider)), '%');
dbms_output.put_line(v_FirstName2); if v_ProgramIDs ='NoProgram' then If v_CurrentRole = 'HQ' OR v_CurrentRole = 'Health' OR v_CurrentRole = 'FFS' Then If v_Role_Type2 = 'ICE' OR v_Role_Type2 = 'CBP' Then Open cur_Out For select A.USER_ID, LAST_NAME, FIRST_NAME, USER_PRIVELEGE,ROLE_TYPE, LOGINCOUNT,SUSPENDED, '' as PROGRAM_NAME from ADM_LIST_WEB_USER A where UPPER(NVL(A.USER_ID,' ')) LIKE v_UserID2 And SUSPENDED = v_User_Active And UPPER(NVL(LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(A.EMAIL, 'XXX')) LIKE v_Email2 And UPPER(ROLE_TYPE) = v_Role_Type2 And UPPER(USER_PRIVELEGE) LIKE v_User_Privilege2 And UPPER(NVL(PROVIDER,' ')) LIKE v_Provider2 And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results order by LAST_NAME, FIRST_NAME; Elsif v_Role_Type = 'HomeStudyPRS' Then Open cur_Out For select USER_ID, LAST_NAME, FIRST_NAME, USER_PRIVELEGE,ROLE_TYPE, Case When v_Role_Type = 'HomeStudyPRS' Then B.HOMESTUDY_PROVIDER ELSE '' End as PROGRAM_NAME,LOGINCOUNT,SUSPENDED from ADM_LIST_WEB_USER A Left Join LIST_HOMESTUDY_PROVIDER B on A.PROVIDER = B.HOMESTUDY_PROVIDER_ID where UPPER(NVL(USER_ID,' ')) LIKE v_UserID2 And SUSPENDED = v_User_Active And UPPER(NVL(LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(EMAIL, 'XXX')) LIKE v_Email2 And UPPER(ROLE_TYPE) = v_Role_Type2 And UPPER(USER_PRIVELEGE) LIKE v_User_Privilege2 And UPPER(NVL(PROVIDER,' ')) LIKE v_Provider2 And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results order by LAST_NAME, FIRST_NAME; Elsif v_Role_Type = 'Program' Then Open cur_Out For select A.USER_ID, LAST_NAME, FIRST_NAME, USER_PRIVELEGE,ROLE_TYPE, LOGINCOUNT,SUSPENDED, LISTAGG(C.PROGRAM_NAME,',') WITHIN GROUP (ORDER BY C.PROGRAM_NAME ) as PROGRAM_NAME from ADM_LIST_WEB_USER A Inner Join ADM_USER_PROGRAM B On A.USER_ID = B.USER_ID AND A.ROLE_TYPE = B.USER_TYPE Inner Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID And C.ISACTIVE = 1 where UPPER(NVL(A.USER_ID,' ')) LIKE v_UserID2 And SUSPENDED = v_User_Active And UPPER(NVL(LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(A.EMAIL, 'XXX')) LIKE v_Email2 And UPPER(ROLE_TYPE) = v_Role_Type2 And UPPER(USER_PRIVELEGE) LIKE v_User_Privilege2 And UPPER(NVL(PROVIDER,' ')) LIKE v_Provider2 And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results GROUP BY A.USER_ID, LAST_NAME, FIRST_NAME,USER_PRIVELEGE,ROLE_TYPE,LOGINCOUNT,SUSPENDED order by LAST_NAME, FIRST_NAME; Else Open cur_Out For select USER_ID, LAST_NAME, FIRST_NAME, USER_PRIVELEGE,ROLE_TYPE, '' as PROGRAM_NAME,LOGINCOUNT,SUSPENDED from ADM_LIST_WEB_USER A where UPPER(NVL(USER_ID,' ')) LIKE v_UserID2 And SUSPENDED = v_User_Active And UPPER(NVL(LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(EMAIL, 'XXX')) LIKE v_Email2 And UPPER(ROLE_TYPE) = v_Role_Type2 And UPPER(USER_PRIVELEGE) LIKE v_User_Privilege2 And UPPER(NVL(PROVIDER,' ')) LIKE v_Provider2 And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results order by LAST_NAME, FIRST_NAME; End If; Elsif v_CurrentRole = 'HomeStudyPRS' Then Open cur_Out For select USER_ID, LAST_NAME, FIRST_NAME, USER_PRIVELEGE,ROLE_TYPE, Case When v_Role_Type = 'HomeStudyPRS' Then B.HOMESTUDY_PROVIDER ELSE '' End as PROGRAM_NAME,LOGINCOUNT,SUSPENDED from ADM_LIST_WEB_USER A Left Join LIST_HOMESTUDY_PROVIDER B on A.PROVIDER = B.HOMESTUDY_PROVIDER_ID where UPPER(NVL(USER_ID,' ')) LIKE v_UserID2 And SUSPENDED = v_User_Active And UPPER(NVL(LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(EMAIL, 'XXX')) LIKE v_Email2 And UPPER(ROLE_TYPE) = v_Role_Type2 And UPPER(USER_PRIVELEGE) LIKE v_User_Privilege2 And UPPER(NVL(PROVIDER,' ')) LIKE v_Provider2 And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results order by LAST_NAME, FIRST_NAME; Elsif v_CurrentRole = 'Program' Then Open cur_Out For Select A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED, Case When v_Role_Type = 'HomeStudyPRS' Then LISTAGG(HOMESTUDY_PROVIDER,',') WITHIN GROUP (ORDER BY A.PROVIDER ) Else LISTAGG(C.PROGRAM_NAME,',') WITHIN GROUP (ORDER BY C.PROGRAM_NAME ) End as PROGRAM_NAME from ADM_LIST_WEB_USER A Left Join ADM_USER_PROGRAM B On A.USER_ID = B.USER_ID Left Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID And C.ISACTIVE = 1 Left Join LIST_HOMESTUDY_PROVIDER D on A.PROVIDER = D.HOMESTUDY_PROVIDER_ID where UPPER(NVL(A.USER_ID,' ')) LIKE v_UserID2 And A.SUSPENDED = v_User_Active And UPPER(NVL(A.LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(A.FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(A.EMAIL, 'XXX')) LIKE v_Email2 And UPPER(A.ROLE_TYPE) = v_Role_Type2 And UPPER(A.USER_PRIVELEGE) LIKE v_User_Privilege2 --And C.PROGRAM_ID in (Select PROGRAM_ID from ADM_USER_PROGRAM Where USER_ID = v_CurrentUser) And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results Group By A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED order by A.LAST_NAME, A.FIRST_NAME; Else Open cur_Out For Select A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED, Case When v_Role_Type = 'HomeStudyPRS' Then LISTAGG(HOMESTUDY_PROVIDER,',') WITHIN GROUP (ORDER BY A.PROVIDER ) Else '' End as PROGRAM_NAME from ADM_LIST_WEB_USER A Left Join ADM_USER_PROGRAM B On A.USER_ID = B.USER_ID Left Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID And C.ISACTIVE = 1 Left Join LIST_HOMESTUDY_PROVIDER D on A.PROVIDER = D.HOMESTUDY_PROVIDER_ID where UPPER(NVL(A.USER_ID,' ')) LIKE v_UserID2 And A.SUSPENDED = v_User_Active And UPPER(NVL(A.LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(A.FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(A.EMAIL, 'XXX')) LIKE v_Email2 And UPPER(A.ROLE_TYPE) = v_Role_Type2 And UPPER(A.USER_PRIVELEGE) LIKE v_User_Privilege2 And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results Group By A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED order by A.LAST_NAME, A.FIRST_NAME; End If; else If v_CurrentRole = 'HQ' OR v_CurrentRole = 'Health' OR v_CurrentRole = 'FFS' Then -- UCP-255 0722/2024 /* Open cur_Out For select USER_ID, LAST_NAME, FIRST_NAME, 0 PROGRAM_ID, USER_PRIVELEGE,ROLE_TYPE, Case When v_Role_Type = 'HomeStudyPRS' Then B.HOMESTUDY_PROVIDER Else '' End as PROGRAM_NAME,LOGINCOUNT,SUSPENDED from ADM_LIST_WEB_USER A Left Join LIST_HOMESTUDY_PROVIDER B on A.PROVIDER = B.HOMESTUDY_PROVIDER_ID where UPPER(NVL(USER_ID,' ')) LIKE v_UserID2 And SUSPENDED = v_User_Active And UPPER(NVL(LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(EMAIL, 'XXX')) LIKE v_Email2 And UPPER(ROLE_TYPE) = v_Role_Type2 And UPPER(USER_PRIVELEGE) LIKE v_User_Privilege2 And USER_ID in (select USER_ID from ADM_USER_PROGRAM where PROGRAM_ID in ( Select to_number(column_value) from Table(SPLIT(v_ProgramIDs, ',')))) And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results order by LAST_NAME, FIRST_NAME; */ Open cur_Out For Select A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED, LISTAGG(C.PROGRAM_NAME,',') WITHIN GROUP (ORDER BY C.PROGRAM_NAME ) as PROGRAM_NAME from ADM_LIST_WEB_USER A Inner Join ADM_USER_PROGRAM B On A.USER_ID = B.USER_ID Inner Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID And C.ISACTIVE = 1 Left Join LIST_HOMESTUDY_PROVIDER D on A.PROVIDER = D.HOMESTUDY_PROVIDER_ID where UPPER(NVL(A.USER_ID,' ')) LIKE v_UserID2 And A.SUSPENDED = v_User_Active And UPPER(NVL(A.LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(A.FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(A.EMAIL, 'XXX')) LIKE v_Email2 And UPPER(A.ROLE_TYPE) = v_Role_Type2 And UPPER(A.USER_PRIVELEGE) LIKE v_User_Privilege2 And A.USER_ID in (select USER_ID from ADM_USER_PROGRAM where PROGRAM_ID in ( Select to_number(column_value) from Table(SPLIT(v_ProgramIDs, ',')))) --And C.PROGRAM_ID in (Select PROGRAM_ID from ADM_USER_PROGRAM Where USER_ID = v_CurrentUser) And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results Group By A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED order by LAST_NAME, FIRST_NAME; Elsif v_CurrentRole = 'HomeStudyPRS' Then If v_Role_Type = 'HomeStudyPRS' Then Open cur_Out For select USER_ID, LAST_NAME, FIRST_NAME, 0 PROGRAM_ID, USER_PRIVELEGE,ROLE_TYPE, B.HOMESTUDY_PROVIDER as PROGRAM_NAME,LOGINCOUNT,SUSPENDED from ADM_LIST_WEB_USER A Left Join LIST_HOMESTUDY_PROVIDER B on A.PROVIDER = B.HOMESTUDY_PROVIDER_ID where UPPER(NVL(USER_ID,' ')) LIKE v_UserID2 and SUSPENDED = v_User_Active and UPPER(NVL(LAST_NAME,' ')) LIKE v_LastName2 and UPPER(NVL(FIRST_NAME,' ')) LIKE v_FirstName2 and UPPER(nvl(EMAIL, 'XXX')) LIKE v_Email2 and UPPER(ROLE_TYPE) like v_Role_Type2 And UPPER(USER_PRIVELEGE) LIKE v_User_Privilege2 And UPPER(NVL(PROVIDER,' ')) LIKE v_Provider2 And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results order by LAST_NAME, FIRST_NAME; Else Open cur_Out For select A.USER_ID, A.LAST_NAME, A.FIRST_NAME, 0 PROGRAM_ID, A.USER_PRIVELEGE,A.ROLE_TYPE, LISTAGG(C.PROGRAM_NAME,',') WITHIN GROUP (ORDER BY C.PROGRAM_NAME ) as PROGRAM_NAME, LOGINCOUNT,SUSPENDED from ADM_LIST_WEB_USER A Inner Join ADM_USER_PROGRAM B On A.USER_ID = B.USER_ID Inner Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID And C.ISACTIVE = 1 where UPPER(NVL(A.USER_ID,' ')) LIKE v_UserID2 And SUSPENDED = v_User_Active And UPPER(NVL(A.LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(A.FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(A.EMAIL, 'XXX')) LIKE v_Email2 And UPPER(ROLE_TYPE) = v_Role_Type2 And UPPER(USER_PRIVELEGE) LIKE v_User_Privilege2 And A.USER_ID in (select USER_ID from ADM_USER_PROGRAM where PROGRAM_ID in ( Select to_number(column_value) from Table(SPLIT(v_ProgramIDs, ',')))) And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results Group By A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED order by LAST_NAME, FIRST_NAME; End If; Elsif v_CurrentRole = 'Program' Then Open cur_Out For Select A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED, LISTAGG(C.PROGRAM_NAME,',') WITHIN GROUP (ORDER BY C.PROGRAM_NAME ) as PROGRAM_NAME from ADM_LIST_WEB_USER A Inner Join ADM_USER_PROGRAM B On A.USER_ID = B.USER_ID Inner Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID And C.ISACTIVE = 1 Left Join LIST_HOMESTUDY_PROVIDER D on A.PROVIDER = D.HOMESTUDY_PROVIDER_ID where UPPER(NVL(A.USER_ID,' ')) LIKE v_UserID2 And A.SUSPENDED = v_User_Active And UPPER(NVL(A.LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(A.FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(A.EMAIL, 'XXX')) LIKE v_Email2 And UPPER(A.ROLE_TYPE) = v_Role_Type2 And UPPER(A.USER_PRIVELEGE) LIKE v_User_Privilege2 And A.USER_ID in (select USER_ID from ADM_USER_PROGRAM where PROGRAM_ID in ( Select to_number(column_value) from Table(SPLIT(v_ProgramIDs, ',')))) --And C.PROGRAM_ID in (Select PROGRAM_ID from ADM_USER_PROGRAM Where USER_ID = v_CurrentUser) And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results Group By A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED order by LAST_NAME, FIRST_NAME; Else Open cur_Out For Select A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED, '' as PROGRAM_NAME from ADM_LIST_WEB_USER A Inner Join ADM_USER_PROGRAM B On A.USER_ID = B.USER_ID Inner Join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID And C.ISACTIVE = 1 Left Join LIST_HOMESTUDY_PROVIDER D on A.PROVIDER = D.HOMESTUDY_PROVIDER_ID where UPPER(NVL(A.USER_ID,' ')) LIKE v_UserID2 And A.SUSPENDED = v_User_Active And UPPER(NVL(A.LAST_NAME,' ')) LIKE v_LastName2 And UPPER(NVL(A.FIRST_NAME,' ')) LIKE v_FirstName2 And UPPER(nvl(A.EMAIL, 'XXX')) LIKE v_Email2 And UPPER(A.ROLE_TYPE) = v_Role_Type2 And UPPER(A.USER_PRIVELEGE) LIKE v_User_Privilege2 And BLOCKED = 0 -- To exclude blocked users accounts from diplaying in search results Group By A.USER_ID, A.LAST_NAME, A.FIRST_NAME, A.USER_PRIVELEGE, A.ROLE_TYPE, A.LOGINCOUNT, A.SUSPENDED order by LAST_NAME, FIRST_NAME; End If; end if; END SP_SEARCH_USER;

PROCEDURE "SP_SET_USER_PWD_EXPIRE" AS BEGIN
/* Update ADM_LIST_WEB_USER U SET U.SUSPENDED = 1 WHERE U.USER_ID IN (Select DISTINCT U.USER_ID FROM ADM_LIST_WEB_USER U INNER JOIN (SELECT USER_ID, max(PASSWORDLASTCHANGED) as MAX_PASSWORDLASTCHANGED FROM ADM_WEB_USER_PWD GROUP BY USER_ID) M ON M.USER_ID = U.USER_ID WHERE Not U.EMAIL is null AND U.SUSPENDED = 0 AND datediff('Day', M.MAX_PASSWORDLASTCHANGED, CURRENT_DATE) > (SELECT to_number(PARAM_VALUE) from UACPORTAL_APP_PARAMS where PARAM_NAME = 'PWD_EXPIRATION_PERIOD')); */ Update ADM_LIST_WEB_USER U SET U.SUSPENDED = 1 WHERE U.SUSPENDED = 0 AND datediff('Day', U.LASTLOGIN, CURRENT_DATE) > (SELECT to_number(PARAM_VALUE) from UACPORTAL_APP_PARAMS where PARAM_NAME = 'LASTLOGIN_EXPIRATION_PERIOD');
COMMIT; END SP_SET_USER_PWD_EXPIRE;

PROCEDURE "SP_SIR_PROGRAM_UAC_INFO" ( Event_ID IN NUMBER, Event_Type VARCHAR2, cur_Out OUT SYS_REFCURSOR ) AS EVENTId_ NUMBER(10) := Event_ID; EventType_ Varchar2(50) := Event_Type;
BEGIN
Open cur_Out For Select distinct D.Event_ID,d.SIR_ID, A.UAC_ID, B.UAC_PROGRAM_ID,ALIEN_NO, FIRST_NAME, LAST_NAME, AKA, Trunc(months_between(sysdate, A.DOB) /12) as Age,DOB, COB, UAC_STATUS, B.DATE_ADMITTED, C.PROGRAM_NAME, C.PROGRAM_ID, A.GENDER, Case When CURRENT_STATUS = 'ADMITTED' Or CURRENT_STATUS = 'IN-TRANSFER' Then TRunc(SysDate) - Trunc(DATE_ADMITTED) When CURRENT_STATUS = 'ENROUTE' Then 0 When CURRENT_STATUS = 'DISCHARGED' Then Trunc(DATE_DISCHARGED) - Trunc(DATE_ADMITTED) End as LOS, CASE WHEN A.UAC_STATUS = 'ADMITTED' THEN DateDiff('day', B.DATE_ADMITTED, SysDate) WHEN A.UAC_STATUS = 'DISCHARGED' THEN DateDiff('day', B.DAte_ADMITTED, DATE_DISCHARGED) WHEN A.UAC_STATUS = 'IN-TRANSFER' THEN DateDiff('day', B.DATE_ADMITTED, SysDate) ELSE 0 END AS LOC , D.CARE_PROVIDER_NAME,D.CARE_PROVIDER_CITY,D.CARE_PROVIDER_STATE,D.SA_HOW_UAC_INVOLVED,C.CITY,C.STATE From UAC_INFO A Inner Join UAC_PROGRAM_INFO B On A.UAC_ID = B.UAC_ID Inner join LIST_PROGRAMS C On B.PROGRAM_ID = C.PROGRAM_ID Inner join UAC_SIR_NEW D on B.UAC_PROGRAM_ID=D.UAC_PROGRAM_ID Where D.EVENT_ID=EVENTId_ And D.EVENT_TYPE=EventType_ And B.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER', 'DISCHARGED') Order by B.DATE_ADMITTED Desc;
END SP_SIR_PROGRAM_UAC_INFO;

PROCEDURE SP_SPONSOR_EXPERIENCE_ADD_HISTORY ( V_USER_ID IN VARCHAR2 , V_JSON IN VARCHAR2 , V_EVENT_TYPE IN VARCHAR2 , V_PROCESS_TYPE IN VARCHAR2 , V_ERROR_TYPE IN VARCHAR2 ) AS BEGIN INSERT INTO SPONSOR_EXPERIENCE_HISTORY (USERID, UPUSER, UPTIME, JSON, EVENT_TYPE, PROCESS_TYPE, ERROR_TYPE) VALUES (V_USER_ID, 'Sponsor Application', SYSTIMESTAMP, V_JSON, V_EVENT_TYPE, V_PROCESS_TYPE, V_ERROR_TYPE);
-- remove records that are older than 60 days DELETE FROM SPONSOR_EXPERIENCE_HISTORY WHERE UPTIME <= sysdate - 60;
END SP_SPONSOR_EXPERIENCE_ADD_HISTORY;

PROCEDURE SP_SPONSOR_EXPERIENCE_ADD_SPONSOR_TYPE ( V_SPONSOR_ID IN NUMBER , V_USER_ID IN VARCHAR2 , V_JSON IN VARCHAR2 , V_EVENT_TYPE IN VARCHAR2 , V_PROCESS_TYPE IN VARCHAR2 , V_SPONSOR_TYPE IN VARCHAR2 ) AS BEGIN
INSERT INTO SPONSOR_EXPERIENCE_INFO (SPONSOR_ID, UPUSER, UPTIME, JSON, EVENT_TYPE, PROCESS_TYPE, SPONSOR_TYPE) SELECT V_SPONSOR_ID, V_USER_ID, SYSTIMESTAMP, V_JSON, V_EVENT_TYPE, V_PROCESS_TYPE, V_SPONSOR_TYPE FROM DUAL WHERE 1 = 0; -- remove records that are older than 90 days --DELETE FROM SPONSOR_EXPERIENCE_INFO WHERE UPTIME <= sysdate - 60;
END SP_SPONSOR_EXPERIENCE_ADD_SPONSOR_TYPE;

PROCEDURE "SP_SPONSOR_EXPERIENCE_INFO" (
SPONSOR_ID  IN NUMBER,
cur_OUT OUT SYS_REFCURSOR
)
AS 
sponsorId_    NUMBER(10) := SPONSOR_ID;
BEGIN
  OPEN cur_OUT FOR
  Select A.* 
  from SPONSOR_EXPERIENCE_INFO A
  Where A.Sponsor_ID = sponsorId_ ;
END SP_SPONSOR_EXPERIENCE_INFO;

PROCEDURE SP_SPONSOR_EXPERIENCE_INSERT_DOCUMENT ( V_PROGRAM_ID IN NUMBER , V_FILE_NAME IN VARCHAR2 , V_FILE_Type IN VARCHAR2 , V_FILE_Size IN NUMBER , V_ORG_FILE_NAME IN VARCHAR2 , V_Remarks IN VARCHAR2 , V_DOCUMENT_TYPE_ID IN VARCHAR2 , V_UPDATEBY IN VARCHAR2 , V_SF_APPLICATION_ID IN VARCHAR2 , V_SF_CONTENT_ID IN VARCHAR2 , cur_Out OUT SYS_REFCURSOR ) AS
numberOfRowsUpdates NUMBER(10.0) :=0; n NUMBER(4); BEGIN /* SELECT C.UAC_PROGRAM_ID into programId From SPONSOR_INFO A Join UAC_SPONSOR_LIST E On A.SPONSOR_ID = E.SPONSOR_ID Join UAC_PROGRAM_INFO C On E.UAC_ID = C.UAC_ID Join List_Programs B On C.PROGRAM_ID = B.PROGRAM_ID Join UAC_INFO U On E.UAC_ID = U.UAC_ID WHERE A.SPONSOR_ID = V_SPONSOR_ID AND U.UAC_ID = V_UAC_ID and (C.CURRENT_STATUS = 'ADMITTED' or C.CURRENT_STATUS ='IN-TRANSFER'); */ --DBMS_OUTPUT.PUT_LINE('programId: ' || programId);
--CHECK IF SF_CONTENT_ID ALready EXISTS for this kid SELECT COUNT(*) into n FROM UAC_DOCUMENTS WHERE UAC_PROGRAM_ID = V_PROGRAM_ID AND SF_CONTENT_ID = V_SF_CONTENT_ID AND (ISDELETED = 0 Or ISDELETED is null); if n = 0 Then INSERT INTO UAC_DOCUMENTS (UAC_PROGRAM_ID,FILE_NAME,FILE_TYPE,FILE_SIZE,FILE_CONTENT,REMARKS,UPLOADED_BY,UPTIME,UPUSER,ISDELETED,DATE_CREATED,ORG_FILE_NAME,DOCUMENT_TYPE_ID,SF_APPLICATION_ID,SF_CONTENT_ID) VALUES (V_PROGRAM_ID ,V_FILE_NAME ,V_FILE_Type ,V_FILE_Size ,NULL ,V_Remarks ,'Sponsor Application' ,SYSTIMESTAMP ,V_UPDATEBY ,0 ,SYSTIMESTAMP ,V_ORG_FILE_NAME ,V_DOCUMENT_TYPE_ID ,V_SF_APPLICATION_ID ,V_SF_CONTENT_ID ); end if;
numberOfRowsUpdates := SQL%ROWCOUNT; --DBMS_OUTPUT.PUT_LINE('Number of Rows Updated: ' || numberOfRowsUpdates);
Open cur_Out For Select numberOfRowsUpdates AS UPDATED_ROW_COUNT from dual;
END SP_SPONSOR_EXPERIENCE_INSERT_DOCUMENT;

PROCEDURE SP_SPONSOR_EXPERIENCE_UPDATE_APPLICATION_ID 
(
  V_SPONSOR_ID IN NUMBER 
, V_UAC_ID IN NUMBER
, V_SF_APPLICATION_ID IN VARCHAR2
, V_SF_APPLICATION_TYPE IN VARCHAR2
, V_ARI_SF_ATTACHMENT_ID IN VARCHAR2
, V_FRA_SF_ATTACHMENT_ID IN VARCHAR2
, V_SF_CONTENT_ID IN VARCHAR2
, V_UPDATEBY IN VARCHAR2
, cur_Out  OUT SYS_REFCURSOR
) AS 

numberOfRowsUpdates NUMBER(10.0) :=0;
ariUpdatedDate date := null;
fraUpdatedDate date := null;

BEGIN
    -- YOU SHOULD NOT RECEIVE AN FRA AND AN ARI AT THE SAME TIME
    -- WHEN WE GET A FRA, WE KNOW THAT ALL DOCUMENTS ARE COMPLETED
    IF (V_ARI_SF_ATTACHMENT_ID IS NOT NULL) THEN
        ariUpdatedDate := SYSTIMESTAMP;
    END IF;
    IF (V_FRA_SF_ATTACHMENT_ID IS NOT NULL) THEN
        fraUpdatedDate := SYSTIMESTAMP;
    END IF;

    IF (V_SF_CONTENT_ID IS NOT NULL) THEN
      Update UAC_SPONSOR_LIST set 
        SF_CONTENT_ID = V_SF_CONTENT_ID,
        UPUSER = 'Sponsor Application',
        UPTIME = SYSTIMESTAMP
        WHERE SPONSOR_ID = V_SPONSOR_ID and UAC_ID = V_UAC_ID and is_deleted = 0;  
    ELSE
        IF (TRIM(UPPER(V_SF_APPLICATION_TYPE)) = 'COMPLETED') THEN
            -- SPONSOR DATA  
            Update UAC_SPONSOR_LIST set SF_APPLICATION_ID = NULL, --V_SF_APPLICATION_ID,
                SF_APPLICATION_TYPE = V_SF_APPLICATION_TYPE,
                --ARI_SF_ATTACHMENT_ID = V_ARI_SF_ATTACHMENT_ID,
                FRA_SF_ATTACHMENT_ID = V_FRA_SF_ATTACHMENT_ID,
                --SF_CONTENT_ID = V_SF_CONTENT_ID,
                DATE_APPLICATION_RECEIVED = SYSTIMESTAMP,
                UPUSER = 'Sponsor Application',
                UPTIME = SYSTIMESTAMP
                WHERE SPONSOR_ID = V_SPONSOR_ID and UAC_ID = V_UAC_ID and is_deleted = 0;
        ELSE
            -- SPONSOR DATA  
            Update UAC_SPONSOR_LIST set SF_APPLICATION_ID = V_SF_APPLICATION_ID,
                SF_APPLICATION_TYPE = V_SF_APPLICATION_TYPE,
                ARI_SF_ATTACHMENT_ID = V_ARI_SF_ATTACHMENT_ID,
                --FRA_SF_ATTACHMENT_ID = V_FRA_SF_ATTACHMENT_ID,
                --SF_CONTENT_ID = V_SF_CONTENT_ID,
                --ARI_DATE_RECEIVED = ariUpdatedDate,
                UPUSER = 'Sponsor Application',
                UPTIME = SYSTIMESTAMP
                WHERE SPONSOR_ID = V_SPONSOR_ID and UAC_ID = V_UAC_ID and is_deleted = 0;
        END IF;
    END IF;
    numberOfRowsUpdates := SQL%ROWCOUNT;
    --DBMS_OUTPUT.PUT_LINE('Number of Rows Updated: ' || numberOfRowsUpdates);

    Update SPONSOR_EXPERIENCE_INFO set SPONSOR_TYPE = trim(upper(V_SF_APPLICATION_TYPE)),
        UPUSER = 'SPONSOR_EXPERIENCE-' || V_UPDATEBY,
        UPTIME = SYSTIMESTAMP
        WHERE 1 = 0; --SPONSOR_ID = V_SPONSOR_ID;

    numberOfRowsUpdates := numberOfRowsUpdates + SQL%ROWCOUNT;
    --DBMS_OUTPUT.PUT_LINE('Number of Rows Updated: ' || numberOfRowsUpdates);

    -- CHILD DATA
    Update UAC_INFO set SF_APPLICATION_TYPE = trim(upper(V_SF_APPLICATION_TYPE)), SF_APPLICATION_ID = V_SF_APPLICATION_ID,
        UPUSER = 'Sponsor Application', 
        UPTIME = SYSTIMESTAMP
        WHERE UAC_ID = V_UAC_ID;    

    numberOfRowsUpdates := numberOfRowsUpdates + SQL%ROWCOUNT;
    --DBMS_OUTPUT.PUT_LINE('Number of Rows Updated: ' || numberOfRowsUpdates);

    Open cur_Out For
        Select numberOfRowsUpdates AS UPDATED_ROW_COUNT from dual;

END SP_SPONSOR_EXPERIENCE_UPDATE_APPLICATION_ID;

PROCEDURE SP_SPONSOR_EXPERIENCE_UPDATE_APPLICATION_TYPE ( V_SPONSOR_ID IN NUMBER , V_UAC_ID IN NUMBER , V_SF_APPLICATION_ID IN VARCHAR2 , V_SF_APPLICATION_TYPE IN VARCHAR2 , V_ARI_SF_ATTACHMENT_ID IN VARCHAR2 , V_FRA_SF_ATTACHMENT_ID IN VARCHAR2 , V_SF_CONTENT_ID IN VARCHAR2 , V_UPDATEBY IN VARCHAR2 , cur_Out OUT SYS_REFCURSOR ) AS
numberOfRowsUpdates NUMBER(10.0) :=0; documentType VARCHAR(25) := NULL;
BEGIN IF (TRIM(UPPER(V_SF_APPLICATION_TYPE)) = 'CANCEL') THEN documentType := V_SF_APPLICATION_TYPE;
-- leave the casestatus page dates alone Update UAC_SPONSOR_LIST set SF_APPLICATION_TYPE = 'CREATE', UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP, SF_APPLICATION_ID = null WHERE SF_APPLICATION_ID = V_SF_APPLICATION_ID and is_deleted = 0; --AND SPONSOR_ID = V_SPONSOR_ID;
-- CHILD DATA Update UAC_INFO set SF_APPLICATION_ID = null, SF_APPLICATION_TYPE = 'CREATE', UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SF_APPLICATION_ID = V_SF_APPLICATION_ID; END IF; if (TRIM(UPPER(V_SF_APPLICATION_TYPE)) = 'COMPLETED') THEN documentType := V_SF_APPLICATION_TYPE;
-- SPONSOR DATA to completed Update UAC_SPONSOR_LIST set SF_APPLICATION_TYPE = 'COMPLETED', --trim(upper(V_SF_APPLICATION_TYPE)), SF_APPLICATION_ID = null, --SF_APPLICATION_ID = V_SF_APPLICATION_ID, FRA_SF_ATTACHMENT_ID = V_FRA_SF_ATTACHMENT_ID, DATE_APPLICATION_RECEIVED = SYSTIMESTAMP, -- this is used for an FRA UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SPONSOR_ID = V_SPONSOR_ID and UAC_ID = V_UAC_ID and is_deleted = 0;
-- set other sponsors to create Update UAC_SPONSOR_LIST set SF_APPLICATION_TYPE = null, SF_APPLICATION_ID = null, --V_SF_APPLICATION_ID, FRA_SF_ATTACHMENT_ID = V_FRA_SF_ATTACHMENT_ID, DATE_APPLICATION_RECEIVED = SYSTIMESTAMP, -- this is used for an FRA UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SPONSOR_ID = V_SPONSOR_ID and UAC_ID <> V_UAC_ID and upper(SF_APPLICATION_TYPE) <> 'COMPLETED'and is_deleted = 0;
-- CHILD DATA Update UAC_INFO set SF_APPLICATION_ID = null, SF_APPLICATION_TYPE = V_SF_APPLICATION_TYPE, UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SF_APPLICATION_ID = V_SF_APPLICATION_ID and UAC_ID = V_UAC_ID; END IF; IF (documentType is NULL) THEN -- SPONSOR DATA and ARI IF (V_ARI_SF_ATTACHMENT_ID IS NOT NULL and upper(V_SF_APPLICATION_TYPE) = 'ARI') THEN Update UAC_SPONSOR_LIST --set SF_APPLICATION_TYPE = trim(upper(V_SF_APPLICATION_TYPE)), SET SF_APPLICATION_ID = V_SF_APPLICATION_ID, ARI_SF_ATTACHMENT_ID = V_ARI_SF_ATTACHMENT_ID, ARI_DATE_RECEIVED = SYSTIMESTAMP, DATE_APPLICATION_RECEIVED = null, UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SPONSOR_ID = V_SPONSOR_ID and UAC_ID = V_UAC_ID and is_deleted = 0; END IF; -- SPONSOR DATA and FRA IF (V_FRA_SF_ATTACHMENT_ID IS NOT NULL and upper(V_SF_APPLICATION_TYPE) = 'FRA') THEN Update UAC_SPONSOR_LIST --set SF_APPLICATION_TYPE = trim(upper(V_SF_APPLICATION_TYPE)), SET SF_APPLICATION_ID = V_SF_APPLICATION_ID, FRA_SF_ATTACHMENT_ID = V_FRA_SF_ATTACHMENT_ID, DATE_APPLICATION_RECEIVED = SYSTIMESTAMP, UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SPONSOR_ID = V_SPONSOR_ID and UAC_ID = V_UAC_ID and is_deleted = 0; END IF; -- SPONSOR DATA and documents IF (V_SF_CONTENT_ID IS NOT NULL) THEN Update UAC_SPONSOR_LIST --set SF_APPLICATION_TYPE = trim(upper(V_SF_APPLICATION_TYPE)), SET SF_APPLICATION_ID = V_SF_APPLICATION_ID, SF_CONTENT_ID = V_SF_CONTENT_ID, UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SPONSOR_ID = V_SPONSOR_ID and UAC_ID = V_UAC_ID and is_deleted = 0; END IF; END IF;
numberOfRowsUpdates := SQL%ROWCOUNT; --DBMS_OUTPUT.PUT_LINE('Number of Rows Updated: ' || numberOfRowsUpdates);
-- CHILD DATA IF (V_UAC_ID > 0 AND documentType is NULL) THEN IF (V_ARI_SF_ATTACHMENT_ID IS NOT NULL) THEN Update UAC_INFO set SF_APPLICATION_TYPE = trim(upper(V_SF_APPLICATION_TYPE)), UPUSER = 'SPONSOR_EXPERIENCE-' || V_UPDATEBY, UPTIME = SYSTIMESTAMP, SF_ATTACHMENT_ID = V_ARI_SF_ATTACHMENT_ID, SF_DOCUMENT_TYPE = 'ARI' WHERE SF_APPLICATION_ID = V_SF_APPLICATION_ID and UAC_id = V_UAC_ID; END IF; IF (V_FRA_SF_ATTACHMENT_ID IS NOT NULL) THEN Update UAC_INFO set SF_APPLICATION_TYPE = trim(upper(V_SF_APPLICATION_TYPE)), UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP, SF_ATTACHMENT_ID = V_FRA_SF_ATTACHMENT_ID, SF_DOCUMENT_TYPE = 'FRA' WHERE SF_APPLICATION_ID = V_SF_APPLICATION_ID and UAC_id = V_UAC_ID; END IF; IF (V_SF_CONTENT_ID IS NOT NULL) THEN Update UAC_INFO set SF_APPLICATION_TYPE = trim(upper(V_SF_APPLICATION_TYPE)), UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP, SF_ATTACHMENT_ID = V_SF_CONTENT_ID WHERE SF_APPLICATION_ID = V_SF_APPLICATION_ID and UAC_id = V_UAC_ID; END IF; END IF;
numberOfRowsUpdates := numberOfRowsUpdates + SQL%ROWCOUNT; --DBMS_OUTPUT.PUT_LINE('Number of Rows Updated: ' || numberOfRowsUpdates);
Open cur_Out For Select numberOfRowsUpdates AS UPDATED_ROW_COUNT from dual;
END SP_SPONSOR_EXPERIENCE_UPDATE_APPLICATION_TYPE;

PROCEDURE SP_SPONSOR_EXPERIENCE_UPDATE_DOCUMENTS ( V_SPONSOR_ID IN NUMBER , V_SF_APPLICATION_ID IN VARCHAR2 , V_ARI_SF_ATTACHMENT_ID IN VARCHAR2 , V_FRA_SF_ATTACHMENT_ID IN VARCHAR2 , V_UPDATEBY IN VARCHAR2 , cur_Out OUT SYS_REFCURSOR ) AS
numberOfRowsUpdates NUMBER(10.0) :=0;
BEGIN -- YOU SHOULD NOT RECEIVE AN FRA AND AN ARI AT THE SAME TIME -- WHEN WE GET A FRA, WE KNOW THAT ALL DOCUMENTS ARE COMPLETED IF (V_ARI_SF_ATTACHMENT_ID IS NOT NULL) THEN Update UAC_SPONSOR_LIST set ARI_SF_ATTACHMENT_ID = V_ARI_SF_ATTACHMENT_ID, --ARI_DATE_RECEIVED = SYSTIMESTAMP, UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SPONSOR_ID = V_SPONSOR_ID and SF_APPLICATION_ID = V_SF_APPLICATION_ID and is_deleted = 0; END IF;
IF (V_FRA_SF_ATTACHMENT_ID IS NOT NULL) THEN Update UAC_SPONSOR_LIST set FRA_SF_ATTACHMENT_ID = V_FRA_SF_ATTACHMENT_ID, --DATE_APPLICATION_RECEIVED = SYSTIMESTAMP, UPUSER = 'Sponsor Application', UPTIME = SYSTIMESTAMP WHERE SPONSOR_ID = V_SPONSOR_ID and SF_APPLICATION_ID = V_SF_APPLICATION_ID and is_deleted = 0; END IF;
OPEN cur_OUT FOR select distinct SF_APPLICATION_ID, SF_APPLICATION_TYPE From UAC_SPONSOR_LIST A Where UPPER(TRIM(SF_APPLICATION_ID)) = UPPER(TRIM(V_SF_APPLICATION_ID)) AND SPONSOR_ID = V_SPONSOR_ID and a.is_deleted = 0;
END SP_SPONSOR_EXPERIENCE_UPDATE_DOCUMENTS;

PROCEDURE SP_SPONSOR_EXPERIENCE_UPDATE_SPONSOR_TYPE 
(
  V_SPONSOR_ID IN NUMBER 
, V_USER_ID IN VARCHAR2 
, V_JSON IN VARCHAR2 
, V_EVENT_TYPE IN VARCHAR2
, V_PROCESS_TYPE IN VARCHAR2
, V_SPONSOR_TYPE IN VARCHAR2
, cur_Out  OUT SYS_REFCURSOR
) AS 

numberOfRowsUpdates NUMBER(10.0) :=0;

BEGIN
    UPDATE SPONSOR_EXPERIENCE_INFO
        SET UPUSER = V_USER_ID, UPTIME = SYSTIMESTAMP, JSON = V_JSON, EVENT_TYPE = V_EVENT_TYPE, PROCESS_TYPE = V_PROCESS_TYPE, SPONSOR_TYPE = V_SPONSOR_TYPE
    WHERE 1 = 0; --SPONSOR_ID = V_SPONSOR_ID;

    numberOfRowsUpdates := numberOfRowsUpdates + SQL%ROWCOUNT;
    --DBMS_OUTPUT.PUT_LINE('Number of Rows Updated: ' || numberOfRowsUpdates);

    -- remove records that are older than 90 days
    --DELETE FROM SPONSOR_EXPERIENCE_HISTORY WHERE UPTIME <= sysdate - 60;

    Open cur_Out For
        Select numberOfRowsUpdates AS UPDATED_ROW_COUNT from dual;

END SP_SPONSOR_EXPERIENCE_UPDATE_SPONSOR_TYPE;

PROCEDURE "SP_SPONSOR_EXPERIENCE_VALIDATE_CREATE_APPLICATION" ( v_JSON_MODEL IN VARCHAR2, v_TYPE IN VARCHAR2, v_USER IN VARCHAR2, cur_OUT OUT SYS_REFCURSOR ) AS
BEGIN OPEN cur_OUT FOR select distinct A.USERID AS SF_ID, NULL AS SF_TYPE From SPONSOR_EXPERIENCE_HISTORY A Where upper(trim(A.JSON)) = upper(trim(v_JSON_MODEL)) AND UPPER(TRIM(A.error_type)) = UPPER(TRIM(v_TYPE)) AND UPPER(TRIM(USERID)) = UPPER(TRIM(v_USER));
END SP_SPONSOR_EXPERIENCE_VALIDATE_CREATE_APPLICATION;

PROCEDURE "SP_SPONSOR_EXPERIENCE_VALIDATE_SF_APPLICATION_ID" 
(
v_SF_APPLICATION_ID IN VARCHAR2,
v_Sponsor_Id  IN NUMBER,
cur_OUT OUT SYS_REFCURSOR
)
AS 

v_ApplicationId_ VARCHAR2(200) := NULL;
BEGIN
    --DBMS_OUTPUT.put_line(v_SF_APPLICATION_ID);
    v_ApplicationId_ := v_SF_APPLICATION_ID;

    OPEN cur_OUT FOR
    select  distinct SF_APPLICATION_ID AS SF_ID, SF_APPLICATION_TYPE AS SF_TYPE
        From UAC_SPONSOR_LIST A 
    Where UPPER(TRIM(A.SF_APPLICATION_ID)) = UPPER(TRIM(nvl(v_ApplicationId_, 'XXX'))) and a.is_deleted = 0; --AND A.SPONSOR_ID = v_Sponsor_Id;
END SP_SPONSOR_EXPERIENCE_VALIDATE_SF_APPLICATION_ID;

PROCEDURE "SP_SPONSOR_EXPERIENCE_VALIDATE_SF_CONTENT_ID" ( v_SF_CONTENT_ID IN VARCHAR2, cur_OUT OUT SYS_REFCURSOR ) AS
BEGIN OPEN cur_OUT FOR select distinct A.SF_CONTENT_ID AS SF_ID, NULL AS SF_TYPE From UAC_DOCUMENTS A Where upper(trim(A.SF_CONTENT_ID)) = upper(trim(v_SF_CONTENT_ID))
END SP_SPONSOR_EXPERIENCE_VALIDATE_SF_CONTENT_ID;

PROCEDURE SP_SSO_ADD_HISTORY ( V_USER_ID IN VARCHAR2 , V_JSON IN VARCHAR2 , V_EVENT_TYPE IN VARCHAR2 , V_PROCESS_TYPE IN VARCHAR2 , V_ERROR_TYPE IN VARCHAR2 ) AS BEGIN INSERT INTO SSO_HISTORY (USERID, UPUSER, UPTIME, JSON, EVENT_TYPE, PROCESS_TYPE, ERROR_TYPE) VALUES (V_USER_ID, 'SSO', SYSTIMESTAMP, V_JSON, V_EVENT_TYPE, V_PROCESS_TYPE, V_ERROR_TYPE);
Delete from sso_history where uptime < sysdate -75;
END SP_SSO_ADD_HISTORY;

PROCEDURE SP_SSO_REMOVE_USER_AND_PRGMS 
(
  V_USER_ID IN VARCHAR2 
) AS 
BEGIN
    DELETE FROM ADM_USER_PROGRAM WHERE USER_ID=v_USER_ID; 
    commit;
    UPDATE ADM_LIST_WEB_USER 
    SET SUSPENDED = 1, UPUSER = 'SSO', UPTIME = SYSTIMESTAMP  
    where User_Id = V_USER_ID; 
    commit;
END SP_SSO_REMOVE_USER_AND_PRGMS;

PROCEDURE sp_SSO_updateUser ( FirstName VARCHAR2, LastName VARCHAR2, RoleType VARCHAR2, UserPrivilege VARCHAR2, UserEmail VARCHAR2, ProviderId VARCHAR2 Default Null, LastLoginUpdate Date, UserId VARCHAR2 ) IS provider_ number := 0; BEGIN if ProviderId is null then provider_ := null; else provider_ := to_number(ProviderId); end if;
update ADM_LIST_WEB_USER set SUSPENDED = 0,PASSWORDRESET=0 where email = UserEmail;
UPDATE ADM_LIST_WEB_USER SET First_Name = FirstName, Last_Name = LastName, Role_type = RoleType, user_privelege = UserPrivilege, email = UserEmail, LastLogin = LastLoginUpdate , SUSPENDED = 0,PASSWORDRESET=0, UPUSER = 'SSO', UPTIME = SYSTIMESTAMP, PROVIDER = provider_ -- added this line values where User_Id = UserId; END sp_SSO_updateUser;

PROCEDURE "SP_UAC_TRANSFER" 
(
  UAC_PROGRAM_ID IN NUMBER,
  UPUSER    IN VARCHAR2
) AS 
uacProgId_ NUMBER(10) := UAC_PROGRAM_ID;
userId_     VARCHAR2(30) := UPUSER;
newProgId_  NUMBER(10);
BEGIN

  Select PROPOSED_PROGRAM_ID into newProgId_ from (
    Select PROPOSED_PROGRAM_ID From UAC_TRANSFER_REQUEST Where UAC_PROGRAM_ID = uacProgId_  And nvl(ISDELETED, 0) = 0 Order by DATE_CREATED Desc
  ) Where RowNum <= 1;

  Insert Into UAC_PROGRAM_INFO (UAC_PROGRAM_ID, UAC_ID, PROGRAM_ID, DATE_REFERRED, CURRENT_STATUS, DATE_CREATED, USER_CREATED, UPTIME, UPUSER)
  Select SEQ_UAC_PROGRAM_INFO.nextval, UAC_ID, newProgId_, sysdate, 'ENROUTE', sysdate, userId_, sysdate, userId_ 
  From UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = uacProgId_ AND CURRENT_STATUS='DISCHARGED';

  Update UAC_INFO Set UAC_STATUS = 'ENROUTE' Where UAC_ID in (Select UAC_ID from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = uacProgId_);
END SP_UAC_TRANSFER;

PROCEDURE SP_UPDATE_ADMISSION
(
  UAC_PROGRAM_ID IN NUMERIC,
  status  IN  VARCHAR2,
  verified IN NUMERIC,
  DateAdmitted IN DATE,
  userId IN VARCHAR2,
  updType IN VARCHAR2,
  gender in VARCHAR2,
  oldGender in VARCHAR2,
  v_status OUT NOCOPY VARCHAR2
) AS 

uacProgId_  NUMBER(10,0) := UAC_PROGRAM_ID;
status_ VARCHAR2(20) := UPPER(status);
verified_  NUMBER(1) := verified;
dateAdmitted_  Date := DateAdmitted;
userId_   VARCHAR2(112) := userId;
uacId_  NUMBER(10);
programid NUMBER(10,0);
n       NUMBER(3) := 0;
bedsM number;
bedsF number;
funded_capacity_M number(4);
funded_capacity_F number(4);
in_care_M number(4);
in_care_F number(4);
in_transfer_M number(4);
in_transfer_F number(4);
beds_unavailable_M number(4);
beds_unavailable_F number(4);
enroute_M number(4);
enroute_F number(4);

BEGIN

    Select UAC_ID,Program_id into uacId_,programid from UAC_PROGRAM_INFO where UAC_PROGRAM_ID = uacProgId_;

  SELECT 
    F.FUNDED_CAPACITY_M,
    F.FUNDED_CAPACITY_F,
    (select COUNT(*)
      from UAC_PROGRAM_INFO P
      join UAC_INFO I
      on P.UAC_ID=I.UAC_ID
      where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER')
      and (I.GENDER='M' or I.GENDER='Male')
      and P.PROGRAM_ID=F.PROGRAM_ID 
    ) AS IN_CARE_M,
    (select COUNT(*)
      from UAC_PROGRAM_INFO P
      join UAC_INFO I
      on P.UAC_ID=I.UAC_ID
      where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER')
      and (I.GENDER='F' or I.GENDER='Female')
      and P.PROGRAM_ID=F.PROGRAM_ID 
    ) AS IN_CARE_F,   
    F.BEDS_UNAVAILABLE_M,
    F.BEDS_UNAVAILABLE_F,
    (Select COUNT(*) 
      from UAC_TRANSFER_REQUEST A
      Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID 
      And nvl(A.ISDELETED,0) = 0 
      And Exists(Select 1 from UAC_PROGRAM_INFO U
                  JOIN UAC_INFO I
                    ON U.UAC_ID=I.UAC_ID
                  Where U.UAC_PROGRAM_ID = A.UAC_PROGRAM_ID 
                  And U.CURRENT_STATUS = 'IN-TRANSFER'
                  And (I.GENDER='M' OR I.GENDER='Male'))
    ) AS IN_TRANSFER_M,
    (Select COUNT(*) 
      from UAC_TRANSFER_REQUEST A
      Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID 
      And nvl(A.ISDELETED,0) = 0 
      And Exists(Select 1 from UAC_PROGRAM_INFO U
                  JOIN UAC_INFO I
                    ON U.UAC_ID=I.UAC_ID
                  Where U.UAC_PROGRAM_ID = A.UAC_PROGRAM_ID 
                  And U.CURRENT_STATUS = 'IN-TRANSFER'
                  And (I.GENDER='F' OR I.GENDER='Female'))
    ) AS IN_TRANSFER_F,
    (Select COUNT(*)
      from UAC_PROGRAM_INFO U
      JOIN UAC_INFO I
        ON U.UAC_ID=I.UAC_ID
      Where CURRENT_STATUS='ENROUTE'
      AND (I.GENDER='M' OR I.GENDER='Male')
      AND U.PROGRAM_ID=F.PROGRAM_ID) AS ENROUTE_M,
    (Select COUNT(*) 
      from UAC_PROGRAM_INFO U
      JOIN UAC_INFO I
        ON U.UAC_ID=I.UAC_ID
      Where CURRENT_STATUS='ENROUTE'
      AND (I.GENDER='F' OR I.GENDER='Female')
      AND U.PROGRAM_ID=F.PROGRAM_ID) AS ENROUTE_F
  into funded_capacity_M,funded_capacity_F,in_care_M,in_care_F,beds_unavailable_M,beds_unavailable_F,in_transfer_M,in_transfer_F,enroute_M,enroute_F
  FROM FACILITY_INFO F
  WHERE F.PROGRAM_ID=programid;

    bedsM := funded_capacity_M - (in_care_M + beds_unavailable_M + in_transfer_M + enroute_M);
    bedsF := funded_capacity_F - (in_care_F + beds_unavailable_F + in_transfer_F + enroute_F);


    if updType = 'new' Then
      Update UAC_INFO Set 
        UAC_STATUS = status_,
        UPTIME = Sysdate, 
        UPUSER = userId_
        Where UAC_ID = uacId_;
      If status_ = 'ADMITTED' Then
        Update UAC_PROGRAM_INFO Set
        BIO_VERIFIED = verified_, 
        CURRENT_STATUS = status_, 
        DATE_ADMITTED = dateAdmitted_,
        UPTIME = Sysdate, 
        UPUSER = userId_
        Where UAC_PROGRAM_ID = uacProgId_;      
      Else  
		if status_ = 'PENDING' Then
			Update UAC_PROGRAM_INFO Set PROGRAM_ID = (SELECT PROGRAM_ID FROM LIST_PROGRAMS WHERE PROGRAM_NAME = 'PENDING'), CURRENT_STATUS = 'PENDING' Where UAC_PROGRAM_ID = uacProgId_;
    ELsIf status_ = 'CANCELLED' Then
		  Delete from UAC_DOCUMENTS WHERE UAC_PROGRAM_ID = uacProgId_;		  
		  Delete from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = uacProgId_;
          Select Count(*) into n from UAC_PROGRAM_INFO where UAC_ID = uacId_ And CURRENT_STATUS = 'DISCHARGED';
          If n > 0 Then
            Update UAC_INFO Set UAC_STATUS = 'DISCHARGED',
            UPTIME = Sysdate, 
            UPUSER = userId_
            Where UAC_ID = uacId_;
		  ELSE
			Delete from UAC_SPONSOR_LIST WHERE UAC_ID = uacId_ and is_deleted = 0;
          End If;
        End If;
      End If;  
    Else
      If oldGender is not null then
        If oldGender <> gender then
          If status_ = 'IN-TRANSFER' Then                        
            if(gender = 'M' or gender = 'Male') Then
              if(bedsM <= 0) Then                            
                v_status := 'NOT ENOUGH BEDS NOT ENOUGH BEDS';
                ROLLBACK;
                RETURN;
              End If;        
            ElsIf(gender = 'F' or gender = 'Female') Then
              if(bedsF <= 0) Then              
                v_status := 'NOT ENOUGH BEDS NOT ENOUGH BEDS';
                ROLLBACK;
                RETURN;
              End If;
            End If;
          End If;          
        End if;
      End IF;
      Update UAC_PROGRAM_INFO Set
      BIO_VERIFIED = verified_,  
      DATE_ADMITTED = dateAdmitted_,
      UPTIME = Sysdate, 
      UPUSER = userId_
      Where UAC_PROGRAM_ID = uacProgId_;
    End If;
    v_status := 'SUCCESS SUCCESS';
END SP_UPDATE_ADMISSION;

PROCEDURE "SP_UPDATE_BACKGROUNDCHECKS" ( vRecord_ID IN NUMBER, vSponsor_id IN NUMBER, vResult in Varchar2, vUAC_ID IN NUMBER default null, vHOUSEHOLD_ID IN NUMBER default null, vALTERNATE_ID IN NUMBER default null, vType IN VARCHAR2
) AS reccount number; resultsiD number; BEGIN if vResult = 'N' then resultsiD := 3; end if; if vResult = 'I' then resultsiD := 4; end if; if vResult = '' then resultsiD := 5; end if; If vType = 'SPONSOR' Then update SPONSOR_BACKGROUND_CHECK set DATE_RECEIVED = (select DATE_RESULTS_RECEIVED from bwa_staging where bwa_staging_id = vRecord_ID),RESULT_ID = resultsiD, DATE_REQUESTED = (select DATE_PRINTED from bwa_staging where bwa_staging_id = vRecord_ID), ORR_PRINT_TRANSMISSION_DATE = (select DATE_TRANSMITTED from bwa_staging where bwa_staging_id = vRecord_ID), ID_DATE_SENT = (select orr_id_date from bwa_staging where bwa_staging_id = vRecord_ID), ARI_SENT_TO_ORR = (select ORR_ARI_DATE from bwa_staging where bwa_staging_id = vRecord_ID), FINGER_PRINT_APPOINTMENT = (select ORR_APT_DATE from bwa_staging where bwa_staging_id = vRecord_ID), fbi_processed = 1, USER_UPDATED = 'API',DATE_UPDATED = Sysdate where sponsor_id = vSponsor_id and occupant_id is null and BG_FBICH = 1 and sponsor_alternate_caregiver_id is null and DATE_RECEIVED is null; end if; if vType = 'HOUSEHOLD' then --update update SPONSOR_BACKGROUND_CHECK set DATE_RECEIVED = (select DATE_RESULTS_RECEIVED from bwa_staging where bwa_staging_id = vRecord_ID), RESULT_ID = resultsiD, DATE_REQUESTED = (select DATE_PRINTED from bwa_staging where bwa_staging_id = vRecord_ID), ORR_PRINT_TRANSMISSION_DATE = (select DATE_TRANSMITTED from bwa_staging where bwa_staging_id = vRecord_ID), ID_DATE_SENT = (select orr_id_date from bwa_staging where bwa_staging_id = vRecord_ID), ARI_SENT_TO_ORR = (select ORR_ARI_DATE from bwa_staging where bwa_staging_id = vRecord_ID), FINGER_PRINT_APPOINTMENT = (select ORR_APT_DATE from bwa_staging where bwa_staging_id = vRecord_ID), USER_UPDATED = 'API',DATE_UPDATED =Sysdate, fbi_processed = 1 where sponsor_id = vSponsor_id and occupant_id = vHOUSEHOLD_ID and uac_id = vUAC_ID and BG_FBICH = 1 and sponsor_alternate_caregiver_id is null and DATE_RECEIVED is null;
end if;
If vType = 'ALTERNATE' Then update SPONSOR_BACKGROUND_CHECK set DATE_RECEIVED = (select DATE_RESULTS_RECEIVED from bwa_staging where bwa_staging_id = vRecord_ID),RESULT_ID = resultsiD, DATE_REQUESTED = (select DATE_PRINTED from bwa_staging where bwa_staging_id = vRecord_ID), ORR_PRINT_TRANSMISSION_DATE = (select DATE_TRANSMITTED from bwa_staging where bwa_staging_id = vRecord_ID), ID_DATE_SENT = (select orr_id_date from bwa_staging where bwa_staging_id = vRecord_ID), ARI_SENT_TO_ORR = (select ORR_ARI_DATE from bwa_staging where bwa_staging_id = vRecord_ID), FINGER_PRINT_APPOINTMENT = (select ORR_APT_DATE from bwa_staging where bwa_staging_id = vRecord_ID), fbi_processed = 1, USER_UPDATED = 'API',DATE_UPDATED =Sysdate where sponsor_id = vSponsor_id and occupant_id is null and uac_id = vUAC_ID and BG_FBICH = 1 and sponsor_alternate_caregiver_id = vALTERNATE_ID and DATE_RECEIVED is null; end if;
END SP_UPDATE_BACKGROUNDCHECKS;

PROCEDURE           SP_UPDATE_BWA_STAGING
 AS 
BEGIN
update BWA_STAGING set PROCESSING_STATUS = 'PROCESSED EXCEL' where PROCESSING_STATUS = 'EXCEL';
END SP_UPDATE_BWA_STAGING;

PROCEDURE           "SP_UPDATE_DISCHARGE_NTFCT" 
(
  UAC_PROGRAM_ID IN NUMERIC
, DISCHARGE_ID IN NUMERIC
, DateOfDischarge IN DATE
, typeOfDischarge IN VARCHAR2  
, sponsorId IN NUMERIC 
, proveOfRelationship IN VARCHAR2 
, progUACTrnsfTo IN NUMERIC
, dhsShelter  IN VARCHAR2
, lawEnforcementId  IN NUMBER
, Address IN VARCHAR2 
, City IN VARCHAR2 
, State IN VARCHAR2 
, Zipcode IN VARCHAR2 
, phone IN VARCHAR2 
, fax IN VARCHAR2
, legalStatusId IN NUMBER
, CMD_Type  IN VARCHAR2
, userId IN VARCHAR2
) AS 

uacId_  NUMERIC(10) := UAC_PROGRAM_ID;
dischargeId_ NUMERIC(10) := DISCHARGE_ID;
dateOfDischarge_  DATE := DateOfDischarge;
typeOfDischarge_  VARCHAR2(50) := typeOfDischarge;
sponsorId_     NUMBER(10) := sponsorId;
proveOfRelationship_    VARCHAR2(4000) := proveOfRelationship;
progUACTrnsfTo_  NUMBER(1) := progUACTrnsfTo;
dhsShelter_     VARCHAR2(112) := dhsShelter;
lawEnforcementId_  NUMBER(2):= lawEnforcementId;
Address_  VARCHAR2(112) := Address;
City_  VARCHAR2(50) := City;
State_        CHAR(2) := State;
Zipcode_    NUMERIC(1,0) := Zipcode;
phone_      NUMERIC(1,0) := phone;
fax_      VARCHAR2(20) := fax;
legalStatusId_  NUMBER(2) := legalStatusId;
userId_   VARCHAR2(30) := userId;
cmdType_  VARCHAR2(30) := Upper(CMD_Type);

maxNo     NUMERIC(10);
BEGIN
  if cmdType_ = 'INSERT' Then
    maxNo := SEQ_DISCHARGE_NOTIFICATION.nextval;
    Insert into UAC_DISCHARGE_NOTIFICATION (DISCHARGE_ID, UAC_PROGRAM_ID,	DATE_OF_DISCHARGE, TYPE_OF_DISCHARGE, SPONSOR_ID,
    PROVE_OF_RELATIONSHIP, PROGRAM_UAC_TRANSFER_TO, DHS_FAMILY_SHELTER, LAW_ENFORCEMENT_ID, ADDRESS, CITY, STATE, ZIP_CODE, PHONE, FAX,
    UAC_LEGAL_STATUS_ID, DATE_CREATED, USER_CREATED, UPTIME, UPUSER)
    Values (maxNo,  uacId_, dateOfDischarge_, typeOfDischarge_, sponsorId_,
    proveOfRelationship_, progUACTrnsfTo_, dhsShelter_, lawEnforcementId_, Address_,City_, State_, Zipcode_,phone_, fax_, legalStatusId_,
    sysdate, userId_, sysdate, userId_);
  elsif cmdType_ = 'UPDATE' Then
    maxNo := dischargeId_;
    Update UAC_DISCHARGE_NOTIFICATION Set
        DATE_OF_DISCHARGE = dateOfDischarge_, 
        TYPE_OF_DISCHARGE = typeOfDischarge_, 
        SPONSOR_ID = sponsorId_,
        PROVE_OF_RELATIONSHIP = proveOfRelationship_, 
        PROGRAM_UAC_TRANSFER_TO = progUACTrnsfTo_, 
        DHS_FAMILY_SHELTER = dhsShelter_, 
        LAW_ENFORCEMENT_ID = lawEnforcementId_, 
        ADDRESS = Address_, 
        CITY = City_, 
        STATE = State_, 
        ZIP_CODE = Zipcode_, 
        PHONE = phone_, 
        FAX = fax_,
        UAC_LEGAL_STATUS_ID = legalStatusId_,
        UPTIME = Sysdate, 
        UPUSER = userId_
      Where DISCHARGE_ID = dischargeId_;
  end if;
END SP_UPDATE_DISCHARGE_NTFCT;

PROCEDURE "SP_UPDATE_EFFORT" 
(
  UAC_PROGRAM_ID IN NUMERIC
, effortId  IN NUMBER
, effortTypeId  IN NUMBER
, contactLocationId IN NUMBER
, lastContactDate IN DATE
, contactDate IN DATE
, trainingDate IN DATE
, nextContactDate IN DATE
, recreation IN VARCHAR2
, recreationNote  IN VARCHAR2
, spentTime IN DATE 
, legalRelief IN NUMBER 
, recValue IN NUMBER
, CMD_Type  IN VARCHAR2
, userId IN VARCHAR2
) AS 

uacId_  NUMERIC(10) := UAC_PROGRAM_ID;
effortId_ NUMERIC(10) := effortId;
effortTypeId_  NUMBER(2) := effortTypeId;
contactLocationId_  NUMBER(3) := contactLocationId;
lastContactDate_     DATE := lastContactDate;
contactDate_    DATE := contactDate;
nextContactDate_  DATE := nextContactDate;
trainingDate_     DATE := trainingDate;
recreation_  VARCHAR2(112):= recreation;
recreationNote_  VARCHAR2(500) := recreationNote;
timeSpent_    DATE := spentTime;
legalRelief_  NUMBER := legalRelief;
recValue_        NUMBER := recValue;
userId_   VARCHAR2(30) := userId;
cmdType_  VARCHAR2(30) := Upper(CMD_Type);

maxNo     NUMERIC(10);
BEGIN
  if cmdType_ = 'INSERT' Then
    maxNo := SEQ_EFFORT.nextval;
    Insert into UAC_EFFORT (EFFORT_ID, UAC_PROGRAM_ID,	EFFORT_TYPE_ID, CONTACT_LOCATION_ID, DATE_OF_LAST_CONTACT,
    DATE_OF_CONTACT, DATE_OF_NEXT_CONTACT, TRAINING_DATE, RECREATION, RECREATION_NOTES, TIME_SPENT, LEGAL_RELIEF_PROVIDED, REC_VALUE,
    DATE_CREATED, USER_CREATED, UPTIME, UPUSER)
    Values (maxNo,  uacId_, effortTypeId_, contactLocationId_, lastContactDate_,
    contactDate_, nextContactDate_, trainingDate_, recreation_, recreationNote_, timeSpent_, legalRelief_, recValue_,
    sysdate, userId_, sysdate, userId_);
  elsif cmdType_ = 'UPDATE' Then
    maxNo := effortId_;
    Update UAC_EFFORT Set
        EFFORT_TYPE_ID = effortTypeId_, 
        CONTACT_LOCATION_ID = contactLocationId_, 
        DATE_OF_LAST_CONTACT = lastContactDate_,
        DATE_OF_CONTACT = contactDate_, 
        DATE_OF_NEXT_CONTACT = nextContactDate_, 
        TRAINING_DATE = trainingDate_, 
        RECREATION = recreation_, 
        RECREATION_NOTES = recreationNote_, 
        TIME_SPENT = timeSpent_, 
        LEGAL_RELIEF_PROVIDED = legalRelief_, 
        REC_VALUE = recValue_,
        UPTIME = Sysdate, 
        UPUSER = userId_
      Where EFFORT_ID = effortId_;
  end if;
END SP_UPDATE_EFFORT;

PROCEDURE SP_UPDATE_FACILITY_INFO_BEDS ( v_ProgId IN NUMBER, v_Gender IN VARCHAR2, v_Action IN VARCHAR2, v_status OUT NOCOPY VARCHAR2, v_page IN varchar2 DEFAULT NULL ) AS
bedsM number; bedsF number;
BEGIN
Delete from TT_FACILITY_INFO;
Insert Into TT_FACILITY_INFO ( FACILITY_ID, FACILITY_NAME, FACILITY_TYPE, REGION, STATE, CITY, FUNDED_CAPACITY_M, FUNDED_CAPACITY_F, COMMENTS, INSTRUCTIONS, CONFIRMED_BY, CONFIRMED_TIME, ISDELETED, MEN_ONLY, REGION_ORDER, PROGRAM_ID, BEDS_UNAVAILABLE_M, BEDS_UNAVAILABLE_F, DFFC_TYPE, VERIFIED ) Select f.FACILITY_ID, f.FACILITY_NAME, f.FACILITY_TYPE, f.REGION, f.STATE, f.CITY, f.FUNDED_CAPACITY_M, f.FUNDED_CAPACITY_F, f.COMMENTS, f.INSTRUCTIONS, f.CONFIRMED_BY, f.CONFIRMED_TIME, f.ISDELETED, f.MEN_ONLY, f.REGION_ORDER, f.PROGRAM_ID, f.BEDS_UNAVAILABLE_M, f.BEDS_UNAVAILABLE_F, f.DFFC_TYPE, f.VERIFIED From FACILITY_INFO f Where F.PROGRAM_ID = v_ProgId;
Update TT_FACILITY_INFO F Set (IN_TRANSFER_M, IN_TRANSFER_F) = ( Select SUM(CASE WHEN (C.GENDER='M' OR C.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (C.GENDER='F' OR C.GENDER='Female') THEN 1 ELSE 0 End) from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER' ) Where Exists(Select 1 from UAC_TRANSFER_REQUEST A Inner Join UAC_PROGRAM_INFO B On A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID Inner Join UAC_INFO C On B.UAC_ID = C.UAC_ID Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID And nvl(A.ISDELETED,0) = 0 And B.CURRENT_STATUS = 'IN-TRANSFER');
Update TT_FACILITY_INFO F Set (IN_CARE_M, IN_CARE_F) = ( select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO P join UAC_INFO I on P.UAC_ID=I.UAC_ID where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER') and P.PROGRAM_ID=F.PROGRAM_ID);
Update TT_FACILITY_INFO F Set (ENROUTE_M, ENROUTE_F) = (Select SUM(CASE WHEN (I.GENDER='M' OR I.GENDER='Male') THEN 1 ELSE 0 END), SUM(CASE WHEN (I.GENDER='F' OR I.GENDER='Female') THEN 1 ELSE 0 End) from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID ) Where Exists(Select 1 from UAC_PROGRAM_INFO U JOIN UAC_INFO I ON U.UAC_ID=I.UAC_ID Where CURRENT_STATUS='ENROUTE' AND U.PROGRAM_ID=F.PROGRAM_ID);
UPDATE TT_FACILITY_INFO SET BEDS_IN_RESERVE_M = FUNDED_CAPACITY_M - (NVL(IN_TRANSFER_M,0) + NVL(ENROUTE_M,0) + NVL(IN_CARE_M,0) + NVL(BEDS_UNAVAILABLE_M,0)), BEDS_IN_RESERVE_F = FUNDED_CAPACITY_F - (NVL(IN_TRANSFER_F,0) + NVL(ENROUTE_F,0) + NVL(IN_CARE_F,0) + NVL(BEDS_UNAVAILABLE_F,0));
Select BEDS_IN_RESERVE_M, BEDS_IN_RESERVE_F into bedsM, bedsF from TT_FACILITY_INFO Where PROGRAM_ID = v_ProgId;
if (v_page = 'IntakeDetail' or v_page = 'Transfer') Then if(v_Action = 'ADD') Then if ((v_Gender = 'M' or v_Gender = 'Male') AND bedsM > 0) Then v_status := 'SUCCESS SUCCESS'; Elsif ((v_Gender = 'F' or v_Gender = 'Female') AND bedsF > 0) Then v_status := 'SUCCESS SUCCESS'; Else v_status := 'NOT ENOUGH BEDS'; End If; Else v_status := 'SUCCESS SUCCESS'; End If; End if;
END SP_UPDATE_FACILITY_INFO_BEDS;

PROCEDURE           SP_UPDATE_GENDER_INTAKE
(
  PROGRAM_ID IN NUMERIC,
  status  IN  VARCHAR2,
  gender in VARCHAR2,
  oldGender in VARCHAR2,
  v_UAC_ID NUMBER,
  v_status OUT NOCOPY VARCHAR2
) AS 


status_ VARCHAR2(20) := UPPER(status);
uacId_  NUMBER(10) :=v_UAC_ID;
programid NUMBER(10,0) := PROGRAM_ID;
bedsM number;
bedsF number;
uacProgId_ NUMBER(10,0);
funded_capacity_M number(4);
funded_capacity_F number(4);
in_care_M number(4);
in_care_F number(4);
in_transfer_M number(4);
in_transfer_F number(4);
beds_unavailable_M number(4);
beds_unavailable_F number(4);
enroute_M number(4);
enroute_F number(4);
uacProposedProgID number(10,0);

BEGIN

  select UAC_PROGRAM_ID into uacProgId_ from UAC_PROGRAM_INFO where UAC_ID=uacId_ And CURRENT_STATUS = status_;

   SELECT PROPOSED_PROGRAM_ID  into uacProposedProgID
    FROM UAC_TRANSFER_REQUEST
    WHERE UAC_PROGRAM_ID = uacProgId_
    AND NVL(ISDELETED,0) = 0;

  SELECT 
    F.FUNDED_CAPACITY_M,
    F.FUNDED_CAPACITY_F,
    (select COUNT(*)
      from UAC_PROGRAM_INFO P
      join UAC_INFO I
      on P.UAC_ID=I.UAC_ID
      where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER')
      and (I.GENDER='M' or I.GENDER='Male')
      and P.PROGRAM_ID=F.PROGRAM_ID 
    ) AS IN_CARE_M,
    (select COUNT(*)
      from UAC_PROGRAM_INFO P
      join UAC_INFO I
      on P.UAC_ID=I.UAC_ID
      where P.CURRENT_STATUS IN ('ADMITTED', 'IN-TRANSFER')
      and (I.GENDER='F' or I.GENDER='Female')
      and P.PROGRAM_ID=F.PROGRAM_ID 
    ) AS IN_CARE_F,   
    F.BEDS_UNAVAILABLE_M,
    F.BEDS_UNAVAILABLE_F,
    (Select COUNT(*) 
      from UAC_TRANSFER_REQUEST A
      Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID 
      And nvl(A.ISDELETED,0) = 0 
      And Exists(Select 1 from UAC_PROGRAM_INFO U
                  JOIN UAC_INFO I
                    ON U.UAC_ID=I.UAC_ID
                  Where U.UAC_PROGRAM_ID = A.UAC_PROGRAM_ID 
                  And U.CURRENT_STATUS = 'IN-TRANSFER'
                  And (I.GENDER='M' OR I.GENDER='Male'))
    ) AS IN_TRANSFER_M,
    (Select COUNT(*) 
      from UAC_TRANSFER_REQUEST A
      Where NVL(A.PROPOSED_PROGRAM_ID,0) = F.PROGRAM_ID 
      And nvl(A.ISDELETED,0) = 0 
      And Exists(Select 1 from UAC_PROGRAM_INFO U
                  JOIN UAC_INFO I
                    ON U.UAC_ID=I.UAC_ID
                  Where U.UAC_PROGRAM_ID = A.UAC_PROGRAM_ID 
                  And U.CURRENT_STATUS = 'IN-TRANSFER'
                  And (I.GENDER='F' OR I.GENDER='Female'))
    ) AS IN_TRANSFER_F,
    (Select COUNT(*)
      from UAC_PROGRAM_INFO U
      JOIN UAC_INFO I
        ON U.UAC_ID=I.UAC_ID
      Where CURRENT_STATUS='ENROUTE'
      AND (I.GENDER='M' OR I.GENDER='Male')
      AND U.PROGRAM_ID=F.PROGRAM_ID) AS ENROUTE_M,
    (Select COUNT(*) 
      from UAC_PROGRAM_INFO U
      JOIN UAC_INFO I
        ON U.UAC_ID=I.UAC_ID
      Where CURRENT_STATUS='ENROUTE'
      AND (I.GENDER='F' OR I.GENDER='Female')
      AND U.PROGRAM_ID=F.PROGRAM_ID) AS ENROUTE_F
  into funded_capacity_M,funded_capacity_F,in_care_M,in_care_F,beds_unavailable_M,beds_unavailable_F,in_transfer_M,in_transfer_F,enroute_M,enroute_F
  FROM FACILITY_INFO F
  WHERE F.PROGRAM_ID=uacProposedProgID;

  bedsM := funded_capacity_M - (in_care_M + beds_unavailable_M + in_transfer_M + enroute_M);
  bedsM := funded_capacity_F - (in_care_F + beds_unavailable_F + in_transfer_F + enroute_F);

  If oldGender is not null then
        If oldGender <> gender then
          If status_ = 'IN-TRANSFER' Then 
            if(gender = 'M' or gender = 'Male') Then
              if(bedsM <= 0) Then                        
                v_status := 'NOT ENOUGH BEDS NOT ENOUGH BEDS';
                ROLLBACK;
                RETURN;
              End If;        
            ElsIf(gender = 'F' or gender = 'Female') Then
              if(bedsF <= 0) Then               
                v_status := 'NOT ENOUGH BEDS NOT ENOUGH BEDS';
                ROLLBACK;
                RETURN;
            End If;
          End if;
        End if;
      End IF;
    End if;
    v_status := 'SUCCESS SUCCESS';
END SP_UPDATE_GENDER_INTAKE;

PROCEDURE           "SP_UPDATE_ISP" 
(
  UAC_PROGRAM_ID IN NUMERIC
, SERVICE_PLAN_ID IN NUMERIC
, Case_Plan_Goal IN VARCHAR2
, Justification IN VARCHAR2  
, TRANSFER IN NUMERIC 
, STEP_DOWN IN NUMERIC 
, STEP_UP IN NUMERIC 
, MENTAL_HEALTH IN NUMERIC 
, REUNIFICATION IN NUMERIC 
, REPATRIATION IN NUMERIC 
, ADJUSTMENT_OF_STATUS IN NUMERIC 
, AGE_OUT IN NUMERIC 
, CMD_Type  IN VARCHAR2
, userId IN VARCHAR2 
, cur_OUT OUT Sys_Refcursor
) AS 

uacId_  NUMERIC(10) := UAC_PROGRAM_ID;
ispId_ NUMERIC(10) := SERVICE_PLAN_ID;
casePlanGoal_  VARCHAR2(500) := Case_Plan_Goal;
justification_  VARCHAR2(500) := Justification;
transfer_     NUMERIC(1,0) := TRANSFER;
stepDown_    NUMERIC(1,0) := STEP_DOWN;
stepUp_  NUMERIC(1,0) := STEP_UP;
mentalHealth_  NUMERIC(1,0) := MENTAL_HEALTH;
reunification_  NUMERIC(1,0) := REUNIFICATION;
repatriation_        NUMERIC(1,0) := REPATRIATION;
adjustmentStatus_    NUMERIC(1,0) := ADJUSTMENT_OF_STATUS;
ageOut_      NUMERIC(1,0) := AGE_OUT;
userId_   VARCHAR2(30) := userId;
cmdType_  VARCHAR2(30) := Upper(CMD_Type);

maxNo     NUMERIC(10);
BEGIN
  if cmdType_ = 'INSERT' Then
    maxNo := SEQ_ISP.nextval;
    Insert into UAC_INDIVIDUAL_SERVICE_PLAN (SERVICE_PLAN_ID, UAC_PROGRAM_ID, CASE_PLAN_GOAL, JUSTIFICATION, TRANSFER,
    STEP_DOWN, STEP_UP, MENTAL_HEALTH, REUNIFICATION, REPATRIATION, ADJUSTMENT_OF_STATUS, AGE_OUT,
    DATE_CREATED, USER_CREATED, UPTIME, UPUSER)
    Values (maxNo,  uacId_, casePlanGoal_, justification_, transfer_,
    stepDown_, stepUp_, mentalHealth_, reunification_,repatriation_, adjustmentStatus_, ageOut_,
    sysdate, userId_, sysdate, userId_);
  elsif cmdType_ = 'UPDATE' Then
    maxNo := ispId_;
    Update UAC_INDIVIDUAL_SERVICE_PLAN Set
        CASE_PLAN_GOAL = casePlanGoal_, 
        JUSTIFICATION = justification_, 
        TRANSFER = transfer_,
        STEP_DOWN = stepDown_, 
        STEP_UP = stepUp_, 
        MENTAL_HEALTH = mentalHealth_, 
        REUNIFICATION = reunification_, 
        REPATRIATION = repatriation_, 
        ADJUSTMENT_OF_STATUS = adjustmentStatus_, 
        AGE_OUT = ageOut_,
        UPTIME = Sysdate, 
        UPUSER = userId_
      Where SERVICE_PLAN_ID = ispId_;
  end if;
  Open cur_OUT For
  Select maxNo as SERVICE_PLAN_ID from dual;
END SP_UPDATE_ISP;

PROCEDURE "SP_UPDATE_ISP_SUB" 
(
  SESSION_ID IN VARCHAR2 
) AS 
sessionId_  VARCHAR2(112) := SESSION_ID;
BEGIN
  Insert into UAC_ISP_SUB(ISP_ID, SERVICE_PLAN_ID, SERVICE, FREQUENCY, TASKS, PERSON_RESPONSIBLE, START_DATE, END_DATE, DATE_CREATED, USER_CREATED, UPTIME, UPUSER)
  Select SEQ_ISP_SUB.nextval, SERVICE_PLAN_ID, SERVICE, FREQUENCY, TASKS, PERSON_RESPONSIBLE, START_DATE, END_DATE, SysDate, UserId, SysDate, UserID
  From TT_UAC_ISP_SUB Where Session_ID = sessionId_;

  Delete from TT_UAC_ISP_SUB Where Session_ID = sessionId_ Or UPTIME < SysDate - 1;
END SP_UPDATE_ISP_Sub;

PROCEDURE "SP_UPDATE_PASSWORD" 
(
  USERID VARCHAR2, 
  PREVIOUSPASSWORD VARCHAR2, 
  updatedByUser  VARCHAR2,
  PasswordReset INTEGER,
  LoginCount INTEGER
) AS 
MaxPwdCounter     INTEGER:=0;
PasswordResetVal INTEGER:=PasswordReset;
LoginCountVal INTEGER:=LoginCount;
BEGIN
dbms_output.put_line(1||MaxPwdCounter);
    select  NVL(MAX(PasswordCounter),0) PasswordCounter into MaxPwdCounter from ADM_WEB_USER_PWD where user_id = USERID;
   dbms_output.put_line(2||MaxPwdCounter);
    IF (MaxPwdCounter >= 10) THEN
    dbms_output.put_line(3||MaxPwdCounter);
        delete ADM_WEB_USER_PWD where user_id = USERID and PasswordCounter = 1;

        update ADM_WEB_USER_PWD 
        set PasswordCounter = PasswordCounter - 1
        where user_id = USERID;

        insert into ADM_WEB_USER_PWD
        values(USERID, PREVIOUSPASSWORD, LOCALTIMESTAMP,MaxPwdCounter, LOCALTIMESTAMP, updatedByUser);
    ELSIF (MaxPwdCounter >=1 AND MaxPwdCounter <10 ) THEN    
        insert into ADM_WEB_USER_PWD
        values(USERID, PREVIOUSPASSWORD, LOCALTIMESTAMP,MaxPwdCounter+1, LOCALTIMESTAMP, updatedByUser);  
    ELSE
      insert into ADM_WEB_USER_PWD
        SELECT USER_ID,PASSWORD,LOCALTIMESTAMP,1,UPTIME,UPUSER
        from ADM_LIST_WEB_USER where USER_ID=USERID;    
    END IF;
    UPDATE ADM_LIST_WEB_USER SET 
    PASSWORD=PREVIOUSPASSWORD,
    UPTIME=LOCALTIMESTAMP,
    UPUSER=updatedByUser,
    PASSWORDRESET = PasswordResetVal,
    LOGINCOUNT = LoginCountVal
    WHERE USER_ID=USERID;
  /*  EXCEPTION
  WHEN no_data_found THEN
     insert into ADM_WEB_USER_PWD
        SELECT USER_ID,PASSWORD,LOCALTIMESTAMP,1,UPTIME,UPUSER
        from ADM_LIST_WEB_USER where USER_ID=USERID;

    UPDATE ADM_LIST_WEB_USER SET 
    PASSWORD=PREVIOUSPASSWORD,
    UPTIME=LOCALTIMESTAMP,
    UPUSER=updatedByUser,
     PASSWORDRESET = PasswordResetVal,
    LOGINCOUNT = LoginCountVal
    WHERE USER_ID=USERID;
    */
END SP_UPDATE_PASSWORD;

PROCEDURE SP_UPDATE_SIR 
(
  UAC_PROGRAM_ID IN NUMERIC
, SIR_ID IN NUMERIC
, DateOfIncident IN TIMESTAMP  
, Datereported IN TIMESTAMP  
, LocationOfIncident IN VARCHAR2
, ABUSE_PERPETRATOR_ID IN NUMERIC 
, RUNAWAY IN NUMERIC 
, RUNAWAY_RISK IN NUMERIC 
, ABSENSE_OF_UAC IN NUMERIC 
, SYNOPSIS_OF_INCIDENT IN VARCHAR2 
, WITNESSES IN VARCHAR2 
, STAFFRESPONSE IN VARCHAR2
, RESOLUTION IN VARCHAR2 
, RECOMMENDATIONS IN VARCHAR2 
, INFO_RELEASED_TO_MEDIA IN VARCHAR2 
, JURISDICTION_NAME IN VARCHAR2
, JURISDICTION_TITLE IN VARCHAR2
, JURISDICTION_PHONE IN VARCHAR2 
, FILER_NAME IN VARCHAR2 
, FILER_TITLE IN VARCHAR2 
, FILER_PHONE IN VARCHAR2 
, CONTACT_NAME IN VARCHAR2 
, CONTACT_TITLE IN VARCHAR2
, CONTACT_PHONE IN VARCHAR2 
, SUPERVISOR_NAME IN VARCHAR2 
, SUPERVISOR_TITLE IN VARCHAR2
, SUPERVISOR_PHONE IN VARCHAR2
, SUPERVISOR_NOTE IN VARCHAR2
, SIR_ADDENDUM_NOTE IN VARCHAR2
, CMD_Type  IN VARCHAR2
, userId IN VARCHAR2 
, cur_OUT OUT Sys_Refcursor
) AS 

uacId_  NUMERIC(10) := UAC_PROGRAM_ID;
sirId_ NUMERIC(10) := SIR_ID;
incidentDate_  TIMESTAMP := DateOfIncident;
reportedDate_  TIMESTAMP := Datereported;
location_  VARCHAR2(2000) := LocationOfIncident;
abusePerId_     NUMERIC(1,0) := ABUSE_PERPETRATOR_ID;
runaway_    NUMERIC(1,0) := RUNAWAY;
runawayRisk_  NUMERIC(1,0) := RUNAWAY_RISK;
absense_  VARCHAR2(20) := ABSENSE_OF_UAC;
sysnopsis_  VARCHAR2(32000) := SYNOPSIS_OF_INCIDENT;
witnesses_        VARCHAR2(32000) := WITNESSES;
staffResponse_    VARCHAR2(32000) := STAFFRESPONSE;
resolustions_      VARCHAR2(32000) := RESOLUTION;
recommendations_  VARCHAR2(32000) := RECOMMENDATIONS;
infoReleased_      VARCHAR2(32000) := INFO_RELEASED_TO_MEDIA;
jurisName_  VARCHAR2(2000) := JURISDICTION_NAME;
jurisTitle_ VARCHAR2(2000) := JURISDICTION_TITLE;
jurisPhone_      VARCHAR2(20) := JURISDICTION_PHONE;
filerName_  VARCHAR2(2000) := FILER_NAME;
filerTitle_ VARCHAR2(2000) := FILER_TITLE;
filerPhone_      VARCHAR2(20) := FILER_PHONE;
contactName_  VARCHAR2(2000) := CONTACT_NAME;
contactTitle_ VARCHAR2(2000) := CONTACT_TITLE;
contactPhone_      VARCHAR2(20) := CONTACT_PHONE;
supervisorName_  VARCHAR2(2000) := SUPERVISOR_NAME;
supervisorTitle_ VARCHAR2(2000) := SUPERVISOR_TITLE;
supervisorPhone_      VARCHAR2(20) := SUPERVISOR_PHONE;
supervisorNote_   VARCHAR2(32000) := SUPERVISOR_NOTE;
addendumNote_   VARCHAR2(32000) := SIR_ADDENDUM_NOTE; 
userId_   VARCHAR2(112) := userId;
cmdType_  VARCHAR2(30) := Upper(CMD_Type);

maxNo     NUMERIC(10);
BEGIN
  if cmdType_ = 'INSERT' Then
    maxNo := SEQ_SIR.nextval;
    Insert into UAC_SIR (SIR_ID, UAC_PROGRAM_ID, DATE_OF_INCIDENT, DATE_REPORTED, LOCATION_OF_INCIDENT,
    ABUSE_PERPETRATOR_ID, RUNAWAY, RUNAWAY_RISK, ABSENSE_OF_UAC, SYNOPSIS_OF_INCIDENT, WITNESSES, STAFF_RESPONSES, RESOLUTION,
    RECOMMENDATIONS, INFO_RELEASED_TO_MEDIA, JURISDICTION_NAME, JURISDICTION_TITLE, JURISDICTION_PHONE, CONTACT_NAME, CONTACT_TITLE,
    CONTACT_PHONE, FILER_NAME, FILER_TITLE, FILER_PHONE, SUPERVISOR_NAME, SUPERVISOR_TITLE, SUPERVISOR_PHONE, SUPERVISOR_NOTE,SIR_ADDENDUM_NOTE, 
    DATE_CREATED, USER_CREATED,UPTIME, UPUSER)
    Values (maxNo, uacId_, incidentDate_, reportedDate_, location_,
    abusePerId_, runaway_, runawayRisk_, absense_,sysnopsis_, witnesses_, staffResponse_, resolustions_,
    recommendations_, infoReleased_, jurisName_, jurisTitle_, jurisPhone_, contactName_, contactTitle_, contactPhone_,
    filerName_, filerTitle_, filerPhone_, supervisorName_, supervisorTitle_, supervisorPhone_, supervisorNote_, addendumNote_,
    sysdate, userId_, sysdate, userId_);
  elsif cmdType_ = 'UPDATE' Then
    maxNo := sirId_;
    Update UAC_SIR Set
      DATE_OF_INCIDENT = incidentDate_, 
      DATE_REPORTED = reportedDate_,  
      LOCATION_OF_INCIDENT = location_,
      ABUSE_PERPETRATOR_ID = abusePerId_, 
      RUNAWAY = runaway_, 
      RUNAWAY_RISK = runawayRisk_, 
      ABSENSE_OF_UAC = absense_, 
      SYNOPSIS_OF_INCIDENT = sysnopsis_, 
      WITNESSES = witnesses_, 
      STAFF_RESPONSES = staffResponse_, 
      RESOLUTION = resolustions_,
      RECOMMENDATIONS = recommendations_, 
      INFO_RELEASED_TO_MEDIA = infoReleased_, 
      JURISDICTION_NAME = jurisName_, 
      JURISDICTION_TITLE = jurisTitle_, 
      JURISDICTION_PHONE = jurisPhone_, 
      CONTACT_NAME = contactName_, 
      CONTACT_TITLE = contactTitle_,
      CONTACT_PHONE = contactPhone_, 
      FILER_NAME = filerName_, 
      FILER_TITLE = filerTitle_, 
      FILER_PHONE = filerPhone_, 
      SUPERVISOR_NAME = supervisorName_, 
      SUPERVISOR_TITLE = supervisorTitle_, 
      SUPERVISOR_PHONE = supervisorPhone_, 
      SUPERVISOR_NOTE = supervisorNote_,
      SIR_ADDENDUM_NOTE = addendumNote_,
      UPTIME = Sysdate, 
      UPUSER = userId_
      Where SIR_ID = sirId_;
  end if;
  Open cur_OUT For
  Select maxNo as SIR_ID from dual;
END SP_UPDATE_SIR;

PROCEDURE "SP_UPDATE_SIR_MEDIA_CONTACT" 
(
  SESSION_ID IN VARCHAR2 
) AS 
sessionId_  VARCHAR2(112) := SESSION_ID;
BEGIN
  Insert into UAC_SIR_MEDIA_CONTACTS(MEDIA_CONTACT_ID, SIR_ID, CONTACT_NAME, AGENCY, PHONE, DATE_CONTACT)
  Select SEQ_SIR_MEDIA.nextval, SIR_ID, CONTACT_NAME, AGENCY, PHONE, DATE_CONTACT
  From TT_SIR_MEDIA_CONTACTS Where Session_ID = sessionId_;

  Delete from TT_SIR_MEDIA_CONTACTS Where Session_ID = sessionId_ Or UPTIME < SysDate - 1;
END SP_UPDATE_SIR_MEDIA_CONTACT;

PROCEDURE "SP_UPDATE_SPONSOR_AFFIDAVITS" 
(
  SESSION_ID IN VARCHAR2 
) AS 
sessionId_  VARCHAR2(112) := SESSION_ID;
BEGIN
  Insert into SPONSOR_AFFIDAVITS_SUPPORT(AFFIDAVIT_ID, SPONSOR_ID, ANNUAL_INCOME, PROOF_OF_INCOME, EMP_NAME, EMP_ADDRESS,
  EMP_CITY, EMP_STATE, EMP_ZIPCODE, EMP_PHONE, HAS_DOC_SUPPORT, DATE_CREATED, USER_CREATED, UPTIME, UPUSER)
  Select SEQ_SPONSOR_AFFIDAVITS.nextval, SPONSOR_ID, ANNUAL_INCOME, PROOF_OF_INCOME, EMP_NAME, EMP_ADDRESS,
  EMP_CITY, EMP_STATE, EMP_ZIPCODE, EMP_PHONE, HAS_DOC_SUPPORT, SysDate, UPUSER, SysDate, UPUSER
  From TT_SPONSOR_AFFIDAVITS_SUPPORT 
  Where Session_ID = sessionId_;

  Delete from TT_SPONSOR_AFFIDAVITS_SUPPORT Where Session_ID = sessionId_ Or UPTIME < SysDate - 1;
END SP_UPDATE_SPONSOR_AFFIDAVITS;

PROCEDURE "SP_UPDATE_SPONSOR_HH_OCCUPANTS" 
(
  SESSION_ID IN VARCHAR2 
) AS 
sessionId_  VARCHAR2(112) := SESSION_ID;
BEGIN
  Insert into SPONSOR_HOUSEHOLD_OCCUPANTS(OCCUPANT_ID, SPONSOR_ID, UAC_ID, NAME_OF_OCCUPANT, AGE, RELATIONSHIP_TO_SPONSOR, RELATIONSHIP_TO_MINOR,
  DEPENDENT_OF_SPONSOR, USER_CREATED, DATE_CREATED, UPTIME, UPUSER, FIRST_NAME, LAST_NAME, GENDER, DOB)
  Select SEQ_SPONSOR_HH.nextval,SPONSOR_ID, UAC_ID, NAME_OF_OCCUPANT, AGE, RELATIONSHIP_TO_SPONSOR, RELATIONSHIP_TO_MINOR,
  DEPENDENT_OF_SPONSOR, UPUSER, SysDate, SysDate, UPUSER, FIRST_NAME, LAST_NAME, GENDER, DOB From TT_SPONSOR_HOUSEHOLD_OCCUPANTS
  Where Session_ID = sessionId_;

  Delete from TT_UAC_ISP_SUB Where Session_ID = sessionId_ Or UPTIME < SysDate - 1;
END SP_UPDATE_SPONSOR_HH_OCCUPANTS;

PROCEDURE "SP_UPDATE_UAC_FAST" ( v_FAST_ID NUMBER, v_UAC_ID NUMBER, v_INTAKE_STAFF_NAME VARCHAR2, --v_FAST_STATUS CHAR, v_FAC_CONTACTINFO VARCHAR2, v_UAC_LOCATION_AT_REF VARCHAR2, v_DETENTION_FACILITY_IF_ANY VARCHAR2, v_UNACCOMPANIED_STATUS VARCHAR2, v_UAC_BEHAVEIOR_HIST VARCHAR2, v_ADDITIONAL_INFO VARCHAR2, v_PLACEMENT_CONSULTATION VARCHAR2, /*v_INTAKES_INDICATION NUMBER, v_CARE_PROVIDER_INDICATION NUMBER, v_FFS_INDICATION NUMBER, v_I_PETTY_OFFENSES NUMBER, v_C_PETTY_OFFENSES NUMBER, v_F_PETTY_OFFENSES NUMBER, v_I_NON_PETTY_OFFENSES NUMBER, v_C_NON_PETTY_OFFENSES NUMBER, v_F_NON_PETTY_OFFENSES NUMBER, v_I_NON_FIREARM_VIOLENCE NUMBER, v_C_NON_FIREARM_VIOLENCE NUMBER, v_F_NON_FIREARM_VIOLENCE NUMBER, v_I_VIOLENT_OFFENSES NUMBER, v_C_VIOLENT_OFFENSES NUMBER, v_F_VIOLENT_OFFENSES NUMBER, v_I_WEAPON1_VIOLENCES NUMBER, v_C_WEAPON1_VIOLENCES NUMBER, v_F_WEAPON1_VIOLENCES NUMBER, v_I_WEAPON2_VIOLENCES NUMBER, v_C_WEAPON2_VIOLENCES NUMBER, v_F_WEAPON2_VIOLENCES NUMBER, v_I_WEAPON3_VIOLATION NUMBER, v_C_WEAPON3_VIOLENCES NUMBER, v_F_WEAPON3_VIOLENCES NUMBER, v_I_VOIW3_VIOLENCE NUMBER, v_C_VOIW3_VIOLENCES NUMBER, v_F_VOIW3_VIOLENCES NUMBER, v_I_SEXUAL_ASSAULT NUMBER, v_C_SEXUAL_ASSAULT NUMBER, v_F_SEXUAL_ASSAULT NUMBER, v_I_SERIOUS_VIOLENT_OFFENSES NUMBER, v_C_SERIOUS_VIOLENT_OFFENSES NUMBER, v_F_SERIOUS_VIOLENT_OFFENSES NUMBER, */ v_I_COMMENTS VARCHAR2, v_C_COMMENTS VARCHAR2, v_F_COMMENTS VARCHAR2, /*v_I_VT_GOV_CUSTODY NUMBER, v_C_VT_GOV_CUSTODY NUMBER, v_F_VT_GOV_CUSTODY NUMBER, v_I_GANG_VIOLATION NUMBER, v_C_GANG_VIOLATION NUMBER, v_F_GANG_VIOLATION NUMBER, */ v_I_COMMENTS_ON_GANG_VIOLATION VARCHAR2, v_C_COMMENTS_ON_GANG_VIOLATION VARCHAR2, v_F_COMMENTS_ON_GANG_VIOLATION VARCHAR2, /* v_I_ESCAPES_FROM_GOV_CUSTODY NUMBER, v_C_ESCAPES_FROM_GOV_CUSTODY NUMBER, v_F_ESCAPES_FROM_GOV_CUSTODY NUMBER, */ v_I_OTHER_RIST_FACTOR VARCHAR2, v_C_OTHER_RIST_FACTOR VARCHAR2, v_F_OTHER_RIST_FACTOR VARCHAR2, v_I_OTHER_RIST_FACTOR_SCORE NUMBER, v_C_OTHER_RIST_FACTOR_SCORE NUMBER, v_F_OTHER_RI