v_I_STAFF_NAME VARCHAR2, v_I_DUCS_SUPERVISOR_NAME VARCHAR2, v_I_DATE_OVERRIDE_APPROVED DATE, v_I_TIME_OVERRIDE_APPROVED DATE, v_I_OVERRIDE_JUSTIFICATION VARCHAR2, v_I_IS_FAST_PLACEMENT NUMBER, v_I_MORE_SERVICE_REQUIRED NUMBER, v_IS_OVERRIDE_OF_PM NUMBER, v_INTAKES_OVERRIDE_PLACEMENT NUMBER, /* v_I_IS_FAST_PLACEMENT NUMBER, v_I_MORE_SERVICE_REQUIRED NUMBER,
*/ v_I_ALERT_TYPE NUMBER, v_UPUSER VARCHAR2, v_CMD_TYPE VARCHAR2, v_PageType VARCHAR2, v_Q1_INT VARCHAR2, v_Q1_FAC VARCHAR2, v_Q1_FFS VARCHAR2, v_Q2_INT VARCHAR2, v_Q2_FAC VARCHAR2, v_Q2_FFS VARCHAR2, v_Q3_INT VARCHAR2, v_Q3_FAC VARCHAR2, v_Q3_FFS VARCHAR2, v_Q4_INT VARCHAR2, v_Q4_FAC VARCHAR2, v_Q4_FFS VARCHAR2, v_TherapeuticNeed NUMBER, v_ChildBehavior NUMBER, v_LegalProcess NUMBER, v_FamilyReunion NUMBER, v_DATE_OF_FINALPLACEMENT DATE, v_FINAL_PROGRAM_NAME VARCHAR2, v_FINAL_PROGRAM_TYPE VARCHAR2, v_IPScore NUMBER ) AS
