/* I_VT_GOV_CUSTODY, C_VT_GOV_CUSTODY, F_VT_GOV_CUSTODY, I_GANG_VIOLATION, C_GANG_VIOLATION, F_GANG_VIOLATION, */ I_COMMENTS_ON_GANG_VIOLATION, C_COMMENTS_ON_GANG_VIOLATION, F_COMMENTS_ON_GANG_VIOLATION, /* I_ESCAPES_FROM_GOV_CUSTODY, C_ESCAPES_FROM_GOV_CUSTODY, F_ESCAPES_FROM_GOV_CUSTODY, */ I_OTHER_RIST_FACTORS, C_OTHER_RIST_FACTORS, F_OTHER_RIST_FACTORS, I_OTHER_RIST_FACTOR_SCORE, C_OTHER_RIST_FACTOR_SCORE, F_OTHER_RIST_FACTOR_SCORE, I_OTHER_RISK_COMMENT, C_OTHER_RISK_COMMENT, F_OTHER_RISK_COMMENT, MENTAL_HELTH_ISSUE, CONFIRM_MENTAL_HELTH_ISSUE, COMMENT_IF_YES_TO_MENTAL_ISSUE, IS_SERVICE_REQUIRED, COMMENT_IF_YES_TO_SERVICE, COMMENT_IF_NO_TO_SERVICE, C_THERAPEUTIC_NEED_IDENTIFIED, F_THERAPEUTIC_NEED_IDENTIFIED, C_THER_NEED_REQUIRE_CHANGE, F_THER_NEED_REQUIRE_CHANGE, I_COMMENT_ON_MENTAL_ISSUE, C_COMMENT_ON_MENTAL_ISSUE, F_COMMENT_ON_MENTAL_ISSUE,
C_EVIDENCE_OF_CHILD_BEHAVIOR, F_EVIDENCE_OF_CHILD_BEHAVIOR, C_HAS_REASON_IN_LEGAL_PROCESS, F_HAS_REASON_IN_LEGAL_PROCESS, C_HAS_REASON_IN_REUNIFICATION, F_HAS_REASON_IN_REUNIFICATION, I_COMMENT_THER_NEED_IDENTIFIED, C_COMMENT_THER_NEED_IDENTIFIED, F_COMMENT_THER_NEED_IDENTIFIED, ATTONEY_LETTER_PROVIDED, ATTONEY_NAME, FFS_NAME, C_STAFF_NAME, I_RECOMMENTED_PROG_TYPE, FFS_SCORE, F_DUCS_SUPERVISOR_NAME, F_STAFF_NAME, IS_PLACEMENT_DIFFERENT, DID_FSS_OVERRIDE, F_DATE_OVERRIDE_APPROVED, F_TIME_OVERRIDE_APPROVED, F_OVERRIDE_JUSTIFICATION, ALERT_TYPE, I_STAFF_NAME, I_DUCS_SUPERVISOR_NAME, I_DATE_OVERRIDE_APPROVED, I_TIME_OVERRIDE_APPROVED, I_OVERRIDE_JUSTIFICATION, I_IS_FAST_PLACEMENT, I_ADDITIONAL_SERVICE_REQUIRED, IS_OVERRIDE_OF_PM, DID_INTAKES_OVERRIDE_PLACEMENT, I_ALERT_TYPE, /*
I_ALERT_TYPE,
*/ UPTIME, UPUSER, Q1_INT, Q1_FAC, Q1_FFS, Q2_INT, Q2_FAC, Q2_FFS, Q3_INT, Q3_FAC, Q3_FFS, Q4_INT, Q4_FAC, Q4_FFS, PM_TherapeuticNeed, PM_ChildBehavior, PM_LegalProcess, PM_FamilyReunion, DATE_OF_FINALPLACEMENT, FINAL_PROGRAM_NAME, FINAL_PROGRAM_TYPE, IPSCORE ) Values (SEQ_UAC_FAST.nextval, v_UAC_ID, v_INTAKE_STAFF_NAME, --v_FAST_STATUS, --v_INTAKES_INDICATION, v_FAC_CONTACTINFO, v_UAC_LOCATION_AT_REF, v_DETENTION_FACILITY_IF_ANY, v_UNACCOMPANIED_STATUS, v_UAC_BEHAVEIOR_HIST, v_ADDITIONAL_INFO, v_PLACEMENT_CONSULTATION, /* v_CARE_PROVIDER_INDICATION, v_FFS_INDICATION, v_I_PETTY_OFFENSES, v_I_NON_PETTY_OFFENSES, v_I_NON_FIREARM_VIOLENCE, v_I_VIOLENT_OFFENSES, v_I_WEAPON1_VIOLENCES, v_I_WEAPON2_VIOLENCES, v_I_WEAPON3_VIOLATION, v_I_VOIW3_VIOLENCE, v_I_SEXUAL_ASSAULT, v_I_SERIOUS_VIOLENT_OFFENSES, */ v_I_COMMENTS, /* v_C_PETTY_OFFENSES, v_C_NON_PETTY_OFFENSES, v_C_NON_FIREARM_VIOLENCE, v_C_VIOLENT_OFFENSES, v_C_WEAPON1_VIOLENCES, v_C_WEAPON2_VIOLENCES, v_C_WEAPON3_VIOLENCES, v_C_VOIW3_VIOLENCES, v_C_SEXUAL_ASSAULT, v_C_SERIOUS_VIOLENT_OFFENSES, */ v_C_COMMENTS, /* v_F_PETTY_OFFENSES, v_F_NON_PETTY_OFFENSES, v_F_NON_FIREARM_VIOLENCE, v_F_VIOLENT_OFFENSES, v_F_WEAPON1_VIOLENCES, v_F_WEAPON2_VIOLENCES, v_F_WEAPON3_VIOLENCES, v_F_VOIW3_VIOLENCES, v_F_SEXUAL_ASSAULT, v_F_SERIOUS_VIOLENT_OFFENSES, */ v_F_COMMENTS, /* v_I_VT_GOV_CUSTODY, v_C_VT_GOV_CUSTODY, v_F_VT_GOV_CUSTODY, v_I_GANG_VIOLATION, v_C_GANG_VIOLATION, v_F_GANG_VIOLATION, */ v_I_COMMENTS_ON_GANG_VIOLATION, v_C_COMMENTS_ON_GANG_VIOLATION, v_F_COMMENTS_ON_GANG_VIOLATION, /*v_I_ESCAPES_FROM_GOV_CUSTODY, v_C_ESCAPES_FROM_GOV_CUSTODY, v_F_ESCAPES_FROM_GOV_CUSTODY, */ v_I_OTHER_RIST_FACTOR, v_C_OTHER_RIST_FACTOR, v_F_OTHER_RIST_FACTOR, v_I_OTHER_RIST_FACTOR_SCORE, v_C_OTHER_RIST_FACTOR_SCORE, v_F_OTHER_RIST_FACTOR_SCORE, v_I_OTHER_RISK_COMMENT, v_C_OTHER_RISK_COMMENT, v_F_OTHER_RISK_COMMENT, v_MENTAL_HELTH_ISSUE, v_CONFIRM_MENTAL_HELTH_ISSUE, v_COMMENT_YES_TO_MENTAL_ISSUE, v_IS_SERVICE_REQUIRED, v_COMMENT_YES_TO_SERVI