v_INTAKES_OVERRIDE_PLACEMENT,
*/ SYSDATE, v_UPUSER, v_Q1_INT, v_Q1_FAC, v_Q1_FFS, v_Q2_INT, v_Q2_FAC, v_Q2_FFS, v_Q3_INT, v_Q3_FAC, v_Q3_FFS, v_Q4_INT, v_Q4_FAC, v_Q4_FFS, v_TherapeuticNeed, v_ChildBehavior, v_LegalProcess, v_FamilyReunion, v_DATE_OF_FINALPLACEMENT, v_FINAL_PROGRAM_NAME, v_FINAL_PROGRAM_TYPE, v_IPScore );
--update UAC_INFO set UAC_FAST_STATUS = v_PageType where UAC_ID=v_UAC_ID; commit; elsif UPPER(v_CMD_TYPE) = 'UPDATE' Then update UAC_FAST set INTAKE_STAFF_NAME=v_INTAKE_STAFF_NAME, --FAST_STATUS=v_FAST_STATUS, FAC_CONTACTINFO=v_FAC_CONTACTINFO, UAC_LOCATION_AT_REF=v_UAC_LOCATION_AT_REF, DETENTION_FACILITY_IF_ANY=v_DETENTION_FACILITY_IF_ANY, UNACCOMPANIED_STATUS=v_UNACCOMPANIED_STATUS, UAC_BEHAVEIOR_HIST=v_UAC_BEHAVEIOR_HIST, ADDITIONAL_INFO=v_ADDITIONAL_INFO, PLACEMENT_CONSULTATION=v_PLACEMENT_CONSULTATION, /* INTAKES_INDICATION=v_INTAKES_INDICATION, CARE_PROVIDER_INDICATION=v_CARE_PROVIDER_INDICATION, FFS_INDICATION=v_FFS_INDICATION, I_PETTY_OFFENSES=v_I_PETTY_OFFENSES, I_NON_PETTY_OFFENSES=v_I_NON_PETTY_OFFENSES, I_NON_FIREARM_VIOLENCE=v_I_NON_FIREARM_VIOLENCE, I_VIOLENT_OFFENSES=v_I_VIOLENT_OFFENSES, I_WEAPON1_VIOLENCES=v_I_WEAPON1_VIOLENCES, I_WEAPON2_VIOLENCES=v_I_WEAPON2_VIOLENCES, I_WEAPON3_VIOLATION=v_I_WEAPON3_VIOLATION, I_VOIW3_VIOLENCE=v_I_VOIW3_VIOLENCE, I_SEXUAL_ASSAULT=v_I_SEXUAL_ASSAULT, I_SERIOUS_VIOLENT_OFFENSES=v_I_SERIOUS_VIOLENT_OFFENSES, */ I_COMMENTS=v_I_COMMENTS, /* C_PETTY_OFFENSES=v_C_PETTY_OFFENSES, C_NON_PETTY_OFFENSES=v_C_NON_PETTY_OFFENSES, C_NON_FIREARM_VIOLENCE=v_C_NON_FIREARM_VIOLENCE, C_VIOLENT_OFFENSES=v_C_VIOLENT_OFFENSES, C_WEAPON1_VIOLENCES=v_C_WEAPON1_VIOLENCES, C_WEAPON2_VIOLENCES=v_C_WEAPON2_VIOLENCES, C_WEAPON3_VIOLENCES=v_C_WEAPON3_VIOLENCES, C_VOIW3_VIOLENCES=v_C_VOIW3_VIOLENCES, C_SEXUAL_ASSAULT=v_C_SEXUAL_ASSAULT, C_SERIOUS_VIOLENT_OFFENSES=v_C_SERIOUS_VIOLENT_OFFENSES, */ C_COMMENTS=v_C_COMMENTS, /*F_PETTY_OFFENSES=v_F_PETTY_OFFENSES, F_NON_PETTY_OFFENSES=v_F_NON_PETTY_OFFENSES, F_NON_FIREARM_VIOLENCE=v_F_NON_FIREARM_VIOLENCE, F_VIOLENT_OFFENSES=v_F_VIOLENT_OFFENSES, F_WEAPON1_VIOLENCES=v_F_WEAPON1_VIOLENCES, F_WEAPON2_VIOLENCES=v_F_WEAPON2_VIOLENCES, F_WEAPON3_VIOLENCES=v_F_WEAPON3_VIOLENCES, F_VOIW3_VIOLENCES=v_F_VOIW3_VIOLENCES, F_SEXUAL_ASSAULT=v_F_SEXUAL_ASSAULT, F_SERIOUS_VIOLENT_OFFENSES=v_F_SERIOUS_VIOLENT_OFFENSES, */ F_COMMENTS=v_F_COMMENTS, /*I_VT_GOV_CUSTODY=v_I_VT_GOV_CUSTODY, C_VT_GOV_CUSTODY=v_C_VT_GOV_CUSTODY, F_VT_GOV_CUSTODY=v_F_VT_GOV_CUSTODY, I_GANG_VIOLATION=v_I_GANG_VIOLATION, C_GANG_VIOLATION=v_C_GANG_VIOLATION, F_GANG_VIOLATION=v_F_GANG_VIOLATION, */ I_COMMENTS_ON_GANG_VIOLATION=v_I_COMMENTS_ON_GANG_VIOLATION, C_COMMENTS_ON_GANG_VIOLATION=v_C_COMMENTS_ON_GANG_VIOLATION, F_COMMENTS_ON_GANG_VIOLATION=v_F_COMMENTS_ON_GANG_VIOLATION, /* I_ESCAPES_FROM_GOV_CUSTODY=v_I_ESCAPES_FROM_GOV_CUSTODY, C_ESCAPES_FROM_GOV_CUSTODY=v_C_ESCAPES_FROM_GOV_CUSTODY, F_ESCAPES_FROM_GOV_CUSTODY=v_F_ESCAPES_FROM_GOV_CUSTODY, */ I_OTHER_RIST_FACTORS=v_I_OTHER_RIST_FACTOR, C_OTHER_RIST_FACTORS=v_C_OTHER_RIST_FACTOR, F_OTHER_RIST_FACTORS=v_F_OTHER_RIST_FACTOR, I_OTHER_RIST_FACTOR_SCORE=v_I_OTHER_RIST_FACTOR_SCORE, C_OTHER_RIST_FACTOR_SCORE=v_C_OTHER_RIST_FACTOR_SCORE, F_OTHER_RIST_FACTOR_SCORE=v_F_OTHER_RIST_FACTOR_SCORE, I_OTHER_RISK_COMMENT=v_I_OTHER_RISK_COMMENT, C_OTHER_RISK_COMMENT=v_C_OTHER_RISK_COMMENT, F_OTHER_RISK_COMMENT=v_F_OTHER_RISK_COMMENT, MENTAL_HELTH_ISSUE=v_MENTAL_HELTH_ISSUE, CONFIRM_MENTAL_HELTH_ISSUE=v_CONFIRM_MENTAL_HELTH_ISSUE, COMMENT_IF_YES_TO_MENTAL_ISSUE=v_COMMENT_YES_TO_MENTAL_ISSUE, IS_SERVICE_REQUIRED=v_IS_SERVICE_REQUIRED, COMMENT_IF_YES_TO_SERVICE=v_COMMENT_YES_TO_SERVICE, COMMENT_IF_NO_TO_SERVICE=v_COMMENT_NO_TO_SERVICE, I_COMMENT_ON_MENTAL_ISSUE=v_I_COMMENT_ON_MENTAL_ISSUE, C_COMMENT_ON_MENTAL_ISSUE=v_C_COMMENT_ON_MENTAL_ISSUE, F_COMMENT_ON_MENTAL_ISSUE=v_F_COMMENT_ON_MENTAL_ISSUE, C_THERAPEUTIC_NEED_IDENTIFIED=v_C_THER_NEED_IDENTIFIED, F_THERAPEUTIC_NEED_IDENTIFIED=v_F_THER_NEED_IDENTIFIED, C_THER_NEED_REQUIRE_CHANGE=v_C_THER_NEED_REQUIRE_CHANGE, F_THER_NEED_REQUIRE_CHANGE=v_F_THER_NEED_REQUIRE_CHANGE,
C_EVIDENCE_OF_CHILD_BEHAVIOR=v_C_EVIDENCE_OF_CHILD_BEHAVIOR, F_EVIDENCE_OF_CHILD_BEHAVIOR=v_F_EVIDENCE_OF_CHILD_BEHAVIOR, C_HAS_REASON_IN_LEGAL_PROCESS=v_C_REASON_IN_LEGAL_PROCESS, F_HAS_REASON_IN_LEGAL_PROCESS=v_F_REASON_IN_LEGAL_PROCESS, C_HAS_REASON_IN_REUNIFICATION=v_C_REASON_IN_REUNIFICATION, F_HAS_REASON_IN_REUNIFICATION=v_F_REASON_IN_REUNIFICATION, I_COMMENT_THER_NEED_IDENTIFIED=v_I_COM_THER_NEED_IDENTIFIED, C_COMMENT_THER_NEED_IDENTIFIED=v_C_COM_THER_NEED_IDENTIFIED, F_COMMENT_THER_NEED_IDENTIFIED=v_F_COM_THER_NEED_IDENTIFIED,
ATTONEY_LETTER_PROVIDED=v_ATTONEY_LETTER_PROVIDED, ATTONEY_NAME=v_ATTONEY_NAME, FFS_NAME=v_FFS_NAME, C_STAFF_NAME=v_C_STAFF_NAME, I_RECOMMENTED_PROG_TYPE=v_I_RECOMMENTED_PROG_TYPE,
FFS_SCORE=v_FFS_SCORE, F_DUCS_SUPERVISOR_NAME=v_F_DUCS_SUPERVISOR_NAME, F_STAFF_NAME=v_F_STAFF_NAME, IS_PLACEMENT_DIFFERENT=v_IS_PLACEMENT_DIFFERENT, DID_FSS_OVERRIDE=v_DID_FSS_OVERRIDE, F_DATE_OVERRIDE_APPROVED=v_F_DATE_OVERRIDE_APPROVED, F_TIME_OVERRIDE_APPROVED=v_F_TIME_OVERRIDE_APPROVED, F_OVERRIDE_JUSTIFICATION=v_F_OVERRIDE_JUSTIFICATION, ALERT_TYPE=v_ALERT_TYPE, I_STAFF_NAME=v_I_STAFF_NAME, I_DUCS_SUPERVISOR_NAME=v_I_DUCS_SUPERVISOR_NAME, I_DATE_OVERRIDE_APPROVED=v_I_DATE_OVERRIDE_APPROVED, I_TIME_OVERRIDE_APPROVED=v_I_TIME_OVERRIDE_APPROVED, I_OVERRIDE_JUSTIFICATION=v_I_OVERRIDE_JUSTIFICATION, I_IS_FAST_PLACEMENT=v_I_IS_FAST_PLACEMENT, I_ADDITIONAL_SERVICE_REQUIRED=v_I_MORE_SERVICE_REQUIRED, IS_OVERRIDE_OF_PM = v_IS_OVERRIDE_OF_PM, DID_INTAKES_OVERRIDE_PLACEMENT=v_INTAKES_OVERRIDE_PLACEMENT, I_ALERT_TYPE=v_I_ALERT_TYPE,
/* I_IS_FAST_PLACEMENT=v_I_IS_FAST_PLACEMENT, I_ADDITIONAL_SERVICE_REQUIRED=v_I_MORE_SERVICE_REQUIRED, I_ALERT_TYPE=v_I_ALERT_TYPE,
*/ UPTIME=sysdate, UPUSER=v_UPUSER, Q1_INT= v_Q1_INT, Q1_FAC= v_Q1_FAC, Q1_FFS = v_Q1_FFS, Q2_INT= v_Q2_INT, Q2_FAC= v_Q2_FAC, Q2_FFS = v_Q2_FFS, Q3_INT= v_Q3_INT, Q3_FAC= v_Q3_FAC, Q3_FFS = v_Q3_FFS, Q4_INT= v_Q4_INT, Q4_FAC= v_Q4_FAC, Q4_FFS = v_Q4_FFS, PM_TherapeuticNeed = v_TherapeuticNeed, PM_ChildBehavior = v_ChildBehavior, PM_LegalProcess = v_LegalProcess, PM_FamilyReunion = v_FamilyReunion, DATE_OF_FINALPLACEMENT = v_DATE_OF_FINALPLACEMENT, FINAL_PROGRAM_NAME = v_FINAL_PROGRAM_NAME, FINAL_PROGRAM_TYPE = v_FINAL_PROGRAM_TYPE, IPSCORE= v_IPScore where FAST_ID=v_FAST_ID; --if v_FAST_STATUS = '1' then -- update UAC_INFO set UAC_FAST_STATUS = 'F' where UAC_ID=v_UAC_ID; --else -- update UAC_INFO set UAC_FAST_STATUS = 'N' where UAC_ID=v_UAC_ID; --end if;
commit; end if;
END SP_UPDATE_UAC_FAST;

PROCEDURE "SP_UPDATE_UAC_IMMUNIZATION" 
(
  v_SESSION_ID IN VARCHAR2,
  v_UAC_PROGRAM_ID NUMBER, 
  v_UPUSER VARCHAR2,
  v_CMD_TYPE VARCHAR2

) AS 
BEGIN
  DELETE FROM UAC_IMMUNIZATION WHERE UAC_PROGRAM_ID=v_UAC_PROGRAM_ID;

  Insert into UAC_IMMUNIZATION(IMMUNIZATION_ID, UAC_PROGRAM_ID, IMMUNE_TYPE_ID, IMMUNE_DATE1,IMMUNE_DATE2, 
  IMMUNE_DATE3,IMMUNE_DATE4,IMMUNE_DATE5,DATE_CREATED, USER_CREATED,UPTIME,UPUSER)
  Select SEQ_MEDICAL_IMMUNIZATION.nextval, v_UAC_PROGRAM_ID, IMMUNE_TYPE_ID,IMMUNE_DATE1,IMMUNE_DATE2,
  IMMUNE_DATE3,IMMUNE_DATE4, IMMUNE_DATE5, SYSDATE, v_UPUSER, SYSDATE, v_UPUSER
  From TT_UAC_IMMUNIZATION Where SESSION_ID = v_SESSION_ID;

  Delete from TT_UAC_IMMUNIZATION Where SESSION_ID = v_SESSION_ID;
  COMMIT;
END SP_UPDATE_UAC_IMMUNIZATION;

PROCEDURE "SP_UPDATE_UAC_INFO" ( v_Token VARCHAR2, v_UAC_ID NUMBER, v_ProgId NUMBER, v_Upuser VARCHAR2, cur_OUT OUT Sys_Refcursor ) AS uacId_ NUMBER(10); uacStatus_ VARCHAR2(20); orgUacStatus_ VARCHAR2(20); n NUMBER(4) := 0; orgProgId_ NUMBER(10) := -1; outUacID NUMBER(10); BEGIN Select UAC_ID, UAC_STATUS into uacId_, uacStatus_ from UAC_INFO_TMP where TOKEN = v_Token; outUacId := uacId_; if nvl(v_ProgId,0) > 0 Then If v_UAC_ID != -1 Then Select UAC_STATUS into orgUacStatus_ From UAC_INFO Where UAC_ID = v_UAC_ID; If uacStatus_ = 'PENDING' And (orgUacStatus_ = 'PENDING' or orgUacStatus_ = 'CANCELLED') Then uacStatus_ := 'ENROUTE'; End If; Else If uacStatus_ = 'PENDING' Then uacStatus_ := 'ENROUTE'; End If; End If; End if;
if v_UAC_ID = -1 Then uacId_ := SEQ_UAC_INFO.NEXTVAL; outUacId := uacId_; Insert into UAC_INFO ( UAC_ID, ALIEN_NO, FIRST_NAME, LAST_NAME, MIDDLE_NAME, AKA, DOB, COB, DATE_OF_ENTRY, MANNER_OF_ENTRY, CITY_OF_ENTRY, STATE_OF_ENTRY, DATE_OF_APPREHENSION, REFERRING_OFFICE, PREGNANCY, INJURY, ILLNESS, HC_OTHER, IMMIGRATION_STATUS, REFERRING_AGENCY, UAC_STATUS, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, GENDER, UAC_FAST_STATUS, STATUS_ID, AGENCY_ID, HC_SUMMARY, HC_ERGENT_CONDITION, UAC_FLAG, PROCESSING_POC, PROCESSING_EMAIL, REFERRAL_NOTES, JUSTIFICATION_FOR_SS_PLACEMENT, KNOWN_GANG_AFFILIATION, NAME_OF_GANG, GANG_AFFILIATION_SUMMARY, DETENTION_TYPE_OF_FACILITY, FACILITY_NAME, FACILITY_PHONE, FACILITY_POC, FACILITY_FAX, FACILITY_ADMISSION_DATE, FACILITY_DISCHARGED_DATE, INCIDENT_RPT_IN_DC, SUM_OF_MM_HEALTH_CONDITION, DATE_OF_CURRENT_LOCATION, UAC_HEALTH_CONCERN, UAC_CRIMINAL_CHARGE, UAC_ADDITIONAL_INFO, CITY_OF_APPREHENSION, STATE_OF_APPREHENSION, CITY_OF_CURRENT_LOCATION, CURRENT_LOCATION_OTHER, STATE_OF_CURRENT_LOCATION, PROCESSING_PHONE, DATE_ORR_APPROVED,REFERRAL_DATE,FAMILYGROUP, FOOT_GUIDE,REVIEWED_BY_ORR, RELATED_TO_OTHER_UC, UAC_SEPARATED_FROM_PARENTS) Select uacId_, ALIEN_NO, FIRST_NAME, LAST_NAME, MIDDLE_NAME, AKA, DOB, COB, DATE_OF_ENTRY, MANNER_OF_ENTRY, CITY_OF_ENTRY, STATE_OF_ENTRY, DATE_OF_APPREHENSION, REFERRING_OFFICE, PREGNANCY, INJURY, ILLNESS, HC_OTHER, IMMIGRATION_STATUS, REFERRING_AGENCY, uacStatus_, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, GENDER, UAC_FAST_STATUS, STATUS_ID, AGENCY_ID, HC_SUMMARY, HC_ERGENT_CONDITION, UAC_FLAG, PROCESSING_POC, PROCESSING_EMAIL, REFERRAL_NOTES, JUSTIFICATION_FOR_SS_PLACEMENT, KNOWN_GANG_AFFILIATION, NAME_OF_GANG, GANG_AFFILIATION_SUMMARY, DETENTION_TYPE_OF_FACILITY, FACILITY_NAME, FACILITY_PHONE, FACILITY_POC, FACILITY_FAX, FACILITY_ADMISSION_DATE, FACILITY_DISCHARGED_DATE, INCIDENT_RPT_IN_DC, SUM_OF_MM_HEALTH_CONDITION, DATE_OF_CURRENT_LOCATION, UAC_HEALTH_CONCERN, UAC_CRIMINAL_CHARGE, UAC_ADDITIONAL_INFO, CITY_OF_APPREHENSION, STATE_OF_APPREHENSION, CITY_OF_CURRENT_LOCATION, CURRENT_LOCATION_OTHER, STATE_OF_CURRENT_LOCATION, PROCESSING_PHONE, DATE_ORR_APPROVED,REFERRAL_DATE, FAMILYGROUP, FOOT_GUIDE, REVIEWED_BY_ORR, RELATED_TO_OTHER_UC, UAC_SEPARATED_FROM_PARENTS From UAC_INFO_TMP Where TOKEN = v_Token;
if nvl(v_ProgId,0) > 0 Then Insert Into UAC_PROGRAM_INFO (UAC_PROGRAM_ID, UAC_ID, PROGRAM_ID, CURRENT_STATUS, DATE_REFERRED, DATE_CREATED, USER_CREATED, Uptime, UpUSER) VALUES (SEQ_UAC_PROGRAM_INFO.nextval, uacId_, v_ProgId, uacStatus_, Sysdate, Sysdate, v_UPUSER, sysdate,v_UPUSER); End If;
Update UAC_INFO_TMP SET UAC_ID = uacId_ where TOKEN = v_Token; elsif v_UAC_ID > -1 Then Select UAC_STATUS into orgUacStatus_ From UAC_INFO Where UAC_ID = v_UAC_ID; Update UAC_INFO A Set ( ALIEN_NO, FIRST_NAME, LAST_NAME, MIDDLE_NAME, AKA, DOB, COB, DATE_OF_ENTRY, MANNER_OF_ENTRY, CITY_OF_ENTRY, STATE_OF_ENTRY, DATE_OF_APPREHENSION, REFERRING_OFFICE, PREGNANCY, INJURY, ILLNESS, HC_OTHER, IMMIGRATION_STATUS, REFERRING_AGENCY, UAC_STATUS, UPTIME, UPUSER, GENDER, UAC_FAST_STATUS, STATUS_ID, AGENCY_ID, HC_SUMMARY, HC_ERGENT_CONDITION, UAC_FLAG, PROCESSING_POC, PROCESSING_EMAIL, REFERRAL_NOTES, JUSTIFICATION_FOR_SS_PLACEMENT, KNOWN_GANG_AFFILIATION, NAME_OF_GANG, GANG_AFFILIATION_SUMMARY, DETENTION_TYPE_OF_FACILITY, FACILITY_NAME, FACILITY_PHONE, FACILITY_POC, FACILITY_FAX, FACILITY_ADMISSION_DATE, FACILITY_DISCHARGED_DATE, INCIDENT_RPT_IN_DC, SUM_OF_MM_HEALTH_CONDITION, DATE_OF_CURRENT_LOCATION, UAC_HEALTH_CONCERN, UAC_CRIMINAL_CHARGE, UAC_ADDITIONAL_INFO, CITY_OF_APPREHENSION, STATE_OF_APPREHENSION, CITY_OF_CURRENT_LOCATION, CURRENT_LOCATION_OTHER, STATE_OF_CURRENT_LOCATION, PROCESSING_PHONE, DATE_ORR_APPROVED, DATE_CANCELLED, CANCELLED_BY,REFERRAL_DATE, FAMILYGROUP, FOOT_GUIDE, REVIEWED_BY_ORR, RELATED_TO_OTHER_UC, UAC_SEPARATED_FROM_PARENTS) = ( Select ALIEN_NO, FIRST_NAME, LAST_NAME, MIDDLE_NAME, AKA, DOB, COB, DATE_OF_ENTRY, MANNER_OF_ENTRY, CITY_OF_ENTRY, STATE_OF_ENTRY, DATE_OF_APPREHENSION, REFERRING_OFFICE, PREGNANCY, INJURY, ILLNESS, HC_OTHER, IMMIGRATION_STATUS, REFERRING_AGENCY, uacStatus_, UPTIME, UPUSER, GENDER, UAC_FAST_STATUS, STATUS_ID, AGENCY_ID, HC_SUMMARY, HC_ERGENT_CONDITION, UAC_FLAG, PROCESSING_POC, PROCESSING_EMAIL, REFERRAL_NOTES, JUSTIFICATION_FOR_SS_PLACEMENT, KNOWN_GANG_AFFILIATION, NAME_OF_GANG, GANG_AFFILIATION_SUMMARY, DETENTION_TYPE_OF_FACILITY, FACILITY_NAME, FACILITY_PHONE, FACILITY_POC, FACILITY_FAX, FACILITY_ADMISSION_DATE, FACILITY_DISCHARGED_DATE, INCIDENT_RPT_IN_DC, SUM_OF_MM_HEALTH_CONDITION, DATE_OF_CURRENT_LOCATION, UAC_HEALTH_CONCERN, UAC_CRIMINAL_CHARGE, UAC_ADDITIONAL_INFO, CITY_OF_APPREHENSION, STATE_OF_APPREHENSION, CITY_OF_CURRENT_LOCATION, CURRENT_LOCATION_OTHER, STATE_OF_CURRENT_LOCATION, PROCESSING_PHONE, DATE_ORR_APPROVED, DATE_CANCELLED, CANCELLED_BY,REFERRAL_DATE, FAMILYGROUP, FOOT_GUIDE,REVIEWED_BY_ORR, RELATED_TO_OTHER_UC, UAC_SEPARATED_FROM_PARENTS From UAC_INFO_TMP Where UAC_ID = A.UAC_ID And TOKEN = v_Token) WHERE UAC_ID=uacId_ And exists (Select 1 from UAC_INFO_TMP Where UAC_ID = A.UAC_ID And TOKEN = v_Token) ;
If uacStatus_ = 'ENROUTE' And (orgUacStatus_ = 'PENDING' or orgUacStatus_ = 'CANCELLED') Then Select count(1) into n from UAC_PROGRAM_INFO Where UAC_ID = v_UAC_ID And CURRENT_STATUS = uacStatus_; if n > 0 Then If nvl(v_ProgId,0) > 0 Then Update UAC_PROGRAM_INFO Set PROGRAM_ID = v_ProgId, Uptime = sysdate, upuser = v_UPUSER Where UAC_ID = v_UAC_ID And CURRENT_STATUS = uacStatus_; else Delete from UAC_PROGRAM_INFO Where UAC_ID = v_UAC_ID And CURRENT_STATUS = uacStatus_; end if; Else Select count(1) into n from UAC_PROGRAM_INFO Where UAC_ID = v_UAC_ID And CURRENT_STATUS = 'PENDING'; if n > 0 Then Update UAC_PROGRAM_INFO Set PROGRAM_ID = v_ProgId, CURRENT_STATUS = 'ENROUTE', Uptime = sysdate, upuser = v_UPUSER Where UAC_ID = v_UAC_ID And CURRENT_STATUS = 'PENDING'; else Insert Into UAC_PROGRAM_INFO (UAC_PROGRAM_ID, UAC_ID, PROGRAM_ID, CURRENT_STATUS, DATE_REFERRED, DATE_CREATED, USER_CREATED, Uptime, UpUSER) Select SEQ_UAC_PROGRAM_INFO.nextval, v_UAC_ID, v_ProgId, uacStatus_, Sysdate, Sysdate, v_UPUSER, sysdate,v_UPUSER From Dual Where Not exists(Select 1 from UAC_PROGRAM_INFO Where UAC_ID = v_UAC_ID And CURRENT_STATUS = uacStatus_); End If; End If; ElsIf uacStatus_ = 'PENDING' And orgUacStatus_ = 'ENROUTE' THEN Update UAC_PROGRAM_INFO Set PROGRAM_ID = (SELECT PROGRAM_ID FROM LIST_PROGRAMS WHERE PROGRAM_NAME = 'PENDING'), CURRENT_STATUS = 'PENDING' Where UAC_ID = v_UAC_ID And CURRENT_STATUS = 'ENROUTE'; Elsif uacStatus_ = 'CANCELLED' And (orgUacStatus_ = 'ENROUTE' Or orgUacStatus_ = 'PENDING') Then Delete from UAC_DOCUMENTS WHERE UAC_PROGRAM_ID IN (SELECT UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO WHERE UAC_ID = v_UAC_ID And CURRENT_STATUS IN ('PENDING', 'ENROUTE')); Select count(1) into n from UAC_PROGRAM_INFO Where UAC_ID = v_UAC_ID And CURRENT_STATUS = 'DISCHARGED'; If n < 1 Then Delete from UAC_SPONSOR_LIST WHERE UAC_ID = v_UAC_ID and is_deleted = 0; End If; Delete from UAC_PROGRAM_INFO WHERE UAC_ID = v_UAC_ID And CURRENT_STATUS IN ('PENDING', 'ENROUTE'); Elsif uacStatus_ = 'ENROUTE' And uacStatus_ = orgUacStatus_ Then Select PROGRAM_ID into orgProgId_ from UAC_PROGRAM_INFO Where UAC_ID = v_UAC_ID And CURRENT_STATUS = uacStatus_; if v_ProgId <> orgProgId_ Then Update UAC_PROGRAM_INFO Set PROGRAM_ID = v_ProgId, Uptime = sysdate, upuser = v_UPUSER Where UAC_ID = v_UAC_ID And CURRENT_STATUS = uacStatus_; End If; End If; end if; delete from UAC_INFO_TMP Where DateDiff('day', Uptime, Sysdate) > 1; Open cur_OUT For Select outUacId as UAC_ID from dual; END SP_UPDATE_UAC_INFO;

PROCEDURE "SP_UPDATE_UAC_INFO_FROM_XML" ( v_Transaction_Id VARCHAR2, v_UAC_ID NUMBER, v_Upuser VARCHAR2 ) AS
uacId_ NUMBER(10) := SEQ_UAC_INFO.nextval; cbp_cob_ VARCHAR2(5) := ''; cob_ VARCHAR2(50) := ''; refAgency_ NUMBER(1); cbp_refSector_ VARCHAR2(3) := ''; refSectorCD_ NUMBER(3) :=0; currLocCD_ NUMBER(3) :=0; cityCurLoc_ VARCHAR2(50) := ''; n NUMBER(4) := 0;
BEGIN
BEGIN Select COB,REFERRING_AGENCY,TO_CHAR(CITY_OF_CURRENT_LOCATION) into cbp_cob_,refAgency_,cbp_refSector_ from UAC_INFO_TMP_FROMXML where TRANSACTION_ID = v_Transaction_Id; EXCEPTION WHEN no_data_found THEN cbp_cob_ := ''; refAgency_ := 0; cbp_refSector_ := ''; END;
BEGIN Select COUNTRY_NAME into cob_ from EXT_COUNTRY_MAPPING where CBP_CODE = UPPER(cbp_cob_); EXCEPTION WHEN no_data_found THEN cob_ := ''; END;
BEGIN Select SECTOR_CODE, LOCATION_CODE into refSectorCD_,currLocCD_ from EXT_LOCATION_MAPPING WHERE CBP_CODE = cbp_refSector_; EXCEPTION WHEN no_data_found THEN refSectorCD_ := 0; currLocCD_ := 0; END;
if nvl(currLocCD_,0) > 0 Then Select DESCRIPTION into cityCurLoc_ from LIST_ICE_CBP where ICE_CBP_ID = currLocCD_ and ICE_CBP_TYPE = 'CBP'; end if;
if v_UAC_ID = -1 Then Insert into UAC_INFO ( UAC_ID, ALIEN_NO, FIRST_NAME, LAST_NAME, MIDDLE_NAME, AKA, DOB, COB, DATE_OF_ENTRY, MANNER_OF_ENTRY, CITY_OF_ENTRY, STATE_OF_ENTRY, DATE_OF_APPREHENSION, REFERRING_OFFICE, PREGNANCY, INJURY, ILLNESS, HC_OTHER, IMMIGRATION_STATUS, REFERRING_AGENCY, UAC_STATUS, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, GENDER, UAC_FAST_STATUS, STATUS_ID, AGENCY_ID, HC_SUMMARY, HC_ERGENT_CONDITION, UAC_FLAG, PROCESSING_POC, PROCESSING_EMAIL, REFERRAL_NOTES, JUSTIFICATION_FOR_SS_PLACEMENT, KNOWN_GANG_AFFILIATION, NAME_OF_GANG, GANG_AFFILIATION_SUMMARY, DETENTION_TYPE_OF_FACILITY, FACILITY_NAME, FACILITY_PHONE, FACILITY_POC, FACILITY_FAX, FACILITY_ADMISSION_DATE, FACILITY_DISCHARGED_DATE, INCIDENT_RPT_IN_DC, SUM_OF_MM_HEALTH_CONDITION, DATE_OF_CURRENT_LOCATION, UAC_HEALTH_CONCERN, UAC_CRIMINAL_CHARGE, UAC_ADDITIONAL_INFO, CITY_OF_APPREHENSION, STATE_OF_APPREHENSION, CITY_OF_CURRENT_LOCATION, STATE_OF_CURRENT_LOCATION, PROCESSING_PHONE, DATE_ORR_APPROVED, REFERRAL_DATE, FAMILYGROUP, CBP_CODE, UAC_SEPARATED_FROM_PARENTS, FOOT_GUIDE) Select uacId_, ALIEN_NO, FIRST_NAME, LAST_NAME, MIDDLE_NAME, AKA, DOB, cob_, DATE_OF_ENTRY, MANNER_OF_ENTRY, CITY_OF_ENTRY, STATE_OF_ENTRY, DATE_OF_APPREHENSION, refSectorCD_, PREGNANCY, INJURY, ILLNESS, HC_OTHER, IMMIGRATION_STATUS, refAgency_, UAC_STATUS, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, GENDER, UAC_FAST_STATUS, STATUS_ID, AGENCY_ID, HC_SUMMARY, HC_ERGENT_CONDITION, UAC_FLAG, PROCESSING_POC, PROCESSING_EMAIL, REFERRAL_NOTES, JUSTIFICATION_FOR_SS_PLACEMENT, KNOWN_GANG_AFFILIATION, NAME_OF_GANG, GANG_AFFILIATION_SUMMARY, DETENTION_TYPE_OF_FACILITY, FACILITY_NAME, FACILITY_PHONE, FACILITY_POC, FACILITY_FAX, FACILITY_ADMISSION_DATE, FACILITY_DISCHARGED_DATE, INCIDENT_RPT_IN_DC, SUM_OF_MM_HEALTH_CONDITION, DATE_OF_CURRENT_LOCATION, UAC_HEALTH_CONCERN, UAC_CRIMINAL_CHARGE, UAC_ADDITIONAL_INFO, CITY_OF_APPREHENSION, STATE_OF_APPREHENSION, cityCurLoc_, '', PROCESSING_PHONE, DATE_ORR_APPROVED, REFERRAL_DATE, FAMILY_GROUP, UPPER(cbp_refSector_), UAC_SEPARATED_FROM_PARENTS, FOOT_GUIDE From UAC_INFO_TMP_FROMXML Where TRANSACTION_ID = v_Transaction_Id; end if; update XMLJOB_ACTIVITY_LOGS Set UAC_ID = uacid_, DATETIME_COMPLETED = SYSDATE WHERE ACTIVITY_ID = TO_NUMBER(v_Transaction_Id); delete from UAC_INFO_TMP_FROMXML Where TRANSACTION_ID = v_Transaction_Id; delete from UAC_INFO_TMP_FROMXML Where DateDiff('day', Uptime, Sysdate) > 1; END SP_UPDATE_UAC_INFO_FROM_XML;

PROCEDURE SP_UPDATE_UAC_UNLOCKED_HIST
(
	UAC_PROGRAM_ID IN NUMERIC
, UNLOCK_REASON_ID IN NUMERIC
, REQUESTER_NAME  IN VARCHAR2
, REQUESTER_EMAIL IN VARCHAR2
, ORIG_REQUEST_DATE  IN DATE
, COMMENTS IN VARCHAR2 
, USERID IN VARCHAR2 
, CMD_Type  IN VARCHAR2
) AS 

uacProgId_  NUMERIC(10) := UAC_PROGRAM_ID;
unlockReasonId_ NUMERIC(5) := UNLOCK_REASON_ID;
requesterName_ VARCHAR2(112) := REQUESTER_NAME;
requesterEmail_ VARCHAR2(112) := REQUESTER_EMAIL;
origRequestDate_  Date := ORIG_REQUEST_DATE;
comments_  VARCHAR2(6000) := COMMENTS;
userId_   VARCHAR2(112) := USERID;
cmdType_  VARCHAR2(30) := Upper(CMD_Type);

maxNo     NUMERIC(10);
BEGIN
  if cmdType_ = 'INSERT' Then
    maxNo := SEQ_UAC_UNLOCKED_HIST.nextval;
    Insert into UAC_UNLOCKED_HIST (UNLOCKED_HIST_ID,UAC_PROGRAM_ID,UNLOCK_REASON_ID,COMMENTS,USER_CREATED,DATETIME_CREATED, REQUESTER_NAME, REQUESTER_EMAIL, ORIGINAL_REQUEST_DATE)
    Values (maxNo, uacProgId_, unlockReasonId_, comments_, userId_, sysdate, requesterName_, requesterEmail_, origRequestDate_);
  end if;
END SP_UPDATE_UAC_UNLOCKED_HIST;

PROCEDURE SP_UPD_ALTERNATECAREGIVER_ARIRECEIVED ( vCAREPLANID IN NUMBER, vARIRECEIVEDTDATE IN DATE, vARINA in number, vUser IN VARCHAR2 ) AS
BEGIN UPDATE SPONSOR_ALTERNATE_CAREGIVER set ARI_DATE_RECEIVED = vARIRECEIVEDTDATE, ari_date_na = vARINA, UPUSER = vUser, UPTIME = Sysdate WHERE SPONSOR_ALTERNATE_CAREGIVER_ID = vCAREPLANID;
END SP_UPD_ALTERNATECAREGIVER_ARIRECEIVED;

