Insert Into TT_SUBSTANCE_USE_HISTORY (SUBSTANCE_ID, UAC_ASSESSMENT_ID, FREQUENCY_OF_USE, DATE_OF_LAST_USE, DATE_CREATED, USER_CREATED, SUBSTANCE_HIST_ID, YES_NO, TOKEN) Select SUBSTANCE_ID, UAC_ASSESSMENT_ID, FREQUENCY_OF_USE, to_timestamp_tz(DATE_OF_LAST_USE, 'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm'), to_timestamp_tz(DATE_CREATED, 'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm'), USER_CREATED, SUBSTANCE_HIST_ID, YES_NO, TOKEN From XMLTABLE(XMLNAMESPACES(default 'http://tempuri.org/DS_UCPortal.xsd'), '/DS_UCPortal/UAC_SUBSTANCE_USE_HISTORY' Passing xmlData_ Columns SUBSTANCE_ID NUMBER(2) path 'SUBSTANCE_ID', UAC_ASSESSMENT_ID NUMBER path 'UAC_ASSESSMENT_ID', FREQUENCY_OF_USE VARCHAR2(2000) path 'FREQUENCY_OF_USE', DATE_OF_LAST_USE VARCHAR2(200) path 'DATE_OF_LAST_USE', DATE_CREATED VARCHAR2(200) path 'DATE_CREATED', USER_CREATED VARCHAR2(112) path 'USER_CREATED', SUBSTANCE_HIST_ID NUMBER path 'SUBSTANCE_HIST_ID', YES_NO NUMBER(1,0) path 'YES_NO', TOKEN VARCHAR2(250) path 'TOKEN' ); Insert Into TT_UAC_CRIMINAL_HISTORY Select CRIMINAL_HISTORY_ID, UAC_ASSESSMENT_ID, CRIME_DISCRIPTION, to_timestamp_tz(CRIME_DATE, 'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm'), LENGTH_OF_SENTENCE, LOCATION, to_timestamp_tz(DATE_CREATED, 'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm'), USER_CREATED, to_timestamp_tz(UPTIME, 'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm'), UPUSER, TOKEN From XMLTABLE(XMLNAMESPACES(default 'http://tempuri.org/DS_UCPortal.xsd'), '/DS_UCPortal/UAC_CRIMINAL_HISTORY' Passing xmlData_ Columns CRIMINAL_HISTORY_ID NUMBER path 'CRIMINAL_HISTORY_ID', UAC_ASSESSMENT_ID NUMBER path 'UAC_ASSESSMENT_ID', CRIME_DISCRIPTION VARCHAR2(500) path 'CRIME_DISCRIPTION', CRIME_DATE VARCHAR2(200) path 'CRIME_DATE', LENGTH_OF_SENTENCE VARCHAR2(500) path 'LENGTH_OF_SENTENCE', LOCATION VARCHAR2(500) path 'LOCATION', DATE_CREATED VARCHAR2(200) path 'DATE_CREATED', USER_CREATED VARCHAR2(112) path 'USER_CREATED', UPTIME VARCHAR2(200) path 'UPTIME', UPUSER VARCHAR2(112) path 'UPUSER', TOKEN VARCHAR2(250) path 'TOKEN' );
Select UAC_ASSESSMENT_ID into uacAssessmentId from TT_UAC_ASSESSMENT where ASSESSMENT_TOKEN = xmlToken; --DBMS_OUTPUT.PUT_LINE('Insert to TT are finished. ASSESSMENT ID is ' || uacAssessmentId);
If uacAssessmentId is null then uacAssessmentId := SEQ_UAC_ASSESSMENT.NEXTVAL; Update TT_UAC_ASSESSMENT SET UAC_ASSESSMENT_ID = uacAssessmentId Where ASSESSMENT_TOKEN = xmlToken; Insert Into UAC_ASSESSMENT Select * From TT_UAC_ASSESSMENT;
Insert Into UAC_FAMILY_RELATIONS Select SEQ_UAC_FAMILY_RELATIONS.NEXTVal, uacAssessmentId, RELATIVE_NAME, AGE, DOB, RELATIONSHIP_ID, RELATIONS_IN_US From TT_UAC_FAMILY_RELATIONS Where TOKEN = xmlToken And (RELATIVE_NAME is not null or DOB is not null or Relationship_ID is not null);
Insert Into UAC_CHILDREN Select SEQ_UAC_CHILDREN.NEXTVAL, uacAssessmentId, CHILD_NAME,AGE, DOB, CURRENT_LOCATION, FATHER_MOTHER_NAME From TT_UAC_CHILDREN WHERE TOKEN=xmlToken And (CHILD_NAME is not null or AGE is not null or DOB is not null or CURRENT_LOCATION is not null or FATHER_MOTHER_NAME is not null);
Insert Into UAC_MEDICATION_HISTORY Select SEQ_UAC_MEDICATION_HISTORY.NEXTVAL, uacAssessmentId, MEDICATION_NAME,DOSAGE, TIMEFRAME, MEDICAL_CONDITION, DATE_CREATED, USER_CREATED FROM TT_UAC_MEDICATION_HISTORY Where TOKEN=xmlToken And (MEDICATION_NAME is not null or DOSAGE is not null or TIMEFRAME is not null or MEDICAL_CONDITION is not null);
Insert Into UAC_MEDICAL_HISTORY Select MM_CONDITION_ID, uacAssessmentId, CLARIFICATION, DATE_CREATED, USER_CREATED, SEQ_UAC_MEDICAL_HISTORY.NEXTVAL, CONDITION_YN From TT_UAC_MEDICAL_HISTORY Where TOKEN=xmlToken And (NVL(CONDITION_YN,0) <> 0 or CLARIFICATION is not null);
Insert Into UAC_CRIMINAL_HISTORY Select SEQ_UAC_CRIMINAL_HISTORY.NEXTVAL, uacAssessmentId, CRIME_DISCRIPTION, CRIME_DATE, LENGTH_OF_SENTENCE, LOCATION, DATE_CREATED, USER_CREATED, UPTIME, UPUSER FROM TT_UAC_CRIMINAL_HISTORY Where TOKEN=xmlToken And (CRIME_DISCRIPTION is not null or LENGTH_OF_SENTENCE is not null or LOCATION is not null or CRIME_DATE is not null);
Insert Into UAC_SUBSTANCE_USE_HISTORY (SUBSTANCE_ID, UAC_ASSESSMENT_ID, FREQUENCY_OF_USE,DATE_OF_LAST_USE, DATE_CREATED,USER_CREATED,SUBSTANCE_HIST_ID, YES_NO) Select SUBSTANCE_ID, uacAssessmentId, FREQUENCY_OF_USE, DATE_OF_LAST_USE, DATE_CREATED, USER_CREATED, SEQ_SUBSTANCE_USE_HISTORY.NEXTVAL, YES_NO FROM TT_SUBSTANCE_USE_HISTORY Where TOKEN=xmlToken And (NVL(YES_NO,0) <> 0 Or DATE_OF_LAST_USE is not null or FREQUENCY_OF_USE is not null); Else -- Delete Records Delete From UAC_FAMILY_RELATIONS Where UAC_ASSESSMENT_ID = uacAssessmentId; Delete From UAC_CHILDREN Where UAC_ASSESSMENT_ID = uacAssessmentId;
UPDATE TT_UAC_MEDICATION_HISTORY A SET (DATE_CREATED, USER_CREATED) = (Select DATE_CREATED, USER_CREATED From UAC_MEDICATION_HISTORY WHERE MEDICATION_HIST_ID = A.MEDICATION_HIST_ID) Where TOKEN = xmlToken And UAC_ASSESSMENT_ID = uacAssessmentId; Delete From UAC_MEDICATION_HISTORY Where UAC_ASSESSMENT_ID = uacAssessmentId;
UPDATE TT_UAC_MEDICAL_HISTORY A SET (DATE_CREATED, USER_CREATED) = (Select DATE_CREATED, USER_CREATED From UAC_MEDICAL_HISTORY WHERE MEDICAL_HIST_ID = A.MEDICAL_HIST_ID) Where TOKEN = xmlToken And UAC_ASSESSMENT_ID = uacAssessmentId; Delete From UAC_MEDICAL_HISTORY Where UAC_ASSESSMENT_ID = uacAssessmentId;
UPDATE TT_UAC_CRIMINAL_HISTORY A SET (DATE_CREATED, USER_CREATED) = (Select DATE_CREATED, USER_CREATED FROM UAC_CRIMINAL_HISTORY WHERE CRIMINAL_HISTORY_ID = A.CRIMINAL_HISTORY_ID) Where TOKEN = xmlToken And UAC_ASSESSMENT_ID = uacAssessmentId; Delete From UAC_CRIMINAL_HISTORY Where UAC_ASSESSMENT_ID = uacAssessmentId;
UPDATE TT_SUBSTANCE_USE_HISTORY A SET (DATE_CREATED, USER_CREATED) = (Select DATE_CREATED, USER_CREATED FROM UAC_SUBSTANCE_USE_HISTORY WHERE SUBSTANCE_HIST_ID = A.SUBSTANCE_HIST_ID) Where TOKEN = xmlToken And UAC_ASSESSMENT_ID = uacAssessmentId; Delete From UAC_SUBSTANCE_USE_HISTORY Where UAC_ASSESSMENT_ID = uacAssessmentId;
UPDATE TT_UAC_ASSESSMENT A SET (DATE_CREATED, USER_CREATED) = (Select DATE_CREATED, USER_CREATED FROM UAC_ASSESSMENT WHERE UAC_ASSESSMENT_ID = A.UAC_ASSESSMENT_ID) Where ASSESSMENT_TOKEN = xmlToken And UAC_ASSESSMENT_ID = uacAssessmentId; Delete FROM UAC_ASSESSMENT Where UAC_ASSESSMENT_ID = uacAssessmentId;
-- Insert Reinsert existing record to UAC Assessment . Insert Into UAC_ASSESSMENT Select * From TT_UAC_ASSESSMENT;
-- Insert New Records first. Insert Into UAC_FAMILY_RELATIONS Select SEQ_UAC_FAMILY_RELATIONS.NEXTVal, uacAssessmentId, RELATIVE_NAME, AGE, DOB, RELATIONSHIP_ID, RELATIONS_IN_US From TT_UAC_FAMILY_RELATIONS Where TOKEN = xmlToken And UAC_RELATIONSHIP_ID is null And (RELATIVE_NAME is not null or DOB is not null or Relationship_ID is not null);
Insert Into UAC_CHILDREN Select SEQ_UAC_CHILDREN.NEXTVAL, uacAssessmentId, CHILD_NAME,AGE, DOB, CURRENT_LOCATION, FATHER_MOTHER_NAME From TT_UAC_CHILDREN WHERE TOKEN=xmlToken And UAC_CHILDREN_ID is null And (CHILD_NAME is not null or AGE is not null or DOB is not null or CURRENT_LOCATION is not null or FATHER_MOTHER_NAME is not null);
Insert Into UAC_MEDICATION_HISTORY Select SEQ_UAC_MEDICATION_HISTORY.NEXTVAL, uacAssessmentId, MEDICATION_NAME,DOSAGE, TIMEFRAME, MEDICAL_CONDITION, DATE_CREATED, USER_CREATED FROM TT_UAC_MEDICATION_HISTORY Where TOKEN=xmlToken And MEDICATION_HIST_ID is null And (MEDICATION_NAME is not null or DOSAGE is not null or TIMEFRAME is not null or MEDICAL_CONDITION is not null);
Insert Into UAC_MEDICAL_HISTORY Select MM_CONDITION_ID, uacAssessmentId, CLARIFICATION, DATE_CREATED, USER_CREATED, SEQ_UAC_MEDICAL_HISTORY.NEXTVAL, CONDITION_YN From TT_UAC_MEDICAL_HISTORY Where TOKEN=xmlToken And MEDICAL_HIST_ID is null And (NVL(CONDITION_YN,0) <> 0 or CLARIFICATION is not null);
Insert Into UAC_CRIMINAL_HISTORY Select SEQ_UAC_CRIMINAL_HISTORY.NEXTVAL, uacAssessmentId, CRIME_DISCRIPTION, CRIME_DATE, LENGTH_OF_SENTENCE, LOCATION, DATE_CREATED, USER_CREATED, UPTIME, UPUSER FROM TT_UAC_CRIMINAL_HISTORY Where TOKEN=xmlToken And CRIMINAL_HISTORY_ID is null And (CRIME_DISCRIPTION is not null or LENGTH_OF_SENTENCE is not null or LOCATION is not null or CRIME_DATE is not null);
Insert Into UAC_SUBSTANCE_USE_HISTORY (SUBSTANCE_ID, UAC_ASSESSMENT_ID, FREQUENCY_OF_USE,DATE_OF_LAST_USE, DATE_CREATED,USER_CREATED,SUBSTANCE_HIST_ID, YES_NO) Select SUBSTANCE_ID, uacAssessmentId, FREQUENCY_OF_USE, DATE_OF_LAST_USE, DATE_CREATED, USER_CREATED, SEQ_SUBSTANCE_USE_HISTORY.NEXTVAL, YES_NO FROM TT_SUBSTANCE_USE_HISTORY Where TOKEN=xmlToken And SUBSTANCE_HIST_ID is null And (NVL(YES_NO,0) <> 0 Or DATE_OF_LAST_USE is not null or FREQUENCY_OF_USE is not null);
-- Insert existing records Insert Into UAC_FAMILY_RELATIONS Select UAC_RELATIONSHIP_ID, UAC_ASSESSMENT_ID, RELATIVE_NAME, AGE, DOB, RELATIONSHIP_ID, RELATIONS_IN_US From TT_UAC_FAMILY_RELATIONS Where TOKEN = xmlToken And UAC_RELATIONSHIP_ID is not null And (RELATIVE_NAME is not null or DOB is not null or Relationship_ID is not null);
Insert Into UAC_CHILDREN Select UAC_CHILDREN_ID, UAC_ASSESSMENT_ID, CHILD_NAME,AGE, DOB, CURRENT_LOCATION, FATHER_MOTHER_NAME From TT_UAC_CHILDREN WHERE TOKEN=xmlToken And UAC_CHILDREN_ID is not null And (CHILD_NAME is not null or AGE is not null or DOB is not null or CURRENT_LOCATION is not null or FATHER_MOTHER_NAME is not null);
Insert Into UAC_MEDICATION_HISTORY Select MEDICATION_HIST_ID, UAC_ASSESSMENT_ID, MEDICATION_NAME,DOSAGE, TIMEFRAME, MEDICAL_CONDITION, DATE_CREATED, USER_CREATED FROM TT_UAC_MEDICATION_HISTORY Where TOKEN=xmlToken And MEDICATION_HIST_ID is not null And (MEDICATION_NAME is not null or DOSAGE is not null or TIMEFRAME is not null or MEDICAL_CONDITION is not null);
Insert Into UAC_MEDICAL_HISTORY Select MM_CONDITION_ID, UAC_ASSESSMENT_ID, CLARIFICATION, DATE_CREATED, USER_CREATED, MEDICAL_HIST_ID, CONDITION_YN From TT_UAC_MEDICAL_HISTORY Where TOKEN=xmlToken And MEDICAL_HIST_ID is not null And (NVL(CONDITION_YN,0) <> 0 or CLARIFICATION is not null);
Insert Into UAC_CRIMINAL_HISTORY Select CRIMINAL_HISTORY_ID, UAC_ASSESSMENT_ID, CRIME_DISCRIPTION, CRIME_DATE, LENGTH_OF_SENTENCE, LOCATION, DATE_CREATED, USER_CREATED, UPTIME, UPUSER FROM TT_UAC_CRIMINAL_HISTORY Where TOKEN=xmlToken And CRIMINAL_HISTORY_ID is not null And (CRIME_DISCRIPTION is not null or LENGTH_OF_SENTENCE is not null or LOCATION is not null or CRIME_DATE is not null);
Insert Into UAC_SUBSTANCE_USE_HISTORY (SUBSTANCE_ID, UAC_ASSESSMENT_ID, FREQUENCY_OF_USE,DATE_OF_LAST_USE, DATE_CREATED,USER_CREATED,SUBSTANCE_HIST_ID, YES_NO) Select SUBSTANCE_ID, UAC_ASSESSMENT_ID, FREQUENCY_OF_USE, DATE_OF_LAST_USE, DATE_CREATED, USER_CREATED, SUBSTANCE_HIST_ID, YES_NO FROM TT_SUBSTANCE_USE_HISTORY Where TOKEN=xmlToken And SUBSTANCE_HIST_ID is not null And (NVL(YES_NO,0) <> 0 Or DATE_OF_LAST_USE is not null or FREQUENCY_OF_USE is not null); End If; EXCEPTION WHEN OTHERS Then Rollback; vStatus := SQLERRM || ' ' || SQLERRM; End; Delete From TT_UAC_ASSESSMENT Where ASSESSMENT_TOKEN = xmlToken; Delete From TT_UAC_CHILDREN Where TOKEN = xmlToken; Delete From TT_UAC_FAMILY_RELATIONS Where TOKEN = xmlToken; Delete From TT_UAC_CRIMINAL_HISTORY Where TOKEN = xmlToken; Delete From TT_UAC_MEDICATION_HISTORY Where TOKEN = xmlToken; Delete From TT_UAC_MEDICAL_HISTORY Where TOKEN = xmlToken; Delete From TT_SUBSTANCE_USE_HISTORY Where TOKEN = xmlToken; End SP_UPD_ASSESSMENT_TT;

PROCEDURE "SP_UPD_CHILD_LOCATION_HIST_TRANSFER" (
vUAC_ID NUMBER
)
AS
uacId_    NUMBER(10,0) := vUAC_ID;
uacProgId_ NUMBER(10,0);
n NUMBER(4);
BEGIN
	--Check if there is at least 1 Discharged UAC_PROGRAM_INFO record for kid
	select count(*) into n from (
      Select 1 from UAC_PROGRAM_INFO WHERE UAC_ID = uacId_ AND CURRENT_STATUS = 'DISCHARGED'
      Union
      Select 0 from dual
    );
    if n > 1 then
		-- Get the most recent Discharged UAC_PROGRAM_INFO record for kid
		SELECT max(UAC_PROGRAM_ID) into uacProgId_
		FROM UAC_PROGRAM_INFO
		WHERE UAC_ID = uacId_ AND CURRENT_STATUS = 'DISCHARGED';

		-- Update Location History on Admission for Transfer kids
		UPDATE UAC_CHILD_LOCATION_HIST
		SET ISDELETED = 0
		WHERE ISDELETED = 1 AND UAC_PROGRAM_ID = uacProgId_;
	End if;
END SP_UPD_CHILD_LOCATION_HIST_TRANSFER;

PROCEDURE "SP_UPD_HOTLINEALERT" ( v_UPUSER IN NVARCHAR2, V_CMD_TYPE IN NVARCHAR2, v_HOTLINE_ALERT_ID IN NUMBER, v_DATE_OF_CALL IN DATE, v_PHONE_REPORTER IN NVARCHAR2, v_LANGUAGE_REPORTER IN NVARCHAR2, v_TRANSCR_TRANSL_NEEDED IN NUMBER, v_FIRST_NAME IN NVARCHAR2, v_LAST_NAME IN NVARCHAR2, v_TYPE_OF_REPORTER IN NVARCHAR2, v_CONTACT_EMAIL IN NVARCHAR2, v_CONTACT_PHONE IN NVARCHAR2,
v_INCIDENT_DESC IN NVARCHAR2, v_INCIDENT_DATE IN DATE, v_STAFF_PRESENT IN NUMBER, v_STAFF_PRESENT_INFO IN NVARCHAR2, v_INCIDENT_REPORTED IN NUMBER, v_INCIDENT_REPORTED_INFO IN NVARCHAR2, v_INCIDENT_REPORTED_DATE IN DATE, v_PROG_ID IN NUMBER, v_PROG_CITY IN NVARCHAR2, v_PROG_STATE IN NVARCHAR2, v_PROG_DESC IN NVARCHAR2, v_INCIDENT_TYPE IN NVARCHAR2, v_INCIDENT_ALLEGATION_TYPE IN NVARCHAR2, v_INCIDENT_SYNOPSIS IN NVARCHAR2, v_REPORTED_TO_CPS IN NUMBER, v_REPORTED_TO_LAW_ENFORCEMENT IN NUMBER, v_HOTLINE_REP_NAME IN NVARCHAR2, v_HOTLINE_REP_EMAIL IN NVARCHAR2, v_HOTLINE_REP_PHONE IN NVARCHAR2, v_INTAKES_NAME IN NVARCHAR2, v_INTAKES_EMAIL IN NVARCHAR2, v_INTAKES_PHONE IN NVARCHAR2, v_PSA_NAME IN NVARCHAR2, v_PSA_EMAIL IN NVARCHAR2, v_PSA_PHONE IN NVARCHAR2, v_NOT_ENOUGHINFO_TO_IDENTIFY IN NUMBER, v_FFS_NAME IN NVARCHAR2, v_FFS_EMAIL IN NVARCHAR2, v_FFS_DATE IN DATE, v_FFS_SUP_NAME IN NVARCHAR2, v_FFS_SUP_EMAIL IN NVARCHAR2, v_FFS_SUP_DATE IN DATE, v_PO_NAME IN NVARCHAR2, v_PO_EMAIL IN NVARCHAR2, v_PO_DATE IN DATE, v_UAC_A_NUMBER IN NVARCHAR2, v_UAC_FIRSTNAME IN NVARCHAR2, v_UAC_LASTNAME IN NVARCHAR2, v_UAC_GENDER IN NVARCHAR2, v_UAC_AGE IN NUMBER, v_UAC_COB IN NVARCHAR2, v_UAC_OTHERDESC IN NVARCHAR2, v_UAC_OTHERUCINVOLVED IN NUMBER, v_UAC_OTHERUCINVOLVEDDESC IN NVARCHAR2, v_COMMENTS IN NVARCHAR2, v_SENTTOORR IN NUMBER, v_SENTTOORRDATE DATE, V_HOTLINE_ALERT_NEW OUT NUMBER ) AS BEGIN if UPPER(v_CMD_TYPE) = 'INSERT' Then INSERT INTO UAC_HOTLINE_ALERT ( HOTLINE_ALERT_ID, DATE_OF_CALL, PHONE_REPORTER, LANGUAGE_REPORTER, TRANSCR_TRANSL_NEEDED, FIRST_NAME, LAST_NAME, TYPE_OF_REPORTER, CONTACT_EMAIL, CONTACT_PHONE, INCIDENT_DESC, INCIDENT_DATE, STAFF_PRESENT, STAFF_PRESENT_INFO, INCIDENT_REPORTED, INCIDENT_REPORTED_INFO, INCIDENT_REPORTED_DATE, PROG_ID, PROG_CITY, PROG_STATE, PROG_DESC, INCIDENT_TYPE, INCIDENT_ALLEGATION_TYPE, INCIDENT_SYNOPSIS, REPORTED_TO_CPS, REPORTED_TO_LAW_ENFORCEMENT, HOTLINE_REP_NAME, HOTLINE_REP_EMAIL, HOTLINE_REP_PHONE, INTAKES_NAME, INTAKES_EMAIL, INTAKES_PHONE, PSA_NAME, PSA_EMAIL, PSA_PHONE, NOT_ENOUGHINFO_TO_IDENTIFY, FFS_NAME, FFS_EMAIL, FFS_DATE, FFS_SUP_NAME, FFS_SUP_EMAIL, FFS_SUP_DATE, PO_NAME, PO_EMAIL, PO_DATE, DATE_CREATED, USER_CREATED, DATE_UPDATED, USER_UPDATED, UAC_A_NUMBER, UAC_FIRSTNAME, UAC_LASTNAME, UAC_GENDER, UAC_AGE, UAC_COB, UAC_OTHERDESC, UAC_OTHERUCINVOLVED, UAC_OTHERUCINVOLVEDDESC, COMMENTS, SENTTOORR, SENTTOORRDATE
) VALUES ( SEQ_HOTLINE_ALERT.NEXTVAL, v_DATE_OF_CALL, v_PHONE_REPORTER, v_LANGUAGE_REPORTER, v_TRANSCR_TRANSL_NEEDED, v_FIRST_NAME, v_LAST_NAME, v_TYPE_OF_REPORTER, v_CONTACT_EMAIL, v_CONTACT_PHONE, v_INCIDENT_DESC, v_INCIDENT_DATE, v_STAFF_PRESENT, v_STAFF_PRESENT_INFO, v_INCIDENT_REPORTED, v_INCIDENT_REPORTED_INFO, v_INCIDENT_REPORTED_DATE, v_PROG_ID, v_PROG_CITY, v_PROG_STATE, v_PROG_DESC, v_INCIDENT_TYPE, v_INCIDENT_ALLEGATION_TYPE, v_INCIDENT_SYNOPSIS, v_REPORTED_TO_CPS, v_REPORTED_TO_LAW_ENFORCEMENT, v_HOTLINE_REP_NAME, v_HOTLINE_REP_EMAIL, v_HOTLINE_REP_PHONE, v_INTAKES_NAME, v_INTAKES_EMAIL, v_INTAKES_PHONE, v_PSA_NAME, v_PSA_EMAIL, v_PSA_PHONE, v_NOT_ENOUGHINFO_TO_IDENTIFY, v_FFS_NAME, v_FFS_EMAIL, v_FFS_DATE, v_FFS_SUP_NAME, v_FFS_SUP_EMAIL, v_FFS_SUP_DATE, v_PO_NAME, v_PO_EMAIL, v_PO_DATE, sysdate, v_UPUSER, sysdate, v_UPUSER, v_UAC_A_NUMBER, v_UAC_FIRSTNAME, v_UAC_LASTNAME, v_UAC_GENDER, v_UAC_AGE, v_UAC_COB, v_UAC_OTHERDESC, v_UAC_OTHERUCINVOLVED, v_UAC_OTHERUCINVOLVEDDESC, v_COMMENTS, v_SENTTOORR, v_SENTTOORRDATE ) RETURNING HOTLINE_ALERT_ID INTO V_HOTLINE_ALERT_NEW; DECLARE x NUMBER := 0;reportedToLaw NUMBER := 0; BEGIN LOOP
IF x > 2 THEN reportedToLaw := 1; else reportedToLaw := 0; END IF; INSERT INTO UAC_HOTLINE_REPORTING ( HOTLINE_ALERT_ID, REPORTED_TO_LAW, DATE_CREATED, USER_CREATED, DATE_UPDATED, USER_UPDATED ) VALUES ( V_HOTLINE_ALERT_NEW, reportedToLaw, sysdate, v_UPUSER, sysdate, v_UPUSER ); x := x + 1; IF x > 5 THEN EXIT; END IF; END LOOP; END;
elsif UPPER(v_CMD_TYPE) = 'UPDATE' Then update UAC_HOTLINE_ALERT set DATE_OF_CALL = v_DATE_OF_CALL, PHONE_REPORTER = v_PHONE_REPORTER, LANGUAGE_REPORTER = v_LANGUAGE_REPORTER, TRANSCR_TRANSL_NEEDED = v_TRANSCR_TRANSL_NEEDED, FIRST_NAME = v_FIRST_NAME, LAST_NAME = v_LAST_NAME, TYPE_OF_REPORTER = v_TYPE_OF_REPORTER, CONTACT_EMAIL = v_CONTACT_EMAIL, CONTACT_PHONE = v_CONTACT_PHONE, INCIDENT_DESC = v_INCIDENT_DESC, INCIDENT_DATE = v_INCIDENT_DATE, STAFF_PRESENT = v_STAFF_PRESENT, STAFF_PRESENT_INFO = v_STAFF_PRESENT_INFO, INCIDENT_REPORTED = v_INCIDENT_REPORTED, INCIDENT_REPORTED_INFO = v_INCIDENT_REPORTED_INFO, INCIDENT_REPORTED_DATE = v_INCIDENT_REPORTED_DATE, PROG_ID = v_PROG_ID, PROG_CITY = v_PROG_CITY, PROG_STATE = v_PROG_STATE, PROG_DESC = v_PROG_DESC, INCIDENT_TYPE = v_INCIDENT_TYPE, INCIDENT_ALLEGATION_TYPE = v_INCIDENT_ALLEGATION_TYPE, INCIDENT_SYNOPSIS = v_INCIDENT_SYNOPSIS, REPORTED_TO_CPS = v_REPORTED_TO_CPS, REPORTED_TO_LAW_ENFORCEMENT = v_REPORTED_TO_LAW_ENFORCEMENT, HOTLINE_REP_NAME = v_HOTLINE_REP_NAME, HOTLINE_REP_EMAIL = v_HOTLINE_REP_EMAIL, HOTLINE_REP_PHONE = v_HOTLINE_REP_PHONE, INTAKES_NAME = v_INTAKES_NAME, INTAKES_EMAIL = v_INTAKES_EMAIL, INTAKES_PHONE = v_INTAKES_PHONE, PSA_NAME = v_PSA_NAME, PSA_EMAIL = v_PSA_EMAIL, PSA_PHONE = v_PSA_PHONE, NOT_ENOUGHINFO_TO_IDENTIFY = v_NOT_ENOUGHINFO_TO_IDENTIFY, FFS_NAME = v_FFS_NAME, FFS_EMAIL = v_FFS_EMAIL, FFS_DATE = v_FFS_DATE, FFS_SUP_NAME = v_FFS_SUP_NAME, FFS_SUP_EMAIL = v_FFS_SUP_EMAIL, FFS_SUP_DATE = v_FFS_SUP_DATE, PO_NAME = v_PO_NAME, PO_EMAIL = v_PO_EMAIL, PO_DATE = v_PO_DATE, DATE_UPDATED = sysdate, USER_UPDATED = v_UPUSER, UAC_A_NUMBER = v_UAC_A_NUMBER, UAC_FIRSTNAME = v_UAC_FIRSTNAME, UAC_LASTNAME = v_UAC_LASTNAME, UAC_GENDER = v_UAC_GENDER, UAC_AGE = v_UAC_AGE, UAC_COB = v_UAC_COB, UAC_OTHERDESC = v_UAC_OTHERDESC, UAC_OTHERUCINVOLVED = v_UAC_OTHERUCINVOLVED, UAC_OTHERUCINVOLVEDDESC = v_UAC_OTHERUCINVOLVEDDESC, COMMENTS = v_COMMENTS, SENTTOORR = v_SENTTOORR, SENTTOORRDATE = v_SENTTOORRDATE where hotline_alert_id = V_HOTLINE_ALERT_ID;
end if; END SP_UPD_HOTLINEALERT;

PROCEDURE "SP_UPD_HOTLINEALERT_REPORTING" ( v_UPUSER IN VARCHAR2, v_STATE IN VARCHAR2 , v_REPORT_DATE IN DATE , v_CASE_NUMBER IN VARCHAR2 , v_OFFICER_NAME IN VARCHAR2 , v_OFFICER_BADGE_NUMBER IN VARCHAR2 , v_HOTLINE_REPORTING_ID IN NUMBER
) AS BEGIN UPDATE UAC_HOTLINE_REPORTING SET STATE = v_STATE, REPORT_DATE = v_REPORT_DATE, CASE_NUMBER = v_CASE_NUMBER, OFFICER_NAME = v_OFFICER_NAME, OFFICER_BADGE_NUMBER = v_OFFICER_BADGE_NUMBER, DATE_UPDATED = sysdate, USER_UPDATED = v_UPUSER WHERE HOTLINE_REPORTING_ID = v_HOTLINE_REPORTING_ID;
END SP_UPD_HOTLINEALERT_REPORTING;

PROCEDURE "SP_UPD_HSPRS_TRANSFER_PROG" ( UAC_PROGRAM_ID IN NUMBER, UAC_ID IN NUMBER, UPUSER IN VARCHAR2 ) AS uacProgId_ NUMBER(10) := UAC_PROGRAM_ID; uacId_ NUMBER(10) := UAC_ID; userId_ VARCHAR2(112) := UPUSER; n NUMBER(1) :=0; BEGIN
-- select count(*) into n From ( -- Select 1 from UAC_PROGRAM_INFO Where UAC_PROGRAM_ID = uacProgId_ AND CURRENT_STATUS = 'ENROUTE' -- ) A; -- -- if n > 0 then UPDATE UAC_HOMESTUDY_CASE_REFERRAL SET UAC_PROGRAM_ID = uacProgId_, REFERRING_FACILITY_NAME = (SELECT LP.PROGRAM_NAME FROM LIST_PROGRAMS LP INNER JOIN UAC_PROGRAM_INFO uP ON LP.PROGRAM_ID = uP.PROGRAM_ID WHERE uP.UAC_PROGRAM_ID = uacProgId_), UPUSER = userId_ WHERE REFERRAL_STATUS IN (1,2,3) AND UAC_PROGRAM_ID = (SELECT A.UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO A INNER JOIN UAC_PROGRAM_EXIT B ON A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID WHERE B.UPTIME = (SELECT MAX(Y.UPTIME) FROM UAC_PROGRAM_INFO X INNER JOIN UAC_PROGRAM_EXIT Y ON X.UAC_PROGRAM_ID = Y.UAC_PROGRAM_ID WHERE X.UAC_ID = uacId_) AND A.UAC_ID = uacId_);
UPDATE UAC_POST_RELEASE_SERVICE SET UAC_PROGRAM_ID = uacProgId_, REFERRING_FACILITY_NAME = (SELECT LP.PROGRAM_NAME FROM LIST_PROGRAMS LP INNER JOIN UAC_PROGRAM_INFO uP ON LP.PROGRAM_ID = uP.PROGRAM_ID WHERE uP.UAC_PROGRAM_ID = uacProgId_), UPUSER = userId_ WHERE REFERRAL_STATUS IN (1,2,3) AND UAC_PROGRAM_ID = (SELECT A.UAC_PROGRAM_ID FROM UAC_PROGRAM_INFO A INNER JOIN UAC_PROGRAM_EXIT B ON A.UAC_PROGRAM_ID = B.UAC_PROGRAM_ID WHERE B.UPTIME = (SELECT MAX(Y.UPTIME) FROM UAC_PROGRAM_INFO X INNER JOIN UAC_PROGRAM_EXIT Y ON X.UAC_PROGRAM_ID = Y.UAC_PROGRAM_ID WHERE X.UAC_ID = uacId_) AND A.UAC_ID = uacId_); -- End If;
END SP_UPD_HSPRS_TRANSFER_PROG;

PROCEDURE "SP_UPD_ICE_CBP_LIST" 
(
  CBP_NAME IN VARCHAR2 
) AS 
BEGIN
  UPDATE LIST_ICE_CBP SET DESCRIPTION = CBP_NAME where DESCRIPTION = 'Other';
  INSERT INTO LIST_ICE_CBP (ICE_CBP_ID, ICE_CBP_TYPE, DESCRIPTION)
  SELECT MAX(ICE_CBP_ID) + 1, 'CBP', 'Other' From LIST_ICE_CBP;
END SP_UPD_ICE_CBP_LIST;

PROCEDURE SP_UPD_INTAKE_CHECKBOXES 
(
  V_UACID IN NUMBER 
, V_CHECKBOXVALUE IN NUMBER 
, V_SELECTED IN NUMBER 
, V_SELECTIONSAME IN NUMBER 
, V_USERID IN VARCHAR2 
) AS 
v_list NUMBER :=0;
v_selectedCheck VARCHAR2(255 BYTE);

BEGIN
	  /* check if a tag been previously saved */
    for rec in( select CHECKBOXES_ID
    from INTAKE_POPULATION_TAG
    where UAC_ID = v_UacId
      and CHECKBOXES_ID = V_CHECKBOXVALUE)

   loop
      v_List := v_List + 1;
   end loop;

    DBMS_OUTPUT.PUT_LINE('count: ' || v_list);

    IF V_SELECTIONSAME = 1
    THEN
      v_selectedCheck := to_char(sysdate, 'fmMonth DD, YYYY HH:MI:SS AM');
    ELSE
      v_selectedCheck := NULL;
    END IF;

    IF V_SELECTED = 1
    THEN
      v_selectedCheck := '(Child tagged on ' || v_selectedCheck || ' by ' || V_USERID || ')';
    ELSE
      v_selectedCheck := '(Tag removed from child on ' || v_selectedCheck || ' by ' || V_USERID || ')';
    END IF;

	  IF v_list > 0
	  THEN
      /* update */
      UPDATE INTAKE_POPULATION_TAG t SET
        CHECKBOXES_ID = v_CheckboxValue
        ,UPTIME = Sysdate
        ,UPUSER = v_UserId
        ,ISSELECTED = v_Selected
        ,TAG_STATUS = v_selectedCheck
      WHERE UAC_ID = v_UacId
        and CHECKBOXES_ID = V_CHECKBOXVALUE;  
	  ELSE
      /* insert */
      INSERT INTO INTAKE_POPULATION_TAG
          (
              UAC_ID 
              ,CHECKBOXES_ID 
              ,TAG_STATUS
              ,DATE_CREATED 
              ,USER_CREATED 
              --,UPTIME 
              --,UPUSER 
              ,ISSELECTED
          )
          VALUES
          (
              v_UacId
              ,v_CheckboxValue
              ,v_selectedCheck
              ,Sysdate
              ,v_UserId
              --,Sysdate
              --,v_UserId
              ,v_Selected
          );
	  END IF;
END SP_UPD_INTAKE_CHECKBOXES;

