PROCEDURE "SP_UPD_MEDICAL_VISIT" ( v_VISIT_ID NUMBER, v_UAC_PROGRAM_ID NUMBER, --per Dilmurat 12/20/2013, replaced UCA_ID and Program_ID with UAC_Program_ID in both table and this SP --v_UAC_ID NUMBER, --v_PROGRAM_ID NUMBER, v_DATE_OF_VISIT DATE, v_VISIT_TYPE_ID VARCHAR2, v_VISIT_REASON_ID VARCHAR2, v_SPECIFY_OTHER_REASON VARCHAR2, v_DIAGNOSIS_ID VARCHAR2, v_SPECIFY_OTHER_DIAGNOSIS VARCHAR2, v_SUMMARY_OF_OUTCOME VARCHAR2, v_UPUSER VARCHAR2, v_CMD_TYPE VARCHAR2 ) AS
v_New_VISIT_ID NUMBER :=0;
BEGIN if UPPER(v_CMD_TYPE) = 'INSERT' Then
SELECT SEQ_MEDICAL_VISIT.NEXTVAL into v_New_VISIT_ID from dual;
Insert into UAC_MEDICAL_VISIT ( VISIT_ID, UAC_PROGRAM_ID, --UAC_ID, PROGRAM_ID, DATE_OF_VISIT, SPECIFY_OTHER_REASON, SPECIFY_OTHER_DIAGNOSIS, SUMMARY_OF_OUTCOME, DATE_CREATED, USER_CREATED,UPTIME,UPUSER) values (v_New_VISIT_ID, v_UAC_PROGRAM_ID, --v_UAC_ID, v_PROGRAM_ID, v_DATE_OF_VISIT, v_SPECIFY_OTHER_REASON, v_SPECIFY_OTHER_DIAGNOSIS, v_SUMMARY_OF_OUTCOME, sysdate,v_UPUSER, sysdate,v_UPUSER);
SP_UPD_MEDICAL_VISIT2(v_New_VISIT_ID,v_VISIT_TYPE_ID,v_VISIT_REASON_ID,v_DIAGNOSIS_ID);
commit; elsif UPPER(v_CMD_TYPE) = 'UPDATE' Then update UAC_MEDICAL_VISIT set DATE_OF_VISIT=v_DATE_OF_VISIT, SPECIFY_OTHER_REASON=v_SPECIFY_OTHER_REASON, SPECIFY_OTHER_DIAGNOSIS=v_SPECIFY_OTHER_DIAGNOSIS, SUMMARY_OF_OUTCOME=v_SUMMARY_OF_OUTCOME, UPTIME=SYSDATE, UPUSER=v_UPUSER where VISIT_ID=v_VISIT_ID;
SP_UPD_MEDICAL_VISIT2(v_VISIT_ID,v_VISIT_TYPE_ID,v_VISIT_REASON_ID,v_DIAGNOSIS_ID);
commit; end if;
END SP_UPD_MEDICAL_VISIT;

PROCEDURE "SP_UPD_MEDICAL_VISIT2" 
(
  v_VISIT_ID NUMBER, 
	v_VISIT_TYPE_ID VARCHAR2, 
  v_VISIT_REASON_ID VARCHAR2,
  v_DIAGNOSIS_ID VARCHAR2
) AS 


BEGIN
    DELETE FROM UAC_MEDICAL_VISIT_TYPE WHERE VISIT_ID=v_VISIT_ID;
    DELETE FROM UAC_MEDICAL_VISIT_REASON WHERE VISIT_ID=v_VISIT_ID;
    DELETE FROM UAC_MEDICAL_VISIT_DIAGNOSIS WHERE VISIT_ID=v_VISIT_ID;

    insert into UAC_MEDICAL_VISIT_TYPE (VISIT_ID,VISIT_TYPE_ID)
    select v_VISIT_ID,  regexp_substr(v_VISIT_TYPE_ID,'[^,]+', 1, level) from dual
    connect by regexp_substr(v_VISIT_TYPE_ID, '[^,]+', 1, level) is not null;


    insert into UAC_MEDICAL_VISIT_REASON (VISIT_ID,VISIT_REASON_ID)
    select v_VISIT_ID, regexp_substr(v_VISIT_REASON_ID,'[^,]+', 1, level) from dual
    connect by regexp_substr(v_VISIT_REASON_ID, '[^,]+', 1, level) is not null;

    insert into UAC_MEDICAL_VISIT_DIAGNOSIS (VISIT_ID,DIAGNOSIS_ID)
    select v_VISIT_ID, regexp_substr(v_DIAGNOSIS_ID,'[^,]+', 1, level) from dual
    connect by regexp_substr(v_DIAGNOSIS_ID, '[^,]+', 1, level) is not null;

    commit;

END SP_UPD_MEDICAL_VISIT2;

