commit; elsif UPPER(v_CMD_TYPE) = 'UPDATE' Then update UAC_MEDICAL_VISIT set DATE_OF_VISIT=v_DATE_OF_VISIT, SPECIFY_OTHER_REASON=v_SPECIFY_OTHER_REASON, SPECIFY_OTHER_DIAGNOSIS=v_SPECIFY_OTHER_DIAGNOSIS, SUMMARY_OF_OUTCOME=v_SUMMARY_OF_OUTCOME, UPTIME=SYSDATE, UPUSER=v_UPUSER where VISIT_ID=v_VISIT_ID; commit; end if;
END SP_UPD_MEDICAL_VISIT_cy;

