PROCEDURE "SP_UPD_PRS_SERVICE_LVL_HIST" 
(
  PRS_ID IN NUMBER,
  UPUSER    IN VARCHAR2 
) AS 
prsID_ NUMBER(10) := PRS_ID;
userId_     VARCHAR2(112) := UPUSER;
BEGIN
  INSERT INTO UAC_PRS_SERVICE_LVL_HISTORY (PRS_ID, REFERRAL_ID, PRS_SERVICE_LVL_ID, UPTIME, UPUSER)
	SELECT PRS_ID, REFERRAL_ID, PRS_SERVICE_LEVEL, SYSDATE, userId_ as UPUSER FROM UAC_POST_RELEASE_SERVICE WHERE PRS_ID = prsID_;
END SP_UPD_PRS_SERVICE_LVL_HIST;

PROCEDURE "SP_UPD_RR_PRIMARY_SPONSOR" 
(
  vUAC_ID IN NUMBER,
  vSPONSOR_ID IN NUMBER,
  vUPUSER    IN VARCHAR2
) AS 
uacID_ NUMBER(10) := vUAC_ID;
sponsorID_   NUMBER(10) := vSPONSOR_ID;
userId_     VARCHAR2(112) := vUPUSER;
n         NUMBER(1) :=0;
BEGIN
	select count(*) into n From (
		SELECT 1 FROM UAC_SPONSOR_LIST 
		WHERE UAC_ID = uacID_ AND SPONSOR_ID = sponsorID_ AND CURRENT_SPONSOR = 1 and is_deleted = 0
		Union
		Select 0 from dual
		);
	if n > 1 then
		UPDATE UAC_SPONSOR_LIST
		SET CURRENT_SPONSOR = 0,
		UPUSER = userId_,
		UPTIME = SYSDATE
		WHERE UAC_ID = uacID_ AND SPONSOR_ID = sponsorID_ AND CURRENT_SPONSOR = 1 and is_deleted = 0
		;
	End If;
END SP_UPD_RR_PRIMARY_SPONSOR;

PROCEDURE SP_UPD_SCHEDULED_HEARING
(
	v_UAC_ASSESSMENT_ID IN NUMBER
, v_SCHEDULED_FOR_HEARING IN NUMBER 
, v_SCHEDULED_FOR_HEARING_DATE IN DATE 
, v_HEARING_CITY IN VARCHAR2
, v_HEARING_STATE IN VARCHAR2
, v_HEARING_OUTCOME  IN NUMERIC
, v_HEARING_OUTCOME_OTHER IN VARCHAR2
, v_USERID IN VARCHAR2 
) AS 

uacAssessmtID_  NUMBER := v_UAC_ASSESSMENT_ID;
scheduledForHearing_ NUMBER := v_SCHEDULED_FOR_HEARING;
scheduledForHearingDate_  Date := v_SCHEDULED_FOR_HEARING_DATE;
hearingCity_ VARCHAR2(112) := v_HEARING_CITY;
hearingState_ VARCHAR2(10) := v_HEARING_STATE;
hearingOutcome_ NUMBER := v_HEARING_OUTCOME;
hearingOther_ VARCHAR2(500) := v_HEARING_OUTCOME_OTHER;
userId_   VARCHAR2(112) := v_USERID;
maxNo     NUMERIC(10);

BEGIN
	maxNo := SEQ_UAC_SCHD_HEARING.nextval;
    Insert into UAC_SCHEDULED_HEARING (HEARING_ID, UAC_ASSESSMENT_ID, SCHEDULED_FOR_HEARING, SCHEDULED_FOR_HEARING_DATE, HEARING_CITY, HEARING_STATE, HEARING_OUTCOME, HEARING_OTHER, USER_CREATED, DATETIME_CREATED)
    Values (maxNo, uacAssessmtID_, scheduledForHearing_, scheduledForHearingDate_, hearingCity_, hearingState_, hearingOutcome_, hearingOther_, userId_, sysdate);

END SP_UPD_SCHEDULED_HEARING;

PROCEDURE "SP_UPD_SPONSOR_ADDRESS_HIST_COR" (
v_SPONSOR_ID  IN NUMBER,
v_UAC_ID  IN NUMBER,
v_OLD_COR IN VARCHAR2,
v_NEW_COR IN VARCHAR2,
v_userId IN VARCHAR2
)
AS 
SPONSOR_ADDR_HST_ID_ NUMBER(10,0);
n NUMBER := 0;
BEGIN
  /*
   Updates COR to old COR for Old sponsor page (NewSponsor.aspx)
   to make sure Old COR is logged in history table not new COR
  */
  SELECT COUNT(*) INTO n FROM UAC_SPONSOR_ADDRESS_HISTORY
  WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND COR = v_NEW_COR AND USER_CREATED = v_userId;
  if n > 0 Then
    SELECT max(SPONSOR_ADDR_HST_ID) INTO SPONSOR_ADDR_HST_ID_ FROM UAC_SPONSOR_ADDRESS_HISTORY
    WHERE UAC_ID = v_UAC_ID AND SPONSOR_ID = v_SPONSOR_ID AND COR = v_NEW_COR AND USER_CREATED = v_userId;

    UPDATE UAC_SPONSOR_ADDRESS_HISTORY
    SET COR = v_OLD_COR
    WHERE SPONSOR_ADDR_HST_ID = SPONSOR_ADDR_HST_ID_;
  End If;

END SP_UPD_SPONSOR_ADDRESS_HIST_COR;

PROCEDURE "SP_UPD_SPONSOR_BGRD_FINGERPRINTS" ( vUAC_ID VARCHAR2, vSPONSOR_ID Number, vFINGER_PRINT_METHOD Number, vUPUSER VARCHAR2, vOCCUPANT_ID NUMBER := null, vSPONSOR_ALTERNATE_CAREGIVER_ID Number := null, vFINGER_PRINT_DATE_AVAILABLE date := null, vFINGER_PRINT_APPOINTMENT date := null, vID_DATE_SENT date := null, vARI_SENT_TO_ORR date := null, vFIELD_PRINTDATE_AVAILABLE date := null, vFIELD_PRINT_APPOINTMENT date := null, vCARD_PRINT_SENT_DATE date := null, vCARD_PRINT_DATE_RECEIVED date := null, vSaveType varchar2 ) AS
cmdType_ VARCHAR2(30) := Upper(vSaveType);
BEGIN If cmdType_ = 'SPONSOR' Then Update SPONSOR_BACKGROUND_CHECK SET FINGER_PRINT_METHOD =vFINGER_PRINT_METHOD, FINGER_PRINT_DATE_AVAILABLE = vFINGER_PRINT_DATE_AVAILABLE, FINGER_PRINT_APPOINTMENT = vFINGER_PRINT_APPOINTMENT, ID_DATE_SENT = vID_DATE_SENT, ARI_SENT_TO_ORR = vARI_SENT_TO_ORR, FIELD_PRINTDATE_AVAILABLE = vFIELD_PRINTDATE_AVAILABLE, FIELD_PRINT_APPOINTMENT = vFIELD_PRINT_APPOINTMENT, CARD_PRINT_SENT_DATE = vCARD_PRINT_SENT_DATE, CARD_PRINT_DATE_RECEIVED = vCARD_PRINT_DATE_RECEIVED, USER_UPDATED = vUPUSER, DATE_UPDATED = sysdate where UAC_ID = vUAC_ID and SPONSOR_ID = vSPONSOR_ID and SPONSOR_ALTERNATE_CAREGIVER_ID is Null and OCCUPANT_ID is Null and bg_fbich = 1; elsif cmdType_ = 'ALTERNATECAREGIVER' Then Update SPONSOR_BACKGROUND_CHECK SET FINGER_PRINT_METHOD =vFINGER_PRINT_METHOD, FINGER_PRINT_DATE_AVAILABLE = vFINGER_PRINT_DATE_AVAILABLE, FINGER_PRINT_APPOINTMENT = vFINGER_PRINT_APPOINTMENT, ID_DATE_SENT = vID_DATE_SENT, ARI_SENT_TO_ORR = vARI_SENT_TO_ORR, FIELD_PRINTDATE_AVAILABLE = vFIELD_PRINTDATE_AVAILABLE, FIELD_PRINT_APPOINTMENT = vFIELD_PRINT_APPOINTMENT, CARD_PRINT_SENT_DATE = vCARD_PRINT_SENT_DATE, CARD_PRINT_DATE_RECEIVED = vCARD_PRINT_DATE_RECEIVED, USER_UPDATED = vUPUSER, DATE_UPDATED = sysdate where UAC_ID = vUAC_ID and SPONSOR_ID = vSPONSOR_ID and SPONSOR_ALTERNATE_CAREGIVER_ID = vSPONSOR_ALTERNATE_CAREGIVER_ID and OCCUPANT_ID is Null and bg_fbich = 1; elsif cmdType_ = 'HOUSEHOLD' Then Update SPONSOR_BACKGROUND_CHECK SET FINGER_PRINT_METHOD =vFINGER_PRINT_METHOD, FINGER_PRINT_DATE_AVAILABLE = vFINGER_PRINT_DATE_AVAILABLE, FINGER_PRINT_APPOINTMENT = vFINGER_PRINT_APPOINTMENT, ID_DATE_SENT = vID_DATE_SENT, ARI_SENT_TO_ORR = vARI_SENT_TO_ORR, FIELD_PRINTDATE_AVAILABLE = vFIELD_PRINTDATE_AVAILABLE, FIELD_PRINT_APPOINTMENT = vFIELD_PRINT_APPOINTMENT, CARD_PRINT_SENT_DATE = vCARD_PRINT_SENT_DATE, CARD_PRINT_DATE_RECEIVED = vCARD_PRINT_DATE_RECEIVED, USER_UPDATED = vUPUSER, DATE_UPDATED = sysdate where UAC_ID = vUAC_ID and SPONSOR_ID = vSPONSOR_ID and OCCUPANT_ID = vOCCUPANT_ID and SPONSOR_ALTERNATE_CAREGIVER_ID is Null and bg_fbich = 1; end if;
END SP_UPD_SPONSOR_BGRD_FINGERPRINTS;

PROCEDURE "SP_UPD_SPONSOR_COUNTER" 
(
  UAC_ID IN NUMBER,
  CounterType CHAR
) AS 
uacId_  NUMBER(10) := UAC_ID;
counterType_  CHAR(1) := CounterType;
BEGIN
  If counterType_ = 'P' Then
    Update SPONSOR_INFO A Set P_COUNTER = (
    Select COUNT(*) From UAC_SPONSOR_LIST WHERE SPONSOR_ID = A.SPONSOR_ID GROUP BY SPONSOR_ID)
    Where SPONSOR_ID in (Select SPONSOR_ID From UAC_SPONSOR_LIST Where SPONSOR_ID = A.SPONSOR_ID And UAC_ID = uacId_ and is_deleted = 0) and a.is_deleted = 0;
  Elsif counterType_ = 'A' Then
    Update SPONSOR_INFO A Set A_COUNTER = (nvl(A_COUNTER,0) + 1)
    Where SPONSOR_ID in (Select SPONSOR_ID From UAC_SPONSOR_LIST Where SPONSOR_ID = A.SPONSOR_ID And UAC_ID = uacId_ And CURRENT_SPONSOR = 1 and is_deleted = 0) and a.is_deleted = 0;
  End If;
END SP_UPD_SPONSOR_COUNTER;

PROCEDURE "SP_UPD_SPONSOR_INFO_HISTORY" (
v_SPONSOR_ID  IN NUMBER,
v_FIRST_NAME IN VARCHAR2,
v_LAST_NAME IN VARCHAR2,
v_SSN IN VARCHAR2,
v_ALIEN_NO IN VARCHAR2,
v_DOB IN DATE,
v_COB IN VARCHAR2,
v_userId IN VARCHAR2
)
AS 
n   NUMBER(4) := 0;
BEGIN
	SELECT COUNT(*) INTO n FROM SPONSOR_INFO_HISTORY WHERE SPONSOR_ID = v_SPONSOR_ID;
	if n < 1 Then
		INSERT INTO SPONSOR_INFO_HISTORY(SPONSOR_ID, FIRST_NAME, LAST_NAME, SSN, ALIEN_NO, DOB, COB, UPTIME, UPUSER )
		SELECT SPONSOR_ID, FIRST_NAME, LAST_NAME, SSN, ALIEN_NO, DOB, COB, UPTIME, UPUSER FROM SPONSOR_INFO WHERE SPONSOR_ID = v_SPONSOR_ID and is_deleted = 0;
	End If;
	INSERT INTO SPONSOR_INFO_HISTORY(SPONSOR_ID, FIRST_NAME, LAST_NAME, SSN, ALIEN_NO, DOB, COB, UPTIME, UPUSER )
	VALUES(v_SPONSOR_ID, v_FIRST_NAME, v_LAST_NAME, v_SSN, v_ALIEN_NO, v_DOB, v_COB, CURRENT_TIMESTAMP, v_userId);

END SP_UPD_SPONSOR_INFO_HISTORY;

PROCEDURE             "SP_UPD_SPONSOR_PRIMARY_SPONSOR" (
vUacID IN NUMBER,
vSponsorId IN NUMBER
)
AS 
BEGIN
  Update UAC_SPONSOR_LIST Set CURRENT_SPONSOR = 0 Where UAC_ID = vUacID and is_deleted = 0;
  Update UAC_SPONSOR_LIST Set CURRENT_SPONSOR = 1 Where UAC_ID = vUacID And SPONSOR_ID = vSponsorId and is_deleted = 0;
END SP_UPD_SPONSOR_PRIMARY_SPONSOR;

PROCEDURE             "SP_UPD_UAC_CASESTATUS_FAMILY" (
UAC_ID    NUMBER,
DATE_ASSESSMENT date,
DATE_APPLICATION_SENT date,
DATE_APPLICATION_RECEIVED date,
DATE_IDENTITY date,
DATE_ADDRESS date,
DATE_RELATIONSHIP_PROOF date,
DATE_ARI_RECEIVED date,
vARINA Number,
v_UPUSER VARCHAR2
)
AS 
uacId_       NUMBER(10,0) := UAC_ID;
dassessment_ date := DATE_ASSESSMENT;
dappsent_ date := DATE_APPLICATION_SENT;
dappreceived_ date := DATE_APPLICATION_RECEIVED;
didentity_ date := DATE_IDENTITY;
daddress_ date := DATE_ADDRESS;
drelation_ date := DATE_RELATIONSHIP_PROOF;
drAriDate_ date := DATE_ARI_RECEIVED;
BEGIN
  UPDATE UAC_SPONSOR_LIST
  SET DATE_ASSESSMENT = dassessment_,
    DATE_APPLICATION_SENT = dappsent_,
    DATE_APPLICATION_RECEIVED = dappreceived_,
    DATE_IDENTITY = didentity_,
    DATE_ADDRESS = daddress_,
    DATE_RELATIONSHIP_PROOF = drelation_,
    ARI_DATE_RECEIVED = drAriDate_,
    ARI_NA = vARINA,
    UPTIME = SYSDATE,
    UPUSER = v_UPUSER
  WHERE UAC_ID = uacId_
  AND CURRENT_SPONSOR = 1 and is_deleted = 0;
END SP_UPD_UAC_CASESTATUS_FAMILY;

PROCEDURE "SP_UPD_UAC_CASE_MANAGER" ( v_UAC_ID NUMBER, v_UAC_PROGRAM_ID IN NUMBER, v_USER_ID VARCHAR2, v_FIRST_NAME VARCHAR2, v_LAST_NAME VARCHAR2, v_PHONE VARCHAR2, v_EMAIL VARCHAR2, v_ORGID IN NUMBER, v_updateType IN VARCHAR2, UPUSER IN VARCHAR2 ) AS cmdType_ VARCHAR2(30) := Upper(v_updateType); n NUMBER(1) := 0; BEGIN If cmdType_ = 'UPDATE' Then
Update UAC_CASE_MANAGERS Set PHONE = v_PHONE, UPUSER = v_USER_ID, UPTIME = Sysdate, ORGANIZATION_ID = v_ORGID Where EMAIL = v_EMAIL AND UAC_ID = v_UAC_ID AND UAC_PROGRAM_ID = v_UAC_PROGRAM_ID AND IS_PRIMARY = 1;
elsif cmdType_ = 'INSERT' Then
Select NVL(Count(*),0) into n from UAC_CASE_MANAGERS Where UAC_PROGRAM_ID = v_UAC_PROGRAM_ID and IS_PRIMARY = 1 And Lower(UPUSER) = Lower(v_USER_ID); if n = 0 then UPDATE UAC_CASE_MANAGERS SET IS_PRIMARY = 0 Where UAC_ID = v_UAC_ID AND UAC_PROGRAM_ID = v_UAC_PROGRAM_ID AND IS_PRIMARY = 1;
INSERT INTO UAC_CASE_MANAGERS ( MGR_ID, UAC_ID, UAC_PROGRAM_ID, FIRST_NAME, LAST_NAME, PHONE, EMAIL, ORGANIZATION_ID, IS_PRIMARY, DATE_CREATED, USER_CREATED, UPUSER, UPTIME ) VALUES (SEQ_UAC_CASE_MANAGERS.nextval, v_UAC_ID, v_UAC_PROGRAM_ID, v_FIRST_NAME, v_LAST_NAME, v_PHONE, v_EMAIL, v_ORGID, 1, SysDate, v_USER_ID, v_USER_ID, SysDate); End if; elsif cmdType_ = 'DELETE' Then
Update UAC_CASE_MANAGERS Set IS_PRIMARY = 0, UPUSER = v_USER_ID, UPTIME = SysDate Where EMAIL = v_EMAIL AND UAC_ID = v_UAC_ID AND UAC_PROGRAM_ID = v_UAC_PROGRAM_ID;
End If;
END SP_UPD_UAC_CASE_MANAGER;

PROCEDURE SP_UPD_UAC_SBSTNCE_USE_HISTORY 
(
  SESSION_ID IN VARCHAR2
) AS 
sessionId_  VARCHAR2(112) := SESSION_ID;
BEGIN
  Insert into UAC_SUBSTANCE_USE_HISTORY(SUBSTANCE_HIST_ID,SUBSTANCE_ID, UAC_ASSESSMENT_ID, FREQUENCY_OF_USE
  , DATE_OF_LAST_USE, DATE_CREATED,USER_CREATED)
  Select SEQ_SUBSTANCE_USE_HISTORY.nextval, SUBSTANCE_ID, UAC_ASSESSMENT_ID, FREQUENCY_OF_USE
  , DATE_OF_LAST_USE, SYSDATE, USER_CREATED
  From TT_UAC_SUBSTANCE_USE_HISTORY Where SESSION_ID = sessionId_;

  Delete from TT_UAC_SUBSTANCE_USE_HISTORY Where SESSION_ID = sessionId_;
END SP_UPD_UAC_SBSTNCE_USE_HISTORY;

PROCEDURE SP_UPD_UCINFO_WITH_XML ( PageType IN VARCHAR2, xmlToken IN VARCHAR2, vStatus OUT NOCOPY VARCHAR2, vSponsorId OUT NOCOPY NUMBER --, xmlData IN CLOB ) AS
xmlData_ XMLType; sId_ NUMBER; ns_ VARCHAR2(250); -- Namespace pageType_ VARCHAR2(100) := Upper(PageType); sUptime TimeStamp(6); uUptime TimeStamp(6); n NUMBER(1); currTime TIMESTAMP(6) := current_timestamp; uacId_ NUMBER; currentSponsor NUMBER(1) := 0; BEGIN vStatus := 'SUCCESS SUCCESS'; --xmlData_ := XMLTYPE(xmlData); Select XMLTYPE(XML_DATA) into xmlData_ from TT_XMLDATA Where XML_TOKEN = xmlToken; If pageType_ = 'NEWSPONSOR' Then Select SPONSOR_ID, to_timestamp_tz(UPTIME, 'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm') Into sId_, sUptime from XMLTABLE(XMLNAMESPACES(default 'http://tempuri.org/DS_UCPortal.xsd'), '/DS_UCPortal/SPONSOR_INFO' Passing xmlData_ Columns SPONSOR_ID NUMBER path 'SPONSOR_ID', UPTIME VARCHAR2(100) path 'UPTIME' );
Select UAC_ID,to_timestamp_tz(UPTIME, 'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm') Into uacId_, uUptime From XMLTABLE(XMLNAMESPACES(default 'http://tempuri.org/DS_UCPortal.xsd'), '/DS_UCPortal/UAC_SPONSOR_LIST' Passing xmlData_ Columns UAC_ID NUMBER path 'UAC_ID', UPTIME VARCHAR2(100) path 'UPTIME' ); If sId_ is null Then sId_ := SEQ_SPONSOR_INFO.NEXTVAL; vSponsorId := sId_;
Insert Into SPONSOR_INFO (SPONSOR_ID, FIRST_NAME, LAST_NAME, SSN, ALIEN_NO, ADDRESS, ADDRESS_SECONDARY, CITY, STATE, ZIP_CODE, DOB, COB, COR, MARRIAGE_STATUS, HOME_PHONE, WORK_PHONE, FAX, EMAIL, GENDER, LEGAL_STATUS_ID, HOUSEHOLD_HAS_DISEASE, HOUSEHOLD_HAS_CRIMINAL_HIST, NOTES, DATE_CREATED, USER_CREATED, UPTIME, UPUSER, FLAG, FLAG_NOTE, FLAG_ADDRESS, FLAG_ADDRESS_NOTE, LATITUDE, LONGITUDE, COUNTY, SMARTY_STREET_VALIDATION ) Select sId_, FIRST_NAME, LAST_NAME, SSN, ALIEN_NO, ADDRESS, ADDRESS_SECONDARY, CITY, STATE, ZIP_CODE, to_timestamp_tz(DOB, 'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm'), COB, COR, MARRIAGE_STATUS, HOME_PHONE, WORK_PHONE, FAX, EMAIL, GENDER, LEGAL_STATUS_ID, HOUSEHOLD_HAS_DISEASE, HOUSEHOLD_HAS_CRIMINAL_HIST, NOTES, currTime, USER_CREATED, currTime, UPUSER, FLAG, FLAG_NOTE, FLAG_ADDRESS, FLAG_ADDRESS_NOTE, LATITUDE, LONGITUDE, COUNTY, SMARTY_STREET_VALIDATION From XMLTABLE(XMLNAMESPACES(default 'http://tempuri.org/DS_UCPortal.xsd'), '/DS_UCPortal/SPONSOR_INFO' Passing xmlData_ Columns FIRST_NAME VARCHAR2(2000) path 'FIRST_NAME', LAST_NAME VARCHAR2(2000) path 'LAST_NAME', SSN VARCHAR2(11) path 'SSN', ALIEN_NO VARCHAR2(10) path 'ALIEN_NO', ADDRESS VARCHAR2(2000) path 'ADDRESS', ADDRESS_SECONDARY VARCHAR2(2000) path 'ADDRESS_SECONDARY', CITY VARCHAR2(2000) path 'CITY', STATE CHAR(2) path 'STATE', ZIP_CODE VARCHAR2(10) path 'ZIP_CODE', DOB VARCHAR2(50) path 'DOB', COB VARCHAR2(50) path 'COB', COR VARCHAR2(50) path 'COR', MARRIAGE_STATUS VARCHAR2(7) path 'MARRIAGE_STATUS', HOME_PHONE VARCHAR2(22) path 'HOME_PHONE', WORK_PHONE VARCHAR2(22) path 'WORK_PHONE', FAX VARCHAR2(22) path 'FAX', EMAIL VARCHAR2(112) path 'EMAIL', GENDER CHAR(1) path 'GENDER', LEGAL_STATUS_ID NUMBER(3) path 'LEGAL_STATUS_ID', HOUSEHOLD_HAS_DISEASE NUMBER(1) path 'HOUSEHOLD_HAS_DISEASE', HOUSEHOLD_HAS_CRIMINAL_HIST NUMBER(1) path 'HOUSEHOLD_HAS_CRIMINAL_HIST', NOTES VARCHAR2(32000) path 'NOTES', USER_CREATED VARCHAR2(112) path 'USER_CREATED', UPUSER VARCHAR2(112) path 'UPUSER', FLAG NUMBER(1) path 'FLAG', FLAG_NOTE VARCHAR2(4000) path 'FLAG_NOTE', FLAG_ADDRESS NUMBER(1) path 'FLAG_ADDRESS', FLAG_ADDRESS_NOTE VARCHAR2(4000) path 'FLAG_ADDRESS_NOTE', LATITUDE VARCHAR2(2000) path 'LATITUDE', LONGITUDE VARCHAR2(2000) path 'LONGITUDE', COUNTY VARCHAR2(2000) path 'ADDRESS_SECONDARY', SMARTY_STREET_VALIDATION VARCHAR2(2000) path 'SMARTY_STREET_VALIDATION' );
