ath 'SPONSOSR_LONGITUDE', SPONSOR_COUNTY VARCHAR2(2000) path 'SPONSOR_ADDRESS_SECONDARY', SMARTY_STREET_VALIDATION VARCHAR2(2000) path 'SMARTY_STREET_VALIDATION' );
