Insert into SPONSOR_HOUSEHOLD_OCCUPANTS(OCCUPANT_ID, SPONSOR_ID, UAC_ID, NAME_OF_OCCUPANT,FIRST_NAME, LAST_NAME, GENDER, DOB, AGE, RELATIONSHIP_TO_SPONSOR, DEPENDENT_OF_SPONSOR, USER_CREATED, DATE_CREATED, UPTIME, UPUSER, QUERY_ID) Select SEQ_SPONSOR_HH.nextval,sId_, UAC_ID, NAME_OF_OCCUPANT, FIRST_NAME, LAST_NAME, GENDER, to_timestamp_tz(DOB,'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm'), AGE, RELATIONSHIP_TO_SPONSOR, DEPENDENT_OF_SPONSOR, USER_CREATED, currTime,currTime, UPUSER, QUERY_ID From XMLTABLE(XMLNAMESPACES(default 'http://tempuri.org/DS_UCPortal.xsd'), '/DS_UCPortal/SPONSOR_HOUSEHOLD_OCCUPANTS' Passing xmlData_ Columns UAC_ID NUMBER path 'UAC_ID', NAME_OF_OCCUPANT VARCHAR2(2000) path 'NAME_OF_OCCUPANT', FIRST_NAME VARCHAR2(2000) path 'FIRST_NAME', LAST_NAME VARCHAR2(2000) path 'LAST_NAME', GENDER CHAR(1) path 'GENDER', DOB VARCHAR2(50) path 'DOB', AGE NUMBER path 'AGE', RELATIONSHIP_TO_SPONSOR NUMBER path 'RELATIONSHIP_TO_SPONSOR', DEPENDENT_OF_SPONSOR NUMBER(22) path 'DEPENDENT_OF_SPONSOR', USER_CREATED VARCHAR2(112) path 'USER_CREATED', UPUSER VARCHAR2(112) path 'UPUSER', QUERY_ID VARCHAR2(7) path 'QUERY_ID' ) Where existsnode(xmlData_, '/DS_UCPortal/SPONSOR_HOUSEHOLD_OCCUPANTS', 'xmlns="http://tempuri.org/DS_UCPortal.xsd"') = 1; Else vSponsorId := sId_; /* Select count(*) into n From ( Select 1 from SPONSOR_INFO Where DateDiff('Sec',UPTIME, sUptime) = 0 And To_NUMBER(To_Char(UPTIME, 'FF9')) = TO_NUMBER(TO_CHAR(sUptime, 'FF9')) And Uptime is not null And SPONSOR_ID = sId_ Union Select 0 from dual ); If n = 1 Then vStatus := 'Someone just updated the record. Please refresh the page and apply your change to new record.'; GOTO ENDPOINT; End If; */ Select count(*) into n From ( Select 1 from UAC_SPONSOR_LIST Where SPONSOR_ID = sId_ And UAC_ID = uacId_ Union Select 0 from dual );
