-- Update if it is existing record UPDATE SPONSOR_HOUSEHOLD_OCCUPANTS A SET (NAME_OF_OCCUPANT,FIRST_NAME, LAST_NAME, GENDER, DOB, AGE, RELATIONSHIP_TO_SPONSOR, DEPENDENT_OF_SPONSOR, UPTIME, UPUSER, QUERY_ID) = ( Select NAME_OF_OCCUPANT, FIRST_NAME, LAST_NAME, GENDER, to_timestamp_tz(DOB,'YYYY-MM-DD"T"HH24:MI:SS.FF9tzh:tzm'), AGE, RELATIONSHIP_TO_SPONSOR, DEPENDENT_OF_SPONSOR, currTime, UPUSER, QUERY_ID From XMLTABLE(XMLNAMESPACES(default 'http://tempuri.org/DS_UCPortal.xsd'), '/DS_UCPortal/SPONSOR_HOUSEHOLD_OCCUPANTS' Passing xmlData_ Columns OCCUPANT_ID NUMBER path 'OCCUPANT_ID', NAME_OF_OCCUPANT VARCHAR2(2000) path 'NAME_OF_OCCUPANT', FIRST_NAME VARCHAR2(2000) path 'FIRST_NAME', LAST_NAME VARCHAR2(2000) path 'LAST_NAME', GENDER CHAR(1) path 'GENDER', DOB VARCHAR2(50) path 'DOB', AGE NUMBER path 'AGE', RELATIONSHIP_TO_SPONSOR NUMBER path 'RELATIONSHIP_TO_SPONSOR', DEPENDENT_OF_SPONSOR NUMBER(22) path 'DEPENDENT_OF_SPONSOR', UPUSER VARCHAR2(112) path 'UPUSER', QUERY_ID VARCHAR2(7) path 'QUERY_ID' ) Where existsnode(xmlData_, '/DS_UCPortal/SPONSOR_HOUSEHOLD_OCCUPANTS', 'xmlns="http://tempuri.org/DS_UCPortal.xsd"') = 1 And OCCUPANT_ID = A.OCCUPANT_ID And OCCUPANT_ID is not null ) Where A.OCCUPANT_ID in (Select OCCUPANT_ID From XMLTABLE(XMLNAMESPACES(default 'http://tempuri.org/DS_UCPortal.xsd'), '/DS_UCPortal/SPONSOR_HOUSEHOLD_OCCUPANTS' Passing xmlData_ Columns OCCUPANT_ID NUMBER path 'OCCUPANT_ID' ) Where OCCUPANT_ID is not null ); End If;
Update SPONSOR_INFO A Set P_COUNTER = ( Select COUNT(*) From UAC_SPONSOR_LIST WHERE SPONSOR_ID = A.SPONSOR_ID) Where SPONSOR_ID = sId_;
Select CURRENT_SPONSOR into currentSponsor From UAC_SPONSOR_LIST where UAC_ID = uacId_ And SPONSOR_ID = sId_; if nvl(currentSponsor,0) = 1 then Update UAC_SPONSOR_LIST Set CURRENT_SPONSOR = 0 Where UAC_ID = uacId_; Update UAC_SPONSOR_LIST Set CURRENT_SPONSOR = 1 Where UAC_ID = uacId_ And SPONSOR_ID = sId_; end if;
End If; <<ENDPOINT>> Null; EXCEPTION WHEN OTHERS Then vStatus := SQLERRM;
END SP_UPD_UCINFO_WITH_XML;

PROCEDURE "SP_UPD_UNIFICATION_SPECIALIST"
(
  v_UAC_PROGRAM_ID IN NUMBER,
  v_USER_ID    VARCHAR2,
  v_PHONE VARCHAR2,
  v_ORGID IN NUMBER,
  v_updateType IN VARCHAR2,
  UPUSER    IN VARCHAR2
) AS
cmdType_  VARCHAR2(30) := Upper(v_updateType);
n   NUMBER(1) := 0;
BEGIN
  If cmdType_ = 'UPDATE' Then

    Update UAC_UNIFICATION_SPECIALIST Set PHONE = v_PHONE, UPUSER = v_USER_ID, UPTIME = Sysdate, ORGANIZATION_ID = v_ORGID Where  UAC_PROGRAM_ID = v_UAC_PROGRAM_ID AND IS_PRIMARY = 1 AND Lower(UPUSER) = Lower(v_USER_ID);

  elsif cmdType_ = 'INSERT' Then

    Select NVL(Count(*),0) into n from UAC_UNIFICATION_SPECIALIST Where UAC_PROGRAM_ID = v_UAC_PROGRAM_ID and IS_PRIMARY = 1 And Lower(UPUSER) = Lower(v_USER_ID);
    if n = 0 then
        UPDATE UAC_UNIFICATION_SPECIALIST SET IS_PRIMARY = 0 Where UAC_PROGRAM_ID = v_UAC_PROGRAM_ID AND IS_PRIMARY = 1;

        INSERT INTO UAC_UNIFICATION_SPECIALIST (
          UAC_PROGRAM_ID,
          PHONE,
          ORGANIZATION_ID,
          IS_PRIMARY,
          DATE_CREATED,
          USER_CREATED,
          UPUSER,
          UPTIME
        )
        VALUES (v_UAC_PROGRAM_ID, v_PHONE, v_ORGID, 1, SysDate, v_USER_ID, v_USER_ID, SysDate);
    End if;
  elsif cmdType_ = 'DELETE' Then

        Update UAC_UNIFICATION_SPECIALIST Set IS_PRIMARY = 0, UPUSER = v_USER_ID, UPTIME = SysDate Where Lower(UPUSER) = Lower(v_USER_ID) AND UAC_PROGRAM_ID = v_UAC_PROGRAM_ID;

  End If;

END SP_UPD_UNIFICATION_SPECIALIST;

PROCEDURE "SP_UPD_USER_PROFILE" ( v_USER_ID VARCHAR2, v_FIRST_NAME VARCHAR2, v_LAST_NAME VARCHAR2, v_ADDRESS VARCHAR2, v_CITY VARCHAR2, v_STATE VARCHAR2, v_ZIP_CODE VARCHAR2, v_PHONE VARCHAR2, v_WORK_PHONE VARCHAR2, v_WORK_FAX VARCHAR2, v_EMAIL VARCHAR2, v_SUSPENDED NUMBER , v_ROLE_TYPE VARCHAR2, v_USER_PRIVELEGE VARCHAR2, v_UPUSER VARCHAR2, v_PROGRAM_IDS VARCHAR2 ) as
BEGIN update ADM_LIST_WEB_USER set FIRST_NAME=v_FIRST_NAME,LAST_NAME=v_LAST_NAME, ADDRESS=v_ADDRESS, CITY=v_CITY, STATE=v_STATE, ZIP_CODE=v_ZIP_CODE, PHONE=v_PHONE,WORK_PHONE=v_WORK_PHONE, WORK_FAX=v_WORK_FAX, EMAIL=v_EMAIL, SUSPENDED=v_SUSPENDED, ROLE_TYPE=v_ROLE_TYPE, USER_PRIVELEGE=v_USER_PRIVELEGE,UPTIME=sysdate, UPUSER=v_UPUSER where USER_ID=v_USER_ID;
SP_UPD_USER_PROGRAM(v_USER_ID,v_PROGRAM_IDS,v_UPUSER); commit; END SP_UPD_USER_PROFILE;

PROCEDURE "SP_UPD_USER_PROGRAM" 
(
  v_USER_ID VARCHAR2, 
	v_PROGRAM_IDS VARCHAR2, 
  v_UPUSER VARCHAR2
) AS 
BEGIN
    DELETE FROM ADM_USER_PROGRAM WHERE USER_ID=v_USER_ID;  
    insert into ADM_USER_PROGRAM (USER_ID,UPTIME,UPUSER,PROGRAM_ID)
    select v_USER_ID, SYSDATE, v_UPUSER,  regexp_substr(v_PROGRAM_IDS,'[^,]+', 1, level) from dual
    connect by regexp_substr(v_PROGRAM_IDS, '[^,]+', 1, level) is not null;
    commit;

END SP_UPD_USER_PROGRAM;

PROCEDURE sp_UpsertSponsorAssessTabHistory (
    p_UAC_ID      IN VARCHAR2,
    p_SponsorId   IN VARCHAR2,
    p_Tab         IN VARCHAR2,
    p_UpUser      IN VARCHAR2,
    p_FullName    IN VARCHAR2
) AS
    v_count NUMBER;
BEGIN
    SELECT COUNT(*) INTO v_count
    FROM SPONSOR_ASSESS_TAB_HISTORY
    WHERE UAC_ID = p_UAC_ID AND SPONSOR_ID = p_SponsorId AND ASSESSMENT_TAB = p_Tab;

    IF v_count > 0 THEN
        UPDATE SPONSOR_ASSESS_TAB_HISTORY
        SET UPDATE_DATE = SYSDATE, UPUSER = p_UpUser, FULL_NAME = p_FullName
        WHERE UAC_ID = p_UAC_ID AND SPONSOR_ID = p_SponsorId AND ASSESSMENT_TAB = p_Tab;
    ELSE
        INSERT INTO SPONSOR_ASSESS_TAB_HISTORY (
            UAC_ID, SPONSOR_ID, ASSESSMENT_TAB, UPDATE_DATE, UPUSER, FULL_NAME
        ) VALUES (
            p_UAC_ID, p_SponsorId, p_Tab, SYSDATE, p_UpUser, p_FullName
        );
    END IF;
END sp_UpsertSponsorAssessTabHistory;

PROCEDURE "SP_USER_STATUS_UPDATE" 
(
  checkedIDs VARCHAR2, 
  notcheckedIDs VARCHAR2, 
  updatedByUser  VARCHAR2
) AS 

Cursor cscheckedVar  is Select column_value as checkeduserID from Table(Split(checkedIDs));
cscheckedVal  cscheckedVar%ROWTYPE;

Cursor csnotcheckedVar  is Select column_value as notcheckeduserID from Table(Split(notcheckedIDs));
csnotcheckedVal  csnotcheckedVar%ROWTYPE;

BEGIN
  OPEN cscheckedVar;
  Loop
    FETCH cscheckedVar Into cscheckedVal;
    EXIT WHEN cscheckedVar%NOTFOUND;

   Update ADM_LIST_WEB_USER Set (SUSPENDED, LOGINCOUNT, UpTime, Upuser) = (select 0, 0, Sysdate, updatedByUser from dual)
      Where USER_ID = cscheckedVal.checkeduserID and SUSPENDED = 1;

  End Loop;
  Close cscheckedVar;

   OPEN csnotcheckedVar;
  Loop
    FETCH csnotcheckedVar Into csnotcheckedVal;
    EXIT WHEN csnotcheckedVar%NOTFOUND;

   Update ADM_LIST_WEB_USER Set (SUSPENDED, LOGINCOUNT, UpTime, Upuser) = (select 1, 0, Sysdate, updatedByUser from dual)
      Where USER_ID = csnotcheckedVal.notcheckeduserID and SUSPENDED = 0;

  End Loop;
  Close csnotcheckedVar;
END SP_USER_STATUS_UPDATE;

PROCEDURE "SP_XMLJOB_ACTIVITY_LOGS" 
(
  ACTIVITY_ID IN NUMBER, 
  NEW_FILENAME IN VARCHAR2,
  STATUS IN VARCHAR2,
  UAC_ID IN NUMBER, 
  TRANSACTION_ID IN VARCHAR2, 
  DATETIME_STARTED IN DATE,
  DATETIME_COMPLETED IN DATE, 
  PROCESS_INFO IN VARCHAR2,
  CMD_Type IN VARCHAR2,
  CBP_OUTGOING_JSON IN VARCHAR2 DEFAULT NULL,
  CBP_STATUS_CODE IN VARCHAR2 DEFAULT NULL,
  CBP_RETURN_MESSAGE IN VARCHAR2 DEFAULT NULL,
  cur_OUT OUT Sys_Refcursor
) AS 

  activityId_ NUMERIC(10) := ACTIVITY_ID;
  new_Filename_ VARCHAR2(512) := NEW_FILENAME;
  status_ VARCHAR2(112) := STATUS;
  uac_Id_ NUMERIC(10) := UAC_ID;
  transactionId_ VARCHAR2(512) := TRANSACTION_ID;
  datetime_Started_ DATE := DATETIME_STARTED;
  datetime_Completed_ DATE := DATETIME_COMPLETED; 
  processInfo_ VARCHAR2(8000) := PROCESS_INFO;
  cmdType_  VARCHAR2(30) := Upper(CMD_Type);
  cbpJson_ VARCHAR2(4000) := CBP_OUTGOING_JSON;
  cbpStatusCode_ VARCHAR2(10) := CBP_STATUS_CODE;
  cbpReturnMsg_ VARCHAR2(4000) := CBP_RETURN_MESSAGE;

maxActivityID NUMBER :=0;

BEGIN

  if cmdType_ = 'INSERT' Then
      maxActivityID := SEQ_XMLJOB_ACTIVITY_LOGS.nextval;
      Insert into XMLJOB_ACTIVITY_LOGS (ACTIVITY_ID,NEW_FILENAME,STATUS,UAC_ID,TRANSACTION_ID,DATETIME_STARTED,DATETIME_COMPLETED,PROCESS_INFO)
        values (maxActivityID,new_Filename_,status_,uac_Id_,transactionId_,datetime_Started_,datetime_Completed_,processInfo_);   
      commit;

  elsif UPPER(cmdType_) = 'UPDATE' Then  
      maxActivityID := activityId_;
      update XMLJOB_ACTIVITY_LOGS set  
        STATUS = status_, 
        PROCESS_INFO = processInfo_,
        TRANSACTION_ID = transactionId_,
        UAC_ID = uac_Id_,
        DATETIME_COMPLETED = Sysdate
      where ACTIVITY_ID = activityId_;           
      commit;

   elsif UPPER(cmdType_) = 'UPDATECBP' Then  
      maxActivityID := activityId_;
      update XMLJOB_ACTIVITY_LOGS set  
        CBP_OUTGOING_JSON = cbpJson_, 
        CBP_STATUS_CODE = cbpStatusCode_,
        CBP_RETURN_MESSAGE = cbpReturnMsg_,
        DATETIME_COMPLETED = Sysdate
      where ACTIVITY_ID = activityId_;           
      commit;
  end if;
  Open cur_OUT For Select maxActivityID as ACTIVITY_ID from dual;
END SP_XMLJOB_ACTIVITY_LOGS;

