PROCEDURE Write_BLOB_To_File (
cur_OUT OUT	SYS_REFCURSOR
)
AS
    v_lob_loc      BLOB;
    v_buffer       RAW(32767);
    v_buffer_size  BINARY_INTEGER;
    v_amount       BINARY_INTEGER;
    v_offset       NUMBER(38) := 1;
    v_chunksize    INTEGER;
    v_out_file     UTL_FILE.FILE_TYPE;
	v_fileName		VARCHAR2(255);
	returnMsg		VARCHAR2(2000);
  vexist  BOOLEAN;
  bsize NUMBER;
  fsize NUMBER;
  n     Number := 0;
BEGIN
	returnMsg := 'START TIME: ' || To_Char(Current_TimeStamp, 'mm/dd/yyyy HH:MI:SS AM');
    -- +-------------------------------------------------------------+
    -- | SELECT THE LOB LOCATOR                                      |
    -- +-------------------------------------------------------------+
    /*
    SELECT  file_Content
    INTO    v_lob_loc
    FROM    UAC_DOCUMENTS
    WHERE   Doc_id = 18;
    */
    Declare Cursor docInfo is 
    Select * from UAC_DOCUMENTS where File_Content is not null  and DateDiff('day', To_Date('11/18/2014', 'mm/dd/yyyy'), Uptime) >= 0 order by DOC_ID DESC; -- And File_Type <> 'text/plain';
    -- +-------------------------------------------------------------+
    -- | FIND OUT THE CHUNKSIZE FOR THIS LOB COLUMN                  |
    -- +-------------------------------------------------------------+
    Begin
		for doc_rec in docInfo
		Loop
			BEGIN
				v_chunksize := doc_rec.File_Size; --DBMS_LOB.GETCHUNKSIZE(doc_rec.File_Content);

				-- +-------------------------------------------------------------+
				-- | OPENING THE LOB IS OPTIONAL                                 |
				-- +-------------------------------------------------------------+

      -- CHECK File Existance 
      vexist := False;
      UTL_FILE.FGETATTR(
        location => 'UAC_DOCS',
        filename => doc_rec.DOC_ID || '_' || doc_rec.File_Name, 
        fexists =>  vexist,
        file_length => fsize,
        block_size  => bsize);


      if vexist = true then
        vexist := False;
      else
        n := n + 1;
				--DBMS_LOB.OPEN(doc_rec.File_Content, DBMS_LOB.LOB_READONLY);
				-- +-------------------------------------------------------------+
				-- | WRITE CONTENTS OF THE LOB TO A FILE                         |
				-- +-------------------------------------------------------------+
				v_out_file := UTL_FILE.FOPEN(
					location      => 'UAC_DOCS', 
					filename      => doc_rec.DOC_ID || '_' || doc_rec.File_Name, 
					open_mode     => 'wb',
					max_linesize  => 32767);

				v_offset := 1;
				IF (v_chunksize < 32767) THEN
					v_buffer_size := v_chunksize;
					v_amount := v_buffer_size;
					DBMS_LOB.READ(lob_loc => doc_rec.File_Content, amount => v_amount, offset => v_offset, buffer => v_buffer);
					UTL_FILE.PUT_RAW (file => v_out_file, buffer => v_buffer, autoflush => true);
					UTL_FILE.FFLUSH(file => v_out_file);
				ELSE
					v_buffer_size := 32767;
					v_amount := v_buffer_size;
				End If;


				WHILE v_amount >= v_buffer_size and v_buffer_size > 0
				LOOP
				  DBMS_LOB.READ(
					  lob_loc    => doc_rec.File_Content,
					  amount     => v_amount,
					  offset     => v_offset,
					  buffer     => v_buffer);

					v_offset := v_offset + v_amount;

					UTL_FILE.PUT_RAW (
						file      => v_out_file,
						buffer    => v_buffer,
						autoflush => true);

					UTL_FILE.FFLUSH(file => v_out_file);

					v_chunksize := v_chunksize - v_buffer_size;
					if v_chunksize < 32760 Then
						v_buffer_size := v_chunksize;
					end if;
				-- +-------------------------------------------------------------+
				-- | HEY WAIT, THIS IS A BINARY FILE! WHAT IS THIS NEW_LINE      |
				-- | PROCEDURE DOING HERE? THIS WAS A TEST I WAS PERFORMING TO   |
				-- | CONFIRM A BUG (bug#: 2883782). IN 9i THERE IS CURRENTLY A   |
				-- | RESTRICTION OF A MAXIMUM OF 32K THAT CAN BE WRITTEN WITH    |
				-- | PUT_RAW UNLESS YOU INSERT NEW LINE CHARACTERS IN BETWEEN    |
				-- | THE DATA. IN 10i THERE IS A NEW BINARY MODE. WHEN FILES ARE |
				-- | OPENED WITH THIS MODE ANY AMOUNT OF RAW DATA CAN BE WRITTEN |
				-- | WITHOUT THE NEED FOR NEW LINES. IN SHORT, THIS IS A BUG     |
				-- | THAT, IF IT CREEPS UP IN ORACLE9i, THERE IS NO SOLUTION!    |
				-- +-------------------------------------------------------------+
				-- UTL_FILE.NEW_LINE(file => v_out_file);

				END LOOP;
				UTL_FILE.FCLOSE(v_out_file);
      End if;
			EXCEPTION
				WHEN OTHERS THEN
				-- Close the file if something goes wrong.
					IF UTL_FILE.is_open(v_out_file) THEN
						UTL_FILE.fclose(v_out_file);
					END IF;
					RAISE;
			End;
			-- +-------------------------------------------------------------+
			-- | CLOSING THE LOB IS MANDATORY IF YOU HAVE OPENED IT          |
			-- +-------------------------------------------------------------+
			--DBMS_LOB.CLOSE(doc_rec.File_Content);
		End Loop;
    End;
	returnMsg := returnMsg || '; END TIME:' || To_Char(Current_TimeStamp, 'mm/dd/yyyy HH:MI:SS AM') || '; Total ' || TO_CHAR(n) || ' Files were loaded.' ;
	OPEN cur_OUT For
	Select returnMsg from dual;
END Write_BLOB_TO_FILE;
